ActionSheet.action_sheet:
import 'package:flutter/material.dart';

import '../../util/context_extension.dart';
import '../popup/td_popup_route.dart';
import 'td_action_sheet.dart';
import 'td_action_sheet_grid.dart';
import 'td_action_sheet_group.dart';
import 'td_action_sheet_list.dart';

export 'td_action_sheet_item.dart';

typedef TDActionSheetItemCallback = void Function(TDActionSheetItem item, int index);

enum TDActionSheetTheme { list, grid, group }

enum TDActionSheetAlign { center, left, right }

/// 动作面板
class TDActionSheet {
  TDActionSheet(
    this.context, {
    this.align = TDActionSheetAlign.center,
    this.cancelText,
    this.count = 8,
    this.rows = 2,
    this.itemHeight = 96.0,
    this.itemMinWidth = 80.0,
    this.description,
    required this.items,
    this.showCancel = true,
    this.showPagination = false,
    this.scrollable = false,
    this.theme = TDActionSheetTheme.list,
    this.visible = false,
    this.onCancel,
    this.onClose,
    this.onSelected,
    this.showOverlay = true,
    this.closeOnOverlayClick = true,
    this.useSafeArea = true,
  }) {
    if (visible) {
      show();
    }
  }

  /// 上下文
  final BuildContext context;

  /// 对齐方式
  final TDActionSheetAlign align;

  /// 取消按钮的文本
  final String? cancelText;

  /// 每页显示的项目数
  /// 当[theme]等于[TDActionSheetTheme.grid]且[showPagination]为true时有效
  final int count;

  /// 显示的行数
  /// 当[theme]等于[TDActionSheetTheme.grid]时有效
  final int rows;

  /// 项目的行高
  /// 当[theme]等于[TDActionSheetTheme.grid]或[theme]等于[TDActionSheetTheme.group]时有效
  final double itemHeight;

  /// 项目的最小宽度
  /// 当[theme]等于[TDActionSheetTheme.grid]且[scrollable]为true时有效
  /// 或当[theme]等于[TDActionSheetTheme.group]时有效
  final double itemMinWidth;

  /// 描述文本
  /// 当[theme]等于[TDActionSheetTheme.grid]或[theme]等于[TDActionSheetTheme.list]时有效
  final String? description;

  /// ActionSheet的项目列表
  final List<TDActionSheetItem> items;

  /// 是否显示取消按钮
  final bool showCancel;

  /// 是否显示遮罩层
  final bool showOverlay;

  /// 点击蒙层时是否关闭
  final bool closeOnOverlayClick;

  /// 主题样式
  final TDActionSheetTheme theme;

  /// 是否立即显示
  final bool visible;

  /// 是否显示分页
  /// 当[theme]等于[TDActionSheetTheme.grid]时有效
  final bool showPagination;

  /// 是否可以横向滚动
  /// 当[theme]等于[TDActionSheetTheme.grid]且[showPagination]为false时有效
  final bool scrollable;

  /// 取消按钮的回调函数
  final VoidCallback? onCancel;

  /// 关闭时的回调函数
  final VoidCallback? onClose;

  /// 选择项目时的回调函数
  final TDActionSheetItemCallback? onSelected;

  /// 使用安全区域
  final bool useSafeArea;

  static TDSlidePopupRoute? _actionSheetRoute;

  /// 显示列表类型面板
  static void showListActionSheet(
    BuildContext context, {
    required List<TDActionSheetItem> items,
    TDActionSheetAlign align = TDActionSheetAlign.center,
    String? cancelText,
    bool showCancel = true,
    VoidCallback? onCancel,
    TDActionSheetItemCallback? onSelected,
    bool showOverlay = true,
    bool closeOnOverlayClick = true,
    VoidCallback? onClose,
    bool useSafeArea = true,
  }) {
    _createRoute(
      context,
      theme: TDActionSheetTheme.list,
      items: items,
      align: align,
      cancelText: cancelText,
      showCancel: showCancel,
      onSelected: onSelected,
      onCancel: onCancel,
      showOverlay: showOverlay,
      closeOnOverlayClick: closeOnOverlayClick,
      onClose: onClose,
      useSafeArea: useSafeArea,
    );
  }

  /// 显示宫格类型面板
  static void showGridActionSheet(
    BuildContext context, {
    required List<TDActionSheetItem> items,
    TDActionSheetAlign align = TDActionSheetAlign.center,
    String? cancelText,
    bool showCancel = true,
    TDActionSheetItemCallback? onSelected,
    bool showOverlay = true,
    bool closeOnOverlayClick = true,
    int count = 8,
    int rows = 2,
    double itemHeight = 96.0,
    double itemMinWidth = 80.0,
    bool scrollable = false,
    bool showPagination = false,
    VoidCallback? onCancel,
    String? description,
    VoidCallback? onClose,
    bool useSafeArea = true,
  }) {
    _createRoute(
      context,
      theme: TDActionSheetTheme.grid,
      items: items,
      align: align,
      cancelText: cancelText,
      showCancel: showCancel,
      onSelected: onSelected,
      onCancel: onCancel,
      showOverlay: showOverlay,
      closeOnOverlayClick: closeOnOverlayClick,
      count: count,
      rows: rows,
      itemHeight: itemHeight,
      itemMinWidth: itemMinWidth,
      scrollable: scrollable,
      showPagination: showPagination,
      description: description,
      onClose: onClose,
      useSafeArea: useSafeArea,
    );
  }

  /// 显示分组类型面板
  static void showGroupActionSheet(
    BuildContext context, {
    required List<TDActionSheetItem> items,
    TDActionSheetAlign align = TDActionSheetAlign.left,
    String? cancelText,
    bool showCancel = true,
    TDActionSheetItemCallback? onSelected,
    bool showOverlay = true,
    bool closeOnOverlayClick = true,
    double itemHeight = 96.0,
    double itemMinWidth = 80.0,
    VoidCallback? onCancel,
    VoidCallback? onClose,
    bool useSafeArea = true,
  }) {
    _createRoute(
      context,
      theme: TDActionSheetTheme.group,
      items: items,
      align: align,
      cancelText: cancelText,
      showCancel: showCancel,
      onSelected: onSelected,
      onCancel: onCancel,
      showOverlay: showOverlay,
      closeOnOverlayClick: closeOnOverlayClick,
      itemHeight: itemHeight,
      itemMinWidth: itemMinWidth,
      onClose: onClose,
      useSafeArea: useSafeArea,
    );
  }

  /// 显示动作面板
  void show() {
    TDActionSheet._createRoute(
      context,
      theme: theme,
      items: items,
      align: align,
      cancelText: cancelText,
      showCancel: showCancel,
      onSelected: onSelected,
      onCancel: onCancel,
      showOverlay: showOverlay,
      closeOnOverlayClick: closeOnOverlayClick,
      count: count,
      rows: rows,
      itemHeight: itemHeight,
      itemMinWidth: itemMinWidth,
      scrollable: scrollable,
      showPagination: showPagination,
      description: description,
      onClose: onClose,
      useSafeArea: useSafeArea,
    );
  }

  void open() {
    show();
  }

  @mustCallSuper
  void close() {
    if (_actionSheetRoute != null) {
      Navigator.of(context).pop();
    }
  }

  /// 创建路由
  static void _createRoute(
    BuildContext context, {
    required TDActionSheetTheme theme,
    required List<TDActionSheetItem> items,
    TDActionSheetAlign align = TDActionSheetAlign.center,
    String? cancelText,
    bool showCancel = true,
    TDActionSheetItemCallback? onSelected,
    bool showOverlay = true,
    bool closeOnOverlayClick = true,
    int count = 8,
    int rows = 2,
    double itemHeight = 96.0,
    double itemMinWidth = 80.0,
    bool scrollable = false,
    bool showPagination = false,
    VoidCallback? onCancel,
    String? description,
    VoidCallback? onClose,
    bool useSafeArea = true,
  }) {
    if (_actionSheetRoute != null) {
      return;
    }

    cancelText = cancelText ?? context.resource.cancel;

    _actionSheetRoute = TDSlidePopupRoute(
      slideTransitionFrom: SlideTransitionFrom.bottom,
      isDismissible: showOverlay ? closeOnOverlayClick : false,
      modalBarrierColor: showOverlay ? null : Colors.transparent,
      builder: (context) {
        switch (theme) {
          case TDActionSheetTheme.list:
            return TDActionSheetList(
              items: items,
              align: align,
              cancelText: cancelText,
              description: description,
              showCancel: showCancel,
              onCancel: onCancel,
              onSelected: onSelected,
              useSafeArea: useSafeArea,
            );
          case TDActionSheetTheme.grid:
            return TDActionSheetGrid(
              items: items,
              align: align,
              onSelected: onSelected,
              showCancel: showCancel,
              showPagination: showPagination,
              scrollable: scrollable,
              cancelText: cancelText,
              description: description,
              count: count,
              rows: rows,
              onCancel: onCancel,
              itemHeight: itemHeight,
              itemMinWidth: itemMinWidth,
              useSafeArea: useSafeArea,
            );
          case TDActionSheetTheme.group:
            return TDActionSheetGroup(
              items: items,
              align: align,
              cancelText: cancelText,
              showCancel: showCancel,
              onCancel: onCancel,
              onSelected: onSelected,
              itemHeight: itemHeight,
              itemMinWidth: itemMinWidth,
              useSafeArea: useSafeArea,
            );
          default:
            return const SizedBox.shrink();
        }
      },
    );
    Navigator.of(context).push(_actionSheetRoute!).then((_) {
      _actionSheetRoute = null;
      onClose?.call();
    });
  }
}

ActionSheet.action_sheet_grid:
import 'package:flutter/material.dart';
import '../../theme/td_colors.dart';
import '../../theme/td_fonts.dart';
import '../../theme/td_radius.dart';
import '../../theme/td_spacers.dart';
import '../../theme/td_theme.dart';
import '../../util/iterable_ext.dart';
import '../../util/list_ext.dart';
import '../badge/td_badge.dart';
import '../text/td_text.dart';
import 'td_action_sheet.dart';
import 'td_action_sheet_item_widget.dart';

class TDActionSheetGrid extends StatefulWidget {
  final List<TDActionSheetItem> items;
  final String? description;
  final TDActionSheetAlign align;
  final int count;
  final int rows;
  final String? cancelText;
  final bool showCancel;
  final bool showPagination;
  final bool scrollable;
  final VoidCallback? onCancel;
  final TDActionSheetItemCallback? onSelected;
  final double itemHeight;
  final double itemMinWidth;
  final bool useSafeArea;

  const TDActionSheetGrid({
    super.key,
    required this.items,
    this.description,
    this.align = TDActionSheetAlign.center,
    this.count = 8,
    this.rows = 2,
    this.cancelText,
    this.showCancel = true,
    this.showPagination = false,
    this.scrollable = false,
    this.onCancel,
    this.onSelected,
    this.itemHeight = 96.0,
    this.itemMinWidth = 80.0,
    this.useSafeArea = true,
  });

  @override
  _TDActionSheetGridState createState() => _TDActionSheetGridState();
}

class _TDActionSheetGridState extends State<TDActionSheetGrid> {
  int currentPage = 0;

  @override
  Widget build(BuildContext context) {
    final borderRadius = Radius.circular(TDTheme.of(context).radiusExtraLarge);
    return Container(
      decoration: BoxDecoration(
        borderRadius:
            BorderRadius.only(topLeft: borderRadius, topRight: borderRadius),
        color: TDTheme.of(context).bgColorContainer,
      ),
      clipBehavior: Clip.antiAlias,
      padding: widget.useSafeArea
          ? EdgeInsets.only(bottom: MediaQuery.of(context).padding.bottom)
          : EdgeInsets.zero,
      child: Column(
        mainAxisSize: MainAxisSize.min,
        children: [
          SizedBox(height: TDTheme.of(context).spacer8),
          if (widget.description != null) _buildDescription(context),
          if (widget.showPagination) ...[
            _buildPaginationGrid(context),
            _buildPaginationDots(context),
            // 横向滚动
          ] else if (widget.scrollable)
            _buildScrollGrid(context)
          else
            _buildGrid(context),
          if (widget.showCancel)
            buildCancelButton(
              context,
              widget.showPagination,
              widget.cancelText,
              widget.onCancel,
            ),
        ],
      ),
    );
  }

  Widget _buildDescription(BuildContext context) {
    return Padding(
      padding: EdgeInsets.only(
        left: TDTheme.of(context).spacer16,
        right: TDTheme.of(context).spacer16,
        top: TDTheme.of(context).spacer4,
      ),
      child: Row(
        mainAxisAlignment: getMainAxisAlignment(widget.align),
        children: [
          TDText(
            widget.description!,
            font: TDTheme.of(context).fontBodyMedium,
            textColor: TDTheme.of(context).textColorPlaceholder,
          ),
        ],
      ),
    );
  }

  Widget _gridWrap(Widget child) {
    return SizedBox(
      height: widget.rows * widget.itemHeight,
      child: child,
    );
  }

  Widget _buildPaginationGrid(BuildContext context) {
    return _gridWrap(
      PageView.builder(
        itemCount: (widget.items.length / widget.count).ceil(),
        // 当页面改变时更新当前页码
        onPageChanged: widget.showPagination
            ? (index) {
                setState(() {
                  currentPage = index;
                });
              }
            : null,
        itemBuilder: (context, pageIndex) {
          // 获取当前页面的项目
          final pageItems = widget.items
              .skip(pageIndex * widget.count)
              .take(widget.count)
              .toList();
          return _buildGrid(context, items: pageItems, pageIndex: pageIndex);
        },
      ),
    );
  }

  Widget _buildScrollGrid(BuildContext context) {
    final chunks =
        widget.items.chunk((widget.items.length / widget.rows).ceil());
    final itemCount = chunks[0].length;
    return _gridWrap(
      ListView.builder(
        itemCount: itemCount,
        padding: EdgeInsets.zero,
        scrollDirection: Axis.horizontal,
        itemBuilder: (context, col) {
          return Column(
            children: List.generate(chunks.length, (row) {
              final index = itemCount * row + col;
              return SizedBox(
                width: widget.itemMinWidth,
                height: widget.itemHeight,
                child: TDActionSheetItemWidget(
                  item: chunks[row].getOrNull(col),
                  index: index,
                  onSelected: widget.onSelected,
                ),
              );
            }),
          );
        },
      ),
    );
  }

  Widget _buildGrid(
    BuildContext context, {
    List<TDActionSheetItem>? items,
    int pageIndex = 0,
  }) {
    // 计算每行的项目数
    final itemsPerRow = widget.count ~/ widget.rows;
    // 获取屏幕宽度
    final screenWidth = MediaQuery.of(context).size.width;
    // 计算子项的宽高比
    final childAspectRatio = screenWidth / itemsPerRow / widget.itemHeight;
    return _gridWrap(
      GridView.builder(
        physics: const NeverScrollableScrollPhysics(),
        padding: EdgeInsets.zero,
        itemCount: (items ?? widget.items).length,
        gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
          crossAxisCount: itemsPerRow,
          childAspectRatio: childAspectRatio,
        ),
        itemBuilder: (context, index) {
          final item = (items ?? widget.items)[index];
          return TDActionSheetItemWidget(
            item: item,
            index: pageIndex * widget.count + index,
            onSelected: widget.onSelected,
          );
        },
      ),
    );
  }

  Widget _buildPaginationDots(BuildContext context) {
    return Row(
      mainAxisAlignment: MainAxisAlignment.center,
      children:
          List.generate((widget.items.length / widget.count).ceil(), (index) {
        return Container(
          margin: EdgeInsets.symmetric(horizontal: TDTheme.of(context).spacer4),
          width: 8.0,
          height: 8.0,
          decoration: BoxDecoration(
            shape: BoxShape.circle,
            color: currentPage == index
                ? TDTheme.of(context).brandNormalColor
                : TDTheme.of(context).bgColorSecondaryContainerActive,
          ),
        );
      }),
    );
  }
}

ActionSheet.action_sheet_group:
import 'package:collection/collection.dart';
import 'package:flutter/widgets.dart';

import '../../theme/td_colors.dart';
import '../../theme/td_fonts.dart';
import '../../theme/td_radius.dart';
import '../../theme/td_spacers.dart';
import '../../theme/td_theme.dart';
import '../../util/list_ext.dart';
import '../text/td_text.dart';
import 'td_action_sheet.dart';
import 'td_action_sheet_item_widget.dart';

class TDActionSheetGroup extends StatelessWidget {
  final List<TDActionSheetItem> items;
  final TDActionSheetAlign align;
  final String? cancelText;
  final bool showCancel;
  final VoidCallback? onCancel;
  final TDActionSheetItemCallback? onSelected;
  final double itemHeight;
  final double itemMinWidth;
  final bool useSafeArea;

  const TDActionSheetGroup({
    super.key,
    required this.items,
    this.align = TDActionSheetAlign.left,
    this.cancelText,
    this.showCancel = true,
    this.onCancel,
    this.onSelected,
    this.itemHeight = 96.0,
    this.itemMinWidth = 80.0,
    this.useSafeArea = true,
  });

  @override
  Widget build(BuildContext context) {
    final borderRadius = Radius.circular(TDTheme.of(context).radiusExtraLarge);
    final groupItems = items.groupBy((item) => item.group);
    final groupKeys = groupItems.keys
        .where((k) => k != null && groupItems[k]?.isNotEmpty == true);

    return Container(
      decoration: BoxDecoration(
        borderRadius:
            BorderRadius.only(topLeft: borderRadius, topRight: borderRadius),
        color: TDTheme.of(context).bgColorContainer,
      ),
      clipBehavior: Clip.antiAlias,
      padding: useSafeArea
          ? EdgeInsets.only(bottom: MediaQuery.of(context).padding.bottom)
          : EdgeInsets.zero,
      child: Column(
        mainAxisSize: MainAxisSize.min,
        children: [
          ...groupKeys.mapIndexed((i, k) {
            final list = groupItems[k]!;
            return Column(
              mainAxisSize: MainAxisSize.min,
              children: [
                Padding(
                  padding: EdgeInsets.fromLTRB(
                    TDTheme.of(context).spacer16,
                    TDTheme.of(context).spacer12,
                    TDTheme.of(context).spacer16,
                    0,
                  ),
                  child: Row(
                    mainAxisAlignment: getMainAxisAlignment(align),
                    children: [
                      TDText(
                        k!,
                        font: TDTheme.of(context).fontBodyMedium,
                        textColor: TDTheme.of(context).textColorPlaceholder,
                      ),
                    ],
                  ),
                ),
                SizedBox(
                  height: itemHeight,
                  child: ListView.builder(
                    itemCount: list.length,
                    padding: EdgeInsets.zero,
                    scrollDirection: Axis.horizontal,
                    itemBuilder: (context, row) {
                      return SizedBox(
                        width: itemMinWidth,
                        child: TDActionSheetItemWidget(
                          item: list[row],
                          onSelected: onSelected,
                          index: items.indexOf(list[row]),
                        ),
                      );
                    },
                  ),
                ),
                if (i != groupKeys.length - 1)
                  Container(
                    decoration: BoxDecoration(
                      color: TDTheme.of(context).fontWhColor1,
                      border: Border(
                        top: BorderSide(
                          color: TDTheme.of(context).componentStrokeColor,
                          width: 0.5,
                        ),
                      ),
                    ),
                  ),
              ],
            );
          }),
          if (showCancel)
            buildCancelButton(context, false, cancelText, onCancel),
        ],
      ),
    );
  }
}

ActionSheet.action_sheet_item:
import 'package:flutter/material.dart';

import '../badge/td_badge.dart';
import 'td_action_sheet.dart';

/// 动作面板项目
class TDActionSheetItem {
  TDActionSheetItem({
    required this.label,
    this.textStyle,
    this.icon,
    this.badge,
    this.disabled = false,
    this.iconSize,
    this.group,
  });

  /// 标题
  final String label;

  /// 标题样式
  final TextStyle? textStyle;

  /// 图标
  final Widget? icon;

  /// 角标
  final TDBadge? badge;

  /// 是否禁用
  final bool disabled;

  /// 图标大小
  final double? iconSize;

  /// 分组，用于带描述多行滚动宫格
  /// 当[TDActionSheet.theme]等于[TDActionSheetTheme.group]时有效
  /// 有效时，如果该值未配置整个[TDActionSheetItem]会被忽略，即不会展示
  final String? group;
}

ActionSheet.action_sheet_item_widget:
import 'package:flutter/material.dart';
import '../../theme/td_colors.dart';
import '../../theme/td_fonts.dart';
import '../../theme/td_spacers.dart';
import '../../theme/td_theme.dart';
import '../../util/context_extension.dart';
import '../badge/td_badge.dart';
import '../text/td_text.dart';
import 'td_action_sheet.dart';

class TDActionSheetItemWidget extends StatelessWidget {
  const TDActionSheetItemWidget({
    super.key,
    this.item,
    required this.index,
    this.onSelected,
  });

  final TDActionSheetItem? item;
  final int index;
  final TDActionSheetItemCallback? onSelected;

  @override
  Widget build(BuildContext context) {
    if (item == null) {
      return const SizedBox.shrink();
    }
    late ValueNotifier<List<double>> _offsetValue;
    late GlobalKey _offsetKey;
    if (item!.badge != null) {
      _offsetValue = ValueNotifier(const [0.0, 0.0]);
      _offsetKey = GlobalKey();
    }
    return GestureDetector(
      onTap: item!.disabled
          ? null
          : () {
              onSelected?.call(item!, index);
              Navigator.maybePop(context);
            },
      child: Column(
        mainAxisAlignment: MainAxisAlignment.center,
        children: [
          if (item!.icon != null) ...[
            Stack(
              clipBehavior: Clip.none,
              children: [
                SizedBox(
                  width: item!.iconSize ?? 40,
                  height: item!.iconSize ?? 40,
                  child: FittedBox(
                    fit: BoxFit.contain,
                    child: item!.icon!,
                  ),
                ),
                if (item!.badge != null)
                  ValueListenableBuilder(
                    valueListenable: _offsetValue,
                    builder: (context, value, child) {
                      _setOffsetValue(_offsetKey, _offsetValue);
                      return Positioned(
                        key: _offsetKey,
                        child: item!.badge!,
                        right: value[0],
                        top: value[1],
                      );
                    },
                  ),
              ],
            ),
            SizedBox(height: TDTheme.of(context).spacer8),
          ],
          TDText(
            item!.label,
            font: TDTheme.of(context).fontBodySmall,
            textColor: TDTheme.of(context).textColorPrimary,
            style: item!.textStyle,
          ),
        ],
      ),
    );
  }

  void _setOffsetValue(GlobalKey<State<StatefulWidget>> offsetKey,
      ValueNotifier<List<double>> offsetValue) {
    WidgetsBinding.instance.addPostFrameCallback((timeStamp) {
      final renderBox =
          offsetKey.currentContext?.findRenderObject() as RenderBox;
      final size = renderBox.size;
      final right = -size.width / 2;
      final top = -size.height / 2;
      if (offsetValue.value[0] != right || offsetValue.value[1] != top) {
        offsetValue.value = [right, top];
      }
    });
  }
}

/// 获取主轴对齐方式
MainAxisAlignment getMainAxisAlignment(TDActionSheetAlign align) {
  switch (align) {
    case TDActionSheetAlign.left:
      return MainAxisAlignment.start;
    case TDActionSheetAlign.right:
      return MainAxisAlignment.end;
    case TDActionSheetAlign.center:
    default:
      return MainAxisAlignment.center;
  }
}

Widget buildCancelButton(
  BuildContext context,
  bool showPagination,
  String? cancelText,
  VoidCallback? onCancel,
) {
  return Padding(
    padding: EdgeInsets.only(
        top: showPagination
            ? TDTheme.of(context).spacer16
            : TDTheme.of(context).spacer8),
    child: GestureDetector(
      onTap: () {
        onCancel?.call();
        Navigator.maybePop(context);
      },
      child: Container(
        decoration: BoxDecoration(
          color: TDTheme.of(context).bgColorContainer,
          border: Border(
            top: BorderSide(
              color: TDTheme.of(context).componentStrokeColor,
              width: 0.5,
            ),
          ),
        ),
        height: 48,
        child: Center(
          child: TDText(
            cancelText ?? context.resource.cancel,
            font: TDTheme.of(context).fontBodyLarge,
            textColor: TDTheme.of(context).textColorPrimary,
          ),
        ),
      ),
    ),
  );
}

ActionSheet.action_sheet_list:
import 'package:flutter/material.dart';
import '../../theme/td_colors.dart';
import '../../theme/td_fonts.dart';
import '../../theme/td_radius.dart';
import '../../theme/td_spacers.dart';
import '../../theme/td_theme.dart';
import '../../util/context_extension.dart';
import '../badge/td_badge.dart';
import '../text/td_text.dart';
import 'td_action_sheet.dart';
import 'td_action_sheet_item_widget.dart';

class TDActionSheetList extends StatelessWidget {
  final List<TDActionSheetItem> items;
  final TDActionSheetAlign align;
  final String? cancelText;
  final String? description;
  final bool showCancel;
  final VoidCallback? onCancel;
  final TDActionSheetItemCallback? onSelected;
  final bool useSafeArea;

  const TDActionSheetList({
    super.key,
    required this.items,
    this.align = TDActionSheetAlign.center,
    this.cancelText,
    this.description,
    this.showCancel = true,
    this.onCancel,
    this.onSelected,
    this.useSafeArea = true,
  });

  @override
  Widget build(BuildContext context) {
    final borderRadius = Radius.circular(TDTheme.of(context).radiusExtraLarge);
    return Container(
      decoration: BoxDecoration(
        borderRadius:
            BorderRadius.only(topLeft: borderRadius, topRight: borderRadius),
        color: TDTheme.of(context).bgColorPage,
      ),
      clipBehavior: Clip.antiAlias,
      child: Column(
        mainAxisSize: MainAxisSize.min,
        children: [
          if (description != null) _buildDescription(context),
          _buildOptionsList(context),
          if (showCancel) _buildCancelButton(context),
        ],
      ),
    );
  }

  /// 构建描述文本
  Widget _buildDescription(BuildContext context) {
    return Container(
      padding: EdgeInsets.symmetric(
        horizontal: TDTheme.of(context).spacer16,
        vertical: TDTheme.of(context).spacer12,
      ),
      decoration: BoxDecoration(
        color: TDTheme.of(context).bgColorContainer,
        border: Border(
          bottom: BorderSide(
            color: TDTheme.of(context).componentStrokeColor,
            width: 0.5,
          ),
        ),
      ),
      child: Row(
        mainAxisAlignment: getMainAxisAlignment(align),
        children: [
          TDText(
            description!,
            font: TDTheme.of(context).fontBodyMedium,
            textColor: TDTheme.of(context).textColorSecondary,
          ),
        ],
      ),
    );
  }

  /// 构建选项列表
  Widget _buildOptionsList(BuildContext context) {
    return Container(
      color: TDTheme.of(context).bgColorContainer,
      child: ListView.builder(
        shrinkWrap: true,
        physics: const NeverScrollableScrollPhysics(),
        // 禁用滚动
        itemCount: items.length,
        padding: EdgeInsets.zero,
        itemBuilder: (context, index) {
          final item = items[index];
          return GestureDetector(
            onTap: item.disabled
                ? null // 如果项被禁用，则不设置点击事件
                : () {
                    onSelected?.call(item, index); // 触发选中回调
                    Navigator.maybePop(context); // 关闭当前页面
                  },
            child: Container(
              height: 56,
              padding: EdgeInsets.symmetric(
                  horizontal: TDTheme.of(context).spacer16),
              decoration: BoxDecoration(
                border: Border(
                  bottom: BorderSide(
                    color: TDTheme.of(context).componentStrokeColor,
                    width: 0.5,
                  ),
                ),
              ),
              child: Row(
                mainAxisAlignment: getMainAxisAlignment(align),
                children: [
                  if (item.icon != null) ...[
                    IconTheme(
                      data: IconThemeData(
                        color: item.disabled
                            // 禁用状态下的图标颜色
                            ? TDTheme.of(context).textDisabledColor
                            : (item.textStyle?.color ??
                                // 正常状态下的图标颜色
                                TDTheme.of(context).textColorPrimary),
                        size: item.textStyle?.fontSize,
                      ),
                      child: SizedBox(
                        width: item.iconSize ?? 24,
                        height: item.iconSize ?? 24,
                        child: item.icon!,
                      ),
                    ),
                    SizedBox(width: TDTheme.of(context).spacer8),
                  ],
                  TDText(
                    item.label,
                    font: TDTheme.of(context).fontBodyLarge,
                    textColor: item.disabled
                        ? TDTheme.of(context).textDisabledColor // 禁用状态下的文本颜色
                        : TDTheme.of(context).textColorPrimary, // 正常状态下的文本颜色
                    style: item.textStyle,
                  ),

                  /// todo 徽标应位于右上角，而不是右边紧挨着，请参考宫格徽标实现
                  if (item.badge != null) ...[
                    SizedBox(width: TDTheme.of(context).spacer8),
                    item.badge!,
                  ],
                ],
              ),
            ),
          );
        },
      ),
    );
  }

  /// 构建取消按钮
  Widget _buildCancelButton(BuildContext context) {
    return GestureDetector(
        onTap: () {
          onCancel?.call();
          Navigator.maybePop(context);
        },
        child: Column(
          children: [
            Container(
              color: TDTheme.of(context).bgColorContainer,
              height: 48,
              margin: EdgeInsets.only(top: TDTheme.of(context).spacer8),
              child: Center(
                child: TDText(
                  cancelText ?? context.resource.cancel,
                  font: TDTheme.of(context).fontBodyLarge,
                  textColor: TDTheme.of(context).textColorPrimary,
                ),
              ),
            ),
            useSafeArea
                ? Container(
                    color: TDTheme.of(context).bgColorContainer,
                    height: MediaQuery.of(context).padding.bottom,
                  )
                : const SizedBox.shrink(),
          ],
        ));
  }
}

Avatar.avatar:
import 'package:flutter/material.dart';
import '../../../tdesign_flutter.dart';

enum TDAvatarSize { large, medium, small }

enum TDAvatarType { icon, normal, customText, display, operation }

enum TDAvatarShape { circle, square }

/// 用于头像显示
class TDAvatar extends StatelessWidget {
  const TDAvatar({
    Key? key,
    this.size = TDAvatarSize.medium,
    this.type = TDAvatarType.normal,
    this.shape = TDAvatarShape.circle,
    this.text,
    this.radius,
    this.icon,
    this.avatarUrl,
    this.avatarSize,
    this.avatarDisplayList,
    this.displayText,
    this.onTap,
    this.defaultUrl = '',
    this.avatarDisplayWidget,
    this.avatarDisplayBorder = 2,
    this.avatarDisplayListAsset,
    this.backgroundColor,
    this.fit,
  }) : super(key: key);

  /// 头像地址
  final String? avatarUrl;

  /// 头像尺寸
  final TDAvatarSize size;

  /// 头像类型
  final TDAvatarType type;

  /// 头像形状
  final TDAvatarShape shape;

  /// 自定义文字
  final String? text;

  /// 自定义圆角
  final double? radius;

  /// 自定义头像大小
  final double? avatarSize;

  /// 自定义图标
  final IconData? icon;

  /// 默认图片（本地）
  final String defaultUrl;

  /// 带操作展示的头像列表
  final List<String>? avatarDisplayList;

  /// 带操作展示的头像列表（本地资源）
  final List<String>? avatarDisplayListAsset;

  /// 带操作展示的头像描边宽度
  final double avatarDisplayBorder;

  /// 带操作头像自定义操作Widget
  final Widget? avatarDisplayWidget;

  /// 纯展示类型末尾文字
  final String? displayText;

  /// 操作点击事件
  final Function()? onTap;

  /// 自定义文案时背景色
  final Color? backgroundColor;

  /// 自定义图片对齐方式
  final BoxFit? fit;

  double _getAvatarWidth() {
    double width;
    switch (size) {
      case TDAvatarSize.large:
        width = 64;
        break;
      case TDAvatarSize.medium:
        width = 48;
        break;
      case TDAvatarSize.small:
        width = 40;
        break;
    }
    return avatarSize ?? width;
  }

  Font? _getTextFont(BuildContext context) {
    Font? font;
    switch (size) {
      case TDAvatarSize.large:
        font = TDTheme.of(context).fontTitleExtraLarge;
        break;
      case TDAvatarSize.medium:
        font = TDTheme.of(context).fontTitleMedium;
        break;
      case TDAvatarSize.small:
        font = TDTheme.of(context).fontTitleSmall;
        break;
    }
    return font;
  }

  double _getIconWidth() {
    double width;
    switch (size) {
      case TDAvatarSize.large:
        width = 32;
        break;
      case TDAvatarSize.medium:
        width = 24;
        break;
      case TDAvatarSize.small:
        width = 20;
        break;
    }
    return width;
  }

  double _getAvatarRadius(BuildContext context) {
    double _radius;
    switch (shape) {
      case TDAvatarShape.circle:
        _radius = _getAvatarWidth() / 2;
        break;
      case TDAvatarShape.square:
        _radius = TDTheme.of(context).radiusDefault;
        break;
    }
    return radius ?? _radius;
  }

  @override
  Widget build(BuildContext context) {
    switch (type) {
      case TDAvatarType.icon:
        return GestureDetector(
          child: Container(
            width: _getAvatarWidth(),
            height: _getAvatarWidth(),
            decoration: BoxDecoration(
              color: backgroundColor ?? TDTheme.of(context).brandFocusColor,
              borderRadius: BorderRadius.circular(_getAvatarRadius(context)),
            ),
            child: Center(
                child: Icon(
              icon ?? TDIcons.user,
              size: _getIconWidth(),
              color: TDTheme.of(context).brandNormalColor,
            )),
          ),
          onTap: onTap,
        );
      case TDAvatarType.normal:
        return GestureDetector(
          child: Container(
            width: _getAvatarWidth(),
            height: _getAvatarWidth(),
            decoration: BoxDecoration(
                color: backgroundColor ?? TDTheme.of(context).brandFocusColor,
                borderRadius: BorderRadius.circular(_getAvatarRadius(context)),
                image: avatarUrl != null
                    ? DecorationImage(image: NetworkImage(avatarUrl!))
                    : defaultUrl != ''
                        ? DecorationImage(image: AssetImage(defaultUrl))
                        : null),
          ),
          onTap: onTap,
        );
      case TDAvatarType.customText:
        return GestureDetector(
          child: Container(
            width: _getAvatarWidth(),
            height: _getAvatarWidth(),
            decoration: BoxDecoration(
              color: backgroundColor ?? TDTheme.of(context).brandNormalColor,
              borderRadius: BorderRadius.circular(_getAvatarRadius(context)),
            ),
            child: Center(
              child: TDText(
                text,
                forceVerticalCenter: true,
                textAlign: TextAlign.center,
                font: _getTextFont(context),
                textColor: TDTheme.of(context).whiteColor1,
              ),
            ),
          ),
          onTap: onTap,
        );
      case TDAvatarType.display:
        return buildDisplayAvatar(context);
      case TDAvatarType.operation:
        return buildOperationAvatar(context);
    }
  }

  double _getDisplayPadding() {
    double padding;
    switch (size) {
      case TDAvatarSize.large:
        padding = 10;
        break;
      case TDAvatarSize.medium:
        padding = 8;
        break;
      case TDAvatarSize.small:
        padding = 6;
        break;
    }
    return padding;
  }

  Widget buildOperationAvatar(BuildContext context) {
    var list = <Widget>[];
    if ((avatarDisplayList == null || avatarDisplayList!.isEmpty) &&
        (avatarDisplayListAsset == null || avatarDisplayListAsset!.isEmpty)) {
      return Container();
    }

    var length = 0;

    if (avatarDisplayList != null) {
      length = avatarDisplayList!.length;
      for (var i = 0; i < avatarDisplayList!.length + 1; i++) {
        var left = (_getAvatarWidth() - _getDisplayPadding()) * i;
        if (i == avatarDisplayList!.length) {
          list.add(Positioned(
              left: left,
              child: GestureDetector(
                onTap: onTap,
                child: Container(
                    child: Center(
                      child: Icon(TDIcons.user_add,
                          size: _getIconWidth(),
                          color: TDTheme.of(context).brandNormalColor),
                    ),
                    width: _getAvatarWidth(),
                    height: _getAvatarWidth(),
                    clipBehavior: Clip.hardEdge,
                    decoration: ShapeDecoration(
                      color: TDTheme.of(context).brandFocusColor,
                      shape: RoundedRectangleBorder(
                          borderRadius: BorderRadius.circular(
                              _getAvatarWidth() - _getDisplayPadding()),
                          side: BorderSide(
                              color: Colors.transparent,
                              width: avatarDisplayBorder)),
                    )),
              )));
        } else {
          list.add(Positioned(
              left: left,
              child: Container(
                  width: _getAvatarWidth(),
                  height: _getAvatarWidth(),
                  clipBehavior: Clip.antiAlias,
                  decoration: ShapeDecoration(
                      shape: RoundedRectangleBorder(
                          borderRadius: BorderRadius.circular(
                              _getAvatarWidth() - _getDisplayPadding()),
                          side: BorderSide(
                              color: TDTheme.of(context).bgColorContainer,
                              width: avatarDisplayBorder)),
                      image: DecorationImage(
                          image: NetworkImage(avatarDisplayList![i]),
                          fit: fit ?? BoxFit.cover)))));
        }
      }
    } else if (avatarDisplayListAsset != null) {
      length = avatarDisplayListAsset!.length;
      for (var i = 0; i < avatarDisplayListAsset!.length + 1; i++) {
        var left = (_getAvatarWidth() - _getDisplayPadding()) * i;
        if (i == avatarDisplayListAsset!.length) {
          list.add(Positioned(
              left: left,
              child: GestureDetector(
                onTap: onTap,
                child: Container(
                    child: Center(
                      child: avatarDisplayWidget ??
                          Icon(TDIcons.user_add,
                              size: _getIconWidth(),
                              color: TDTheme.of(context).brandNormalColor),
                    ),
                    width: _getAvatarWidth(),
                    height: _getAvatarWidth(),
                    clipBehavior: Clip.hardEdge,
                    decoration: ShapeDecoration(
                      color: TDTheme.of(context).brandFocusColor,
                      shape: RoundedRectangleBorder(
                          borderRadius: BorderRadius.circular(
                              _getAvatarWidth() - _getDisplayPadding()),
                          side: BorderSide(
                              color: TDTheme.of(context).bgColorContainer,
                              width: avatarDisplayBorder)),
                    )),
              )));
        } else {
          list.add(Positioned(
              left: left,
              child: Container(
                  width: _getAvatarWidth(),
                  height: _getAvatarWidth(),
                  clipBehavior: Clip.antiAlias,
                  decoration: ShapeDecoration(
                      shape: RoundedRectangleBorder(
                          borderRadius: BorderRadius.circular(
                              _getAvatarWidth() - _getDisplayPadding()),
                          side: BorderSide(
                              color: TDTheme.of(context).bgColorContainer,
                              width: avatarDisplayBorder)),
                      image: DecorationImage(
                          image: AssetImage(avatarDisplayListAsset![i]),
                          fit: fit ?? BoxFit.fill)))));
        }
      }
    }

    return SizedBox(
      height: _getAvatarWidth(),
      width: _getAvatarWidth() * (length + 1) - length * _getDisplayPadding(),
      child: Stack(children: list),
    );
  }

  Widget buildDisplayAvatar(BuildContext context) {
    var list = <Widget>[];
    if ((avatarDisplayList == null || avatarDisplayList!.isEmpty) &&
        (avatarDisplayListAsset == null || avatarDisplayListAsset!.isEmpty)) {
      return Container();
    }

    var length = 0;

    if (avatarDisplayList != null) {
      length = avatarDisplayList!.length;
      for (var i = avatarDisplayList!.length; i >= 0; i--) {
        var left = (_getAvatarWidth() - _getDisplayPadding()) * i;
        if (i == avatarDisplayList!.length) {
          list.add(Positioned(
              left: left,
              child: Container(
                  child: Center(
                    child: TDText(
                      displayText,
                      fontWeight: FontWeight.w600,
                      forceVerticalCenter: true,
                      textAlign: TextAlign.center,
                      font: _getTextFont(context),
                      textColor: TDTheme.of(context).brandNormalColor,
                    ),
                  ),
                  width: _getAvatarWidth(),
                  height: _getAvatarWidth(),
                  clipBehavior: Clip.hardEdge,
                  decoration: ShapeDecoration(
                    color: TDTheme.of(context).brandFocusColor,
                    shape: RoundedRectangleBorder(
                        borderRadius: BorderRadius.circular(
                            _getAvatarWidth() - _getDisplayPadding()),
                        side: BorderSide(
                            color: TDTheme.of(context).bgColorContainer,
                            width: avatarDisplayBorder)),
                  ))));
        } else {
          list.add(Positioned(
              left: left,
              child: Container(
                  width: _getAvatarWidth(),
                  height: _getAvatarWidth(),
                  clipBehavior: Clip.antiAlias,
                  decoration: ShapeDecoration(
                      shape: RoundedRectangleBorder(
                          borderRadius: BorderRadius.circular(
                              _getAvatarWidth() - _getDisplayPadding()),
                          side: BorderSide(
                              color: TDTheme.of(context).bgColorContainer,
                              width: avatarDisplayBorder)),
                      image: DecorationImage(
                          image: NetworkImage(avatarDisplayList![i]),
                          fit: fit ?? BoxFit.cover)))));
        }
      }
    } else if (avatarDisplayListAsset != null) {
      length = avatarDisplayListAsset!.length;
      for (var i = avatarDisplayListAsset!.length; i >= 0; i--) {
        var left = (_getAvatarWidth() - _getDisplayPadding()) * i;
        if (i == avatarDisplayListAsset!.length) {
          list.add(Positioned(
              left: left,
              child: Container(
                  child: Center(
                    child: TDText(
                      displayText,
                      fontWeight: FontWeight.w600,
                      forceVerticalCenter: true,
                      textAlign: TextAlign.center,
                      font: _getTextFont(context),
                      textColor: TDTheme.of(context).brandNormalColor,
                    ),
                  ),
                  width: _getAvatarWidth(),
                  height: _getAvatarWidth(),
                  clipBehavior: Clip.hardEdge,
                  decoration: ShapeDecoration(
                    color: TDTheme.of(context).brandFocusColor,
                    shape: RoundedRectangleBorder(
                        borderRadius: BorderRadius.circular(
                            _getAvatarWidth() - _getDisplayPadding()),
                        side: BorderSide(
                            color: TDTheme.of(context).bgColorContainer,
                            width: avatarDisplayBorder)),
                  ))));
        } else {
          list.add(Positioned(
              left: left,
              child: Container(
                  width: _getAvatarWidth(),
                  height: _getAvatarWidth(),
                  clipBehavior: Clip.antiAlias,
                  decoration: ShapeDecoration(
                      shape: RoundedRectangleBorder(
                          borderRadius: BorderRadius.circular(
                              _getAvatarWidth() - _getDisplayPadding()),
                          side: BorderSide(
                              color: TDTheme.of(context).bgColorContainer,
                              width: avatarDisplayBorder)),
                      image: DecorationImage(
                          image: AssetImage(avatarDisplayListAsset![i]),
                          fit: fit ?? BoxFit.cover)))));
        }
      }
    }

    return SizedBox(
      height: _getAvatarWidth(),
      width: _getAvatarWidth() * (length + 1) - length * _getDisplayPadding(),
      child: Stack(children: list),
    );
  }
}

Backtop.backtop:
import 'package:flutter/material.dart';

import '../../../tdesign_flutter.dart';
import '../../util/context_extension.dart';

enum TDBackTopTheme {
  light, // 明亮主题
  dark // 暗黑主题
}

enum TDBackTopStyle {
  circle, // 圆形
  halfCircle, // 半圆形
}

class TDBackTop extends StatefulWidget {
  const TDBackTop({
    Key? key,
    this.controller,
    this.theme = TDBackTopTheme.light,
    this.style = TDBackTopStyle.circle,
    this.showText = false,
    this.onClick,
  }) : super(key: key);

  /// 页面滚动的控制器
  final ScrollController? controller;

  /// 主题
  final TDBackTopTheme theme;

  /// 样式，圆形和半圆
  final TDBackTopStyle style;

  /// 是否展示文字
  final bool showText;

  /// 按钮点击事件
  final VoidCallback? onClick;

  @override
  State<TDBackTop> createState() => _TDBackTopState();
}

class _TDBackTopState extends State<TDBackTop> {
  bool _isAnimating = false;

  late final Color _bgColor;
  late final Color _borderColor;
  late final Color _fontColor;

  @override
  void initState() {
    super.initState();
    _initColors();
  }

  void _initColors() {
    final theme = TDTheme.of(context);
    _bgColor = _getColorByTheme(
      lightColor: theme.grayColor1,
      darkColor: theme.grayColor13,
    );
    _borderColor = _getColorByTheme(
      lightColor: theme.grayColor4,
      darkColor: theme.grayColor9,
    );
    _fontColor = _getColorByTheme(
      lightColor: theme.textColorPrimary,
      darkColor: theme.textColorAnti,
    );
  }

  Color _getColorByTheme({
    required Color lightColor,
    required Color darkColor,
  }) {
    return widget.theme == TDBackTopTheme.light ? lightColor : darkColor;
  }

  @override
  Widget build(BuildContext context) {
    return GestureDetector(
      onTap: _handleTap,
      child: widget.style == TDBackTopStyle.circle
          ? _buildCircleWidget(context)
          : _buildHalfCircleWidget(context),
    );
  }

  Future<void> _handleTap() async {
    /// 防抖处理，防止短时间内重复触发
    if (_isAnimating) {
      return;
    }

    if (widget.controller != null && widget.controller!.hasClients) {
      _isAnimating = true;
      await widget.controller!.animateTo(
        0,
        duration: const Duration(milliseconds: 500),
        curve: Curves.easeIn,
      );
      _isAnimating = false;
    }

    widget.onClick?.call();
  }

  Widget _buildCircleWidget(BuildContext context) {
    return Container(
      width: 48,
      height: 48,
      padding: EdgeInsets.symmetric(vertical: widget.showText ? 6 : 13),
      decoration: BoxDecoration(
          borderRadius: BorderRadius.circular(TDTheme.of(context).radiusCircle),
          border: Border.all(color: _borderColor, width: 0.5),
          color: _bgColor),
      child: Center(
          child: Column(
        children: [
          Icon(
            TDIcons.backtop,
            size: 20,
            color: _fontColor,
          ),
          Visibility(
            visible: widget.showText,
            child: TDText(
              context.resource.top,
              maxLines: 1,
              overflow: TextOverflow.visible,
              style: TextStyle(
                  fontSize: 10, color: _fontColor, fontWeight: FontWeight.w600),
            ),
          )
        ],
      )),
    );
  }

  Widget _buildHalfCircleWidget(BuildContext context) {
    return ConstrainedBox(
        constraints: const BoxConstraints(minWidth: 38),
        child: Container(
          height: 40,
          padding: const EdgeInsets.symmetric(horizontal: 8, vertical: 4),
          decoration: BoxDecoration(
              color: _bgColor,
              borderRadius: BorderRadius.only(
                  topLeft: Radius.circular(TDTheme.of(context).radiusCircle),
                  bottomLeft:
                      Radius.circular(TDTheme.of(context).radiusCircle)),
              border: Border.all(color: _borderColor, width: 0.5)),
          child: Row(
            mainAxisSize: MainAxisSize.min,
            crossAxisAlignment: CrossAxisAlignment.center,
            children: [
              Icon(
                TDIcons.backtop,
                size: 22,
                color: _fontColor,
              ),
              const SizedBox(
                width: 2,
              ),
              Visibility(
                visible: widget.showText,
                child: SizedBox(
                    height: 32,
                    child: Column(
                      crossAxisAlignment: CrossAxisAlignment.center,
                      mainAxisAlignment: MainAxisAlignment.center,
                      children: [
                        /// todo 将 “返回”、“顶部” 两个文本拆分开组合，中文语境下可以，但对于其他语言未必可行
                        TDText(
                          context.resource.back,
                          style: TextStyle(
                              height: 1.2,
                              fontSize: 10,
                              color: _fontColor,
                              fontWeight: FontWeight.w600),
                        ),
                        TDText(
                          context.resource.top,
                          style: TextStyle(
                              height: 1.2,
                              fontSize: 10,
                              color: _fontColor,
                              fontWeight: FontWeight.w600),
                        ),
                      ],
                    )),
              )
            ],
          ),
        ));
  }
}

Badge.badge:
import 'dart:math';

import 'package:flutter/cupertino.dart';
import 'package:flutter/material.dart';

import '../../../tdesign_flutter.dart';
import '../../util/context_extension.dart';

enum TDBadgeType {
  /// 红点样式
  redPoint,

  /// 消息样式
  message,

  /// 气泡样式
  bubble,

  /// 方形样式
  square,

  /// 角标样式
  subscript
}

enum TDBadgeBorder {
  /// 大圆角 8px
  large,

  /// 小圆角 2px
  small
}

enum TDBadgeSize {
  /// 宽 20px
  large,

  /// 宽 16px
  small,
}

class TDBadge extends StatefulWidget {
  const TDBadge(
    this.type, {
    Key? key,
    this.count,
    this.maxCount = '99',
    this.border = TDBadgeBorder.large,
    this.size = TDBadgeSize.small,
    this.color,
    this.textColor,
    this.message,
    this.widthLarge = 32,
    this.widthSmall = 12,
    this.padding,
    this.showZero = true,
  }) : super(key: key);

  /// 红点数量
  final String? count;

  /// 最大红点数量
  final String? maxCount;

  /// 红点样式
  final TDBadgeType type;

  /// 红点尺寸
  final TDBadgeSize size;

  /// 红点圆角大小
  final TDBadgeBorder border;

  /// 红点颜色
  final Color? color;

  /// 文字颜色
  final Color? textColor;

  /// 消息内容
  final String? message;

  /// 角标大三角形宽
  final double widthLarge;

  /// 角标小三角形宽
  final double widthSmall;

  /// 角标自定义padding
  final EdgeInsetsGeometry? padding;

  /// 值为0是否显示
  final bool showZero;

  @override
  State<StatefulWidget> createState() => _TDBadgeState();
}

class _TDBadgeState extends State<TDBadge> {
  String badgeNum = '';

  void updateBadgeNum(String? newCount) {
    if (newCount == null) {
      return;
    }
    setState(() {
      // 如果 newCount 超过了 maxCount，则显示 `${maxCount}+`
      final countValue = int.tryParse(newCount) ?? 0;
      final maxCountValue = int.tryParse(widget.maxCount ?? '') ?? 0;
      if (maxCountValue > 0 && countValue > maxCountValue) {
        badgeNum = '${maxCountValue}+';
      } else {
        badgeNum = newCount;
      }
    });
  }

  double getBadgeSize() {
    switch (widget.size) {
      case TDBadgeSize.large:
        return 20;
      case TDBadgeSize.small:
        return 16;
    }
  }

  Font? getBadgeFont(BuildContext context) {
    switch (widget.size) {
      case TDBadgeSize.large:
        return TDTheme.of(context).fontMarkSmall;
      case TDBadgeSize.small:
        return TDTheme.of(context).fontMarkExtraSmall;
    }
  }

  bool get visible {
    final parsedValue = double.tryParse(value);
    return widget.showZero ||
        (parsedValue != null && parsedValue != 0) ||
        parsedValue == null;
  }

  String get value {
    return widget.message ?? widget.count ?? context.resource.badgeZero;
  }

  @override
  void initState() {
    super.initState();
    updateBadgeNum(widget.count);
  }

  @override
  void didUpdateWidget(covariant TDBadge oldWidget) {
    super.didUpdateWidget(oldWidget);
    if (oldWidget.count != widget.count) {
      updateBadgeNum(widget.count);
    }
  }

  @override
  Widget build(BuildContext context) {
    switch (widget.type) {
      case TDBadgeType.redPoint:
        return Container(
          alignment: Alignment.center,
          height: getBadgeSize() / 2,
          width: getBadgeSize() / 2,
          decoration: BoxDecoration(
              color: widget.color ?? TDTheme.of(context).errorNormalColor,
              borderRadius: BorderRadius.circular(getBadgeSize() / 4)),
        );
      case TDBadgeType.message:
        return Visibility(
            visible: visible,
            child: badgeNum.length == 1
                ? Container(
                    height: getBadgeSize(),
                    width: getBadgeSize(),
                    decoration: BoxDecoration(
                      color:
                          widget.color ?? TDTheme.of(context).errorNormalColor,
                      borderRadius: BorderRadius.circular(getBadgeSize() / 2),
                    ),
                    child: Center(
                      child: TDText(
                        widget.message ?? '$badgeNum',
                        forceVerticalCenter: true,
                        font: getBadgeFont(context),
                        fontWeight: FontWeight.w500,
                        textColor: widget.textColor ??
                            TDTheme.of(context).textColorAnti,
                        textAlign: TextAlign.center,
                      ),
                    ))
                : Container(
                    height: getBadgeSize(),
                    padding: const EdgeInsets.only(left: 5, right: 5),
                    decoration: BoxDecoration(
                      color:
                          widget.color ?? TDTheme.of(context).errorNormalColor,
                      borderRadius: BorderRadius.circular(getBadgeSize() / 2),
                    ),
                    child: Center(
                      child: TDText(
                        widget.message ?? '$badgeNum',
                        forceVerticalCenter: true,
                        font: getBadgeFont(context),
                        fontWeight: FontWeight.w500,
                        textColor: widget.textColor ??
                            TDTheme.of(context).textColorAnti,
                        textAlign: TextAlign.center,
                      ),
                    ),
                  ));
      case TDBadgeType.subscript:
        return ClipPath(
          clipper: TrapezoidPath(widget.widthLarge, widget.widthSmall),
          child: Container(
            alignment: Alignment.topRight,
            color: widget.color ?? TDTheme.of(context).errorNormalColor,
            height: 32,
            width: 32,
            child: Transform.rotate(
                angle: pi / 4,
                child: Padding(
                  padding: widget.padding ??
                      const EdgeInsets.only(left: 4, bottom: 8),
                  child: TDText(
                    widget.message ?? '$badgeNum',
                    font: getBadgeFont(context),
                    fontWeight: FontWeight.w500,
                    textColor:
                        widget.textColor ?? TDTheme.of(context).textColorAnti,
                    textAlign: TextAlign.center,
                  ),
                )),
          ),
        );
      case TDBadgeType.bubble:
        return Visibility(
            visible: visible,
            child: Container(
              height: 16,
              padding: const EdgeInsets.only(left: 4, right: 4),
              decoration: BoxDecoration(
                color: widget.color ?? TDTheme.of(context).errorNormalColor,
                borderRadius: const BorderRadius.only(
                    topLeft: Radius.circular(10),
                    topRight: Radius.circular(10),
                    bottomRight: Radius.circular(10),
                    bottomLeft: Radius.circular(1)),
              ),
              child: Center(
                child: TDText(
                  widget.message ?? '$badgeNum',
                  forceVerticalCenter: true,
                  font: getBadgeFont(context),
                  fontWeight: FontWeight.w500,
                  textColor:
                      widget.textColor ?? TDTheme.of(context).textColorAnti,
                  textAlign: TextAlign.center,
                ),
              ),
            ));
      case TDBadgeType.square:
        return Visibility(
            visible: visible,
            child: IntrinsicWidth(
                child: Container(
              height: getBadgeSize(),
              padding: const EdgeInsets.only(left: 5, right: 5),
              decoration: BoxDecoration(
                color: widget.color ?? TDTheme.of(context).errorNormalColor,
                borderRadius: widget.border == TDBadgeBorder.large
                    ? BorderRadius.circular(8)
                    : BorderRadius.circular(2),
              ),
              child: Center(
                child: TDText(
                  widget.message ?? '$badgeNum',
                  forceVerticalCenter: true,
                  font: getBadgeFont(context),
                  fontWeight: FontWeight.w500,
                  textColor:
                      widget.textColor ?? TDTheme.of(context).textColorAnti,
                  textAlign: TextAlign.center,
                ),
              ),
            )));
    }
  }
}

class TrapezoidPath extends CustomClipper<Path> {
  final double widthLarge;
  final double widthSmall;

  TrapezoidPath(this.widthLarge, this.widthSmall);

  @override
  Path getClip(Size size) {
    var path = Path();
    path.moveTo(0, 0);
    path.lineTo(widthLarge - widthSmall, 0);
    path.lineTo(widthLarge, widthSmall);
    path.lineTo(widthLarge, widthLarge);
    path.lineTo(0, 0);
    path.close();
    return path;
  }

  @override
  bool shouldReclip(CustomClipper<Path> oldClipper) {
    return true;
  }
}

