import 'package:flutter/material.dart';

import '../../../tdesign_flutter.dart';

enum TDButtonSize { large, medium, small, extraSmall }

enum TDButtonType { fill, outline, text, ghost }

enum TDButtonShape { rectangle, round, square, circle, filled }

enum TDButtonTheme { defaultTheme, primary, danger, light }

enum TDButtonStatus { defaultState, active, disable }

enum TDButtonIconPosition { left, right }

typedef TDButtonEvent = void Function();

/// TD常规按钮
class TDButton extends StatefulWidget {
  const TDButton({
    Key? key,
    this.text,
    this.size = TDButtonSize.medium,
    this.type = TDButtonType.fill,
    this.shape = TDButtonShape.rectangle,
    this.theme,
    this.child,
    this.disabled = false,
    this.isBlock = false,
    this.style,
    this.activeStyle,
    this.disableStyle,
    this.textStyle,
    this.disableTextStyle,
    this.width,
    this.height,
    this.onTap,
    this.icon,
    this.iconWidget,
    this.iconTextSpacing,
    this.onLongPress,
    this.margin,
    this.padding,
    this.iconPosition = TDButtonIconPosition.left,
    this.gradient,
  }) : super(key: key);

  /// 自控件
  final Widget? child;

  /// 文本内容
  final String? text;

  /// 禁止点击
  final bool disabled;

  /// 自定义宽度
  final double? width;

  /// 自定义高度
  final double? height;

  /// 尺寸
  final TDButtonSize size;

  /// 类型：填充，描边，文字
  final TDButtonType type;

  /// 形状：圆角，胶囊，方形，圆形，填充
  final TDButtonShape shape;

  /// 主题
  final TDButtonTheme? theme;

  /// 自定义样式，有则优先用它，没有则根据 type 和 theme 选取。如果设置了 style，则 activeStyle 和 disableStyle 也应该设置
  final TDButtonStyle? style;

  /// 自定义点击样式，有则优先用它，没有则根据 type 和 theme 选取
  final TDButtonStyle? activeStyle;

  /// 自定义禁用样式，有则优先用它，没有则根据 type 和 theme 选取
  final TDButtonStyle? disableStyle;

  /// 自定义可点击状态文本样式
  final TextStyle? textStyle;

  /// 自定义不可点击状态文本样式
  final TextStyle? disableTextStyle;

  /// 点击事件
  final TDButtonEvent? onTap;

  /// 长按事件
  final TDButtonEvent? onLongPress;

  /// 图标icon
  final IconData? icon;

  /// 自定义图标 icon 控件
  final Widget? iconWidget;

  /// 自定义图标与文本之间距离
  final double? iconTextSpacing;

  /// 图标位置
  final TDButtonIconPosition? iconPosition;

  /// 自定义 padding
  final EdgeInsetsGeometry? padding;

  /// 自定义 margin
  final EdgeInsetsGeometry? margin;

  /// 是否为通栏按钮
  final bool isBlock;

  /// 渐变背景色，优先级高于backgroundColor
  final Gradient? gradient;

  @override
  State<StatefulWidget> createState() => _TDButtonState();
}

class _TDButtonState extends State<TDButton> {
  TDButtonStatus _buttonStatus = TDButtonStatus.defaultState;
  TDButtonStyle? _innerDefaultStyle;
  TDButtonStyle? _innerActiveStyle;
  TDButtonStyle? _innerDisableStyle;
  double? _width;
  double? _height;
  EdgeInsetsGeometry? _margin;
  Alignment? _alignment;
  TextStyle? _textStyle;
  double? _iconSize;

  _updateParams() {
    _buttonStatus =
        widget.disabled ? TDButtonStatus.disable : TDButtonStatus.defaultState;
    _innerDefaultStyle = widget.style;
    _innerActiveStyle = widget.activeStyle;
    _innerDisableStyle = widget.disableStyle;
    _width = _getWidth();
    _height = _getHeight();
    _margin = _getMargin();
    _alignment = widget.shape == TDButtonShape.filled || widget.isBlock
        ? Alignment.center
        : null;
    if (widget.text != null) {
      _textStyle = widget.disabled ? widget.disableTextStyle : widget.textStyle;
    }
    if (widget.icon != null) {
      _iconSize = _getIconSize();
    }
  }

  TDButtonStyle get style {
    switch (_buttonStatus) {
      case TDButtonStatus.defaultState:
        return _defaultStyle;
      case TDButtonStatus.active:
        return _activeStyle;
      case TDButtonStatus.disable:
        return _disableStyle;
    }
  }

  @override
  void initState() {
    super.initState();
    _updateParams();
  }

  @override
  void didChangeDependencies() {
    super.didChangeDependencies();
    _updateParams();
  }


  @override
  Widget build(BuildContext context) {
    Widget display = Container(
      width: _width,
      height: _height,
      alignment: _alignment,
      padding: _getPadding(),
      margin: _margin,
      decoration: BoxDecoration(
        color: widget.gradient != null ? null : style.backgroundColor,
        gradient: widget.gradient,
        border: _getBorder(context),
        borderRadius: style.radius ?? BorderRadius.all(_getRadius()),
      ),
      child: widget.child ?? _getChild(),
    );

    if (widget.disabled) {
      return display;
    }
    return GestureDetector(
      child: display,
      onTap: widget.onTap,
      onLongPress: widget.onLongPress,
      onTapDown: (TapDownDetails details) {
        if (widget.disabled) {
          return;
        }
        setState(() {
          _buttonStatus = TDButtonStatus.active;
        });
      },
      onTapUp: (TapUpDetails details) {
        Future.delayed(const Duration(milliseconds: 100), () {
          if (mounted && !widget.disabled) {
            setState(() {
              _buttonStatus = TDButtonStatus.defaultState;
            });
          }
        });
      },
      onTapCancel: () {
        if (widget.disabled) {
          return;
        }
        setState(() {
          _buttonStatus = TDButtonStatus.defaultState;
        });
      },
    );
  }

  Border? _getBorder(BuildContext context) {
    if (style.frameWidth != null && style.frameWidth != 0) {
      return Border.all(
        color: style.frameColor ?? TDTheme.of(context).componentStrokeColor,
        width: style.frameWidth!,
      );
    }
    return null;
  }

  Widget _getChild() {
    var icon = _getIcon();
    if (widget.text == null && icon == null) {
      return Container();
    }
    var children = <Widget>[];
    // 系统Icon会导致不居中，因此自绘icon指定height
    if (icon != null && widget.iconPosition == TDButtonIconPosition.left) {
      children.add(icon);
    }
    if (widget.text != null) {
      var text = TDText(
        widget.text!,
        font: _getTextFont(),
        textColor: style.textColor ?? TDTheme.of(context).textColorPrimary,
        style: _textStyle,
        forceVerticalCenter: true,
        overflow: TextOverflow.ellipsis,
      );
      children.add(Flexible(child: text));
    }
    if (icon != null && widget.iconPosition == TDButtonIconPosition.right) {
      children.add(icon);
    }

    if (children.length == 2) {
      children.insert(
        1,
        SizedBox(width: widget.iconTextSpacing ?? 8),
      );
    }
    return Row(
      mainAxisSize: MainAxisSize.min,
      mainAxisAlignment: MainAxisAlignment.center,
      children: children,
    );
  }

  Widget? _getIcon() {
    if (widget.iconWidget != null) {
      return widget.iconWidget;
    }
    if (widget.icon != null) {
      return Icon(
        widget.icon,
        size: _iconSize,
        color: style.textColor,
      );
      return RichText(
        overflow: TextOverflow.visible,
        text: TextSpan(
          text: String.fromCharCode(widget.icon!.codePoint),
          style: TextStyle(
            inherit: false,
            color: style.textColor,
            height: 1,
            fontSize: _iconSize,
            fontFamily: widget.icon!.fontFamily,
            package: widget.icon!.fontPackage,
          ),
        ),
      );
    }

    return null;
  }

  Font _getTextFont() {
    switch (widget.size) {
      case TDButtonSize.large:
        return TDTheme.of(context).fontMarkLarge ??
            Font(size: 16, lineHeight: 24);
      case TDButtonSize.medium:
        return TDTheme.of(context).fontMarkLarge ??
            Font(size: 16, lineHeight: 24);
      case TDButtonSize.small:
        return TDTheme.of(context).fontMarkMedium ??
            Font(size: 14, lineHeight: 22);
      case TDButtonSize.extraSmall:
        return TDTheme.of(context).fontMarkMedium ??
            Font(size: 14, lineHeight: 22);
    }
  }

  double? _getWidth() {
    if (widget.width != null) {
      return widget.width;
    }
    if (!widget.isBlock &&
        (widget.shape == TDButtonShape.square ||
            widget.shape == TDButtonShape.circle)) {
      switch (widget.size) {
        case TDButtonSize.large:
          return 48;
        case TDButtonSize.medium:
          return 40;
        case TDButtonSize.small:
          return 32;
        case TDButtonSize.extraSmall:
          return 28;
      }
    }
    return null;
  }

  double _getHeight() {
    if (widget.height != null) {
      return widget.height!;
    }
    switch (widget.size) {
      case TDButtonSize.large:
        return 48;
      case TDButtonSize.medium:
        return 40;
      case TDButtonSize.small:
        return 32;
      case TDButtonSize.extraSmall:
        return 28;
    }
  }

  double _getIconSize() {
    switch (widget.size) {
      case TDButtonSize.large:
        return 24;
      case TDButtonSize.medium:
        return 20;
      case TDButtonSize.small:
        return 18;
      case TDButtonSize.extraSmall:
        return 14;
    }
  }

  EdgeInsetsGeometry? _getMargin() {
    if (widget.margin != null) {
      return widget.margin;
    }
    return widget.isBlock ? const EdgeInsets.symmetric(horizontal: 16) : null;
  }

  EdgeInsetsGeometry? _getPadding() {
    if (widget.padding != null) {
      return widget.padding;
    }
    var equalSide = widget.shape == TDButtonShape.square ||
        widget.shape == TDButtonShape.circle;

    double horizontalPadding;
    double verticalPadding;
    switch (widget.size) {
      case TDButtonSize.large:
        horizontalPadding = equalSide ? 12 : 20;
        verticalPadding = 12;
        break;
      case TDButtonSize.medium:
        horizontalPadding = equalSide ? 10 : 16;
        verticalPadding = equalSide ? 10 : 8;
        break;
      case TDButtonSize.small:
        horizontalPadding = equalSide ? 7 : 12;
        verticalPadding = equalSide ? 7 : 5;
        break;
      case TDButtonSize.extraSmall:
        horizontalPadding = equalSide ? 5 : 8;
        verticalPadding = equalSide ? 5 : 3;
        break;
    }
    if (style.frameWidth != null && style.frameWidth != 0) {
      horizontalPadding = horizontalPadding - style.frameWidth!;
      verticalPadding = verticalPadding - style.frameWidth!;
      if (horizontalPadding < 0) {
        horizontalPadding = 0;
      }
      if (verticalPadding < 0) {
        verticalPadding = 0;
      }
    }
    return EdgeInsets.only(
        left: horizontalPadding,
        right: horizontalPadding,
        bottom: verticalPadding,
        top: verticalPadding);
  }

  @override
  void didUpdateWidget(covariant TDButton oldWidget) {
    super.didUpdateWidget(oldWidget);
    _updateParams();
  }

  TDButtonStyle _generateInnerStyle() {
    switch (widget.type) {
      case TDButtonType.fill:
        return TDButtonStyle.generateFillStyleByTheme(
            context, widget.theme, _buttonStatus);
      case TDButtonType.outline:
        return TDButtonStyle.generateOutlineStyleByTheme(
            context, widget.theme, _buttonStatus);
      case TDButtonType.text:
        return TDButtonStyle.generateTextStyleByTheme(
            context, widget.theme, _buttonStatus);
      case TDButtonType.ghost:
        return TDButtonStyle.generateGhostStyleByTheme(
            context, widget.theme, _buttonStatus);
    }
  }

  Radius _getRadius() {
    switch (widget.shape) {
      case TDButtonShape.rectangle:
      case TDButtonShape.square:
        return Radius.circular(TDTheme.of(context).radiusDefault);
      case TDButtonShape.round:
      case TDButtonShape.circle:
        return Radius.circular(TDTheme.of(context).radiusRound);
      case TDButtonShape.filled:
        return Radius.zero;
    }
  }

  TDButtonStyle get _defaultStyle {
    if (_innerDefaultStyle != null) {
      return _innerDefaultStyle!;
    }
    _innerDefaultStyle = widget.style ?? _generateInnerStyle();
    return _innerDefaultStyle!;
  }

  TDButtonStyle get _activeStyle {
    if (_innerActiveStyle != null) {
      return _innerActiveStyle!;
    }
    _innerActiveStyle = widget.style ?? _generateInnerStyle();
    return _innerActiveStyle!;
  }

  TDButtonStyle get _disableStyle {
    if (_innerDisableStyle != null) {
      return _innerDisableStyle!;
    }
    _innerDisableStyle = widget.style ?? _generateInnerStyle();
    return _innerDisableStyle!;
  }
}

Button.button_style:
import 'package:flutter/material.dart';

import '../../../tdesign_flutter.dart';

/// TDButton按钮样式
class TDButtonStyle {
  /// 背景颜色
  Color? backgroundColor;

  /// 边框颜色
  Color? frameColor;

  /// 文字颜色
  Color? textColor;

  /// 边框宽度
  double? frameWidth;

  /// 自定义圆角
  BorderRadiusGeometry? radius;

  /// 渐变背景色
  Gradient? gradient;

  TDButtonStyle(
      {this.backgroundColor, this.frameColor, this.textColor, this.frameWidth, this.radius, this.gradient});

  /// 生成不同主题的填充按钮样式
  TDButtonStyle.generateFillStyleByTheme(
      BuildContext context, TDButtonTheme? theme, TDButtonStatus status) {
    switch (theme) {
      case TDButtonTheme.primary:
        textColor = TDTheme.of(context).textColorAnti;
        backgroundColor = _getBrandColor(context, status);
        break;
      case TDButtonTheme.danger:
        textColor = TDTheme.of(context).textColorAnti;
        backgroundColor = _getErrorColor(context, status);
        break;
      case TDButtonTheme.light:
        textColor = _getBrandColor(context, status);
        backgroundColor = _getLightColor(context, status);
        break;
      case TDButtonTheme.defaultTheme:
      default:
        textColor = _getDefaultTextColor(context, status);
        backgroundColor = _getDefaultBgColor(context, status);
    }
    frameColor = backgroundColor;
  }

  /// 生成不同主题的描边按钮样式
  TDButtonStyle.generateOutlineStyleByTheme(
      BuildContext context, TDButtonTheme? theme, TDButtonStatus status) {
    switch (theme) {
      case TDButtonTheme.primary:
        textColor = _getBrandColor(context, status);
        backgroundColor = status == TDButtonStatus.active
            ? TDTheme.of(context).bgColorContainerActive
            : Colors.transparent;
        frameColor = textColor;
        break;
      case TDButtonTheme.danger:
        textColor = _getErrorColor(context, status);
        backgroundColor = status == TDButtonStatus.active
            ? TDTheme.of(context).bgColorContainerActive
            : Colors.transparent;
        frameColor = textColor;
        break;
      case TDButtonTheme.light:
        textColor = _getBrandColor(context, status);
        backgroundColor = _getLightColor(context, status);
        frameColor = textColor;
        break;
      case TDButtonTheme.defaultTheme:
      default:
        textColor = _getDefaultTextColor(context, status);
        backgroundColor = _getOutlineDefaultBgColor(context, status);
        frameColor = TDTheme.of(context).componentBorderColor;
    }
    frameWidth = 1;
  }

  /// 生成不同主题的文本按钮样式
  TDButtonStyle.generateTextStyleByTheme(
      BuildContext context, TDButtonTheme? theme, TDButtonStatus status) {
    switch (theme) {
      case TDButtonTheme.primary:
        textColor = _getBrandColor(context, status);
        break;
      case TDButtonTheme.danger:
        textColor = _getErrorColor(context, status);
        break;
      case TDButtonTheme.light:
        textColor = _getBrandColor(context, status);
        break;
      case TDButtonTheme.defaultTheme:
      default:
        textColor = _getDefaultTextColor(context, status);
    }
    backgroundColor = status == TDButtonStatus.active
        ? TDTheme.of(context).bgColorContainerActive
        : Colors.transparent;
    frameColor = backgroundColor;
  }

  /// 生成不同主题的幽灵按钮样式
  TDButtonStyle.generateGhostStyleByTheme(
      BuildContext context, TDButtonTheme? theme, TDButtonStatus status) {
    switch (theme) {
      case TDButtonTheme.primary:
        textColor = status == TDButtonStatus.disable
            ? TDTheme.of(context).fontWhColor4
            : _getBrandColor(context, status);
        break;
      case TDButtonTheme.danger:
        textColor = status == TDButtonStatus.disable
            ? TDTheme.of(context).fontWhColor4
            : _getErrorColor(context, status);
        break;
      case TDButtonTheme.light:
        textColor = status == TDButtonStatus.disable
            ? TDTheme.of(context).fontWhColor4
            : _getBrandColor(context, status);
        break;
      case TDButtonTheme.defaultTheme:
      default:
        switch (status) {
          case TDButtonStatus.active:
            textColor = TDTheme.of(context).fontWhColor2;
            break;
          case TDButtonStatus.disable:
            textColor = TDTheme.of(context).fontWhColor4;
            break;
          default:
            textColor = TDTheme.of(context).fontWhColor1;
        }
    }
    backgroundColor = Colors.transparent;
    frameColor = textColor;
    frameWidth = 1;
  }

  Color _getBrandColor(BuildContext context, TDButtonStatus status) {
    switch (status) {
      case TDButtonStatus.defaultState:
        return TDTheme.of(context).brandNormalColor;
      case TDButtonStatus.active:
        return TDTheme.of(context).brandClickColor;
      case TDButtonStatus.disable:
        return TDTheme.of(context).brandDisabledColor;
    }
  }

  Color _getLightColor(BuildContext context, TDButtonStatus status) {
    switch (status) {
      case TDButtonStatus.defaultState:
      case TDButtonStatus.disable:
        return TDTheme.of(context).brandLightColor;
      case TDButtonStatus.active:
        return TDTheme.of(context).brandFocusColor;
    }
  }

  Color _getErrorColor(BuildContext context, TDButtonStatus status) {
    switch (status) {
      case TDButtonStatus.defaultState:
        return TDTheme.of(context).errorNormalColor;
      case TDButtonStatus.active:
        return TDTheme.of(context).errorClickColor;
      case TDButtonStatus.disable:
        return TDTheme.of(context).errorDisabledColor;
    }
  }

  Color _getDefaultBgColor(BuildContext context, TDButtonStatus status) {
    switch (status) {
      case TDButtonStatus.defaultState:
        return TDTheme.of(context).bgColorComponent;
      case TDButtonStatus.active:
        return TDTheme.of(context).bgColorComponentHover;
      case TDButtonStatus.disable:
        return TDTheme.of(context).bgColorComponentDisabled;
    }
  }

  Color _getDefaultTextColor(BuildContext context, TDButtonStatus status) {
    switch (status) {
      case TDButtonStatus.defaultState:
      case TDButtonStatus.active:
        return TDTheme.of(context).textColorPrimary;
      case TDButtonStatus.disable:
        return TDTheme.of(context).textDisabledColor;
    }
  }

  Color _getOutlineDefaultBgColor(BuildContext context, TDButtonStatus status) {
    switch (status) {
      case TDButtonStatus.defaultState:
        return Colors.transparent;
      case TDButtonStatus.active:
        return TDTheme.of(context).bgColorContainerActive;
      case TDButtonStatus.disable:
        return TDTheme.of(context).bgColorComponentDisabled;
    }
  }
}

Calendar.calendar:
import 'package:flutter/material.dart';
import '../../../tdesign_flutter.dart';
import '../../util/context_extension.dart';
import '../../util/iterable_ext.dart';

export 'td_calendar_body.dart';
export 'td_calendar_cell.dart';
export 'td_calendar_header.dart';
export 'td_calendar_popup.dart';
export 'td_calendar_style.dart';

typedef CalendarFormat = TDate? Function(TDate? day);

enum CalendarType { single, multiple, range }

enum CalendarTrigger { closeBtn, confirmBtn, overlay }

enum DateSelectType { selected, disabled, start, centre, end, empty }

/// 日历组件
class TDCalendar extends StatefulWidget {
  const TDCalendar({
    Key? key,
    this.firstDayOfWeek = 0,
    this.format,
    this.maxDate,
    this.minDate,
    this.title,
    this.titleWidget,
    this.type = CalendarType.single,
    this.value,
    this.displayFormat = 'year month',
    this.cellHeight = 60,
    this.height,
    this.width,
    this.style,
    this.onChange,
    this.onCellClick,
    this.onCellLongPress,
    this.onHeaderClick,
    this.useSafeArea = true,
    this.useTimePicker = false,
    this.timePickerModel,
    this.monthTitleHeight = 22,
    this.monthTitleBuilder,
    this.pickerHeight = 178,
    this.pickerItemCount = 3,
    this.isTimeUnit = true,
    this.animateTo = false,
    this.cellWidget,
  }) : super(key: key);

  /// 第一天从星期几开始，默认 0 = 周日
  final int? firstDayOfWeek;

  /// 用于格式化日期的函数，可定义日期前后的显示内容和日期样式
  final CalendarFormat? format;

  /// 最大可选的日期（fromMillisecondsSinceEpoch），不传则默认半年后
  final int? maxDate;

  /// 最小可选的日期（fromMillisecondsSinceEpoch），不传则默认今天
  final int? minDate;

  /// 标题
  final String? title;

  /// 标题组件
  final Widget? titleWidget;

  /// 日历的选择类型，single = 单选；multiple = 多选；range = 区间选择
  final CalendarType? type;

  /// 当前选择的日期（fromMillisecondsSinceEpoch），不传则默认今天，当 type = single 时数组长度为1
  final List<int>? value;

  /// 年月显示格式，`year`表示年，`month`表示月，如`year month`表示年在前、月在后、中间隔一个空格
  final String? displayFormat;

  /// 高度
  final double? height;

  /// 日期高度
  final double? cellHeight;

  /// 宽度
  final double? width;

  /// 自定义样式
  final TDCalendarStyle? style;

  /// 选中值变化时触发
  final void Function(List<int> value)? onChange;

  /// 点击日期时触发
  final void Function(
    int value,
    DateSelectType type,
    TDate tdate,
  )? onCellClick;

  /// 长安日期时触发
  final void Function(
    int value,
    DateSelectType type,
    TDate tdate,
  )? onCellLongPress;

  /// 点击周时触发
  final void Function(
    int index,
    String week,
  )? onHeaderClick;

  /// 是否使用安全区域，默认true
  final bool? useSafeArea;

  /// 是否显示时间选择器
  final bool? useTimePicker;

  /// 自定义时间选择器
  final List<DatePickerModel>? timePickerModel;

  /// 月标题高度
  final double? monthTitleHeight;

  /// 月标题构建器
  final Widget Function(
    BuildContext context,
    DateTime monthDate,
  )? monthTitleBuilder;

  /// 时间选择器List的视窗高度
  final double? pickerHeight;

  /// 选择器List视窗中item个数，pickerHeight / pickerItemCount即item高度
  final int? pickerItemCount;

  /// 是否显示时间单位
  final bool? isTimeUnit;

  /// 动画滚动到指定位置
  final bool? animateTo;

  /// 自定义日期单元格组件
  final Widget? Function(
    BuildContext context,
    TDate tdate,
    DateSelectType selectType,
  )? cellWidget;

  List<DateTime>? get _value => value?.map((e) {
        final date = DateTime.fromMillisecondsSinceEpoch(e);
        return DateTime(date.year, date.month, date.day);
      }).toList();

  List<DateTime>? get _valueTime => value?.map((item) {
        return DateTime.fromMillisecondsSinceEpoch(item);
      }).toList();

  @override
  _TDCalendarState createState() => _TDCalendarState();
}

class _TDCalendarState extends State<TDCalendar> {
  late List<String> weekdayNames;
  late List<String> monthNames;
  late TDCalendarInherited? inherited;
  late TDCalendarStyle _style;
  final List<DatePickerModel> timePickerModelList = [];

  @override
  void didChangeDependencies() {
    super.didChangeDependencies();
    weekdayNames = [
      context.resource.sunday,
      context.resource.monday,
      context.resource.tuesday,
      context.resource.wednesday,
      context.resource.thursday,
      context.resource.friday,
      context.resource.saturday,
    ];
    monthNames = [
      context.resource.january,
      context.resource.february,
      context.resource.march,
      context.resource.april,
      context.resource.may,
      context.resource.june,
      context.resource.july,
      context.resource.august,
      context.resource.september,
      context.resource.october,
      context.resource.november,
      context.resource.december,
    ];
    _style = widget.style ?? TDCalendarStyle.generateStyle(context);
  }

  @override
  Widget build(BuildContext context) {
    inherited = TDCalendarInherited.of(context);
    _initValue();
    timePickerModelList.clear();
    final verticalGap = _style.verticalGap ?? TDTheme.of(context).spacer8;
    return Container(
      height: widget.height,
      width: widget.width ?? double.infinity,
      decoration: _style.decoration,
      child: Column(
        children: [
          TDCalendarHeader(
            firstDayOfWeek: widget.firstDayOfWeek ?? 0,
            weekdayGap: TDTheme.of(context).spacer4,
            padding: TDTheme.of(context).spacer16,
            weekdayStyle: _style.weekdayStyle,
            weekdayHeight: 46,
            title: widget.title,
            titleStyle: _style.titleStyle,
            titleWidget: widget.titleWidget,
            titleMaxLine: _style.titleMaxLine,
            titleOverflow: TextOverflow.ellipsis,
            closeBtn: inherited?.usePopup ?? false,
            closeColor: _style.titleCloseColor,
            weekdayNames: weekdayNames,
            onClose: inherited?.onClose,
            onClick: widget.onHeaderClick,
          ),
          Expanded(
            child: TDCalendarBody(
              type: widget.type ?? CalendarType.single,
              firstDayOfWeek: widget.firstDayOfWeek ?? 0,
              maxDate: widget.maxDate,
              minDate: widget.minDate,
              value: widget._value,
              bodyPadding: _style.bodyPadding ?? TDTheme.of(context).spacer16,
              displayFormat: widget.displayFormat ?? 'year month',
              monthNames: monthNames,
              monthTitleStyle: _style.monthTitleStyle,
              verticalGap: verticalGap,
              cellHeight: widget.cellHeight ?? 60,
              monthTitleHeight: widget.monthTitleHeight ?? 22,
              monthTitleBuilder: widget.monthTitleBuilder,
              animateTo: widget.animateTo ?? false,
              builder: (date, dateList, data, rowIndex, colIndex) {
                return TDCalendarCell(
                  height: widget.cellHeight ?? 60,
                  tdate: date,
                  format: widget.format,
                  type: widget.type ?? CalendarType.single,
                  data: data,
                  padding: verticalGap / 2,
                  onChange: (value) {
                    final time = _getValue(value);
                    inherited?.selected.value = time;
                    widget.onChange?.call(time);
                  },
                  onCellClick: widget.onCellClick,
                  onCellLongPress: widget.onCellLongPress,
                  dateList: dateList,
                  rowIndex: rowIndex,
                  colIndex: colIndex,
                  cellWidget: widget.cellWidget,
                );
              },
            ),
          ),
          if (widget.useTimePicker == true) _getTimePicker(),
          if (inherited?.usePopup == true)
            inherited?.confirmBtn ??
                Padding(
                  padding: widget.useSafeArea == true
                      ? EdgeInsets.only(top: TDTheme.of(context).spacer16)
                      : EdgeInsets.symmetric(
                          vertical: TDTheme.of(context).spacer16),
                  child: TDButton(
                    theme: TDButtonTheme.primary,
                    text: context.resource.confirm,
                    isBlock: true,
                    size: TDButtonSize.large,
                    onTap: inherited?.onConfirm,
                  ),
                ),
          if (widget.useSafeArea == true)
            SizedBox(height: MediaQuery.of(context).padding.bottom)
        ],
      ),
    );
  }

  Widget _getTimePicker() {
    final noRange = widget.type != CalendarType.range;
    final now = DateTime.now();
    final valueTime = widget._valueTime;
    return Container(
      decoration: BoxDecoration(
        color: TDTheme.of(context).bgColorContainer,
        boxShadow: const [
          BoxShadow(
            color: Color.fromRGBO(0, 0, 0, 0.04),
            blurRadius: 12,
            offset: Offset(0, -2),
          ),
        ],
      ),
      child: Row(
        children: List.generate(
          noRange ? 1 : 2,
          (index) {
            final timePickerModel = widget.timePickerModel?.getOrNull(index) ??
                DatePickerModel(
                  useYear: false,
                  useMonth: false,
                  useDay: false,
                  useWeekDay: false,
                  useHour: true,
                  useMinute: true,
                  useSecond: false,
                  dateStart: [1999, 01, 01],
                  dateEnd: [2999, 12, 31],
                  dateInitial: [
                    ...[1999, 01, 01],
                    valueTime?.getOrNull(index)?.hour ?? now.hour,
                    valueTime?.getOrNull(index)?.minute ?? now.minute,
                    valueTime?.getOrNull(index)?.second ?? now.second
                  ],
                );
            final timePicker = TDDatePicker(
              title: noRange
                  ? context.resource.time
                  : index == 0
                      ? context.resource.start
                      : context.resource.end,
              leftText: '',
              rightText: '',
              model: timePickerModel,
              pickerHeight: widget.pickerHeight ?? 178,
              pickerItemCount: widget.pickerItemCount ?? 3,
              isTimeUnit: widget.isTimeUnit ?? true,
              onConfirm: (selected) {},
              onSelectedItemChanged: (wheelIndex, index) {
                final time = _getValue(inherited?.selected.value ?? []);
                inherited?.selected.value = time;
                widget.onChange?.call(time);
              },
            );
            timePickerModelList.add(timePickerModel);
            return Expanded(child: timePicker);
          },
        ),
      ),
    );
  }

  List<int> _getValue(List<int> value) {
    var dateValue = value.map((e) {
      final date = DateTime.fromMillisecondsSinceEpoch(e);
      return DateTime(date.year, date.month, date.day).millisecondsSinceEpoch;
    }).toList();
    if (widget.useTimePicker != true) {
      return dateValue;
    }
    final milliseconds = timePickerModelList.map((model) {
      final hour = model.useHour
          ? model.hourFixedExtentScrollController.selectedItem
          : 0;
      final minute = model.useMinute
          ? model.minuteFixedExtentScrollController.selectedItem
          : 0;
      final second = model.useSecond
          ? model.secondFixedExtentScrollController.selectedItem
          : 0;
      return (hour * 60 * 60 + minute * 60 + second) * 1000;
    }).toList();
    if (widget.type == CalendarType.range && dateValue.length == 1) {
      dateValue.add(dateValue.first);
    }
    return dateValue.mapWidthIndex((e, index) {
      if (widget.type != CalendarType.range) {
        return e + (milliseconds.getOrNull(0) ?? 0);
      }
      return e + (milliseconds.getOrNull(index) ?? 0);
    }).toList();
  }

  void _initValue() {
    if (inherited == null) {
      return;
    }
    WidgetsBinding.instance.addPostFrameCallback((timeStamp) {
      inherited!.selected.value = _getValue(widget.value ?? []);
    });
  }
}

Calendar.calendar_body:
import 'dart:async';

import 'package:flutter/material.dart';
import 'package:flutter/scheduler.dart';
import '../../../tdesign_flutter.dart';
import '../../util/context_extension.dart';
import '../../util/iterable_ext.dart';

class TDCalendarBody extends StatelessWidget {
  const TDCalendarBody({
    Key? key,
    this.maxDate,
    this.minDate,
    required this.type,
    this.value,
    required this.firstDayOfWeek,
    required this.builder,
    required this.bodyPadding,
    required this.displayFormat,
    required this.monthNames,
    this.monthTitleStyle,
    this.monthTitleBuilder,
    required this.cellHeight,
    required this.monthTitleHeight,
    required this.verticalGap,
    required this.animateTo,
  }) : super(key: key);

  final int? maxDate;
  final int? minDate;
  final CalendarType type;
  final List<DateTime>? value;
  final int firstDayOfWeek;
  final Widget Function(
    TDate? date,
    List<TDate?> dateList,
    Map<DateTime, List<TDate?>> data,
    int rowIndex,
    int colIndex,
  ) builder;
  final double bodyPadding;
  final String displayFormat;
  final List<String> monthNames;
  final TextStyle? monthTitleStyle;
  final Widget Function(
    BuildContext context,
    DateTime monthDate,
  )? monthTitleBuilder;
  final double monthTitleHeight;
  final double verticalGap;
  final double cellHeight;
  final bool animateTo;

  @override
  Widget build(BuildContext context) {
    final scrollController = ScrollController();
    final min = _getDefDate(minDate);
    final max = _getDefDate(maxDate, 6);
    final months = _monthsBetween(min, max);
    final data = <DateTime, List<TDate?>>{};
    final monthHeight = <int, double>{};
    _scrollToItem(scrollController, months, monthHeight);
    return ListView.builder(
      padding: EdgeInsets.all(bodyPadding),
      controller: scrollController,
      itemCount: months.length,
      itemExtentBuilder: (index, dimensions) =>
          _getMonthHeight(months, index, monthHeight),
      itemBuilder: (context, index) {
        final monthDate = months[index];
        final monthYear = monthDate.year.toString() + context.resource.year;
        final monthMonth = monthNames[monthDate.month - 1];
        final monthDateText = displayFormat
            .replaceFirst('year', monthYear)
            .replaceFirst('month', monthMonth);
        late List<TDate?> monthData;
        if (data.containsKey(monthDate)) {
          monthData = data[monthDate]!;
        } else {
          monthData = data[monthDate] = _getDaysInMonth(monthDate, min, max);
        }

        final keyList = [...data.keys];
        final currentIndex = keyList.indexOf(monthDate);
        keyList.forEachWidthIndex((key, index) {
          if (index < currentIndex - 10 || index > currentIndex + 10) {
            // 保留最近 10 个月的数据，防止内存泄露
            data.remove(key);
          }
        });
        return Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            SizedBox(
              height: monthTitleHeight,
              child: monthTitleBuilder?.call(context, monthDate) ??
                  TDText(monthDateText, style: monthTitleStyle),
            ),
            ...List.generate(
              (monthData.length / 7).ceil(),
              (rowIndex) => [
                SizedBox(height: verticalGap),
                Row(
                  children: [
                    for (int colIndex = 0; colIndex < 7; colIndex++) ...[
                      if (colIndex != 0) SizedBox(width: verticalGap / 2),
                      Expanded(
                        child: builder(
                          (rowIndex * 7 + colIndex < monthData.length)
                              ? monthData[rowIndex * 7 + colIndex]
                              : null,
                          monthData,
                          data,
                          rowIndex,
                          colIndex,
                        ),
                      ),
                    ]
                  ],
                ),
              ],
            ).expand((element) => element).toList(),
            SizedBox(height: index == months.length - 1 ? 0 : bodyPadding),
          ],
        );
      },
    );
  }

  void _scrollToItem(ScrollController scrollController, List<DateTime> months,
      Map<int, double> monthHeight) {
    if (value == null || value!.isEmpty) {
      return;
    }
    final scrollDate = value!.reduce((a, b) => a.isBefore(b) ? a : b);
    var lastMonthDay = DateTime(months.last.year, months.last.month + 1);
    lastMonthDay = lastMonthDay.add(const Duration(days: -1));
    if (months.first.isAfter(scrollDate) || lastMonthDay.isBefore(scrollDate)) {
      return;
    }
    WidgetsBinding.instance.addPostFrameCallback((_) {
      var height = 0.0;
      for (var i = 0; i < months.length; i++) {
        final item = months[i];
        if (item.year == scrollDate.year && item.month == scrollDate.month) {
          break;
        }
        height += (_getMonthHeight(months, i, monthHeight) ?? 0);
      }
      if (height <= 0) {
        return;
      }
      if (animateTo) {
        scrollController.animateTo(
          height,
          duration: const Duration(milliseconds: 200),
          curve: Curves.easeInOut,
        );
      } else {
        scrollController.jumpTo(height);
      }
    });
  }

  DateTime _getDefDate(int? date, [int? addMonth]) {
    final now = date == null
        ? DateTime.now()
        : DateTime.fromMillisecondsSinceEpoch(date);
    if (addMonth == null) {
      return DateTime(now.year, now.month, now.day);
    }
    final month = now.month + addMonth;
    return DateTime(now.year, date == null ? month : now.month, now.day);
  }

  List<DateTime> _monthsBetween(DateTime min, DateTime max) {
    final months = <DateTime>[];
    var current = DateTime(min.year, min.month);
    while (current.compareTo(max) <= 0) {
      months.add(current);
      current = DateTime(current.year, current.month + 1);
    }
    return months;
  }

  List<TDate?> _getDaysInMonth(DateTime curDate, DateTime min, DateTime max) {
    final daysInMonth =
        List<TDate?>.generate(_getPreOffset(curDate), (index) => null);
    final daysInMonthCount = DateTime(curDate.year, curDate.month + 1, 0)
        .day; // 获取下个月的第一天的前一天，即当前月的最后一天
    for (var day = 1; day <= daysInMonthCount; day++) {
      final date = DateTime(curDate.year, curDate.month, day);
      var selectType = DateSelectType.empty;
      if (date.compareTo(min) == -1 || date.compareTo(max) == 1) {
        selectType = DateSelectType.disabled;
      } else if (type == CalendarType.single && (value?.length ?? 0) >= 1) {
        if (date.compareTo(value![0]) == 0) {
          selectType = DateSelectType.selected;
        }
      } else if (type == CalendarType.multiple && value != null) {
        if (value!.isContains((e) => date.compareTo(e) == 0)) {
          selectType = DateSelectType.selected;
        }
      } else if (type == CalendarType.range && (value?.length ?? 0) >= 1) {
        final end = (value?.length ?? 0) > 1 ? value![1] : null;
        if (date.compareTo(value![0]) == 0) {
          selectType = DateSelectType.start;
        }
        if (end != null && value![0].compareTo(end) < 0) {
          if (date.compareTo(end) == 0) {
            selectType = DateSelectType.end;
          }
          if (date.compareTo(value![0]) == 1 && date.compareTo(end) == -1) {
            selectType = DateSelectType.centre;
          }
        }
      }
      daysInMonth.add(TDate(
        date: date,
        typeNotifier: DateSelectTypeNotifier(selectType),
        isLastDayOfMonth: daysInMonthCount == day,
      ));
    }
    var sufOffset = 7 - daysInMonth.length % 7;
    sufOffset = sufOffset == 7 ? 0 : sufOffset;
    List.generate(sufOffset, (index) => daysInMonth.add(null));
    return daysInMonth;
  }

  int _getPreOffset(DateTime date) {
    final year = date.year;
    final month = date.month;
    var dayOneWeek = DateTime(year, month).weekday;
    dayOneWeek = dayOneWeek == 7 ? 0 : dayOneWeek;
    var preOffset = dayOneWeek - firstDayOfWeek;
    preOffset = preOffset < 0 ? preOffset + 7 : preOffset;
    return preOffset;
  }

  /// 获取月份高度，带缓存
  double _getMonthHeight(
      List<DateTime> months, int index, Map<int, double> monthHeight) {
    if (months.getOrNull(index) == null) {
      return 1;
    }
    if (monthHeight.containsKey(index)) {
      return monthHeight[index]!;
    }
    final item = months[index];
    final isLast = index == months.length - 1;
    final preOffset = _getPreOffset(item);
    final daysInMonthCount = DateTime(item.year, item.month + 1, 0).day;
    final daysInMonth = preOffset + daysInMonthCount;
    final height = monthTitleHeight +
        (daysInMonth / 7).ceil() * (verticalGap + cellHeight) +
        (isLast ? 0 : bodyPadding);
    monthHeight[index] = height;
    return height;
  }
}

