import 'package:flutter/material.dart';
import '../../../tdesign_flutter.dart';

class TDCalendarHeader extends StatelessWidget {
  const TDCalendarHeader({
    Key? key,
    required this.firstDayOfWeek,
    required this.weekdayGap,
    required this.padding,
    this.weekdayStyle,
    required this.weekdayHeight,
    this.title,
    this.titleStyle,
    this.titleWidget,
    this.titleMaxLine,
    this.titleOverflow,
    this.closeBtn = true,
    this.closeColor,
    this.onClose,
    this.onClick,
    required this.weekdayNames,
  }) : super(key: key);

  final int firstDayOfWeek;
  final double weekdayGap;
  final double padding;
  final TextStyle? weekdayStyle;
  final double weekdayHeight;
  final String? title;
  final TextStyle? titleStyle;
  final Widget? titleWidget;
  final int? titleMaxLine;
  final TextOverflow? titleOverflow;
  final bool closeBtn;
  final Color? closeColor;
  final VoidCallback? onClose;
  final List<String> weekdayNames;
  final void Function(int index, String week)? onClick;

  List<String> _getWeeks(BuildContext context) {
    final ans = <String>[];
    var i = firstDayOfWeek % 7;
    while (ans.length < 7) {
      ans.add(weekdayNames[i]);
      i = (i + 1) % 7;
    }
    return ans;
  }

  @override
  Widget build(BuildContext context) {
    final list = _getWeeks(context);
    return Container(
      padding: EdgeInsets.symmetric(horizontal: padding),
      child: Column(
        children: [
          if (title?.isNotEmpty == true || titleWidget != null || closeBtn)
            Container(
              padding: EdgeInsets.symmetric(vertical: padding),
              child: Row(
                children: [
                  if (closeBtn) const SizedBox(width: 24),
                  Expanded(
                    child: Center(
                      child: titleWidget ??
                          TDText(
                            title,
                            style: titleStyle,
                            maxLines: titleMaxLine,
                            overflow: TextOverflow.ellipsis,
                          ),
                    ),
                  ),
                  if (closeBtn)
                    SizedBox(
                      width: 24,
                      child: GestureDetector(
                        child: Icon(TDIcons.close, color: closeColor),
                        onTap: () {
                          onClose?.call();
                        },
                      ),
                    ),
                ],
              ),
            ),
          Row(
            children: [
              for (int index = 0; index < list.length; index++) ...[
                if (index != 0) SizedBox(width: weekdayGap),
                Expanded(
                  child: GestureDetector(
                    onTap: () {
                      onClick?.call(index, list[index]);
                    },
                    child: SizedBox(
                      height: weekdayHeight,
                      child: Center(
                        child: TDText(
                          list[index],
                          style: weekdayStyle,
                        ),
                      ),
                    ),
                  ),
                ),
              ]
            ],
          ),
        ],
      ),
    );
  }
}

Calendar.calendar_popup:
import 'package:flutter/material.dart';
import '../../../tdesign_flutter.dart';

typedef CalendarBuilder = Widget Function(BuildContext context);

enum CalendarTrigger { closeBtn, confirmBtn, overlay }

/// 单元格组件popup模式
class TDCalendarPopup {
  TDCalendarPopup(
    this.context, {
    this.top,
    this.autoClose = true,
    this.confirmBtn,
    this.visible,
    this.onClose,
    this.onConfirm,
    this.builder,
    this.child,
  }) {
    if (builder == null && child == null) {
      throw FlutterError('[TDCalendarPopup] builder or child must be not null');
    }
    if (visible == true) {
      show();
    }
  }

  /// 上下文
  final BuildContext context;

  /// 距离顶部的距离
  final double? top;

  /// 自动关闭；在点击关闭按钮、确认按钮、遮罩层时自动关闭
  final bool? autoClose;

  /// 自定义确认按钮
  final Widget? confirmBtn;

  /// 默认是否显示日历
  final bool? visible;

  /// 关闭时触发
  final VoidCallback? onClose;

  /// 控件构建器，优先级高于[child]
  final CalendarBuilder? builder;

  /// 日历控件
  final TDCalendar? child;

  /// 点击确认按钮时触发
  final void Function(List<int> value)? onConfirm;

  static TDSlidePopupRoute? _calendarPopup;

  /// 当前选中值
  final ValueNotifier<List<int>> _selected = ValueNotifier<List<int>>([]);

  bool get _autoClose => autoClose ?? true;

  /// 当前选中值
  List<int> get selected => _selected.value;

  /// 打开日历
  void show() {
    if (_calendarPopup != null) {
      return;
    }
    _calendarPopup = TDSlidePopupRoute(
      isDismissible: false,
      slideTransitionFrom: SlideTransitionFrom.bottom,
      modalTop: top,
      barrierClick: () {
        if (_autoClose) {
          close();
        }
      },
      builder: (context) {
        final childWidget = builder?.call(context) ?? child;
        return TDCalendarInherited(
          selected: _selected,
          usePopup: true,
          confirmBtn: confirmBtn,
          onClose: _onClose,
          onConfirm: _onConfirm,
          child: childWidget!,
        );
      },
    );
    Navigator.of(context).push(_calendarPopup!).then((_) {
      _deleteRouter();
    });
  }

  void _onClose() {
    if (_autoClose) {
      close();
    }
  }

  void _onConfirm() {
    onConfirm?.call(_selected.value);
    if (_autoClose) {
      close();
    }
  }

  /// 关闭日历
  void close() {
    if (_calendarPopup != null) {
      Navigator.of(context).pop();
      // _deleteRouter();
    }
  }

  void _deleteRouter() {
    _calendarPopup = null;
    onClose?.call();
  }
}

class TDCalendarInherited extends InheritedWidget {
  const TDCalendarInherited({
    required Widget child,
    this.onClose,
    required this.selected,
    this.usePopup = true,
    this.onConfirm,
    this.confirmBtn,
    Key? key,
  }) : super(child: child, key: key);

  final VoidCallback? onClose;
  final ValueNotifier<List<int>> selected;
  final bool? usePopup;
  final VoidCallback? onConfirm;
  final Widget? confirmBtn;

  @override
  bool updateShouldNotify(covariant TDCalendarInherited oldWidget) {
    return false;
  }

  static TDCalendarInherited? of(BuildContext context) {
    return context.dependOnInheritedWidgetOfExactType<TDCalendarInherited>();
  }
}

Calendar.calendar_style:
import 'package:flutter/material.dart';
import '../../../tdesign_flutter.dart';

/// 日历组件样式
class TDCalendarStyle {
  TDCalendarStyle({
    this.decoration,
    this.titleStyle,
    this.titleMaxLine,
    this.titleCloseColor,
    this.weekdayStyle,
    this.monthTitleStyle,
    this.cellStyle,
    this.centreColor,
    this.cellDecoration,
    this.cellPrefixStyle,
    this.cellSuffixStyle,
  });

  BoxDecoration? decoration;

  /// header区域 [TDCalendar.title]的样式
  TextStyle? titleStyle;

  /// header区域 [TDCalendar.title]的行数
  int? titleMaxLine;

  /// header区域 关闭图标的颜色
  Color? titleCloseColor;

  /// header区域 周 文字样式
  TextStyle? weekdayStyle;

  /// body区域 年月文字样式
  TextStyle? monthTitleStyle;

  /// 日期样式
  TextStyle? cellStyle;

  /// 当天日期样式
  TextStyle? todayStyle;

  /// 日期decoration
  BoxDecoration? cellDecoration;

  /// 日期范围内背景样式
  Color? centreColor;

  /// 日期前面的字符串的样式
  TextStyle? cellPrefixStyle;

  /// 日期后面的字符串的样式
  TextStyle? cellSuffixStyle;

  /// 日期垂直间距，水平间距为[verticalGap] / 2
  double? verticalGap;

  /// 月与月之间的垂直间距
  double? bodyPadding;

  /// 生成默认样式
  TDCalendarStyle.generateStyle(BuildContext context) {
    decoration = BoxDecoration(
      color: TDTheme.of(context).bgColorContainer,
      borderRadius: BorderRadius.vertical(
        top: Radius.circular(TDTheme.of(context).radiusExtraLarge),
      ),
    );
    titleStyle = TextStyle(
      fontSize: TDTheme.of(context).fontTitleLarge?.size,
      fontWeight: TDTheme.of(context).fontTitleLarge?.fontWeight,
      color: TDTheme.of(context).textColorPrimary,
    );
    titleMaxLine = 1;
    titleCloseColor = titleStyle?.color;
    weekdayStyle = TextStyle(
      fontSize: TDTheme.of(context).fontTitleSmall?.size,
      color: TDTheme.of(context).textColorSecondary,
    );
    monthTitleStyle = TextStyle(
      fontSize: TDTheme.of(context).fontMarkMedium?.size,
      fontWeight: TDTheme.of(context).fontMarkMedium?.fontWeight,
      color: TDTheme.of(context).textColorPrimary,
    );
    verticalGap = TDTheme.of(context).spacer8;
    bodyPadding = TDTheme.of(context).spacer16;
  }

  /// 日期样式
  TDCalendarStyle.cellStyle(BuildContext context, DateSelectType? type) {
    final radius6 = TDTheme.of(context).radiusDefault;
    final defStyle = TextStyle(
      fontSize: TDTheme.of(context).fontTitleMedium?.size,
      height: TDTheme.of(context).fontTitleMedium?.height,
      fontWeight: TDTheme.of(context).fontTitleMedium?.fontWeight,
    );
    final prefixStyle = TextStyle(
      fontSize: TDTheme.of(context).fontBodyExtraSmall?.size,
      height: TDTheme.of(context).fontBodyExtraSmall?.height,
      fontWeight: FontWeight.w400,
    );
    centreColor = TDTheme.of(context).brandLightColor;
    switch (type) {
      case DateSelectType.empty:
        cellStyle =
            defStyle.copyWith(color: TDTheme.of(context).textColorPrimary);
        todayStyle = defStyle.copyWith(color: TDTheme.of(context).brandNormalColor);
        cellPrefixStyle =
            prefixStyle.copyWith(color: TDTheme.of(context).errorNormalColor);
        cellSuffixStyle = prefixStyle.copyWith(
            color: TDTheme.of(context).textColorPlaceholder);
        cellDecoration = null;
        break;
      case DateSelectType.disabled:
        cellStyle =
            defStyle.copyWith(color: TDTheme.of(context).textDisabledColor);
        todayStyle = defStyle.copyWith(color: TDTheme.of(context).brandDisabledColor);
        cellPrefixStyle =
            prefixStyle.copyWith(color: TDTheme.of(context).errorDisabledColor);
        cellSuffixStyle =
            prefixStyle.copyWith(color: TDTheme.of(context).textDisabledColor);
        cellDecoration = null;
        break;
      case DateSelectType.selected:
        cellStyle = defStyle.copyWith(color: TDTheme.of(context).textColorAnti);
        cellPrefixStyle =
            prefixStyle.copyWith(color: TDTheme.of(context).textColorAnti);
        cellSuffixStyle =
            prefixStyle.copyWith(color: TDTheme.of(context).textColorAnti);
        cellDecoration = BoxDecoration(
          borderRadius: BorderRadius.circular(radius6),
          color: TDTheme.of(context).brandNormalColor,
        );
        break;
      case DateSelectType.centre:
        cellStyle =
            defStyle.copyWith(color: TDTheme.of(context).textColorPrimary);
        cellPrefixStyle =
            prefixStyle.copyWith(color: TDTheme.of(context).errorNormalColor);
        cellSuffixStyle = prefixStyle.copyWith(
            color: TDTheme.of(context).textColorPlaceholder);
        cellDecoration = BoxDecoration(
          color: centreColor,
        );
        break;
      case DateSelectType.start:
        cellStyle = defStyle.copyWith(color: TDTheme.of(context).textColorAnti);
        cellPrefixStyle =
            prefixStyle.copyWith(color: TDTheme.of(context).textColorAnti);
        cellSuffixStyle =
            prefixStyle.copyWith(color: TDTheme.of(context).textColorAnti);
        cellDecoration = BoxDecoration(
          color: TDTheme.of(context).brandNormalColor,
          borderRadius: BorderRadius.horizontal(left: Radius.circular(radius6)),
        );
        break;
      case DateSelectType.end:
        cellStyle = defStyle.copyWith(color: TDTheme.of(context).textColorAnti);
        cellPrefixStyle =
            prefixStyle.copyWith(color: TDTheme.of(context).textColorAnti);
        cellSuffixStyle =
            prefixStyle.copyWith(color: TDTheme.of(context).textColorAnti);
        cellDecoration = BoxDecoration(
          color: TDTheme.of(context).brandNormalColor,
          borderRadius:
              BorderRadius.horizontal(right: Radius.circular(radius6)),
        );
        break;
      default:
        break;
    }
  }
}

