import 'package:flutter/material.dart';

import '../../../tdesign_flutter.dart';

typedef TapCallback = void Function(int index);

class TDCustomTab extends StatefulWidget {
  final List<String> tabs;
  final TapCallback? onTap;
  final int? initialIndex;

  const TDCustomTab(
      {super.key, required this.tabs, this.onTap, this.initialIndex});

  @override
  State<TDCustomTab> createState() => _TDCustomTabState();
}

class _TDCustomTabState extends State<TDCustomTab> {
  ScrollController _scrollController = ScrollController();
  int _currentTabIndex = 0;

  @override
  void initState() {
    // TODO: implement initState
    super.initState();
    _currentTabIndex = widget.initialIndex ?? 0;
  }

  @override
  void didUpdateWidget(TDCustomTab oldWidget) {
    super.didUpdateWidget(oldWidget);
    if (widget.initialIndex != oldWidget.initialIndex) {
      _currentTabIndex = widget.initialIndex!;
    }
  }

  @override
  Widget build(BuildContext context) {
    return SingleChildScrollView(
      scrollDirection: Axis.horizontal,
      controller: _scrollController,
      child: Row(
          mainAxisAlignment: MainAxisAlignment.start,
          children: List.generate(widget.tabs.length, (index) {
            return GestureDetector(
                onTap: () {
                  _onChangeTab(index);
                },
                child: SizedBox(
                    width: 96,
                    height: 52,
                    child: Stack(
                      children: [
                        Center(
                          child: TDText(
                            widget.tabs[index],
                            style: TextStyle(
                                fontSize: 16,
                                color: _currentTabIndex == index
                                    ? TDTheme.of(context).brandNormalColor
                                    : TDTheme.of(context).textColorPrimary),
                            fontWeight: _currentTabIndex == index
                                ? FontWeight.w600
                                : FontWeight.w400,
                          ),
                        ),
                        if (_currentTabIndex == index)
                          Positioned(
                            bottom: 0,
                            left: (96 - 20) / 2,
                            child: Center(
                              child: Container(
                                width: 20,
                                height: 1.5,
                                color: TDTheme.of(context).brandNormalColor,
                              ),
                            ),
                          ),
                      ],
                    )));
          })),
    );
  }

  void _onChangeTab(int index) {
    if (widget.onTap != null) {
      widget.onTap!(index);
    }
  }
}

Cascader.multi_cascader:
import 'package:flutter/material.dart';
import '../../../tdesign_flutter.dart';
import '../../util/context_extension.dart';

typedef MultiCascaderCallback = void Function(
    List<MultiCascaderListModel> selected);

class TDMultiCascader extends StatefulWidget {
  /// 选择器标题
  final String? title;

  /// 标题样式
  final TextStyle? titleStyle;

  /// 展示风格 可选项：step/tab
  final String? theme;

  ///	每级展示的次标题
  final List<String>? subTitles;

  /// 选择器List的视窗高度，默认200
  final double cascaderHeight;

  /// 若为null表示全部从零开始
  final List<int>? initialIndexes;

  /// 选择器的数据源
  final List<Map> data;

  /// 初始化数据
  final String? initialData;

  /// 背景颜色
  final Color? backgroundColor;

  /// 顶部圆角
  final double? topRadius;

  /// 是否开启字母排序
  final bool isLetterSort;

  /// 关闭按钮文本
  final String? closeText;

  /// 选择器关闭按钮回调
  final Function? onClose;

  /// 自定义选择器右上角按钮
  final TDCascaderAction? action;

  /// 值发生变更时触发
  final MultiCascaderCallback onChange;

  const TDMultiCascader(
      {super.key,
      this.title,
      this.titleStyle,
      this.theme,
      this.subTitles,
      required this.data,
      required this.cascaderHeight,
      this.initialIndexes,
      this.initialData,
      this.backgroundColor,
      this.topRadius,
      this.closeText,
      this.isLetterSort = false,
      this.onClose,
      this.action,
      required this.onChange});

  @override
  State<TDMultiCascader> createState() => _TDMultiCascaderState();
}

class _TDMultiCascaderState extends State<TDMultiCascader>
    with TickerProviderStateMixin {
  List<MultiCascaderListModel> _tabListData = [];

  /// 当前tab选中的值
  String? _selectTabValue = '';

  /// 当前tab索引
  int _currentTabIndex = 0;

  /// tab 层级
  int _level = 0;

  /// 缓存列表数据
  List<MultiCascaderListModel> _listData = [];

  /// tab选中对应的列表数据
  List<MultiCascaderListModel> _selectListData = [];

  final ScrollController _scrollListController = ScrollController();

  @override
  void initState() {
    super.initState();
    List.generate(widget.data.length, (index) {
      MultiCascaderListModel item = MultiCascaderListModel(
        labelFun: () => widget.data[index]['label'],
        value: widget.data[index]['value'],
        segmentValue: widget.data[index]['segmentValue'],
        level: 0,
      );
      _listData.add(item);

      if (widget.data[index]['children'] != null &&
          widget.data[index]['children'].length > 0) {
        _buildRecursiveList(
            1, widget.data[index]['value'], widget.data[index]['children']);
      }
    });
    if (widget.isLetterSort) {
      _listDataSegmenter();
    }
    // 优先使用 initialIndexes，如果没有则使用 initialData
    String? initValue;
    if (widget.initialIndexes != null && widget.initialIndexes!.isNotEmpty) {
      initValue = _initLocationByIndexes(widget.initialIndexes!);
    } else {
      _initLocation(widget.initialData??'');
    }

    _currentTabIndex = _tabListData.length - 1;
    _level = _currentTabIndex>0?_currentTabIndex:0;
    if(_currentTabIndex>=0){
      _tabListData = _tabListData.reversed.toList();
      _selectTabValue = initValue ?? widget.initialData;
      _selectListData =
          _listData.where((element) => element.parentValue == _tabListData[_currentTabIndex].parentValue).toList();
    }else{
      _selectListData = _listData.where((element) => element.level == 0).toList();
      _tabListData.add(MultiCascaderListModel(
        labelFun: ()=>context.resource.cascadeLabel,
      ));
    }
  }

  @override
  Widget build(BuildContext context) {
    var maxWidth = MediaQuery.of(context).size.width;

    return Container(
      width: maxWidth,
      decoration: BoxDecoration(
        color: widget.backgroundColor ?? TDTheme.of(context).bgColorContainer,
        borderRadius: BorderRadius.only(
          topLeft: Radius.circular(
              widget.topRadius ?? TDTheme.of(context).radiusExtraLarge),
          topRight: Radius.circular(
              widget.topRadius ?? TDTheme.of(context).radiusExtraLarge),
        ),
      ),
      child: Column(
        mainAxisSize: MainAxisSize.min,
        children: [
          _buildTitle(context),
          _buildTabThemeBox(context),
          Expanded(child: _buildContentBox(context))
        ],
      ),
    );
  }

  @override
  void didChangeDependencies() {
    /// 该方法在开始处必须调用父类的方法
    super.didChangeDependencies();
    Future.delayed(const Duration(seconds: 1), () {
      List.generate(_selectListData.length, (index) {
        if (_selectListData[index].value == _selectTabValue) {
          _scrollToListIndex(index);
        }
      });
    });
  }

  @override
  void dispose() {
    _scrollListController.dispose();
    super.dispose();
  }

  void _initLocation(String value) {
    List<MultiCascaderListModel> list =
        _listData.where((element) => element.value == value).toList();
    if (list.isNotEmpty) {
      _tabListData.add(list[0]);
      if (list[0].parentValue != null) {
        _initLocation(list[0].parentValue!);
      }
    }
  }

  /// 根据索引列表初始化选中位置
  String? _initLocationByIndexes(List<int> indexes) {
    if (indexes.isEmpty) return null;

    String? lastValue = _getValueByIndexes(indexes);

    if (lastValue != null) {
      _initLocation(lastValue);
    }

    return lastValue;
  }

  /// 根据索引列表获取对应的值
  String? _getValueByIndexes(List<int> indexes) {
    if (indexes.isEmpty) return null;

    List<dynamic> currentLevel = widget.data;
    String? value;

    for (int i = 0; i < indexes.length; i++) {
      int index = indexes[i];

      if (index >= 0 && index < currentLevel.length) {
        Map item = currentLevel[index];
        value = item['value'];

        if (i < indexes.length - 1 && item.containsKey('children') && item['children'] is List && item['children'].isNotEmpty) {
          currentLevel = item['children'];
        } else {
          break;
        }
      } else {
        return null;
      }
    }

    return value;
  }

  void _listDataSegmenter() {
    _listData.sort((a, b) {
      if (a.segmentValue == null || b.segmentValue == null) {
        return 0;
      } else {
        return a.segmentValue!
            .toLowerCase()
            .compareTo(b.segmentValue!.toLowerCase());
      }
    });
  }

  void _buildRecursiveList(int depth, String parentValue, List<Map> data) {
    List.generate(data.length, (index) {
      MultiCascaderListModel item = MultiCascaderListModel(
        labelFun: () => data[index]['label'],
        value: data[index]['value'],
        parentValue: parentValue,
        segmentValue: data[index]['segmentValue'],
        level: depth,
      );
      _listData.add(item);
      if (data[index]['children'] != null &&
          data[index]['children'].length > 0) {
        _buildRecursiveList(
            depth + 1, data[index]['value'], data[index]['children']);
      }
    });
  }

  Widget _buildTitle(BuildContext context) {
    return SizedBox(
      height: 58,
      child: Stack(
        children: [
          widget.title == null
              ? Container()
              : Center(
                  child: TDText(
                    widget.title,
                    style: widget.titleStyle ??
                        TextStyle(
                            fontSize: TDTheme.of(context).fontTitleLarge!.size,
                            fontWeight: FontWeight.w700,
                            color: TDTheme.of(context).textColorPrimary),
                  ),
                ),
          Positioned(
              right: 0,
              top: 0,
              child: GestureDetector(
                  onTap: () {
                    if (widget.action != null) {
                      var result = _tabListData
                          .where((element) =>
                              element.label != context.resource.cascadeLabel)
                          .toList();
                      widget.action?.onConfirm(result);
                      if (result.isNotEmpty) {
                        // 返回数据不空，才会自己关闭。如果数据是空的，有业务在回调中自己选择是否关闭
                        Navigator.of(context).pop();
                      }
                    } else if (widget.onClose != null) {
                      widget.onClose!();
                    }
                  },
                  child: Container(
                    height: 58,
                    alignment: Alignment.center,
                    child: Padding(
                      padding: const EdgeInsets.only(left: 2, right: 16),
                      child: widget.action?.build(context) ??
                          (widget.closeText == null
                              ? Icon(
                                  TDIcons.close,
                                  color: TDTheme.of(context).textColorPrimary,
                                )
                              : TDText(
                                  widget.closeText,
                                  style: TextStyle(
                                      fontSize: TDTheme.of(context)
                                          .fontTitleMedium!
                                          .size,
                                      color:
                                          TDTheme.of(context).textColorPrimary),
                                )),
                    ),
                  ))),
        ],
      ),
    );
  }

  Widget _buildTabThemeBox(BuildContext context) {
    String them = widget.theme ?? 'step';
    return them == 'step' ? _buildStepBox(context) : _buildTabBox(context);
  }

  Widget _buildStepBox(BuildContext context) {
    var maxWidth = MediaQuery.of(context).size.width;
    return Container(
        decoration: BoxDecoration(
            border: Border(
                bottom: BorderSide(
                    color: TDTheme.of(context).componentStrokeColor,
                    width: 0.5))),
        width: maxWidth,
        child: ListView(
            shrinkWrap: true,
            children: List.generate(_tabListData.length, (index) {
              MultiCascaderListModel tabItem = _tabListData[index];
              return GestureDetector(
                  onTap: () {
                    _tabListChange(index);
                  },
                  child: SizedBox(
                    height: 38,
                    child: Row(
                      crossAxisAlignment: CrossAxisAlignment.center,
                      mainAxisAlignment: MainAxisAlignment.center,
                      children: [
                        SizedBox(
                          // 圆和线
                          height: 8,
                          child: LeftLineWidget(
                            isCircleFill: tabItem.value == null &&
                                    tabItem.value != _selectTabValue
                                ? false
                                : true,
                            isShowTopLine: index == 0 ? false : true,
                          ),
                        ),
                        Expanded(
                          child: TDText(
                            '${_tabListData[index].label}',
                            style: TextStyle(
                                fontSize: 14,
                                color: _currentTabIndex == index
                                    ? TDTheme.of(context).brandNormalColor
                                    : TDTheme.of(context).textColorPrimary),
                            fontWeight: _currentTabIndex == index
                                ? FontWeight.w600
                                : FontWeight.w400,
                          ),
                        ),
                        Padding(
                          padding: const EdgeInsets.only(left: 2, right: 16),
                          child: Icon(
                            TDIcons.chevron_right,
                            color: TDTheme.of(context).textColorPrimary,
                          ),
                        ),
                      ],
                    ),
                  ));
            })));
  }

  Widget _buildTabBox(BuildContext context) {
    var maxWidth = MediaQuery.of(context).size.width;
    return Container(
      height: 48,
      decoration: BoxDecoration(
          border: Border(
              bottom: BorderSide(
                  color: TDTheme.of(context).componentStrokeColor,
                  width: 0.5))),
      width: maxWidth,
      child: TDCustomTab(
        tabs: List.generate(_tabListData.length, (index) {
          return _tabListData[index].label ?? '';
        }),
        initialIndex: _currentTabIndex,
        onTap: _tabListChange,
      ),
    );
  }

  Widget _buildContentBox(BuildContext context) {
    var maxWidth = MediaQuery.of(context).size.width;
    return Container(
        width: maxWidth,
        padding: const EdgeInsets.symmetric(horizontal: 16),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            if (widget.subTitles != null)
              Container(
                  height: 50,
                  padding: const EdgeInsets.only(
                    top: 20,
                  ),
                  child: TDText(
                    widget.subTitles![_level],
                    style: TextStyle(
                        color: TDTheme.of(context).textColorPlaceholder),
                    font: TDTheme.of(context).fontTitleSmall,
                  ) //,
                  ),
            Expanded(
                child: PageView(
              scrollDirection: Axis.horizontal,
              reverse: false,
              controller: PageController(initialPage: 1, keepPage: false),
              children: List.generate(1, (index) {
                return ListView.builder(
                  controller: _scrollListController,
                  itemCount: _selectListData.length,
                  itemBuilder: (context, index) {
                    MultiCascaderListModel item = _selectListData[index];
                    MultiCascaderListModel preItem = index == 0
                        ? MultiCascaderListModel()
                        : _selectListData[index - 1];
                    return GestureDetector(
                      onTap: () {
                        int level = 0;
                        if (item.level == 0 && _currentTabIndex == 0) {
                          _tabListData.clear();
                          _tabListData.add(MultiCascaderListModel(
                            labelFun: () => context.resource.cascadeLabel,
                          ));
                        }
                        if (item.level != null) {
                          level = item.level!;
                        }

                        if (widget.subTitles != null &&
                            widget.subTitles!.length - 1 > _level) {
                          _level = level + 1;
                        }
                        List isList = _tabListData
                            .where((element) => element.level == item.level)
                            .toList();
                        if (isList.isNotEmpty) {
                          _tabListData.removeAt(level);
                        }
                        setState(() {
                          _tabListData.insert(level, item);
                          _selectTabValue = item.value;
                          //下一级查询
                          _getChildrenListData(level + 1, item.value!);
                        });
                      },
                      child: Container(
                          height: 56,
                          decoration: BoxDecoration(
                              border: Border.all(color: Colors.transparent)),
                          child: Row(
                            mainAxisAlignment: MainAxisAlignment.spaceBetween,
                            crossAxisAlignment: CrossAxisAlignment.center,
                            children: [
                              Row(
                                mainAxisAlignment: MainAxisAlignment.start,
                                children: [
                                  if (item.segmentValue != null)
                                    SizedBox(
                                      width: 32,
                                      child: item.segmentValue !=
                                              preItem.segmentValue
                                          ? TDText(
                                              '${item.segmentValue}',
                                              font: Font(
                                                  size: 16, lineHeight: 24),
                                            )
                                          : null,
                                    ),
                                  TDText(
                                    '${item.label}',
                                    font: Font(size: 16, lineHeight: 24),
                                  ),
                                ],
                              ),
                              if (_selectTabValue == item.value)
                                Icon(
                                  TDIcons.check,
                                  color: TDTheme.of(context).brandNormalColor,
                                )
                            ],
                          )),
                    );
                  },
                );
              }),
            ))
          ],
        ));
  }

  void _tabListChange(int index) {
    MultiCascaderListModel tabItem = _tabListData[index];
    _currentTabIndex = index;
    if (tabItem.level != null) {
      _selectTabValue = tabItem.value;
    }
    if (index < _tabListData.length - 1) {
      _getFindListData(level: tabItem.level!, value: tabItem.value);
    } else {
      int cruIndex = index > 0 ? index - 1 : index;
      _getFindListData(level: index, parentValue: _tabListData[cruIndex].value);
    }
    _level = index;
    setState(() {});
  }

  void _getChildrenListData(int level, String value) {
    //查询层级数据
    var selectLevelData = _listData
        .where((element) =>
            element.level == (level) && element.parentValue == value)
        .toList();
    //判断下级是否存在
    if (selectLevelData.isNotEmpty) {
      //获取下级数据
      var childList = selectLevelData
          .where((element) => element.parentValue == value)
          .toList();
      _selectListData = childList;
      _currentTabIndex += 1;
    } else {
      var result = _tabListData
          .where((element) => element.label != context.resource.cascadeLabel)
          .toList();
      widget.onChange(result);
      Navigator.of(context).pop();
    }
  }

  /// 查询列表数据
  void _getFindListData(
      {required int level, String? parentValue, String? value}) {
    List<MultiCascaderListModel> list = [];
    //查询层级数据
    List<MultiCascaderListModel> selectLevelData =
        _listData.where((element) => element.level == (level)).toList();
    if (selectLevelData.isNotEmpty) {
      if (level == 0) {
        list = selectLevelData;
      } else if (value != null) {
        list =
            selectLevelData.where((element) => element.value == value).toList();
      } else {
        list = selectLevelData
            .where((element) => element.parentValue == parentValue)
            .toList();
      }
      _selectListData = list;
    }
  }

  /// 定位选项在列表中位置
  void _scrollToListIndex(int index) async {
    // 计算列表中特定索引的位置
    double scrollTo = index * 56.0; // 每个列表项的高度是56.0
    await _scrollListController.animateTo(
      scrollTo,
      duration: const Duration(milliseconds: 1),
      curve: Curves.ease,
    );
  }
}

class LeftLineWidget extends StatelessWidget {
  /// 是否实心圆
  final bool isCircleFill;

  /// 线条颜色
  final Color? topLineColor;

  /// 是否显示圆圈上方线条
  final bool isShowTopLine;

  const LeftLineWidget(
      {super.key,
      this.isShowTopLine = false,
      this.topLineColor,
      this.isCircleFill = false});

  @override
  Widget build(BuildContext context) {
    return Container(
      margin: const EdgeInsets.symmetric(horizontal: 16),
      width: 16,
      child: CustomPaint(
        painter: LeftLinePainter(
            isShowTopLine: isShowTopLine,
            topLineColor: topLineColor ?? TDTheme.of(context).brandNormalColor,
            isCircleFill: isCircleFill),
      ),
    );
  }
}

class LeftLinePainter extends CustomPainter {
  static const double _topHeight = 16;

  // static const Color _lightColor = Color.fromRGBO(0, 82, 217, 1);

  /// 是否实心圆
  final bool isCircleFill;

  /// 线条颜色
  final Color topLineColor;

  /// 是否显示圆圈上方线条
  final bool isShowTopLine;

  const LeftLinePainter(
      {required this.topLineColor,
      required this.isShowTopLine,
      required this.isCircleFill});

  @override
  void paint(Canvas canvas, Size size) {
    double lineWidth = 1;
    double topHeight = size.height / 2;
    double centerX = size.width / 2;
    Paint linePain = Paint();
    linePain.color = Colors.transparent;
    linePain.strokeWidth = lineWidth;
    linePain.strokeCap = StrokeCap.square;
    canvas.drawLine(Offset(centerX, 0), Offset(centerX, topHeight), linePain);
    Paint circlePaint = Paint();
    circlePaint.color = topLineColor;
    circlePaint.strokeWidth = 1;
    circlePaint.style =
        isCircleFill ? PaintingStyle.fill : PaintingStyle.stroke;
    linePain.color = isShowTopLine ? (topLineColor) : Colors.transparent;
    canvas.drawLine(Offset(centerX, -size.height),
        Offset(centerX, -size.height - _topHeight), linePain);
    canvas.drawCircle(Offset(centerX, topHeight), centerX * 0.5, circlePaint);
  }

  @override
  bool shouldRepaint(CustomPainter oldDelegate) {
    return true;
  }
}

class MultiCascaderListModel {
  String? Function()? labelFun;

  String? get label => labelFun?.call();

  String? value;

  /// 父级值
  String? parentValue;

  /// 分组值
  String? segmentValue;

  int? level;

  MultiCascaderListModel(
      {this.labelFun,
      this.value,
      this.parentValue,
      this.level,
      this.segmentValue});
}

Cell.cell:
import 'package:flutter/material.dart';

import '../../../tdesign_flutter.dart';
import 'td_cell_inherited.dart';

typedef TDCellClick = void Function(TDCell cell);

enum TDCellAlign { top, middle, bottom }

/// 单元格组件
class TDCell extends StatefulWidget {
  const TDCell({
    Key? key,
    this.align = TDCellAlign.middle,
    this.arrow = false,
    this.bordered = true,
    this.description,
    this.descriptionWidget,
    this.hover = true,
    this.image,
    this.imageSize,
    this.imageWidget,
    this.leftIcon,
    this.leftIconWidget,
    this.note,
    this.noteWidget,
    this.noteMaxWidth,
    this.noteMaxLine = 1,
    this.required = false,
    this.title,
    this.titleWidget,
    this.onClick,
    this.onLongPress,
    this.style,
    this.rightIcon,
    this.rightIconWidget,
    this.disabled = false,
    this.imageCircle = 50,
    this.showBottomBorder = false,
    this.height,
  }) : super(key: key);

  /// 内容的对齐方式，默认居中对齐。可选项：top/middle/bottom
  final TDCellAlign? align;

  /// 是否显示右侧箭头
  final bool? arrow;

  /// 是否显示下边框，仅在TDCellGroup组件下起作用
  final bool? bordered;

  /// 下方内容描述文字
  final String? description;

  /// 下方内容描述组件
  final Widget? descriptionWidget;

  /// 是否开启点击反馈
  final bool? hover;

  /// 主图
  final ImageProvider? image;

  /// 主图尺寸
  final double? imageSize;

  /// 主图圆角，默认50（圆形）
  final double? imageCircle;

  /// 主图组件
  final Widget? imageWidget;

  /// 左侧图标，出现在单元格标题的左侧
  final IconData? leftIcon;

  /// 左侧图标组件
  final Widget? leftIconWidget;

  /// 和标题同行的说明文字
  final String? note;

  /// 说明文字组件
  final Widget? noteWidget;

  /// 说明文字组件 最大宽度，超过部分显示省略号，防止文字溢出
  final double? noteMaxWidth;

  /// 说明文字组件 最大行数
  final int noteMaxLine;

  /// 是否显示表单必填星号
  final bool? required;

  /// 最右侧图标
  final IconData? rightIcon;

  /// 最右侧图标组件
  final Widget? rightIconWidget;

  /// 标题
  final String? title;

  /// 标题组件
  final Widget? titleWidget;

  /// 点击事件
  final TDCellClick? onClick;

  /// 长按事件
  final TDCellClick? onLongPress;

  /// 自定义样式
  final TDCellStyle? style;

  /// 禁用
  final bool? disabled;

  /// 是否显示下边框（建议TDCellGroup组件下false，避免与bordered重叠）
  final bool? showBottomBorder;

  /// 高度
  final double? height;

  @override
  _TDCellState createState() => _TDCellState();
}

class _TDCellState extends State<TDCell> {
  var _status = 'default';

  bool get disabled {
    return widget.disabled ?? false;
  }


  @override
  Widget build(BuildContext context) {
    final theme = TDTheme.of(context);
    final style = widget.style ??
        TDCellInherited.of(context)?.style ??
        TDCellStyle.cellStyle(context);
    final crossAxisAlignment = _getAlign();
    final color = _status == 'default'
        ? style.backgroundColor
        : style.clickBackgroundColor;
    final border = (widget.showBottomBorder ?? false)
        ? Border(
      bottom: BorderSide(
        width: 0.5,
        color: style.borderedColor ?? theme.componentStrokeColor,
      ),
    )
        : null;

    return GestureDetector(
      behavior: HitTestBehavior.opaque,
      onTap: () {
        if (widget.onClick != null && !disabled) {
          widget.onClick!(widget);
        }
        TDSwipeCellInherited.of(context)?.cellClick();
      },
      onLongPress: widget.onLongPress != null && !disabled
          ? () => widget.onLongPress!(widget)
          : null,
      onTapDown: (_) => _setStatus('active', 0),
      onTapUp: (_) => _setStatus('default', 100),
      onTapCancel: () => _setStatus('default', 0),
      child: Container(
        height: widget.height,
        padding: style.padding,
        decoration: BoxDecoration(color: color, border: border),
        child: Row(
          crossAxisAlignment: crossAxisAlignment,
          children: [
            ..._buildImage(),
            Expanded(
              child: Row(
                crossAxisAlignment: crossAxisAlignment,
                children: [
                  if (widget.leftIcon != null ||
                      widget.leftIconWidget != null) ...[
                    widget.leftIconWidget ??
                        Icon(widget.leftIcon,
                            size: 24, color: style.leftIconColor),
                    SizedBox(width: theme.spacer12),
                  ],
                  Expanded(
                    child: Column(
                      crossAxisAlignment: CrossAxisAlignment.start,
                      // spacing: theme.spacer4,
                      children: [
                        Row(
                          children: [
                            if (widget.titleWidget != null)
                              Flexible(child: widget.titleWidget!)
                            else if (widget.title?.isNotEmpty == true)
                              Flexible(
                                  child: TDText(widget.title!,
                                      style: style.titleStyle)),
                            if (widget.required ?? false)
                              TDText(' *', style: style.requiredStyle),
                          ],
                        ),
                        if ((widget.titleWidget != null || widget.title != null) &&
                            (widget.descriptionWidget != null || widget.description?.isNotEmpty == true))
                          SizedBox(height: TDTheme.of(context).spacer4),
                        if (widget.descriptionWidget != null)
                          widget.descriptionWidget!
                        else if (widget.description?.isNotEmpty ?? false)
                          TDText(widget.description!,
                              style: style.descriptionStyle),
                      ],
                    ),
                  ),
                ],
              ),
            ),
            Wrap(
              spacing: theme.spacer4,
              crossAxisAlignment: WrapCrossAlignment.center,
              children: [
                if (widget.noteWidget != null)
                  widget.noteWidget!
                else if (widget.note?.isNotEmpty ?? false)
                  ConstrainedBox(
                      constraints: BoxConstraints(
                          maxWidth: widget.noteMaxWidth ??
                              MediaQuery.of(context).size.width - 84),
                      child: TDText(
                        widget.note!,
                        style: style.noteStyle,
                        overflow: TextOverflow.ellipsis,
                        maxLines: widget.noteMaxLine,
                      )),
                if (widget.rightIconWidget != null)
                  widget.rightIconWidget!
                else if (widget.rightIcon != null)
                  Icon(widget.rightIcon, size: 24, color: style.rightIconColor),
                if (widget.arrow ?? false)
                  Icon(TDIcons.chevron_right,
                      size: 24, color: style.arrowColor),
              ],
            ),
          ],
        ),
      ),
    );
  }

  CrossAxisAlignment _getAlign() {
    switch (widget.align) {
      case TDCellAlign.top:
        return CrossAxisAlignment.start;
      case TDCellAlign.middle:
        return CrossAxisAlignment.center;
      case TDCellAlign.bottom:
        return CrossAxisAlignment.end;
      default:
        return CrossAxisAlignment.center;
    }
  }

  void _setStatus(String status, int milliseconds) {
    if (disabled || !(widget.hover ?? true)) {
      return;
    }
    if (milliseconds == 0) {
      setState(() {
        _status = status;
      });
      return;
    }
    Future.delayed(Duration(milliseconds: milliseconds), () {
      setState(() {
        _status = status;
      });
    });
  }

  List<Widget> _buildImage() {
    final imageSize = widget.imageSize ?? 48;
    final imageWidgets = <Widget>[];

    if (widget.imageWidget != null) {
      imageWidgets.add(widget.imageWidget!);
    } else if (widget.image != null) {
      imageWidgets.add(ClipRRect(
        borderRadius: BorderRadius.circular(widget.imageCircle ?? 50),
        child: Image(
          image: widget.image!,
          width: imageSize,
          height: imageSize,
          fit: BoxFit.cover,
        ),
      ));
    }

    if (imageWidgets.isNotEmpty) {
      imageWidgets.add(SizedBox(width: TDTheme.of(context).spacer12));
    }

    return imageWidgets;
  }
}

