import 'package:flutter/material.dart';

import '../../../tdesign_flutter.dart';

/// 单元格组件样式
class TDCellStyle {
  TDCellStyle({
    this.context,
    this.leftIconColor,
    this.rightIconColor,
    this.titleStyle,
    this.requiredStyle,
    this.descriptionStyle,
    this.noteStyle,
    this.arrowColor,
    this.borderedColor,
    this.groupBorderedColor,
    this.backgroundColor,
    this.clickBackgroundColor,
    this.groupTitleStyle,
    this.padding,
    this.cardBorderRadius,
    this.cardPadding,
    this.titlePadding,
    @deprecated this.titleBackgroundColor,
  }) {
    if (context != null) {
      defaultStyle(context!);
    }
  }

  /// 传递context，会生成默认样式
  BuildContext? context;

  /// 左侧图标颜色
  Color? leftIconColor;

  /// 右侧图标颜色
  Color? rightIconColor;

  /// 标题文字样式
  TextStyle? titleStyle;

  /// 必填星号文字样式
  TextStyle? requiredStyle;

  /// 内容描述文字样式
  TextStyle? descriptionStyle;

  /// 说明文字样式
  TextStyle? noteStyle;

  /// 箭头颜色
  Color? arrowColor;

  /// 单元格边框颜色
  Color? borderedColor;

  /// 单元格组边框颜色
  Color? groupBorderedColor;

  /// 默认状态背景颜色
  Color? backgroundColor;

  /// 点击状态背景颜色
  Color? clickBackgroundColor;

  /// 单元组标题文字样式
  TextStyle? groupTitleStyle;

  /// 单元格内边距
  EdgeInsets? padding;

  /// 卡片模式边框圆角
  BorderRadius? cardBorderRadius;

  /// 卡片模式内边距
  EdgeInsets? cardPadding;

  /// 单元格组标题内边距
  EdgeInsets? titlePadding;

  /// 单元格组标题背景颜色
  Color? titleBackgroundColor;

  /// 生成单元格默认样式
  TDCellStyle.cellStyle(BuildContext context) {
    defaultStyle(context);
  }

  defaultStyle(BuildContext context) {
    backgroundColor = TDTheme.of(context).bgColorContainer;
    clickBackgroundColor = TDTheme.of(context).bgColorContainerHover;
    leftIconColor = TDTheme.of(context).brandNormalColor;
    rightIconColor = TDTheme.of(context).brandNormalColor;
    titleStyle = TextStyle(
      color: TDTheme.of(context).textColorPrimary,
      fontSize: TDTheme.of(context).fontBodyLarge?.size ?? 16,
      height: TDTheme.of(context).fontBodyLarge?.height ?? 24,
      fontWeight: FontWeight.w400,
    );
    requiredStyle =
        titleStyle!.copyWith(color: TDTheme.of(context).errorNormalColor);
    descriptionStyle = TextStyle(
      color: TDTheme.of(context).textColorSecondary,
      fontSize: TDTheme.of(context).fontBodyMedium?.size ?? 14,
      height: TDTheme.of(context).fontBodyMedium?.height ?? 22,
      fontWeight: FontWeight.w400,
    );
    noteStyle =
        titleStyle!.copyWith(color: TDTheme.of(context).textColorPlaceholder);
    arrowColor = TDTheme.of(context).textColorPlaceholder;

    groupBorderedColor = TDTheme.of(context).componentStrokeColor;
    borderedColor = TDTheme.of(context).componentStrokeColor;
    groupTitleStyle = TextStyle(
      color: TDTheme.of(context).textColorPrimary,
      fontSize: TDTheme.of(context).fontTitleLarge?.size ?? 18,
      height: TDTheme.of(context).fontTitleLarge?.height ?? 26,
      fontWeight:
          TDTheme.of(context).fontTitleLarge?.fontWeight ?? FontWeight.w600,
    );

    padding = EdgeInsets.all(TDTheme.of(context).spacer16);
    cardBorderRadius =
        BorderRadius.all(Radius.circular(TDTheme.of(context).radiusLarge));
    cardPadding =
        EdgeInsets.symmetric(horizontal: TDTheme.of(context).spacer16);
    titlePadding = EdgeInsets.only(
      left: TDTheme.of(context).spacer16,
      right: TDTheme.of(context).spacer16,
      top: TDTheme.of(context).spacer24,
      bottom: TDTheme.of(context).spacer8,
    );
    titleBackgroundColor = Colors.transparent;
  }
}

Checkbox.check_box:
import 'package:flutter/material.dart';
import '../../../tdesign_flutter.dart';

import '../../util/auto_size.dart';

///
/// 选择框的样式
///
enum TDCheckboxStyle {
  circle, // 圆形
  square, // 方形
  check, // 无背景勾选样式
}

///
/// 内容相对icon的位置，上、下、左、右，默认内容在icon的右边
///
enum TDContentDirection {
  left, // content在icon的左边
  right, // content在icon的右边
}

enum TDCheckBoxSize {
  large, // 大 高度56
  small, // 小 高度48
}

///
/// 自定义Icon
///
typedef IconBuilder = Widget? Function(BuildContext context, bool checked);

///
/// 自定义Content
///
typedef ContentBuilder = Widget Function(
    BuildContext context, bool checked, String? content);

typedef OnCheckValueChanged = void Function(bool selected);

///
/// 复选框组件。
///
/// FuiCheckbox支持3种内置样式的的复选框，还支持各种自定义样式，除了提供勾选之外还提供了内
/// 容选项，内容包含一个主标题和副标题，并且支持完全自定义内容，支持指定内容的方向等等
///
///
class TDCheckbox extends StatefulWidget {
  const TDCheckbox(
      {this.id,
      Key? key,
      this.title,
      this.subTitle,
      this.titleFont,
      this.subTitleFont,
      this.enable = true,
      this.checked = false,
      this.titleMaxLine,
      this.subTitleMaxLine = 1,
      this.customIconBuilder,
      this.customContentBuilder,
      this.insetSpacing = 16,
      this.style,
      this.spacing,
      this.backgroundColor,
      this.selectColor,
      this.disableColor,
      this.size = TDCheckBoxSize.small,
      this.cardMode = false,
      this.showDivider = true,
      this.contentDirection = TDContentDirection.right,
      this.onCheckBoxChanged,
      this.titleColor,
      this.subTitleColor,
      this.checkBoxLeftSpace,
      this.customSpace})
      : super(key: key);

  /// id
  /// 当FuiCheckBox嵌入到FuiCheckBoxGroup内时，这个值需要赋值，否则不会被纳入Group管理
  final String? id;

  /// 文本
  final String? title;

  /// 标题字体大小
  final Font? titleFont;

  /// 辅助文字
  final String? subTitle;

  /// 副标题字体大小
  final Font? subTitleFont;

  /// 不可用
  final bool enable;

  /// 选中状态。默认为`false`
  /// 当FuiCheckBox嵌入到FuiCheckBoxGroup的时候，这个值表示初始状态，后续的状态会由Group管理
  final bool checked;

  /// 标题的行数
  final int? titleMaxLine;

  /// 辅助文字的行数
  final int? subTitleMaxLine;

  /// 文字和非图标侧的距离
  final double? insetSpacing;

  /// icon和文字的距离
  final double? spacing;

  /// 复选框样式：圆形或方形
  final TDCheckboxStyle? style;

  /// 复选框大小
  final TDCheckBoxSize size;

  /// 展示为卡片模式
  final bool cardMode;

  /// 是否展示分割线
  final bool showDivider;

  /// 文字相对icon的方位
  final TDContentDirection contentDirection;

  /// 切换监听
  final OnCheckValueChanged? onCheckBoxChanged;

  /// 自定义Checkbox显示样式
  final IconBuilder? customIconBuilder;

  /// 完全自定义内容
  final ContentBuilder? customContentBuilder;

  /// 背景颜色
  final Color? backgroundColor;

  /// 选择颜色
  final Color? selectColor;

  /// 禁用选择颜色
  final Color? disableColor;

  /// 标题文字颜色
  final Color? titleColor;

  /// 副标题文字颜色
  final Color? subTitleColor;

  /// 选项框左侧间距
  final double? checkBoxLeftSpace;

  /// 自定义组件间距
  final EdgeInsetsGeometry? customSpace;

  @override
  State createState() => TDCheckboxState();

  /// 默认的checkBox icon
  Widget buildDefaultIcon(
      BuildContext context, TDCheckboxGroupState? groupState, bool isChecked) {
    if (cardMode == true) {
      return Container();
    }
    Widget current;
    var size = 24.0;
    final style =
        this.style ?? groupState?.widget.style ?? TDCheckboxStyle.circle;
    final theme = TDTheme.of(context);
    final deSelectedColor = style == TDCheckboxStyle.check
        ? Colors.transparent
        : theme.componentBorderColor;
    current = Icon(
      style == TDCheckboxStyle.circle
          ? isChecked
              ? TDIcons.check_circle_filled
              : TDIcons.circle
          : style == TDCheckboxStyle.square
              ? isChecked
                  ? TDIcons.check_rectangle_filled
                  : TDIcons.rectangle
              : isChecked
                  ? TDIcons.check
                  : TDIcons.check,
      size: size,
      color: !enable
          ? (isChecked
              ? (disableColor ?? theme.brandDisabledColor)
              : deSelectedColor)
          : isChecked
              ? selectColor ?? theme.brandNormalColor
              : deSelectedColor,
    );
    return current;
  }
}

class TDCheckboxState extends State<TDCheckbox> {
  bool checked = false;
  bool _pressed = false;

  /// 不可取消勾选，在radioButton的严格模式下，只能切换不能取消勾选
  bool canNotCancel = false;

  @override
  void initState() {
    checked = widget.checked;
    super.initState();
  }

  @override
  void didUpdateWidget(TDCheckbox oldWidget) {
    checked = widget.checked;
    super.didUpdateWidget(oldWidget);
  }

  double _spacing(TDCheckboxGroupState? groupState) {
    return widget.spacing ?? groupState?.widget.spacing ?? 8;
  }

  EdgeInsets _getPadding(TDCheckBoxSize size) {
    if (widget.cardMode) {
      return const EdgeInsets.only(top: 16);
    }
    switch (size) {
      case TDCheckBoxSize.small:
        return const EdgeInsets.only(top: 12, bottom: 12);
      case TDCheckBoxSize.large:
        return const EdgeInsets.only(top: 16, bottom: 16);
    }
  }

  @override
  Widget build(BuildContext context) {
    // 检查是否包含在FuiCheckBoxGroup内，如果是的话，状态由Group管理
    final groupState = TDCheckboxGroupInherited.of(context)?.state;
    final id = widget.id;
    //  只有设置了id的CheckBox才会纳入Group管理
    if (groupState != null && id != null) {
      // CheckBox嵌入在CheckBoxGroup的勾选状态的获取优先级：
      // 1.CheckBoxGroup里设置的checkedIds包含当前id
      // 2.没有checkedIds属性，且没有勾选过，使用当前CheckedBox设置的checked属性
      // 3.用户勾选之后的状态
      checked = groupState.getCheckBoxStateById(id, checked);
    }

    // 构建icon
    var icon = _buildCheckboxIcon(context, groupState, checked);

    // 内容
    var content = _buildContent(context, groupState, checked);

    if (icon == null && content == null) {
      throw Exception('Icon and content cannot both be null!');
    }

    Widget? current;

    if (icon == null) {
      current = content!;
    } else {
      current = icon;

      if (content != null) {
        final spacing = _spacing(groupState);
        var contentDirection =
            groupState?.widget.contentDirection ?? widget.contentDirection;
        switch (contentDirection) {
          case TDContentDirection.left:
            current = Stack(
              alignment: Alignment.bottomCenter,
              children: [
                Padding(
                  padding: widget.customSpace ?? _getPadding(widget.size),
                  child: Column(
                    crossAxisAlignment: CrossAxisAlignment.start,
                    children: [
                      Row(
                        mainAxisSize: MainAxisSize.min,
                        crossAxisAlignment: CrossAxisAlignment.center,
                        children: [
                          Expanded(
                              child: Padding(
                            padding: EdgeInsets.only(
                                left: widget.insetSpacing ?? 16),
                            child: content,
                          )),
                          SizedBox(
                            width: spacing,
                          ),
                          Padding(
                            padding: const EdgeInsets.only(right: 16),
                            child: icon,
                          ),
                        ],
                      ),
                      Visibility(
                        visible:
                            widget.subTitle != null && widget.subTitle != '',
                        child: Padding(
                          padding: EdgeInsets.only(
                              left: widget.insetSpacing ?? 16, right: 16),
                          child: TDText(widget.subTitle ?? '',
                              maxLines: widget.subTitleMaxLine,
                              overflow: TextOverflow.ellipsis,
                              textColor: widget.enable
                                  ? (widget.subTitleColor ??
                                      TDTheme.of(context).textColorPlaceholder)
                                  : TDTheme.of(context).textDisabledColor,
                              font: TDTheme.of(context).fontBodyMedium),
                        ),
                      )
                    ],
                  ),
                ),
                Visibility(
                    visible: !widget.cardMode && widget.showDivider,
                    child: const TDDivider(
                      margin: EdgeInsets.only(left: 16),
                    ))
              ],
            );
            break;
          case TDContentDirection.right:
            current = Stack(
              alignment: Alignment.bottomCenter,
              children: [
                Padding(
                  padding: widget.customSpace ?? _getPadding(widget.size),
                  child: Column(
                    crossAxisAlignment: CrossAxisAlignment.start,
                    children: [
                      Row(
                        mainAxisSize: MainAxisSize.min,
                        crossAxisAlignment: CrossAxisAlignment.center,
                        children: [
                          Padding(
                            padding: EdgeInsets.only(
                                left: widget.checkBoxLeftSpace ?? 16),
                            child: icon,
                          ),
                          SizedBox(
                            width: widget.cardMode ? 0 : spacing,
                          ),
                          Expanded(
                              child: Padding(
                            padding: EdgeInsets.only(
                                right: widget.insetSpacing ?? 16),
                            child: content,
                          )),
                        ],
                      ),
                      Visibility(
                        visible:
                            widget.subTitle != null && widget.subTitle != '',
                        child: Padding(
                          padding: EdgeInsets.only(
                              top: widget.cardMode ? 4.scale : 0,
                              left: widget.cardMode ? 16 : 48,
                              right: widget.insetSpacing ?? 16),
                          child: TDText(widget.subTitle ?? '',
                              maxLines: widget.subTitleMaxLine,
                              overflow: TextOverflow.ellipsis,
                              textColor: widget.enable
                                  ? (widget.subTitleColor ??
                                      TDTheme.of(context).textColorPlaceholder)
                                  : TDTheme.of(context).textDisabledColor,
                              font: widget.subTitleFont ??
                                  TDTheme.of(context).fontBodyMedium),
                        ),
                      )
                    ],
                  ),
                ),
                Visibility(
                    visible: !widget.cardMode && widget.showDivider,
                    child: const TDDivider(
                      margin: EdgeInsets.only(left: 48),
                    ))
              ],
            );
            break;
        }
      }
    }

    if (!(canNotCancel && checked)) {
      if (_pressed) {
        // 点击效果
        current = Opacity(
          opacity: 0.68,
          child: current,
        );
      }

      // 开关样式的话自己会处理点击事件
      current = GestureDetector(
        behavior: HitTestBehavior.translucent,
        onTapDown: (detail) {
          _pressState(true);
        },
        onTapUp: (detail) {
          _pressState(false);
        },
        onTapCancel: () {
          _pressState(false);
        },
        onTap: () {
          onValueChange(id, !checked, groupState);
        },
        child: current,
      );
    }

    return Container(
      clipBehavior: widget.cardMode ? Clip.hardEdge : Clip.none,
      decoration: BoxDecoration(
          color: widget.backgroundColor ?? TDTheme.of(context).bgColorContainer,
          border: widget.cardMode
              ? checked
                  ? Border.all(
                      width: 1.5,
                      color: widget.selectColor ??
                          TDTheme.of(context).brandNormalColor)
                  : Border.all(width: 1.5, color: Colors.transparent)
              : null,
          borderRadius: widget.cardMode
              ? const BorderRadius.all(Radius.circular(6))
              : null),
      child: Stack(
        children: [
          current,
          Positioned(
              top: 0,
              left: 0,
              child: Visibility(
                  visible: widget.cardMode && checked,
                  child: RadioCornerIcon(
                    length: 28,
                    radius: 4,
                    selectColor: widget.selectColor,
                  ))),
        ],
      ),
    );
  }

  /// 点击效果
  void _pressState(bool pressed) {
    if (!widget.enable) {
      return;
    }
    _pressed = pressed;
    setState(() {});
  }

  /// 选中状态的变化
  void onValueChange(
    String? id,
    bool value,
    TDCheckboxGroupState? groupState,
  ) {
    if (!widget.enable) {
      return;
    }
    setState(() {
      checked = value;
      if (groupState != null && id != null) {
        groupState.toggle(id, checked);
      }
      widget.onCheckBoxChanged?.call(checked);
    });
  }

  ///
  /// 构建选择框边上的文本内容
  ///
  Widget? _buildContent(
    BuildContext context,
    TDCheckboxGroupState? groupState,
    bool checked,
  ) {
    final title = widget.title;
    final customContent =
        widget.customContentBuilder ?? groupState?.widget.customContentBuilder;

    var content = customContent?.call(context, checked, title);
    if (content == null) {
      if (title != null || customContent != null && title != null) {
        content = TDText(title,
            maxLines: widget.titleMaxLine ?? groupState?.widget.titleMaxLine,
            overflow: TextOverflow.ellipsis,
            textColor: widget.enable
                ? (widget.titleColor ?? TDTheme.of(context).textColorPrimary)
                : TDTheme.of(context).textDisabledColor,
            font: widget.titleFont ??
                TDTheme.of(context)
                    .fontBodyLarge); // TODO custom fontSize https://github.com/Tencent/tdesign-flutter/issues/66
      }
    }
    return content;
  }

  /// 构建icon
  Widget? _buildCheckboxIcon(
      BuildContext context, TDCheckboxGroupState? groupState, bool isCheck) {
    final iconBuilder =
        widget.customIconBuilder ?? groupState?.widget.customIconBuilder;
    if (iconBuilder != null) {
      return iconBuilder.call(context, isCheck);
    }
    return widget.buildDefaultIcon(context, groupState, isCheck);
  }
}

class RadioCornerIcon extends StatelessWidget {
  final double length;
  final double radius;
  final Color? selectColor;

  const RadioCornerIcon(
      {Key? key,
      required this.length,
      required this.radius,
      required this.selectColor})
      : super(key: key);

  @override
  Widget build(BuildContext context) {
    return SizedBox(
      width: length,
      height: length,
      child: Stack(
        alignment: Alignment.topLeft,
        children: [
          CustomPaint(
            painter: RadioCorner(
                length: length,
                radius: radius,
                fillColor: selectColor ?? TDTheme.of(context).brandNormalColor),
          ),
          const Positioned(
              top: 3,
              left: 2,
              child: Icon(
                TDIcons.check,
                size: 14,
                color: Colors.white,
              ))
        ],
      ),
    );
  }
}

class RadioCorner extends CustomPainter {
  final double length;
  final double radius;
  final Color fillColor;

  RadioCorner(
      {required this.length, required this.radius, required this.fillColor});

  @override
  void paint(Canvas canvas, Size size) {
    var paint = Paint()
      ..isAntiAlias = true
      ..strokeWidth = 1
      ..color = fillColor
      ..style = PaintingStyle.fill;
    var rect = Rect.fromCircle(center: Offset(radius, radius), radius: radius);
    var pi = 3.1415;
    var path = Path();
    path.moveTo(0, radius);
    path.addArc(
      rect,
      180 * (pi / 180.0),
      90 * (pi / 180.0),
    );
    path.moveTo(radius, 0);
    path.lineTo(length, 0);
    path.lineTo(0, length);
    path.lineTo(0, radius);
    canvas.drawPath(path, paint);
  }

  @override
  bool shouldRepaint(covariant CustomPainter oldDelegate) => false;
}

Checkbox.check_box_group:
import 'package:flutter/cupertino.dart';

import '../../util/auto_size.dart';
import '../../util/map_ext.dart';
import 'td_check_box.dart';

///
/// CheckBoxGroup变化监听器
///
typedef OnGroupChange = void Function(List<String> checkedIds);

///
/// 控制CheckBoxGroup
///
class TDCheckboxGroupController {
  TDCheckboxGroupState? _state;

  ///
  /// 选择全部
  /// 这个方法会忽略最大可够选数
  ///
  void toggleAll(bool check) {
    _state?.toggleAll(check);
  }

  ///
  /// 反选
  ///
  void reverseAll() {
    _state?._reverseAll();
  }

  ///
  /// 选中某一个选项
  ///
  void toggle(String id, bool check) {
    _state?.toggle(id, check, true);
  }

  ///
  /// 打勾的选项
  ///
  List<String> allChecked() {
    return _state?.checkBoxStates.where((k, v) => v).keys.toList() ?? [];
  }

  ///
  /// 某一项的选中状态
  ///
  bool checked(String id) {
    var list = allChecked();
    return list.contains(id);
  }
}

///
/// CheckBox组，可以通过控制器控制组内的多个CheckBox的选择状态
///
/// child的属性可以是任意包含TDCheckBox的容器组件，例如：
/// ```dart
/// TDCheckboxGroup(
///   child: Row(
///     children: [
///       TDCheckBox(),
///       Column(
///         children: [
///           TDCheckBox()
///           ...
///         ]
///       )
///       ...
///     ]
///   )
/// )
/// ```
///
///
class TDCheckboxGroup extends StatefulWidget {

  const TDCheckboxGroup(
      {required this.child,
        Key? key,
        this.onChangeGroup,
        this.controller,
        this.checkedIds,
        this.maxChecked,
        this.titleMaxLine,
        this.customContentBuilder,
        this.contentDirection,
        this.style,
        this.spacing,
        this.customIconBuilder,
        this.onOverloadChecked}) : super(key: key);

  ///
  /// 可以是任意包含TDCheckBox的容器，比如：
  /// ```
  /// Row(
  ///   children: [
  ///     TDCheckBox(),
  ///     TDCheckBox(),
  ///     ...
  ///   ]
  /// )
  /// ```
  ///
  final Widget child;

  /// 状态变化监听器
  final OnGroupChange? onChangeGroup;

  /// 可以通过控制器操作勾选状态
  final TDCheckboxGroupController? controller;

  /// 最多可以勾选多少
  final int? maxChecked;

  /// 勾选的CheckBox id列表
  final List<String>? checkedIds;

  /// 超过最大可勾选的个数
  final VoidCallback? onOverloadChecked;

  /// CheckBox标题的行数
  final int? titleMaxLine;


  /// CheckBox完全自定义内容
  final ContentBuilder? customContentBuilder;

  /// CheckBoxicon和文字的距离
  final double? spacing;

  /// CheckBox复选框样式：圆形或方形
  final TDCheckboxStyle? style;

  /// 文字相对icon的方位
  final TDContentDirection? contentDirection;

  /// 自定义选择icon的样式
  final IconBuilder? customIconBuilder;

  @override
  State<StatefulWidget> createState() {
    return TDCheckboxGroupState();
  }
}


class TDCheckboxGroupState extends State<TDCheckboxGroup> {
  ///
  /// 管理所有子CheckBox的状态
  ///
  Map<String, bool> checkBoxStates = {};

  @override
  void initState() {
    super.initState();
    // 如果有controller的话，把state设置给controller
    widget.controller?._state = this;

    _syncCheckState(widget.checkedIds);
  }

  /// 把group中配置的默认选中id，同步到状态中
  void _syncCheckState(List<String>? checkIds) {
    checkBoxStates.clear();
    checkIds?.forEach((element) {
      checkBoxStates[element] = true;
    });
  }


  @override
  void didUpdateWidget(TDCheckboxGroup oldWidget) {
    super.didUpdateWidget(oldWidget);
    final oldCheckIds = oldWidget.checkedIds;
    final newCheckIds = widget.checkedIds;
    if (oldCheckIds != newCheckIds) {
      _syncCheckState(newCheckIds);
    }
  }


  ///
  /// 根据id获取CheckBox的勾选状态
  ///
  ///
  bool getCheckBoxStateById(String id, bool checked) {
    if (checkBoxStates[id] == null) {
      // checkBox本身的状态
      checkBoxStates[id] = checked;
    }
    return checkBoxStates[id]!;
  }

  /// 勾选单个CheckBox
  bool toggle(String id, bool check, [bool notify = false]) {
    // 检查是否超过用户设置的最大可勾选数
    if (widget.maxChecked != null && check) {
      if (checkBoxStates.count((k, v) => v) >= widget.maxChecked!) {
        widget.onOverloadChecked?.call();
        return false;
      }
    }
    checkBoxStates[id] = check;
    if (notify) {
      setState(() {});
    }
    _notifyChange();
    return true;
  }

  /// 操作所有CheckBox
  void toggleAll(bool check, [bool notify = true]) {
    var isChanged = false;
    checkBoxStates.forEachCanBreak((k, v) {
      if (check) {
        if (!toggle(k, check)) {
          // 勾选失败，退出循环
          return true;
        }
      } else {
        toggle(k, check);
      }
      isChanged = true;
      return false;
    });

    if (isChanged && notify) {
      setState(() {});
      _notifyChange();
    }
  }

  /// 反选
  void _reverseAll() {
    final reverseValue =
    checkBoxStates.map((key, value) => MapEntry(key, !value));
    checkBoxStates.forEach((key, value) {
      checkBoxStates[key] = false;
    });
    checkBoxStates.forEach((k, v) {
      var check = reverseValue[k] ?? false;
      toggle(k, check);
    });
    setState(() {});
    _notifyChange();
  }

  void _notifyChange() {
    final change = widget.onChangeGroup;
    if (change != null) {
      final checkedIds = checkBoxStates.where((k, v) => v).keys.toList();
      change.call(checkedIds);
    }
  }

  @override
  Widget build(BuildContext context) {
    return TDCheckboxGroupInherited(this, widget.child);
  }
}

class TDCheckboxGroupInherited extends InheritedWidget {
  final TDCheckboxGroupState state;

  ///
  /// 获取树上的Group节点
  ///
  static TDCheckboxGroupInherited? of(BuildContext context) {
    return context.dependOnInheritedWidgetOfExactType<TDCheckboxGroupInherited>();
  }

  const TDCheckboxGroupInherited(this.state, Widget child, {Key? key}) : super(child: child, key: key);

  @override
  bool updateShouldNotify(covariant TDCheckboxGroupInherited oldWidget) {
    return true;
  }
}


class TDCheckboxGroupContainer extends TDCheckboxGroup {

  TDCheckboxGroupContainer({
    Key? key,
    Widget? child, // 使用child 则请勿设置direction
    Axis? direction, // direction 对 directionalTdRadios 起作用
    List<TDCheckbox>? directionalTdCheckboxes,
    List<String>? selectIds, // 默认选择项的id组
    bool? passThrough, // 非通栏单选样式 用于使用child 或 direction == Axis.vertical 场景
    bool cardMode = false,
    int? titleMaxLine, // item的行数
    int? maxSelected, // 最大勾选数
    TDCheckboxStyle? style,// 勾选样式
    TDCheckboxGroupController? controller,
    IconBuilder? customIconBuilder,
    ContentBuilder? customContentBuilder,
    double? spacing, // icon和文字距离
    TDContentDirection? contentDirection,
    OnCheckBoxGroupChange? onCheckBoxGroupChange,
    VoidCallback? onOverloadChecked,
  })  : assert(() {
    // 使用direction属性则必须配合directionalTdCheckboxes，child字段无效
    if (direction != null && directionalTdCheckboxes == null) {
      throw FlutterError(
          '[TDCheckboxGroupContainer] direction and directionalTdCheckboxes must set at the same time');
    }
    // 未使用direction则必须设置child
    if (direction == null && child == null) {
      throw FlutterError(
          '[TDCheckboxGroupContainer] direction means use child as the exact one, but child is null');
    }
    // 横向单选框 每个选项有字数限制
    if (direction == Axis.horizontal && directionalTdCheckboxes != null) {
      directionalTdCheckboxes.forEach((element) {
        if (element.subTitle != null) {
          throw FlutterError(
              'horizontal checkbox style should not have subTilte, '
                  'because there left no room for it');
        }
      });
      var maxWordCount = 2;
      var tips =
          '[TDCheckboxGroupContainer] checkbox title please not exceed $maxWordCount words.\n'
          '2tabs: 7words maximum\n'
          '3tabs: 4words maximum\n'
          '4tabs: 2words maximum';
      if (directionalTdCheckboxes.length == 2) {
        maxWordCount = 7;
      }
      if (directionalTdCheckboxes.length == 3) {
        maxWordCount = 4;
      }
      if (directionalTdCheckboxes.length == 4) {
        maxWordCount = 2;
      }
      directionalTdCheckboxes.forEach((checkbox) {
        if ((checkbox.title?.length ?? 0) > maxWordCount) {
          throw FlutterError(tips);
        }
      });
    }
    // 卡片模式要求每个TDRadio必须设置cardMode属性为true，且不能有子标题（空间不够）
    if (cardMode == true) {
      assert(direction != null && directionalTdCheckboxes != null);
      directionalTdCheckboxes!.forEach((element) {
        // if use cardMode at TDRadioGroup, then every TDRadio should
        // set it's own carMode to true.
        if (element.cardMode == false) {
          throw FlutterError(
              'if use cardMode at TDCheckboxGroupContainer, then every '
                  'TDCheckbox should set it\'s own carMode to true.');
        }
        if (element.subTitle != null && direction == Axis.horizontal) {
          throw FlutterError(
              'horizontal card style should not have subTilte, '
                  'because there left no room for it');
        }
      });
    }
    return true;
  }()),
        super(
        child: Container(
          clipBehavior: (passThrough ?? false) && direction != Axis.horizontal
              ? Clip.hardEdge
              : Clip.none,
          decoration: (passThrough ?? false) && direction != Axis.horizontal
              ? BoxDecoration(borderRadius: BorderRadius.circular(10))
              : null,
          margin: (passThrough ?? false) && direction != Axis.horizontal
              ? const EdgeInsets.symmetric(horizontal: 16)
              : null,
          child: direction == null
              ? child!
              : (direction == Axis.vertical
              ? ListView.separated(
            padding: const EdgeInsets.all(0),
            shrinkWrap: true,
            physics: const NeverScrollableScrollPhysics(),
            itemBuilder: (BuildContext context, int index) {
              return Container(
                margin: cardMode
                    ? const EdgeInsets.symmetric(horizontal: 16)
                    : null,
                height: cardMode ? 82 : null,
                child: directionalTdCheckboxes[index],
              );
            },
            itemCount: directionalTdCheckboxes!.length,
            separatorBuilder: (BuildContext context, int index) {
              if (cardMode) {
                return const SizedBox(
                  height: 12,
                );
              }
              return const SizedBox.shrink();
            },
          )
              : Container(
            margin: cardMode
                ? EdgeInsets.symmetric(horizontal: 16.scale)
                : null,
            alignment: cardMode ? Alignment.topLeft : null,
            child: cardMode
                ? Wrap(
              spacing: 12.scale,
              runSpacing: 12,
              runAlignment: WrapAlignment.spaceEvenly,
              children: directionalTdCheckboxes!.map((element) {
                return SizedBox(
                  width: 106.3.scale,
                  height: 56,
                  child: element,
                );
              }).toList(),
            )
                : Row(
              mainAxisSize: MainAxisSize.min,
              children: directionalTdCheckboxes!
                  .map((e) => Expanded(child: e))
                  .toList(),
            ),
          )),
        ),
        key: key,
        onChangeGroup: (ids) {
          selectIds = ids;
          onCheckBoxGroupChange?.call(ids);
        },
        onOverloadChecked: onOverloadChecked,
        controller: controller,
        checkedIds: selectIds,
        maxChecked: maxSelected,
        titleMaxLine: titleMaxLine,
        contentDirection: contentDirection,
        customIconBuilder: customIconBuilder,
        customContentBuilder: customContentBuilder,
        style: style,
        spacing: spacing,
      );

  @override
  State<StatefulWidget> createState() {
    return TDCheckboxGroupContainerState();
  }
}

class TDCheckboxGroupContainerState extends TDCheckboxGroupState {

}

typedef OnCheckBoxGroupChange = void Function(List<String> ids);

Collapse.collapse:
/*
 * Created by dorayhong@tencent.com on 6/4/23.
 */

import 'package:flutter/material.dart';

import '../../../tdesign_flutter.dart';
import 'td_collapse_salted_key.dart';
import 'td_inset_divider.dart';
import 'td_nonanimated_expand_icon.dart';

/// 折叠面板的组件样式
enum TDCollapseStyle {
  /// Block 通栏风格
  block,

  /// Card 卡片风格
  card
}

/// 折叠面板列表组件，需配合 [TDCollapsePanel] 使用
class TDCollapse extends StatefulWidget {
  const TDCollapse({
    required this.children,
    this.style = TDCollapseStyle.block,
    this.expansionCallback,
    this.animationDuration = kThemeAnimationDuration,
    this.elevation = 0,
    Key? key,
  })  : _allowOnlyOnePanelOpen = false,
        initialOpenPanelValue = null,
        super(key: key);

  const TDCollapse.accordion({
    required this.children,
    this.style = TDCollapseStyle.block,
    this.expansionCallback,
    this.animationDuration = kThemeAnimationDuration,
    this.elevation = 0,
    this.initialOpenPanelValue,
    Key? key,
  })  : _allowOnlyOnePanelOpen = true,
        super(key: key);

  /// 折叠面板列表的样式
  /// - [TDCollapseStyle.block] 通栏风格
  /// - [TDCollapseStyle.card] 卡片风格
  final TDCollapseStyle style;

  /// 折叠面板列表的子组件
  final List<TDCollapsePanel> children;

  /// 折叠面板列表的回调函数；
  /// 回调时，入参为当前点击的折叠面板的索引 index 和是否展开的状态 isExpanded
  final ExpansionPanelCallback? expansionCallback;

  /// 折叠面板列表的动画时长
  final Duration animationDuration;

  /// 折叠面板列表的阴影
  final double elevation;

  /// 折叠面板列表的默认展开面板的值；
  /// 当使用 [TDCollapse.accordion] 时，此值生效
  final Object? initialOpenPanelValue;

  final bool _allowOnlyOnePanelOpen;

  @override
  State createState() => _TDCollapseState();
}

class _TDCollapseState extends State<TDCollapse> {
  TDCollapsePanel? _currentOpenPanel;

  @override
  void initState() {
    super.initState();

    if (!widget._allowOnlyOnePanelOpen) {
      return;
    }

    assert(_allPanelsHaveValue(),
        'When allowing only one panel to be open, every panel must have a value.');
    assert(_allPanelsHaveDistinctValues(),
        'When allowing only one panel to be open, every panel must have a distinct value.');

    if (widget.initialOpenPanelValue != null) {
      _currentOpenPanel = _searchPanelByValue(widget.initialOpenPanelValue);
    }
  }

  @override
  void didUpdateWidget(TDCollapse oldWidget) {
    super.didUpdateWidget(oldWidget);

    if (!widget._allowOnlyOnePanelOpen) {
      _currentOpenPanel = null;
      return;
    }

    assert(_allPanelsHaveValue(),
        'When allowing only one panel to be open, every panel must have a value.');
    assert(_allPanelsHaveDistinctValues(),
        'When allowing only one panel to be open, every panel must have a distinct value.');

    // when the widget is updated to accordion mode
    // we need to initialize the current open panel to defaultOpenPanelValue
    if (!oldWidget._allowOnlyOnePanelOpen) {
      _currentOpenPanel = _searchPanelByValue(widget.initialOpenPanelValue);
    }
  }

  @override
  Widget build(BuildContext context) {
    final items = <MergeableMaterialItem>[];

    for (var index = 0; index < widget.children.length; index += 1) {
      if (_isChildExpanded(index) &&
          index != 0 &&
          !_isChildExpanded(index - 1)) {
        items.add(_buildGap(context, index * 2 - 1));
      }

      final isLastChild = index == widget.children.length - 1;
      final child = widget.children[index];

      final titleWidget = _buildTitleWidget(context, child, index);
      final expandIconWidget = _buildExpandIconWidget(context, child, index);

      final borderRadius =
          _isCardStyle() ? _createRadius(index) : BorderRadius.zero;

      items.add(
        MaterialSlice(
            key: TDCollapseSaltedKey<BuildContext, int>(context, index * 2),
            color:
                child.backgroundColor ?? TDTheme.of(context).bgColorContainer,
            child: Column(
              // to prevent collapse state change when parent rebuild
              key: TDCollapseSaltedKey<BuildContext, int>(context, index * 2),
              children: [
                MergeSemantics(
                  child: InkWell(
                    borderRadius: borderRadius,
                    onTap: () => _handlePressed(index, _isChildExpanded(index)),
                    child: Row(
                      children: [
                        Expanded(
                          child: AnimatedContainer(
                            duration: widget.animationDuration,
                            curve: Curves.fastOutSlowIn,
                            margin: EdgeInsets.zero,
                            child: ConstrainedBox(
                              constraints: const BoxConstraints(
                                minHeight: kMinInteractiveDimension,
                              ),
                              child: titleWidget,
                            ),
                          ),
                        ),
                        expandIconWidget,
                      ],
                    ),
                  ),
                ),
                AnimatedCrossFade(
                  firstChild: Container(height: 0.0),
                  secondChild: Column(
                    children: [
                      const TDInsetDivider(),
                      Container(
                        padding: EdgeInsets.all(TDTheme.of(context).spacer16),
                        child: child.body,
                      ),
                    ],
                  ),
                  firstCurve:
                      const Interval(0.0, 0.6, curve: Curves.fastOutSlowIn),
                  secondCurve:
                      const Interval(0.4, 1.0, curve: Curves.fastOutSlowIn),
                  sizeCurve: Curves.fastOutSlowIn,
                  crossFadeState: _isChildExpanded(index)
                      ? CrossFadeState.showSecond
                      : CrossFadeState.showFirst,
                  duration: widget.animationDuration,
                ),
                if (!isLastChild) const TDInsetDivider()
              ],
            )),
      );

      if (_isChildExpanded(index) && !isLastChild) {
        items.add(_buildGap(context, index * 2 + 1));
      }
    }

    // FIXME: 非连续展开的 item 会导致 expanded 时动画丢失
    Widget collapse = MergeableMaterial(
      hasDividers: false,
      elevation: widget.elevation,
      children: items,
    );

    if (_isCardStyle()) {
      collapse = Container(
        child: ClipRRect(
          child: collapse,
          borderRadius: BorderRadius.circular(TDTheme.of(context).radiusLarge),
        ),
        margin: EdgeInsets.symmetric(
          horizontal: TDTheme.of(context).spacer16,
        ),
      );
    }

    return collapse;
  }

  MergeableMaterialItem _buildGap(BuildContext context, int value) {
    return MaterialGap(
      size: 0.0,
      key: TDCollapseSaltedKey<BuildContext, int>(context, value),
    );
  }

  BorderRadius _createRadius(int index) {
    final radius = Radius.circular(TDTheme.of(context).radiusLarge);

    final isFirst = index == 0;
    if (isFirst) {
      return BorderRadius.only(topLeft: radius, topRight: radius);
    }

    final isLast = index == widget.children.length - 1;
    if (isLast) {
      return BorderRadius.only(bottomLeft: radius, bottomRight: radius);
    }

    return BorderRadius.zero;
  }

  bool _isCardStyle() {
    return widget.style == TDCollapseStyle.card;
  }

  bool _isChildExpanded(int index) {
    final child = widget.children[index];

    if (widget._allowOnlyOnePanelOpen) {
      return _currentOpenPanel?.value == child.value;
    }

    return child.isExpanded;
  }

  void _handlePressed(int index, bool isExpanded) {
    widget.expansionCallback?.call(index, isExpanded);

    if (!widget._allowOnlyOnePanelOpen) {
      return;
    }

    // collapse the current open panel by calling its expansion callback to false
    for (var childIndex = 0;
        childIndex < widget.children.length;
        childIndex += 1) {
      final curChild = widget.children[childIndex];
      if (widget.expansionCallback != null &&
          childIndex != index &&
          curChild.value == _currentOpenPanel?.value) {
        widget.expansionCallback!(childIndex, false);
      }
    }

    setState(() {
      _currentOpenPanel = isExpanded ? null : widget.children[index];
    });
  }

  Widget _buildTitleWidget(
      BuildContext context, TDCollapsePanel child, int index) {
    final titleWidget = child.headerBuilder(context, _isChildExpanded(index));
    return ListTile(
      title: titleWidget,
    );
  }

  Widget _buildExpandIconWidget(
      BuildContext context, TDCollapsePanel child, int index) {
    Widget expandedIcon = Container(
      key: TDCollapseSaltedKey<BuildContext, int>(context, index * 2),
      margin: const EdgeInsetsDirectional.all(0.0),
      child: TdNonAnimatedExpandIcon(
        isExpanded: _isChildExpanded(index),
        padding: child.expandIconTextBuilder != null
            ? EdgeInsets.only(
                right: TDTheme.of(context).spacer16,
                top: TDTheme.of(context).spacer16,
                bottom: TDTheme.of(context).spacer16,
                left: 0,
              )
            : EdgeInsets.all(TDTheme.of(context).spacer16),
      ),
    );

    return Row(
      children: [
        if (child.expandIconTextBuilder != null)
          Text(child.expandIconTextBuilder!(context, _isChildExpanded(index)),
              textAlign: TextAlign.right,
              style: TextStyle(
                color: TDTheme.of(context).textColorPlaceholder,
              )),
        expandedIcon,
      ],
    );
  }

  bool _allPanelsHaveValue() {
    return widget.children.every((TDCollapsePanel child) {
      return child.value != null;
    });
  }

  bool _allPanelsHaveDistinctValues() {
    final valueSet = <Object?>{};
    return widget.children.every((TDCollapsePanel child) {
      if (!valueSet.add(child.value)) {
        return false;
      }
      return true;
    });
  }

  TDCollapsePanel? _searchPanelByValue(Object? value) {
    for (var index = 0; index < widget.children.length; index += 1) {
      final child = widget.children[index];
      if (child.value == value) {
        return child;
      }
    }
    return null;
  }
}

