Divider.dashed_widget:
import 'dart:math';

import 'package:flutter/material.dart';

import '../../../tdesign_flutter.dart';

/// 虚线控件
class DashedWidget extends StatelessWidget {
  const DashedWidget({
    Key? key,
    this.color,
    this.gap = 2,
    this.solidLength = 2,
    this.width,
    this.height = 0.5,
    this.direction = Axis.horizontal,
  })  : assert(gap > 0, 'gap 必须大于 0'),
        assert(solidLength > 0, 'solidLength 必须大于 0'),
        super(key: key);

  final Color? color;
  final double gap;
  final double solidLength;
  final double? width;
  final double height;
  final Axis direction;

  @override
  Widget build(BuildContext context) {
    if (direction == Axis.horizontal) {
      return SizedBox(
        width: width ?? MediaQuery.of(context).size.width,
        height: height,
        child: CustomPaint(
          painter: DashedPainter(
              color: color ?? TDTheme.of(context).componentStrokeColor,
              strokeWidth: height,
              direction: direction),
        ),
      );
    }

    return SizedBox(
      width: width,
      height: height ?? MediaQuery.of(context).size.height,
      child: CustomPaint(
        painter: DashedPainter(
            color: color ?? TDTheme.of(context).componentStrokeColor,
            strokeWidth: width ?? 1,
            direction: direction),
      ),
    );
  }
}

/// 绘制虚线自定义控件
class DashedPainter extends CustomPainter {
  DashedPainter({
    required this.color,
    this.strokeWidth = 1,
    this.gap = 2,
    this.solidLength = 2,
    this.direction = Axis.horizontal,
  });

  final Color color;
  final double strokeWidth;
  final double gap;
  final double solidLength;
  final Axis direction;

  @override
  void paint(Canvas canvas, Size size) {
    var paint = Paint()
      ..color = color
      ..style = PaintingStyle.stroke
      ..strokeWidth = strokeWidth;

    _drawDashedLine(canvas, paint, size);
  }

  void _drawDashedLine(Canvas canvas, Paint paint, Size size) {
    final isHorizontal = direction == Axis.horizontal;
    final lineLength = isHorizontal ? size.width : size.height;
    final fixedCoordinate = (isHorizontal ? size.height : size.width) / 2;

    double currentPosition = 0;
    var drawSolid = true;

    while (currentPosition < lineLength) {
      final segmentLength = drawSolid ? solidLength : gap;
      final nextPosition = min(currentPosition + segmentLength, lineLength);

      if (drawSolid) {
        final start = isHorizontal
            ? Offset(currentPosition, fixedCoordinate)
            : Offset(fixedCoordinate, currentPosition);
        final end = isHorizontal
            ? Offset(nextPosition, fixedCoordinate)
            : Offset(fixedCoordinate, nextPosition);
        canvas.drawLine(start, end, paint);
      }

      currentPosition = nextPosition;
      drawSolid = !drawSolid;
    }
  }

  @override
  bool shouldRepaint(DashedPainter oldDelegate) {
    return color != oldDelegate.color ||
        strokeWidth != oldDelegate.strokeWidth ||
        gap != oldDelegate.gap ||
        solidLength != oldDelegate.solidLength ||
        direction != oldDelegate.direction;
  }
}

Divider.divider:
import 'package:flutter/material.dart';

import '../../../tdesign_flutter.dart';
import 'dashed_widget.dart';

enum TextAlignment { left, center, right }

/// 分割线
/// 对于非flutter原有的控件，则只需满足TDesign规范即可；
/// 如果有业务在实际使用，还需兼容实际业务场景。
class TDDivider extends StatelessWidget {
  const TDDivider({
    Key? key,
    this.color,
    this.margin,
    this.width,
    this.height = 0.5,
    this.text,
    this.textStyle,
    this.widget,
    this.gapPadding,
    this.hideLine = false,
    this.isDashed = false,
    this.alignment = TextAlignment.center,
    this.direction = Axis.horizontal,
  }) : super(key: key);

  /// 线条颜色
  final Color? color;

  /// 文字位置
  final TextAlignment alignment;

  /// 外部填充
  final EdgeInsetsGeometry? margin;

  /// 线条和中间文本之间的填充
  final EdgeInsetsGeometry? gapPadding;

  /// 宽度，需要竖向线条时使用
  final double? width;

  /// 高度，横向线条使用
  final double height;

  /// 文本字符串，使用默认样式
  final String? text;

  /// 自定义文本样式
  final TextStyle? textStyle;

  /// 中间控件，可自定义样式
  final Widget? widget;

  /// 隐藏线条，使用纯文本分割
  final bool hideLine;

  /// 是否为虚线
  final bool isDashed;

  /// 方向，竖直虚线必须传
  final Axis direction;

  @override
  Widget build(BuildContext context) {
    // 普通直线
    if (widget == null && text == null) {
      return _buildLine(context, width: width, margin: margin);
    }

    // 隐藏线条，纯文本分割
    if (hideLine) {
      return Container(
        width: width,
        height: height,
        margin: margin,
        child: _buildMiddleWidget(context),
      );
    }

    // 文本 + 线条
    return _buildDivider(context, alignment);
  }

  Widget _buildDivider(BuildContext context, TextAlignment alignment) {
    final middleWidget = Padding(
        padding: gapPadding ?? const EdgeInsets.symmetric(horizontal: 8),
        child: _buildMiddleWidget(context));

    final line = Expanded(child: Center(child: _buildLine(context)));

    final lineWithWidth = _buildLine(context, width: 16);

    List<Widget> children;

    switch (alignment) {
      case TextAlignment.left:
        children = [lineWithWidth, middleWidget, line];
        break;
      case TextAlignment.center:
        children = [line, middleWidget, line];
        break;
      case TextAlignment.right:
        children = [line, middleWidget, lineWithWidth];
        break;
    }

    return Container(
      width: width,
      margin: margin,
      child: Row(
        crossAxisAlignment: CrossAxisAlignment.center,
        children: children,
      ),
    );
  }

  /// 绘制线条
  Container _buildLine(BuildContext context,
      {double? width, EdgeInsetsGeometry? margin}) {
    final lineColor = color ?? TDTheme.of(context).componentStrokeColor;

    if (isDashed) {
      return Container(
        width: width,
        margin: margin,
        child: DashedWidget(
          width: width,
          height: height,
          color: lineColor,
          direction: direction,
        ),
      );
    }

    return Container(
      width: width,
      height: height,
      margin: margin,
      color: lineColor,
    );
  }

  /// 构建中间控件
  Widget _buildMiddleWidget(BuildContext context) {
    return widget ??
        TDText(
          text,
          font: TDTheme.of(context).fontBodySmall,
          textColor: TDTheme.of(context).textColorPlaceholder,
          forceVerticalCenter: true,
          style: textStyle,
        );
  }
}

Drawer.drawer:
import 'package:flutter/material.dart';

import '../../theme/td_colors.dart';
import '../../theme/td_spacers.dart';
import '../../theme/td_theme.dart';
import '../cell/td_cell.dart';
import '../cell/td_cell_group.dart';
import '../cell/td_cell_style.dart';
import '../icon/td_icons.dart';
import '../popup/td_popup_route.dart';
import 'td_drawer_widget.dart';

/// 抽屉方向
enum TDDrawerPlacement { left, right }

/// 抽屉组件
class TDDrawer {
  TDDrawer(
    this.context, {
    this.closeOnOverlayClick = true,
    this.footer,
    this.items,
    this.placement = TDDrawerPlacement.right,
    this.showOverlay = true,
    this.title,
    this.titleWidget,
    this.visible,
    this.onClose,
    this.onItemClick,
    this.width = 280,
    this.drawerTop,
    this.style,
    this.hover = true,
    this.backgroundColor,
    this.bordered = true,
    this.isShowLastBordered = true,
    this.contentWidget,
  }) {
    if (visible == true) {
      show();
    }
  }

  /// 上下文
  final BuildContext context;

  /// 点击蒙层时是否关闭抽屉
  final bool? closeOnOverlayClick;

  /// 抽屉的底部
  final Widget? footer;

  /// 抽屉里的列表项
  final List<TDDrawerItem>? items;

  /// 自定义内容，优先级高于[items]/[footer]/[title]
  final Widget? contentWidget;

  /// 抽屉方向
  final TDDrawerPlacement? placement;

  /// 是否显示遮罩层
  final bool? showOverlay;

  /// 抽屉的标题
  final String? title;

  /// 抽屉的标题组件
  final Widget? titleWidget;

  /// 组件是否可见
  final bool? visible;

  /// 关闭时触发
  final VoidCallback? onClose;

  /// 点击抽屉里的列表项触发
  final TDDrawerItemClickCallback? onItemClick;

  /// 宽度
  final double? width;

  /// 距离顶部的距离
  final double? drawerTop;

  /// 列表自定义样式
  final TDCellStyle? style;

  /// 是否开启点击反馈
  final bool? hover;

  /// 组件背景颜色
  final Color? backgroundColor;

  /// 是否显示边框
  final bool? bordered;

  /// 是否显示最后一行分割线
  final bool? isShowLastBordered;

  TDSlidePopupRoute? _drawerRoute;

  void show() {
    if (_drawerRoute != null) {
      return; // 如果抽屉已经显示了，就不要再显示
    }

    final overlayEnabled = showOverlay ?? true;
    final dismissible = overlayEnabled && (closeOnOverlayClick ?? true);

    _drawerRoute = TDSlidePopupRoute(
      slideTransitionFrom: placement == TDDrawerPlacement.right
          ? SlideTransitionFrom.right
          : SlideTransitionFrom.left,
      isDismissible: dismissible,
      modalBarrierColor: overlayEnabled ? null : Colors.transparent,
      modalTop: drawerTop,
      builder: (context) {
        return TDDrawerWidget(
          footer: footer,
          items: items,
          contentWidget: contentWidget,
          title: title,
          titleWidget: titleWidget,
          onItemClick: onItemClick,
          width: width,
          style: style,
          hover: hover,
          backgroundColor: backgroundColor,
          bordered: bordered,
          isShowLastBordered: isShowLastBordered,
        );
      },
    );

    Navigator.of(context).push(_drawerRoute!).then((_) {
      // 当抽屉关闭时，将_drawerRoute置为null
      _deleteRouter();
    });
  }

  void open() {
    show();
  }

  @mustCallSuper
  void close() {
    if (_drawerRoute != null) {
      Navigator.of(context).pop();
      _deleteRouter();
    }
  }

  void _deleteRouter() {
    _drawerRoute = null;
    onClose?.call();
  }
}

Drawer.drawer_widget:
import 'package:flutter/material.dart';

