}

DropdownMenu.dropdown_item:
import 'dart:async';
import 'dart:collection';
import 'dart:math';

import 'package:flutter/material.dart';

import '../../../tdesign_flutter.dart';
import '../../util/context_extension.dart';
import '../../util/list_ext.dart';
import '../tag/td_select_tag.dart';
import '../tag/td_tag_styles.dart';
import 'td_dropdown_inherited.dart';
import 'td_dropdown_popup.dart';

typedef TDDropdownItemContentBuilder = Widget Function(BuildContext context,
    _TDDropdownItemState itemState, TDDropdownPopup? popupState);

typedef TDDropdownItemOptionsCallback = void Function(
    List<TDDropdownItemOption>? options);

List<TDDropdownItemOption?> _getSelected(List<TDDropdownItemOption>? options) {
  return options?.where((element) => element.selected == true).toList() ?? [];
}

/// 补充列数，使最后一行的选项宽度一样
int _num(List list, int? n) {
  var val = n ?? 1;
  if (list.length < val) {
    return val;
  }
  return list.length + list.length % val;
}

/// 下拉菜单控制器
class TDDropdownItemController {
  _TDDropdownItemState? _state;

  void _bindState(_TDDropdownItemState _tdDropdownMenuState) {
    _state = _tdDropdownMenuState;
  }

  /// 将所有选项重置为未选中状态
  void reset() {
    _state?.reset();
  }

  /// 更新选项内容。注意：增删内容可能导致高度展示异常，请谨慎操作
  void updateOptions(TDDropdownItemOptionsCallback callback) {
    _state?.updateOptions(callback);
  }
}

/// 下拉菜单内容
class TDDropdownItem<T> extends StatefulWidget {
  const TDDropdownItem({
    Key? key,
    this.disabled = false,
    this.label,
    this.arrowIcon,
    this.multiple = false,
    this.options = const [],
    this.builder,
    this.optionsColumns = 1,
    this.onChange,
    this.onConfirm,
    this.onReset,
    this.minHeight,
    this.maxHeight,
    this.tabBarWidth,
    this.tabBarAlign,
    this.tabBarFlex = 1,
    this.controller,
  }) : super(key: key);

  /// 是否禁用
  final bool? disabled;

  /// 标题
  final String? label;

  /// 自定义箭头图标
  final IconData? arrowIcon;

  /// 是否多选
  final bool? multiple;

  /// 选项数据
  final List<TDDropdownItemOption>? options;

  /// 完全自定义展示内容
  final TDDropdownItemContentBuilder? builder;

  /// 选项分栏（1-3）
  final int? optionsColumns;

  /// 值改变时触发
  final ValueChanged<T?>? onChange;

  /// 点击确认时触发
  final ValueChanged<T?>? onConfirm;

  /// 点击重置时触发
  final VoidCallback? onReset;

  /// 内容最小高度
  final double? minHeight;

  /// 内容最大高度
  final double? maxHeight;

  /// 该item在menu上的宽度，仅在[TDDropdownMenu.isScrollable]为true时有效
  final double? tabBarWidth;

  /// [label]和[arrowIcon]/[TDDropdownMenu.arrowIcon]的对齐方式
  final MainAxisAlignment? tabBarAlign;

  /// 该item在menu上的宽度占比，仅在[TDDropdownMenu.isScrollable]为false时有效
  final int? tabBarFlex;

  /// 下拉菜单控制器
  final TDDropdownItemController? controller;

  static const double operateHeight = 73;

  double? get minContentHeight => multiple == true
      ? (minHeight != null ? minHeight! + TDDropdownItem.operateHeight : null)
      : minHeight;

  double? get maxContentHeight => multiple == true
      ? (maxHeight != null ? maxHeight! + TDDropdownItem.operateHeight : null)
      : maxHeight;

  @override
  _TDDropdownItemState createState() => _TDDropdownItemState();

  String getLabel() {
    if (multiple == true) {
      return label ?? '';
    }
    var list = _getSelected(options);
    if (list.isEmpty) {
      return label ?? '';
    }
    return list[0]?.label ?? label ?? '';
  }
}

class _TDDropdownItemState extends State<TDDropdownItem> {
  late TDDropdownPopup popupState;
  late ValueNotifier<TDDropdownMenuDirection> directionListenable;

  @override
  void initState() {
    super.initState();
    widget.controller?._bindState(this);
  }

  @override
  Widget build(BuildContext context) {
    popupState = TDDropdownInherited.of(context)!.popupState;
    directionListenable = TDDropdownInherited.of(context)!.directionListenable;
    if (widget.builder != null) {
      return widget.builder!(context, this, popupState);
    }
    return widget.multiple == true || (widget.optionsColumns ?? 1) > 1
        ? _getCheckboxList()
        : _getRadioList();
  }

  Widget _getCheckboxList() {
    var isMultiple = widget.multiple == true;
    var paddingNum = TDTheme.of(context).spacer16;
    var groupChunk = _groupChunkOptions();
    var maxContentHeight = widget.maxContentHeight != null
        ? widget.maxContentHeight!
        : directionListenable.value == TDDropdownMenuDirection.auto
            ? double.infinity
            : max<double>(
                popupState.maxContentHeight - TDDropdownItem.operateHeight, 0);
    return Column(
      children: [
        Container(
          color: TDTheme.of(context).bgColorContainer,
          child: ConstrainedBox(
            constraints: BoxConstraints(
                minHeight: widget.minContentHeight ?? 0.0,
                maxHeight: maxContentHeight),
            child: SingleChildScrollView(
              child: Column(
                children: List.generate(groupChunk.length, (index) {
                  var entry = groupChunk.entries.elementAt(index);
                  var chunks = entry.value;
                  var selectIds = _getSelected(widget.options)
                      .map((e) => e!.value)
                      .toList();
                  return Column(
                    children: [
                      groupChunk.length == 1 && entry.key == '__default__'
                          ? const SizedBox.shrink()
                          : Container(
                              width: double.infinity,
                              padding: EdgeInsets.only(
                                  left: paddingNum,
                                  top: paddingNum,
                                  right: paddingNum),
                              color: TDTheme.of(context).bgColorContainer,
                              child: TDText(entry.key == '__default__'
                                  ? context.resource.other
                                  : entry.key),
                            ),
                      Container(
                        padding: EdgeInsets.all(paddingNum),
                        color: TDTheme.of(context).bgColorContainer,
                        child: TDCheckboxGroupContainer(
                          selectIds: isMultiple
                              ? selectIds
                              : selectIds.isEmpty
                                  ? []
                                  : [selectIds[0]],
                          onCheckBoxGroupChange: _handleSelectChange,
                          child: Column(
                            children: List.generate(chunks.length, (ri) {
                              var num = _num(chunks[ri], widget.optionsColumns);
                              return Padding(
                                padding:
                                    _getPadding(chunks.length, ri, 'bottom'),
                                child: Row(
                                  children: List.generate(num, (ci) {
                                    return Expanded(
                                      child: Padding(
                                        padding: _getPadding(num, ci, 'right'),
                                        child: _getCheckboxItem(chunks[ri], ci),
                                      ),
                                    );
                                  }),
                                ),
                              );
                            }),
                          ),
                        ),
                      ),
                    ],
                  );
                }),
              ),
            ),
          ),
        ),
        if (isMultiple) _getCheckboxOperate(),
      ],
    );
  }

  Widget _getRadioList() {
    var selected = _getSelected(widget.options);
    var radios = TDRadioGroup(
      onRadioGroupChange: _handleSelectChange,
      radioCheckStyle: TDRadioStyle.check,
      selectId: selected.isEmpty ? null : selected[0]?.value,
      child: Column(
        children: List.generate(
          widget.options?.length ?? 0,
          (index) => TDRadio(
            id: widget.options![index].value,
            title: widget.options![index].label,
            selectColor: widget.options![index].selectedColor,
            enable: !(widget.options![index].disabled ?? false),
            contentDirection: TDContentDirection.left,
          ),
        ),
      ),
    );
    return widget.minContentHeight != null || widget.maxContentHeight != null
        ? Container(
            color: TDTheme.of(context).bgColorContainer,
            child: ConstrainedBox(
              constraints: BoxConstraints(
                  minHeight: widget.minContentHeight ?? 0.0,
                  maxHeight: widget.maxContentHeight ?? double.infinity),
              child: widget.maxContentHeight != null
                  ? SingleChildScrollView(child: radios)
                  : radios,
            ),
          )
        : radios;
  }

  Widget? _getCheckboxItem(List<TDDropdownItemOption> cols, int index) {
    var col = index >= cols.length ? null : cols[index];
    if (col == null) {
      return null;
    }
    var enable = !(col.disabled ?? false);
    return TDCheckbox(
      id: col.value,
      title: col.label,
      enable: !(col.disabled ?? false),
      selectColor: col.selectedColor,
      disableColor: col.disabledColor,
      customIconBuilder: (context, checked) => null,
      customContentBuilder: (context, checked, content) => Container(
        height: 40,
        decoration: BoxDecoration(
          color: enable
              ? checked
                  ? TDTheme.of(context).brandLightColor
                  : TDTheme.of(context).bgColorSecondaryContainer
              : TDTheme.of(context).bgColorSecondaryContainerHover,
          borderRadius: BorderRadius.all(
            Radius.circular(TDTheme.of(context).radiusDefault),
          ),
        ),
        child: Center(
          child: TDText(
            content,
            textColor: enable
                ? checked
                    ? TDTheme.of(context).brandColor7
                    : TDTheme.of(context).textColorPrimary
                : TDTheme.of(context).textDisabledColor,
          ),
        ),
      ),
    );
  }

  Widget _getCheckboxOperate() {
    return Container(
      height: TDDropdownItem.operateHeight,
      padding: EdgeInsets.all(TDTheme.of(context).spacer16),
      decoration: BoxDecoration(
        color: TDTheme.of(context).bgColorContainer,
        border: Border(
          top: BorderSide(
            color: TDTheme.of(context).componentStrokeColor,
            width: 0.5,
          ),
          bottom: directionListenable.value == TDDropdownMenuDirection.up
              ? BorderSide(
                  color: TDTheme.of(context).componentStrokeColor,
                  width: 0.5,
                )
              : BorderSide.none,
        ),
      ),
      child: Row(
        // spacing: TDTheme.of(context).spacer16,
        mainAxisAlignment: MainAxisAlignment.spaceBetween,
        children: [
          Expanded(
            child: TDButton(
              text: context.resource.reset,
              theme: TDButtonTheme.light,
              onTap: () {
                reset();
                widget.onReset?.call();
              },
            ),
          ),
          SizedBox(width: TDTheme.of(context).spacer16),
          Expanded(
            child: TDButton(
              text: context.resource.confirm,
              theme: TDButtonTheme.primary,
              onTap: () {
                _handleClose();
                widget.onConfirm?.call(
                    _getSelected(widget.options).map((e) => e!.value).toList());
              },
            ),
          ),
        ],
      ),
    );
  }

  EdgeInsets _getPadding(int length, int index, String direction) {
    var value = length - 1 == index ? 0.0 : TDTheme.of(context).spacer12;
    if (direction == 'bottom') {
      return EdgeInsets.only(bottom: value);
    }
    if (direction == 'right') {
      return EdgeInsets.only(right: value);
    }
    return EdgeInsets.all(value);
  }

  Map<String, List<List<TDDropdownItemOption>>> _groupChunkOptions() {
    var groupedOptions = widget.options
            ?.groupBy<String>((option) => option.group ?? '__default__') ??
        {};
    var groupedChunkOptions = <String, List<List<TDDropdownItemOption>>>{};
    var def = groupedOptions.remove('__default__');
    if (def != null) {
      groupedOptions['__default__'] = def;
    }
    groupedOptions.forEach((key, value) {
      groupedChunkOptions[key] = value.chunk(widget.optionsColumns ?? 1);
    });
    return groupedChunkOptions;
  }

  void _handleSelectChange(selected) {
    var isRadio = widget.multiple != true && selected is List<String>;
    if (isRadio && selected.isNotEmpty) {
      selected = [selected.last];
    }
    widget.options?.forEach((element) {
      element.selected = selected is List<String>
          ? selected.contains(element.value)
          : element.value == selected;
    });
    if (isRadio) {
      setState(() {});
    }
    widget.onChange
        ?.call(_getSelected(widget.options).map((e) => e!.value).toList());
    if (widget.multiple != true && selected.isNotEmpty) {
      _handleClose();
    }
  }

  void _handleClose() async {
    if (widget.multiple != true || (widget.optionsColumns ?? 1) > 1) {
      await Future.delayed(const Duration(milliseconds: 100));
    }
    await Navigator.maybePop(context);
  }

  void reset() {
    widget.options?.forEach((element) {
      element.selected = false;
    });
    setState(() {});
  }

  void updateOptions(TDDropdownItemOptionsCallback callback) {
    callback(widget.options);
    setState(() {});
  }
}

/// 选项数据
class TDDropdownItemOption {
  TDDropdownItemOption({
    required this.value,
    required this.label,
    this.disabled = false,
    this.group,
    this.selected = false,
    this.selectedColor,
    this.disabledColor,
  });

  /// 选项值
  String value;

  /// 选项标题
  final String label;

  /// 是否禁用
  bool? disabled;

  /// 分组，相同的为一组
  final String? group;

  /// 是否选中
  bool selected;

  /// 选中颜色
  final Color? selectedColor;

  /// 禁用颜色
  final Color? disabledColor;
}

DropdownMenu.dropdown_menu:
import 'dart:async';

import 'package:flutter/material.dart';

import '../../theme/td_colors.dart';
import '../../theme/td_fonts.dart';
import '../../theme/td_theme.dart';
import '../icon/td_icons.dart';
import '../text/td_text.dart';
import './td_dropdown_item.dart';
import 'td_dropdown_popup.dart';

/// 菜单展开方向
enum TDDropdownMenuDirection {
  /// 向下
  down,

  /// 向上
  up,

  /// 根据内容高度动态展示方向
  auto,
}

/// 下拉菜单构建器
typedef TDDropdownItemBuilder = List<TDDropdownItem> Function(
    BuildContext context);

/// 自定义标签内容
typedef LabelBuilder = Widget Function(
    BuildContext context, String label, bool isOpened, int index);

/// 下拉菜单
class TDDropdownMenu extends StatefulWidget {
  const TDDropdownMenu({
    Key? key,
    this.builder,
    this.items,
    this.closeOnClickOverlay = true,
    this.direction = TDDropdownMenuDirection.auto,
    this.duration = 200.0,
    this.showOverlay = true,
    this.isScrollable = false,
    this.arrowIcon,
    this.labelBuilder,
    this.onMenuOpened,
    this.onMenuClosed,
    this.width,
    this.height = 48,
    this.tabBarAlign = MainAxisAlignment.center,
    this.decoration,
  }) : super(key: key);

  /// 下拉菜单构建器，优先级高于[items]
  final TDDropdownItemBuilder? builder;

  /// 下拉菜单
  final List<TDDropdownItem>? items;

  /// 是否在点击遮罩层后关闭菜单
  final bool? closeOnClickOverlay;

  /// 菜单展开方向（down、up、auto）
  final TDDropdownMenuDirection? direction;

  /// 动画时长，毫秒
  final double? duration;

  /// 是否显示遮罩层
  final bool? showOverlay;

  /// 自定义箭头图标
  final IconData? arrowIcon;

  /// 自定义标签内容
  final LabelBuilder? labelBuilder;

  /// 展开菜单事件
  final ValueChanged<int>? onMenuOpened;

  /// 关闭菜单事件
  final ValueChanged<int>? onMenuClosed;

  /// 是否开启滚动列表
  final bool? isScrollable;

  /// menu的宽度
  final double? width;

  /// menu的高度
  final double? height;

  /// [TDDropdownItem.label]和[arrowIcon]/[TDDropdownItem.arrowIcon]的对齐方式
  final MainAxisAlignment? tabBarAlign;

  /// 下拉菜单的装饰器
  final Decoration? decoration;

  @override
  _TDDropdownMenuState createState() => _TDDropdownMenuState();
}

class _TDDropdownMenuState extends State<TDDropdownMenu>
    with TickerProviderStateMixin {
  List<TDDropdownItem>? _items;
  List<AnimationController>? _iconControllers;
  late List<Animation<double>> _iconAnimations;
  late List<bool> _isOpened;
  TDDropdownPopup? _dropdownPopup;

  @override
  void initState() {
    super.initState();
    _init();
  }

  @override
  void dispose() {
    _iconControllers?.forEach((controller) {
      controller.dispose();
    });
    super.dispose();
  }

  @override
  void didUpdateWidget(TDDropdownMenu oldWidget) {
    super.didUpdateWidget(oldWidget);
    if (widget.builder != oldWidget.builder ||
        widget.items != oldWidget.items) {
      _init();
    }
  }

  @override
  Widget build(BuildContext context) {
    var tabBar = widget.isScrollable == true
        ? SingleChildScrollView(
            scrollDirection: Axis.horizontal,
            physics: const BouncingScrollPhysics(),
            child: Row(
              children: List.generate(
                _items?.length ?? 0,
                (index) => SizedBox(
                  width: _items![index].tabBarWidth,
                  child: _tabBarContent(index),
                ),
              ),
            ),
          )
        : Row(
            children: List.generate(
              _items?.length ?? 0,
              (index) {
                return Expanded(
                  flex: _items![index].tabBarFlex ?? 1,
                  child: _tabBarContent(index),
                );
              },
            ),
          );
    return Container(
      height: widget.height,
      width: widget.width ?? double.infinity,
      decoration: widget.decoration ??
          BoxDecoration(
            color: TDTheme.of(context).bgColorContainer,
            border: Border(
              bottom: BorderSide(
                color: TDTheme.of(context).componentStrokeColor,
                width: 0.5,
              ),
            ),
          ),
      child: tabBar,
    );
  }

  void _init() {
    var items = widget.builder?.call(context) ?? widget.items ?? [];
    if (items.length == _items?.length) {
      _items = items;
      return;
    }
    _isOpened = List.filled(items.length, false);
    _items = items;
    _iconControllers?.forEach((controller) {
      controller.dispose();
    });
    _iconControllers = List.generate(
        _items?.length ?? 0,
        (index) => AnimationController(
              duration:
                  Duration(milliseconds: (widget.duration ?? 200).toInt()),
              vsync: this,
            ));
    _iconAnimations = _iconControllers
            ?.map((e) => Tween<double>(begin: 0, end: 0.5).animate(e))
            .toList() ??
        [];
  }

  Widget _tabBarContent(int index) {
    final color = _disabled(index)
        ? TDTheme.of(context).textDisabledColor
        : _isOpened[index]
            ? TDTheme.of(context).brandNormalColor
            : TDTheme.of(context).textColorPrimary;

    return GestureDetector(
      behavior: HitTestBehavior.opaque,
      onTap: () async {
        if (_disabled(index)) {
          return;
        }
        _isOpened[index] ? await Navigator.maybePop(context) : _openMenu(index);
      },
      child: Row(
        mainAxisSize: MainAxisSize.min,
        mainAxisAlignment: _items![index].tabBarAlign ??
            widget.tabBarAlign ??
            MainAxisAlignment.center,
        children: [
          Flexible(child: _getText(index, color)),
          _getIcon(index, color)
        ],
      ),
    );
  }

  Widget _getText(int index, Color color) {
    final label = _items![index].getLabel();
    if (widget.labelBuilder != null) {
      return widget.labelBuilder!(context, label, _isOpened[index], index);
    }
    return TDText(
      label,
      font: TDTheme.of(context).fontBodyMedium,
      textColor: color,
      maxLines: 1,
      overflow: TextOverflow.ellipsis,
    );
  }

  Widget _getIcon(int index, Color color) {
    var arrowIcon = _items![index].arrowIcon ??
        widget.arrowIcon ??
        (widget.direction == TDDropdownMenuDirection.up
            ? TDIcons.caret_up_small
            : TDIcons.caret_down_small);
    return RotationTransition(
      turns: _iconAnimations[index],
      child: Icon(arrowIcon, size: 20, color: color),
    );
  }

  bool _disabled(int index) {
    return _items![index].disabled == true;
  }

  Future<void> openMenu(int index) async {
    await _openMenu(index);
  }

  Future<void> closeMenu() async {
    await _closeMenu();
  }

  /// 打开菜单
  Future<void> _openMenu(int index) async {
    /// 如果已经打开了，则关闭
    if (_isOpened.contains(true)) {
      await Navigator.maybePop(context);
    }
    _dropdownPopup ??= TDDropdownPopup(
      child: _items![index],
      parentContext: context,
      handleClose: _closeMenu,
      direction: widget.direction,
      showOverlay: widget.showOverlay,
      closeOnClickOverlay: widget.closeOnClickOverlay,
      duration: Duration(milliseconds: (widget.duration ?? 200).toInt()),
    );
    unawaited(_dropdownPopup!.add(_items![index]).then((value) {
      widget.onMenuOpened?.call(index);
    }));

    _isOpened = List.filled(_items?.length ?? 0, false);
    _isOpened[index] = true;
    setState(() {});
    _iconControllers?.asMap().forEach((key, value) {
      if (value.status == AnimationStatus.completed) {
        value.reverse();
      } else if (key == index) {
        value.forward();
      }
    });
  }

  /// 关闭菜单
  Future<void> _closeMenu() async {
    var index = _isOpened.indexOf(true);
    if (index < 0) {
      return;
    }
    _isOpened = List.filled(_items?.length ?? 0, false);
    setState(() {});
    _iconControllers?.forEach((value) {
      if (value.status == AnimationStatus.completed) {
        value.reverse();
      }
    });
    await _dropdownPopup?.remove();
    if (index >= 0 && widget.onMenuClosed != null) {
      widget.onMenuClosed!(index);
    }
  }
}

DropdownMenu.dropdown_panel:
import 'dart:async';
import 'dart:math';

import 'package:flutter/material.dart';

import 'td_dropdown_menu.dart';
import 'td_dropdown_popup.dart';

class TDDropdownPanel extends StatefulWidget {
  const TDDropdownPanel({
    Key? key,
    required this.initContentTop,
    required this.initContentBottom,
    required this.reverseHeight,
    required this.duration,
    required this.directionListenable,
    required this.colorAlphaListenable,
    required this.direction,
    required this.closeListenable,
    required this.onOpened,
    required this.child,
  }) : super(key: key);

  final double initContentTop;
  final double initContentBottom;
  final double reverseHeight;
  final Duration duration;
  final ValueNotifier<TDDropdownPopupDirection> directionListenable;
  final ValueNotifier<bool> colorAlphaListenable;
  final TDDropdownPopupDirection direction;
  final ValueNotifier<FutureCallback?> closeListenable;
  final VoidCallback onOpened;
  final Widget child;

  @override
  _TDDropdownPanelState createState() => _TDDropdownPanelState();
}

class _TDDropdownPanelState extends State<TDDropdownPanel> with SingleTickerProviderStateMixin {
  double? contentTop, contentBottom;
  late AnimationController _controller;

  @override
  void initState() {
    super.initState();
    _controller = AnimationController(vsync: this, duration: widget.duration);
    widget.closeListenable.value = close;
  }

  @override
  void didUpdateWidget(TDDropdownPanel oldWidget) {
    super.didUpdateWidget(oldWidget);
    if (widget.directionListenable != oldWidget.directionListenable) {
      widget.closeListenable.value = close;
    }
  }

  @override
  void dispose() {
    _controller.dispose();
    super.dispose();
  }

  @override
  Widget build(BuildContext context) {
    return PositionedTransition(
      rect: _getAnimation(),
      child: SingleChildScrollView(
        child: Builder(
          builder: (BuildContext context) {
            open(context);
            return widget.child;
          },
        ),
      ),
    );
  }

  void open(BuildContext itemContext) {
    if (contentBottom != null || contentTop != null) {
      return;
    }
    WidgetsBinding.instance.addPostFrameCallback((timeStamp) {
      var renderBox = itemContext.findRenderObject() as RenderBox;
      var size = renderBox.size;
      if (widget.directionListenable.value == TDDropdownPopupDirection.auto) {
        // 比较展开方向（down）的高度能不能放下item，能将方向更新为down
        // 否则比较反方向（up）的高度是否大于down的方向，大于则将方向更新为up，否则保持为down
        if (widget.direction == TDDropdownPopupDirection.down) {
          if (widget.initContentBottom >= size.height) {
            widget.directionListenable.value = TDDropdownPopupDirection.down;
          } else {
            if (widget.reverseHeight > widget.initContentBottom) {
              widget.directionListenable.value = TDDropdownPopupDirection.up;
            } else {
              widget.directionListenable.value = TDDropdownPopupDirection.down;
            }
          }
        } else {
          if (widget.initContentTop >= size.height) {
            widget.directionListenable.value = TDDropdownPopupDirection.up;
          } else {
            if (widget.reverseHeight > widget.initContentTop) {
              widget.directionListenable.value = TDDropdownPopupDirection.down;
            } else {
              widget.directionListenable.value = TDDropdownPopupDirection.up;
            }
          }
        }
        return;
      }
      if (widget.direction == TDDropdownPopupDirection.down) {
        contentBottom = widget.initContentBottom - size.height;
      } else {
        contentTop = widget.initContentTop - size.height;
      }
      setState(() {});
      WidgetsBinding.instance.addPostFrameCallback((_) {
        if (_controller.status == AnimationStatus.dismissed) {
          widget.colorAlphaListenable.value = true;
          _controller.duration = widget.duration;
          _controller.forward().whenCompleteOrCancel(() {
            widget.onOpened();
          });
        }
      });
    });
  }

  Animation<RelativeRect> _getAnimation() {
    return RelativeRectTween(
      begin: RelativeRect.fromLTRB(0, widget.initContentTop, 0, widget.initContentBottom),
      end: RelativeRect.fromLTRB(0, contentTop ?? widget.initContentTop, 0, contentBottom ?? widget.initContentBottom),
    ).animate(_controller);
  }

  Future<void> close() {
    widget.colorAlphaListenable.value = false;
    _controller.duration = widget.duration ~/ 2;
    return _controller.reverse();
  }
}

DropdownMenu.dropdown_popup:
import 'dart:async';

import 'package:flutter/material.dart';

import 'td_dropdown_inherited.dart';
import 'td_dropdown_item.dart';
import 'td_dropdown_menu.dart';
import 'td_dropdown_panel.dart';

typedef TDDropdownPopupDirection = TDDropdownMenuDirection;
typedef FutureCallback = Future<void> Function();

class TDDropdownPopup {
  TDDropdownPopup({
    required this.parentContext,
    required this.child,
    required this.handleClose,
    this.direction = TDDropdownPopupDirection.auto,
    this.showOverlay = true,
    this.closeOnClickOverlay = true,
    this.duration = const Duration(milliseconds: 200),
  });

  final BuildContext parentContext;
  final TDDropdownItem child;
  final FutureCallback handleClose;
  final TDDropdownPopupDirection? direction;
  final bool? showOverlay;
  final bool? closeOnClickOverlay;
  final Duration? duration;

  /// _overlay1：下拉方向的
  late double _overlay1Top,
      _overlay1Bottom,

      /// _overlay2：menu部分的
      _overlay2Top,
      _overlay2Bottom,

      /// _overlay3：下拉反方向的
      _overlay3Top,
      _overlay3Bottom,

      /// _overlay3Height：下拉反方向的高度，用于判断auto方向
      _overlay3Height,

      /// _initContent：初始内容
      _initContentTop,
      _initContentBottom;
  final _closeListenable = ValueNotifier<FutureCallback?>(null);
  final _directionListenable =
      ValueNotifier<TDDropdownPopupDirection>(TDDropdownPopupDirection.auto);
  final _colorAlphaListenable = ValueNotifier(false);

  Duration get _duration => duration ?? const Duration(milliseconds: 200);

  double get maxContentHeight => direction == TDDropdownPopupDirection.down
      ? _initContentBottom
      : _initContentTop;

  void _init(TDDropdownPopupDirection d) {
    final ancestor = Navigator.of(parentContext).context.findRenderObject();
    final popupContainerHeight = (ancestor as RenderBox).size.height;
    var renderBox = parentContext.findRenderObject() as RenderBox;
    var position = renderBox.localToGlobal(Offset.zero, ancestor: ancestor);
    var size = renderBox.size;
    if (d == TDDropdownPopupDirection.down) {
      _overlay1Top = position.dy + size.height;
      _overlay2Top = position.dy;
      _overlay3Top = 0;
      _initContentTop = position.dy + size.height;

      _overlay1Bottom = 0;
      _overlay2Bottom = popupContainerHeight - position.dy - size.height;
      _overlay3Bottom = popupContainerHeight - position.dy;

      _overlay3Height = position.dy;
      _initContentBottom = popupContainerHeight - position.dy - size.height;
    } else {
      _overlay1Top = 0;
      _overlay2Top = position.dy;
      _overlay3Top = position.dy + size.height;
      _initContentTop = position.dy;

      _overlay1Bottom = popupContainerHeight - position.dy;
      _overlay2Bottom = popupContainerHeight - position.dy - size.height;
      _overlay3Bottom = 0;

      _overlay3Height = popupContainerHeight - position.dy - size.height;
      _initContentBottom = popupContainerHeight - position.dy;
    }
  }

  Future<void> add([TDDropdownItem? updateChild]) {
    var completer = Completer<void>();
    _directionListenable.value = direction ?? TDDropdownPopupDirection.auto;
    final overlayEntry = OverlayEntry(
      builder: (BuildContext context) {
        return _directionListenable.value == TDDropdownPopupDirection.auto
            ? ValueListenableBuilder(
                valueListenable: _directionListenable,
                builder: (context, value, child) =>
                    value == TDDropdownPopupDirection.auto
                        ? child!
                        : _getPopup(
                            value, updateChild, completer), // 每次重新渲染item，更新高度
                child: _getPopup(
                    TDDropdownPopupDirection.down, updateChild, completer),
              )
            : _getPopup(_directionListenable.value, updateChild, completer);
      },
    );
    Navigator.push(
        parentContext, _PopupOverlayRoute(overlayEntry, handleClose));
    return completer.future;
  }

  Widget _getPopup(TDDropdownMenuDirection value, TDDropdownItem? updateChild,
      Completer<void> completer) {
    _init(value);
    final barrier = GestureDetector(
      behavior: HitTestBehavior.opaque,
      onTap: _overlayClick,
    );
    return Stack(children: [
      if (_directionListenable.value != TDDropdownPopupDirection.auto) ...[
        _getOverlay1(barrier),
        _getOverlay2(),
        _getOverlay3(barrier),
      ],
      TDDropdownInherited(
        popupState: this,
        directionListenable: _directionListenable,
        child: TDDropdownPanel(
          duration: _duration,
          direction: value,
          directionListenable: _directionListenable,
          colorAlphaListenable: _colorAlphaListenable,
          initContentBottom: _initContentBottom,
          initContentTop: _initContentTop,
          reverseHeight: _overlay3Height,
          closeListenable: _closeListenable,
          onOpened: () {
            completer.complete();
          },
          child: updateChild ?? child,
        ),
      ),
    ]);
  }

  Widget _getOverlay1(Widget barrier) {
    return Positioned(
      top: _overlay1Top,
      bottom: _overlay1Bottom,
      left: 0,
      right: 0,
      child: showOverlay == true
          ? ValueListenableBuilder(
              builder: (BuildContext context, value, Widget? child) {
                return AnimatedContainer(
                  color: value ? Colors.black54 : Colors.black54.withAlpha(0),
                  duration: value ? _duration : _duration ~/ 2,
                  child: barrier,
                );
              },
              valueListenable: _colorAlphaListenable,
            )
          : barrier,
    );
  }

  Widget _getOverlay2() {
    return Positioned(
      top: _overlay2Top,
      bottom: _overlay2Bottom,
      left: 0,
      right: 0,
      child: GestureDetector(
        onVerticalDragUpdate: (details) {},
        onHorizontalDragUpdate: (details) {},
        behavior: HitTestBehavior.translucent,
      ),
    );
  }

  Widget _getOverlay3(Widget barrier) {
    return Positioned(
      top: _overlay3Top,
      bottom: _overlay3Bottom,
      left: 0,
      right: 0,
      child: barrier,
    );
  }

  void _overlayClick() {
    if (!(closeOnClickOverlay ?? true)) {
      return;
    }
    Navigator.maybePop(parentContext);
  }

  Future<void> remove() async {
    await _closeListenable.value?.call();
    _closeListenable.value = null;
  }
}

class _PopupOverlayRoute<T> extends OverlayRoute<T> {
  final OverlayEntry overlayEntry;
  final FutureCallback handleClose;

  _PopupOverlayRoute(this.overlayEntry, this.handleClose);

  @override
  Iterable<OverlayEntry> createOverlayEntries() {
    return [overlayEntry];
  }

  @override
  Future<RoutePopDisposition> willPop() async {
    await handleClose();
    return super.willPop();
  }
}

