import 'package:flutter/widgets.dart';

// GENERATED CODE - DO NOT MODIFY BY HAND
// ignore_for_file: non_constant_identifier_names
// ignore_for_file: constant_identifier_names
@immutable
class _TDIconsData extends IconData {
  const _TDIconsData(int codePoint, this.name)
      : super(
    codePoint,
    fontFamily: 'TDIcons',
    fontPackage: 'tdesign_flutter',
  );

  final String name;
}


class TDIcons {

  /// 私有构造方法，不支持外部创建，仅提供静态常量给外部使用
  const TDIcons._();
  static const accessibility_filled = _TDIconsData(0xE001, 'accessibility_filled');
  static const accessibility = _TDIconsData(0xE002, 'accessibility');
