import 'dart:math';

import 'package:flutter/material.dart';

import '../../../tdesign_flutter.dart';
import '../../util/iterable_ext.dart';

export 'sticky_header/sticky_header_widget.dart';
export 'td_indexes_anchor.dart';
export 'td_indexes_list.dart';

/// 索引
class TDIndexes extends StatefulWidget {
  const TDIndexes({
    Key? key,
    this.indexList,
    this.indexListMaxHeight = 0.8,
    this.sticky = true,
    this.stickyOffset = 0,
    this.capsuleTheme = false,
    this.reverse = false,
    this.scrollController,
    this.onChange,
    this.onSelect,
    required this.builderContent,
    this.builderAnchor,
    this.builderIndex,
  }) : super(key: key);

  /// 索引字符列表。不传默认 A-Z
  final List<String>? indexList;

  /// 索引列表最大高度（父容器高度的百分比，默认 0.8）
  final double? indexListMaxHeight;

  /// 锚点是否吸顶
  final bool? sticky;

  /// 锚点吸顶时与顶部的距离
  final double? stickyOffset;

  /// 锚点是否为胶囊式样式
  final bool? capsuleTheme;

  /// 反方向滚动置顶
  final bool? reverse;

  /// 滚动控制器
  final ScrollController? scrollController;

  /// 索引发生变更时触发事件
  final void Function(String index)? onChange;

  /// 点击侧边栏时触发事件
  final void Function(String index)? onSelect;

  /// 内容自定义构建
  final Widget? Function(BuildContext context, String index) builderContent;

  /// 锚点自定义构建
  final Widget? Function(
      BuildContext context, String index, bool isPinnedToTop)? builderAnchor;

  /// 索引文本自定义构建，包括索引激活左侧提示
  final Widget Function(BuildContext context, String index, bool isActive)?
      builderIndex;

  @override
  _TDIndexesState createState() => _TDIndexesState();
}

class _TDIndexesState extends State<TDIndexes> {
  late List<String> _indexList;
  late ValueNotifier<String> _activeIndex;
  late ScrollController _scrollController;
  final _anchorKeys = <String, BuildContext>{};
  final _contentKeys = <String, BuildContext>{};
  var _isAnimating = false;

  /// A-Z 字母字符列表
  static final List<String> _defaultAZList = List.generate(
    26,
        (index) => String.fromCharCode(65 + index),
  );

  @override
  void initState() {
    super.initState();
    _indexList = widget.indexList ?? _defaultAZList;
    _activeIndex = ValueNotifier(_indexList.getOrNull(0) ?? '');
    _scrollController = widget.scrollController ?? ScrollController();
  }

  @override
  void didUpdateWidget(TDIndexes oldWidget) {
    super.didUpdateWidget(oldWidget);
    if (widget.indexList != oldWidget.indexList) {
      _indexList = widget.indexList ?? _defaultAZList;
      _activeIndex = ValueNotifier(_indexList.getOrNull(0) ?? '');
    }
    if (widget.scrollController != oldWidget.scrollController) {
      _scrollController.dispose();
      _scrollController = widget.scrollController ?? ScrollController();
    }
  }

  @override
  void dispose() {
    _scrollController.dispose();
    super.dispose();
  }

  @override
  Widget build(BuildContext context) {
    return Container(
      color: TDTheme.of(context).bgColorContainer,
      child: Stack(
        children: [
          CustomScrollView(
            controller: _scrollController,
            reverse: widget.reverse ?? false,
            slivers: _slivers(),
          ),
          TDIndexesList(
            indexList: _indexList,
            activeIndex: _activeIndex,
            onSelect: (newIndex, oldIndex) {
              widget.onSelect?.call(newIndex);
              widget.onChange?.call(newIndex);
              _scrollToTarget(newIndex, oldIndex);
            },
            indexListMaxHeight: widget.indexListMaxHeight ?? 0.8,
            builderIndex: widget.builderIndex,
          ),
        ],
      ),
    );
  }

  List<Widget> _slivers() {
    final capsuleTheme = widget.capsuleTheme ?? false;
    final stickyOffset = widget.stickyOffset ?? 0;
    _anchorKeys.clear();
    _contentKeys.clear();
    return _indexList.map((e) {
      final isPinnedOffset = capsuleTheme && _activeIndex.value == e;
      return SliverStickyHeader.builder(
        sticky: widget.sticky ?? true,
        pinnedOffset: isPinnedOffset
            ? TDTheme.of(context).spacer8 + stickyOffset
            : stickyOffset,
        builder: (context, state) {
          _anchorKeys[e] = context;
          if (state.isPinned && _activeIndex.value != e && !_isAnimating) {
            WidgetsBinding.instance.addPostFrameCallback((_) {
              _activeIndex.value = e;
              widget.onChange?.call(e);
            });
          }
          return TDIndexesAnchor(
            text: e,
            capsuleTheme: capsuleTheme,
            activeIndex: _activeIndex,
            builderAnchor: widget.builderAnchor,
            sticky: widget.sticky ?? true,
          );
        },
        sliver: SliverToBoxAdapter(
          child: Builder(
            builder: (context) {
              _contentKeys[e] = context;
              return Padding(
                padding: isPinnedOffset
                    ? EdgeInsets.only(top: TDTheme.of(context).spacer8)
                    : EdgeInsets.zero,
                child: widget.builderContent(context, e),
              );
            },
          ),
        ),
      );
    }).toList();
  }

  void _scrollToTarget(String newIndex, String oldIndex) {
    _isAnimating = true;

    /// isUp: 是否（手指）向上滑动
    final isUp = _indexList.indexOf(newIndex) > _indexList.indexOf(oldIndex);
    if (isUp) {
      var index = oldIndex;
      final contentRenderBox =
          _contentKeys[index]?.findRenderObject() as RenderBox?;
      if (contentRenderBox != null) {
        final contentHeight = contentRenderBox.size.height;
        final maxScrollExtent = _scrollController.position.maxScrollExtent;
        final targetOffset = contentRenderBox.localToGlobal(
            Offset(0, contentHeight),
            ancestor: context.findRenderObject());
        final scrollOffset = targetOffset.dy + _scrollController.offset;
        _scrollController.jumpTo(min(maxScrollExtent, scrollOffset));
      }
      index = _indexList[_indexList.indexOf(index) + 1];
      WidgetsBinding.instance.addPostFrameCallback((_) {
        if (index != newIndex) {
          _scrollToTarget(newIndex, index);
        } else {
          _isAnimating = false;
        }
      });
    } else {
      final anchorContext = _anchorKeys[newIndex];
      if (anchorContext != null) {
        Scrollable.ensureVisible(anchorContext).then((value) {
          WidgetsBinding.instance.addPostFrameCallback((_) {
            _isAnimating = false;
          });
        });
      }
    }
  }
}

Indexes.indexes_anchor:
import 'package:flutter/material.dart';

import '../../../tdesign_flutter.dart';

/// 索引锚点
class TDIndexesAnchor extends StatelessWidget {
  const TDIndexesAnchor({
    Key? key,
    required this.sticky,
    required this.text,
    required this.capsuleTheme,
    this.builderAnchor,
    required this.activeIndex,
  }) : super(key: key);

  /// 索引是否吸顶
  final bool sticky;

  /// 锚点文本
  final String text;

  /// 是否为胶囊式样式
  final bool capsuleTheme;

  /// 选中索引
  final ValueNotifier<String> activeIndex;

  /// 索引锚点构建
  final Widget? Function(
      BuildContext context, String index, bool isPinnedToTop)? builderAnchor;

  @override
  Widget build(BuildContext context) {
    return ValueListenableBuilder(
      valueListenable: activeIndex,
      builder: (context, value, child) {
        final isPinned = value == text;
        final customAnchor = builderAnchor?.call(context, text, isPinned);
        return customAnchor ??
            Container(
              padding: EdgeInsets.symmetric(
                  vertical: TDTheme.of(context).spacer4,
                  horizontal: TDTheme.of(context).spacer16),
              margin: capsuleTheme
                  ? EdgeInsets.symmetric(
                      horizontal: TDTheme.of(context).spacer8)
                  : null,
              decoration: BoxDecoration(
                color: isPinned
                    ? TDTheme.of(context).bgColorContainer
                    : TDTheme.of(context).bgColorSecondaryContainer,
                borderRadius: capsuleTheme
                    ? BorderRadius.circular(TDTheme.of(context).radiusCircle)
                    : null,
                border: isPinned
                    ? capsuleTheme
                        ? Border.all(
                            color: TDTheme.of(context).componentStrokeColor)
                        : Border(
                            bottom: BorderSide(
                                color: TDTheme.of(context).componentStrokeColor,
                                width: 0.5))
                    : null,
              ),
              child: TDText(
                text,
                forceVerticalCenter: true,
                font: isPinned
                    ? TDTheme.of(context).fontMarkMedium
                    : TDTheme.of(context).fontTitleSmall,
                textColor: isPinned
                    ? TDTheme.of(context).brandNormalColor
                    : TDTheme.of(context).textColorPrimary,
              ),
            );
      },
    );
  }
}

Indexes.indexes_list:
import 'dart:async';

import 'package:flutter/material.dart';
import 'package:flutter/rendering.dart';
import 'package:flutter/scheduler.dart';

import '../../../tdesign_flutter.dart';
import '../../util/iterable_ext.dart';
import 'sticky_header/sticky_header_widget.dart';
import 'td_indexes_anchor.dart';

/// 索引
class TDIndexesList extends StatefulWidget {
  const TDIndexesList({
    Key? key,
    required this.indexList,
    this.indexListMaxHeight = 0.8,
    required this.activeIndex,
    required this.onSelect,
    this.builderIndex,
  }) : super(key: key);

  /// 索引字符列表。不传默认 A-Z
  final List<String> indexList;

  /// 索引列表最大高度（父容器高度的百分比，默认0.8）
  final double indexListMaxHeight;

  /// 选中索引
  final ValueNotifier<String> activeIndex;

  /// 点击侧边栏时触发事件
  final void Function(String newIndex, String oldIndex) onSelect;

  /// 索引文本自定义构建，包括索引激活左侧提示
  final Widget Function(BuildContext context, String index, bool isActive)?
      builderIndex;

  @override
  State<TDIndexesList> createState() => _TDIndexesListState();
}

class _TDIndexesListState extends State<TDIndexesList> {
  late Map<String, GlobalKey> _containerKeys;
  final _indexSize = 20.0;
  Timer? _hideTipTimer;
  var _showTip = false;

  @override
  void initState() {
    super.initState();
    _initContainerKeys();
  }

  void _initContainerKeys() {
    _containerKeys =
        widget.indexList.asMap().map((index, e) => MapEntry(e, GlobalKey()));
  }

  @override
  void didUpdateWidget(TDIndexesList oldWidget) {
    super.didUpdateWidget(oldWidget);
    if (widget.indexList != oldWidget.indexList) {
      _initContainerKeys();
    }
  }

  @override
  void dispose() {
    _hideTipTimer?.cancel();
    super.dispose();
  }

  @override
  Widget build(BuildContext context) {
    return Positioned(
      right: TDTheme.of(context).spacer8,
      top: 0,
      bottom: 0,
      child: Align(
        child: FractionallySizedBox(
          heightFactor: widget.indexListMaxHeight,
          child: Align(
            child: GestureDetector(
              behavior: HitTestBehavior.opaque,
              onVerticalDragUpdate: (details) {
                _changeSelect(details.globalPosition);
              },
              onTapUp: (details) {
                _changeSelect(details.globalPosition);
                _hideTip();
              },
              onVerticalDragEnd: (details) {
                _hideTip();
              },
              child: ValueListenableBuilder(
                valueListenable: widget.activeIndex,
                builder: (context, value, child) {
                  return Column(
                    mainAxisSize: MainAxisSize.min,
                    children: widget.indexList.map(
                      (e) {
                        final isActive = value == e;
                        if (widget.builderIndex != null) {
                          return Container(
                            key: _containerKeys[e],
                            child: widget.builderIndex!(context, e, isActive),
                          );
                        }
                        return Stack(
                          clipBehavior: Clip.none,
                          children: [
                            if (_showTip && value == e)
                              Positioned(
                                top: -TDTheme.of(context).spacer48 / 2 +
                                    _indexSize / 2,
                                left: -TDTheme.of(context).spacer48,
                                child: Container(
                                  height: TDTheme.of(context).spacer48,
                                  width: TDTheme.of(context).spacer48,
                                  decoration: BoxDecoration(
                                    borderRadius: BorderRadius.circular(
                                        TDTheme.of(context).radiusCircle),
                                    color: TDTheme.of(context).brandLightColor,
                                  ),
                                  child: Center(
                                    child: TDText(
                                      e,
                                      forceVerticalCenter: true,
                                      font: TDTheme.of(context)
                                          .fontTitleExtraLarge,
                                      textColor:
                                          TDTheme.of(context).brandNormalColor,
                                    ),
                                  ),
                                ),
                              ),
                            Container(
                              key: _containerKeys[e],
                              padding: EdgeInsets.only(
                                  left: TDTheme.of(context).spacer8),
                              child: Container(
                                width: _indexSize,
                                height: _indexSize,
                                decoration: isActive
                                    ? BoxDecoration(
                                        borderRadius: BorderRadius.circular(
                                            TDTheme.of(context).radiusCircle),
                                        color: TDTheme.of(context)
                                            .brandNormalColor,
                                      )
                                    : null,
                                child: Center(
                                  child: TDText(
                                    e,
                                    forceVerticalCenter: true,
                                    font: isActive
                                        ? TDTheme.of(context).fontMarkSmall
                                        : TDTheme.of(context).fontLinkSmall,
                                    textColor: isActive
                                        ? TDTheme.of(context).textColorAnti
                                        : TDTheme.of(context).textColorPrimary,
                                  ),
                                ),
                              ),
                            ),
                          ],
                        );
                      },
                    ).toList(),
                  );
                },
              ),
            ),
          ),
        ),
      ),
    );
  }

  void _changeSelect(Offset globalPosition) {
    final newIndex = _fingerInsideContainer(globalPosition);
    if (newIndex != null && newIndex != widget.activeIndex.value) {
      final oldIndex = widget.activeIndex.value;
      widget.activeIndex.value = newIndex;
      _showTip = true;
      widget.onSelect.call(newIndex, oldIndex);
    }
  }

  String? _fingerInsideContainer(Offset globalPosition) {
    for (var entry in _containerKeys.entries) {
      final renderBox =
          entry.value.currentContext?.findRenderObject() as RenderBox?;
      if (renderBox != null) {
        final localPosition = renderBox.globalToLocal(globalPosition);
        final isIn =
            renderBox.hitTest(BoxHitTestResult(), position: localPosition);
        if (isIn) {
          return entry.key;
        }
      }
    }
    return null;
  }

  void _hideTip() {
    _hideTipTimer?.cancel();
    _hideTipTimer = Timer(
      const Duration(seconds: 1),
      () {
        setState(() {
          _showTip = false;
        });
      },
    );
  }
}

