import 'package:flutter/material.dart';
import 'package:flutter/src/scheduler/binding.dart';

import '../../../tdesign_flutter.dart';

/// 链接设置
class MessageLink {
  MessageLink({
    required this.name,
    required this.uri,
    this.color,
  });

  /// 名称
  final String name;

  /// 资源链接
  final Uri? uri;

  /// 颜色
  final Color? color;
}

/// 跑马灯配置
class MessageMarquee {
  MessageMarquee({this.speed, this.loop, this.delay});

  /// 速度
  final int? speed;

  /// 循环次数
  final int? loop;

  /// 延迟时间(毫秒)
  final int? delay;
}

/// 定义消息主题枚举
enum MessageTheme {
  /// 普通通知
  info,

  /// 成功通知
  success,

  /// 警示通知
  warning,

  /// 错误通知
  error
}

/// TDMessage 组件
class TDMessage extends StatefulWidget {
  const TDMessage({
    Key? key,
    this.closeBtn,
    this.content,
    this.duration = 3000,
    this.icon = true,
    this.link,
    this.marquee,
    this.offset,
    this.theme = MessageTheme.info,
    this.visible = true,
    this.onCloseBtnClick,
    this.onDurationEnd,
    this.onLinkClick,
  }) : super(key: key);

  /// 通知内容
  final String? content;

  /// 消息内置计时器
  final int? duration;

  /// 是否显示
  final bool? visible;

  /// 自定义消息前面的图标
  final dynamic icon;

  /// 链接名称
  final dynamic link;

  /// 关闭按钮
  final dynamic closeBtn;

  /// 跑马灯效果
  final MessageMarquee? marquee;

  /// 相对于 placement 的偏移量
  final List<double>? offset;

  /// 消息组件风格 info/success/warning/error
  final MessageTheme? theme;

  /// 点击关闭按钮触发
  final VoidCallback? onCloseBtnClick;

  /// 计时结束后触发
  final VoidCallback? onDurationEnd;

  /// 点击链接文本时触发
  final VoidCallback? onLinkClick;

  @override
  _TDMessageState createState() => _TDMessageState();

  static void showMessage({
    required BuildContext context,
    String? content,
    bool? visible,
    int? duration,
    dynamic closeBtn,
    dynamic icon,
    dynamic link,
    MessageMarquee? marquee,
    List<double>? offset,
    MessageTheme? theme,
    VoidCallback? onCloseBtnClick,
    VoidCallback? onDurationEnd,
    VoidCallback? onLinkClick,
  }) {
    final overlay = Overlay.of(context);
    late OverlayEntry overlayEntry;
    overlayEntry = OverlayEntry(
      builder: (context) => TDMessage(
        content: content,
        visible: visible,
        duration: duration,
        closeBtn: closeBtn,
        icon: icon,
        link: link,
        marquee: marquee,
        offset: offset,
        theme: theme,
        onDurationEnd: () {
          onDurationEnd?.call();
          overlayEntry.remove();
        },
        onCloseBtnClick: onCloseBtnClick,
        onLinkClick: onLinkClick,
      ),
    );
    overlay.insert(overlayEntry);
  }
}

class _TDMessageState extends State<TDMessage> with TickerProviderStateMixin {
  bool _isVisible = true;
  double _topOffset = 0;
  double initTopOffset = 80;
  double totalWidth = 343;
  AnimationController? animationController;
  bool _isAnimationRunning = false;

  @override
  void initState() {
    super.initState();
    _topOffset = (widget.offset?[1] ?? initTopOffset) - 30;
    animationController = AnimationController(
      vsync: this,
    );

    WidgetsBinding.instance.addPostFrameCallback((_) {
      if (mounted) {
        setState(() {
          _topOffset = widget.offset?[1] ?? initTopOffset;
        });
      }
    });

    if (widget.duration != null && widget.duration! > 0) {
      Future.delayed(Duration(milliseconds: widget.duration!), _closeMessage);
    }

    if (widget.marquee != null) {
      animationController = AnimationController(
        vsync: this,
        duration: Duration(milliseconds: widget.marquee!.speed ?? 10000),
      );
    }
  }

  @override
  void dispose() {
    animationController?.stop();
    animationController?.dispose();
    animationController = null;
    super.dispose();
  }

  void _closeMessage() {
    if (mounted) {
      animationController?.stop();
      setState(() {
        _topOffset = (widget.offset?[1] ?? initTopOffset) - 30;
        _isAnimationRunning = false;
      });
      Future.delayed(const Duration(milliseconds: 300), () {
        if (mounted) {
          setState(() {
            _isVisible = false;
          });
          widget.onDurationEnd?.call();
        }
      });
    }
  }

  void startAnimation() {
    if (mounted && animationController != null && !_isAnimationRunning) {
      setState(() {
        _isAnimationRunning = true;
      });
      if (widget.marquee!.loop == 0) {
        animationController!.forward();
      } else if (widget.marquee!.loop == 1) {
        animationController!.repeat();
      }
    }
  }

  @override
  Widget build(BuildContext context) {
    if (widget.visible == false) {
      return const SizedBox.shrink();
    }
    var _leftOffset = widget.offset?[0] ??
        (MediaQuery.of(context).size.width - totalWidth) / 2;

    Widget getText(BuildContext context) {
      if (widget.marquee == null) {
        return Align(
          alignment: Alignment.centerLeft,
          child: Text(
            widget.content ?? '',
            style: TextStyle(color: TDTheme.of(context).textColorPrimary),
            maxLines: 1,
            overflow: TextOverflow.ellipsis,
          ),
        );
      } else {
        final textPainter = TextPainter(
          text: TextSpan(
              text: widget.content ?? '',
              style: TextStyle(color: TDTheme.of(context).textColorPrimary)),
          maxLines: 1,
          textDirection: TextDirection.ltr,
        )..layout(minWidth: 0, maxWidth: double.infinity);
        final textWidth = textPainter.width;

        final containerWidth = calculateTextWidth();

        final animationDuration =
            Duration(milliseconds: (widget.marquee!.speed ?? 10000));
        animationController!.duration = animationDuration;

        final tween = Tween<Offset>(
          begin: Offset.zero,
          end: Offset(-textWidth, 0),
        );

        if (widget.marquee!.delay != null && widget.marquee!.delay! > 0) {
          Future.delayed(
              Duration(milliseconds: widget.marquee!.delay!), startAnimation);
        } else {
          startAnimation();
        }

        return Align(
            alignment: Alignment.center,
            child: ClipRect(
              child: SizedBox(
                width: containerWidth,
                child: AnimatedBuilder(
                  animation:
                      animationController ?? const AlwaysStoppedAnimation(0),
                  builder: (context, child) {
                    final offset = tween.evaluate(
                        animationController ?? const AlwaysStoppedAnimation(0));
                    return OverflowBox(
                      minWidth: 0,
                      maxWidth: double.infinity,
                      alignment: Alignment.centerLeft,
                      child: Transform.translate(
                        offset: offset,
                        child: SizedBox(
                          child: Text(
                            widget.content ?? '',
                            style: TextStyle(
                                color: TDTheme.of(context).textColorPrimary),
                            maxLines: 1,
                          ),
                        ),
                      ),
                    );
                  },
                ),
              ),
            ));
      }
    }

    Widget getIcon(BuildContext context) {
      if (widget.icon is Widget) {
        return widget.icon;
      } else {
        switch (widget.theme) {
          case MessageTheme.info:
            return Icon(
              TDIcons.error_circle_filled,
              color: TDTheme.of(context).brandNormalColor,
            );
          case MessageTheme.success:
            return Icon(
              TDIcons.check_circle_filled,
              color: TDTheme.of(context).successNormalColor,
            );
          case MessageTheme.warning:
            return Icon(
              TDIcons.error_circle_filled,
              color: TDTheme.of(context).warningNormalColor,
            );
          case MessageTheme.error:
            return Icon(
              TDIcons.error_circle_filled,
              color: TDTheme.of(context).errorNormalColor,
            );
          case null:
            return const SizedBox.shrink();
        }
      }
    }

    void clickCloseButton() {
      _closeMessage();
      widget.onCloseBtnClick?.call();
    }

    Widget getCloseBtn(BuildContext context) {
      if (widget.closeBtn is Widget) {
        return GestureDetector(
          onTap: clickCloseButton,
          child: widget.closeBtn!,
        );
      } else if (widget.closeBtn == true) {
        return GestureDetector(
          onTap: clickCloseButton,
          child: Icon(
            TDIcons.close,
            color: TDTheme.of(context).textColorPlaceholder,
          ),
        );
      } else if (widget.closeBtn is String) {
        return GestureDetector(
          onTap: clickCloseButton,
          child: Text(widget.closeBtn),
        );
      } else {
        return const SizedBox.shrink();
      }
    }

    void clickLink() {
      widget.onLinkClick?.call();
    }

    Widget getLink(BuildContext context) {
      if (widget.link is MessageLink) {
        return Align(
            alignment: Alignment.center,
            child: TDLink(
              label: widget.link.name,
              style: TDLinkStyle.primary,
              type: TDLinkType.basic,
              uri: widget.link.uri,
              size: TDLinkSize.medium,
              color: widget.link.color ?? TDTheme.of(context).brandNormalColor,
              linkClick: (link) => clickLink(),
            ));
      } else if (widget.link is String) {
        return Align(
            alignment: Alignment.center,
            child: GestureDetector(
              onTap: clickLink,
              child: Text(
                widget.link ?? '',
                style: TextStyle(
                  color: TDTheme.of(context).brandNormalColor,
                  fontSize: 14,
                ),
                maxLines: 1,
                overflow: TextOverflow.ellipsis,
              ),
            ));
      } else {
        return const SizedBox.shrink();
      }
    }

    return AnimatedPositioned(
      duration: const Duration(milliseconds: 300),
      curve: Curves.easeInOut,
      top: _topOffset,
      left: _leftOffset,
      child: _isVisible
          ? Material(
              color: Colors.transparent,
              child: Container(
                width: totalWidth,
                height: 48,
                padding: const EdgeInsets.fromLTRB(16, 13, 16, 13),
                decoration: BoxDecoration(
                    color: TDTheme.of(context).bgColorContainer,
                    borderRadius: BorderRadius.circular(
                        TDTheme.of(context).radiusDefault),
                    boxShadow: TDTheme.of(context).shadowsMiddle),
                child: Row(
                  crossAxisAlignment: CrossAxisAlignment.center,
                  children: [
                    if (widget.icon != false)
                      Padding(
                        padding: const EdgeInsets.only(right: 10),
                        child: Align(
                          alignment: Alignment.center,
                          child: SizedBox(
                            width: 20,
                            height: 22,
                            child: getIcon(context),
                          ),
                        ),
                      ),
                    Expanded(
                      child: Row(
                        crossAxisAlignment: CrossAxisAlignment.center,
                        children: [
                          Expanded(
                            child: getText(context),
                            flex: 3,
                          ),
                          if (widget.link != null)
                            Container(
                                margin: const EdgeInsets.only(left: 8),
                                width: 40,
                                height: 22,
                                child: getLink(context)),
                          if (widget.closeBtn != null)
                            Align(
                              alignment: Alignment.center,
                              child: SizedBox(
                                width: 22,
                                height: 22,
                                child: getCloseBtn(context),
                              ),
                            ),
                        ],
                      ),
                    ),
                  ],
                ),
              ),
            )
          : const SizedBox.shrink(),
    );
  }

  double calculateTextWidth() {
    var width = totalWidth - 32;
    if (widget.icon != null && widget.icon != false) {
      width -= 30;
    }
    if (widget.link != null) {
      width -= 36;
    }
    if (widget.closeBtn != null) {
      width -= 34;
    }
    return width;
  }
}

Navbar.nav_bar:
import 'package:flutter/material.dart';
import '../../../tdesign_flutter.dart';

typedef TDBarItemAction = void Function();

class TDNavBar extends StatefulWidget implements PreferredSizeWidget {
  const TDNavBar({
    Key? key,
    this.leftBarItems,
    this.rightBarItems,
    this.titleWidget,
    this.title,
    this.titleColor,
    this.titleFont,
    this.titleFontFamily,
    this.titleFontWeight = FontWeight.w500,
    this.centerTitle = true,
    this.opacity = 1.0,
    this.backgroundColor,
    this.titleMargin = 16,
    this.padding,
    this.height = 48,
    this.screenAdaptation = true,
    this.useDefaultBack = true,
    this.onBack,
    this.useBorderStyle = false,
    this.border,
    this.belowTitleWidget,
    this.boxShadow,
    this.flexibleSpace,
  }) : super(key: key);

  /// 左边操作项
  final List<TDNavBarItem>? leftBarItems;

  /// 右边操作项
  final List<TDNavBarItem>? rightBarItems;

  /// 标题控件，优先级高于 title 文案
  final Widget? titleWidget;

  /// 标题文案
  final String? title;

  /// 标题颜色
  final Color? titleColor;

  /// 标题字体尺寸
  final Font? titleFont;

  /// 标题字体粗细
  final FontWeight? titleFontWeight;

  /// 标题字体样式
  final FontFamily? titleFontFamily;

  /// 标题是否居中
  final bool centerTitle;

  /// 透明度
  final double opacity;

  /// 背景颜色
  final Color? backgroundColor;

  /// 内部填充
  final EdgeInsetsGeometry? padding;

  /// 中间文案左右两边间距
  final double titleMargin;

  /// 高度
  final double height;

  /// 是否进行屏幕适配，默认 true
  final bool screenAdaptation;

  /// 是否使用默认的返回
  final bool useDefaultBack;

  /// 返回事件
  final VoidCallback? onBack;

  /// 是否使用边框模式
  final bool useBorderStyle;

  /// 边框
  final TDNavBarItemBorder? border;

  /// belowTitleWidget navbar 下方的 widget
  final Widget? belowTitleWidget;

  /// 底部阴影
  final List<BoxShadow>? boxShadow;

  /// 固定背景
  final Widget? flexibleSpace;

  @override
  State<StatefulWidget> createState() => _TDNavBarState();

  @override
  Size get preferredSize => Size.fromHeight(height);
}

class _TDNavBarState extends State<TDNavBar> {
  Widget _addBorder(List<Widget> items) {
    var border = widget.border ?? TDNavBarItemBorder();
    var borderColor = border.color ?? TDTheme.of(context).componentStrokeColor;
    var children = <Widget>[];
    for (var i = 0; i < items.length; i++) {
      children.add(items[i]);
      if (widget.useBorderStyle && i != items.length - 1) {
        children.add(
          Container(
            width: border.width,
            height: 16.0,
            color: borderColor,
          ),
        );
      }
    }
    var child = Row(
      children: children,
      mainAxisSize: MainAxisSize.min,
    );
    return Container(
      decoration: BoxDecoration(
        borderRadius: BorderRadius.circular(border.radius),
        border: Border.all(
          color: borderColor,
          width: border.width,
        ),
      ),
      padding: border.padding ??
          EdgeInsets.symmetric(horizontal: TDTheme.of(context).spacer4),
      child: child,
    );
  }

  Widget get backButton {
    return TDNavBarItem(
      icon: TDIcons.chevron_left,
      iconSize: 28.0,
      action: () {
        widget.onBack?.call();
        Navigator.maybePop(context);
      },
    ).toWidget(context);
  }

  Widget _buildTitleBarItems(bool isLeft) {
    var barItems = (isLeft ? widget.leftBarItems : widget.rightBarItems) ?? [];
    var children = barItems
        .map(
          (e) => e.toWidget(context, isLeft: isLeft),
        )
        .toList();

    return Row(
      children: [
        if (isLeft && widget.useDefaultBack) backButton,
        if (children.isNotEmpty)
          widget.useBorderStyle
              ? _addBorder(children)
              : Row(
                  children: children,
                  mainAxisSize: MainAxisSize.min,
                ),
      ],
      mainAxisSize: MainAxisSize.min,
    );
  }

  TextStyle _getTitleStyle(BuildContext context) {
    var titleColor = widget.titleColor ?? TDTheme.of(context).textColorPrimary;

    var titleFont = widget.titleFont ?? TDTheme.of(context).fontBodyLarge;

    return widget.titleFontFamily == null
        ? TextStyle(
            fontSize: titleFont?.size,
            color: titleColor,
            fontWeight: widget.titleFontWeight ?? FontWeight.w500,
            decoration: TextDecoration.none,
          )
        : TextStyle(
            fontSize: titleFont?.size,
            color: titleColor,
            fontWeight: widget.titleFontWeight ?? FontWeight.w500,
            decoration: TextDecoration.none,
            fontFamily: widget.titleFontFamily!.fontFamily,
            package: 'tdesign_flutter');
  }

  Widget _getTitleWidget(BuildContext context) {
    return widget.titleWidget ??
        Text(
          widget.title ?? '',
          maxLines: 1,
          overflow: TextOverflow.ellipsis,
          style: _getTitleStyle(context),
        );
  }

  Widget _getNavbarChild() {
    final Widget toolbar = NavigationToolbar(
      leading: _buildTitleBarItems(true),
      middle: _getTitleWidget(context),
      trailing: _buildTitleBarItems(false),
      middleSpacing: widget.titleMargin,
      centerMiddle: widget.centerTitle,
    );
    if (widget.belowTitleWidget == null) {
      return toolbar;
    }
    var children = <Widget>[Expanded(child: toolbar)];
    children.add(widget.belowTitleWidget as Widget);
    return Column(
      crossAxisAlignment: CrossAxisAlignment.start,
      children: children,
    );
  }

  @override
  Widget build(BuildContext context) {
    var _backgroundColor =
        widget.backgroundColor ?? TDTheme.of(context).bgColorContainer;
    if (_backgroundColor != Colors.transparent) {
      _backgroundColor = _backgroundColor.withOpacity(widget.opacity);
    }

    var paddingTop =
        widget.screenAdaptation ? MediaQuery.of(context).padding.top : 0.0;
    var padding = widget.padding ??
        EdgeInsets.symmetric(
          horizontal: TDTheme.of(context).spacer16,
          vertical: TDTheme.of(context).spacer4,
        );
    Widget appBar = Container(
        height: widget.height + paddingTop,
        padding: padding.add(EdgeInsets.only(top: paddingTop)),
        decoration: BoxDecoration(
          color: _backgroundColor,
          boxShadow: widget.boxShadow,
        ),
        child: _getNavbarChild());
    if (widget.flexibleSpace != null) {
      appBar = Stack(
        fit: StackFit.passthrough,
        children: <Widget>[
          widget.flexibleSpace!,
          appBar,
        ],
      );
    }

    return appBar;
  }
}

class TDNavBarItem {
  /// 图标
  IconData? icon;

  /// 图标颜色
  Color? iconColor;

  /// 操作回调
  TDBarItemAction? action;

  /// 图标尺寸
  double? iconSize;

  /// 内部填充
  EdgeInsetsGeometry? padding;

  /// 图标组件，优先级高于 icon
  Widget? iconWidget;

  TDNavBarItem({
    this.icon,
    this.iconColor,
    this.action,
    this.iconSize = 24.0,
    this.padding,
    this.iconWidget,
  });

  Widget toWidget(BuildContext context, {bool isLeft = true}) =>
      GestureDetector(
        behavior: HitTestBehavior.opaque,
        onTap: action,
        child: Padding(
          padding: padding ??
              (isLeft
                  ? EdgeInsets.only(right: TDTheme.of(context).spacer8)
                  : EdgeInsets.only(left: TDTheme.of(context).spacer8)),
          child: iconWidget ??
              Icon(
                icon,
                size: iconSize,
                color: iconColor,
              ),
        ),
      );
}

class TDNavBarItemBorder {
  double width;
  double radius;
  Color? color;
  EdgeInsetsGeometry? padding;

  TDNavBarItemBorder({
    this.width = 1.0,
    this.radius = 22.0,
    this.color,
    this.padding,
  });
}

NoticeBar.notice_bar:
import 'dart:async';
import 'dart:convert';
import 'dart:math' as math;

import 'package:flutter/cupertino.dart';
import 'package:flutter/gestures.dart';
import 'package:flutter/material.dart';
import 'package:flutter/widgets.dart';

import '../../../tdesign_flutter.dart';

class TDNoticeBar extends StatefulWidget {
  const TDNoticeBar({
    super.key,
    this.content,
    this.context,
    this.style,
    this.left,
    this.right,
    this.speed = 50,
    this.interval = 3000,
    this.marquee = false,
    this.direction = Axis.horizontal,
    this.theme = TDNoticeBarTheme.info,
    this.prefixIcon,
    this.suffixIcon,
    this.onTap,
    this.height = 22,
    this.maxLines = 1,
  })  : assert(content == null || content is String || content is List<String>,
            'content must be String or List<String>'),
        assert(speed == null || speed >= 0, 'speed must not be less than 0'),
        assert(interval == null || interval >= 0,
            'interval must not be less than 0');

  /// 文本内容（请使用content属性）
  @deprecated
  final dynamic context;

  /// 文本内容（字符串或字符串数组等）
  final dynamic content;

  /// 公告栏样式 [TDNoticeBarStyle]
  final TDNoticeBarStyle? style;

  /// 左侧内容（自定义左侧内容，优先级高于prefixIcon）
  final Widget? left;

  /// 右侧内容（自定义右侧内容，优先级高于suffixIcon）
  final Widget? right;

  /// 跑马灯效果
  final bool? marquee;

  /// 滚动速度
  final double? speed;

  /// 步进滚动间隔时间（毫秒）
  final int? interval;

  /// 滚动方向
  final Axis? direction;

  /// 主题
  final TDNoticeBarTheme? theme;

  /// 左侧图标
  final IconData? prefixIcon;

  /// 右侧图标
  final IconData? suffixIcon;

  /// 点击事件
  final ValueChanged? onTap;

  /// 文字高度 (当使用prefixIcon或suffixIcon时，icon大小值等于该属性）
  final double height;

  /// 文本行数（仅静态有效）
  final int? maxLines;

  @override
  State<StatefulWidget> createState() => _TDNoticeBarState();
}

class _TDNoticeBarState extends State<TDNoticeBar> {
  ScrollController? _scrollController;
  Timer? _timer;

  Size? _size;
  TDNoticeBarStyle? _style;
  Color? _backgroundColor;

  final GlobalKey _key = GlobalKey();
  final GlobalKey _contentKey = GlobalKey();

  dynamic _content;

  @override
  void initState() {
    /// todo 初始化内容数据，兼用旧版本，后续版本待移除
    _content = widget.content ?? widget.context;

    super.initState();
    _scrollController = ScrollController();
    WidgetsBinding.instance.addPostFrameCallback((time) {
      if (widget.marquee == true) {
        _startTimer();
      }
    });
  }

  void _init() {
    // 初始化样式及左右widget
    if (widget.style != null) {
      _style = widget.style;
    } else {
      _style = TDNoticeBarStyle.generateTheme(context, theme: widget.theme);
    }
    _backgroundColor = _style!.backgroundColor;
  }

  @override
  void dispose() {
    super.dispose();
    _timer?.cancel();
    _scrollController?.dispose();
  }

  void _startTimer() {
    if (widget.direction == Axis.horizontal) {
      _scroll();
    } else if (widget.direction == Axis.vertical) {
      _step();
    }
  }

  void _scroll() {
    var scrollDistance =
        _getContextWidth() + (_size!.width - _style!.getPadding.horizontal);
    var remainder = scrollDistance % widget.speed!;
    _scrollController!.jumpTo(0);
    var offset = 0.0 + widget.speed!;
    _scrollController!.animateTo(offset,
        duration: const Duration(seconds: 1), curve: Curves.linear);
    _timer = Timer.periodic(const Duration(seconds: 1), (timer) async {
      if (offset < scrollDistance - remainder) {
        offset += widget.speed!;
        await _scrollController!.animateTo(offset,
            duration: const Duration(seconds: 1), curve: Curves.linear);
      } else {
        // 剩余距离小于50 先滚动这部分 然后滚动剩余部分
        // 剩余距离滚动所需时间
        var time = (remainder / widget.speed! * 1000).round();
        // 滚动最后一部分（触底）
        await _scrollController!.animateTo(scrollDistance,
            duration: Duration(milliseconds: time), curve: Curves.linear);
        // 回到顶部（衔接）
        _scrollController!.jumpTo(0);
        // 修改起始位置
        offset = widget.speed! - remainder;
        // 计算新起点最后阶段滚动距离
        remainder = (scrollDistance - offset) % widget.speed!;
        // 滚动至新起点（弥补触底speed滚动长度）
        await _scrollController!.animateTo(offset,
            duration: Duration(milliseconds: 1000 - time),
            curve: Curves.linear);
      }
    });
  }

  void _step() {
    var step = 0;
    var offset = 0.0;
    _timer = Timer.periodic(Duration(milliseconds: widget.interval!), (timer) {
      var time = (widget.height / widget.speed! * 1000).round();
      if (step >= _content.length) {
        step = 0;
        offset = 0;
        _scrollController!.jumpTo(0);
      }
      step++;
      // 固定滚动行高（22）
      offset += widget.height;
      _scrollController!.animateTo(offset,
          duration: Duration(milliseconds: time), curve: Curves.linear);
    });
  }

  /// 获取文本内容尺寸消息
  Size _getFontSize() {
    var text = _content;
    if (_content is List<String>) {
      text = _content[0];
    }
    final textPainter = TextPainter(
      text: TextSpan(
        text: text,
        style: _style!.textStyle,
      ),
      locale: Localizations.localeOf(context),
      textDirection: TextDirection.ltr,
      maxLines: widget.marquee! ? 1 : widget.maxLines,
    )..layout(maxWidth: _size!.width);
    return textPainter.size;
  }

  /// 获取文本内容宽度
  double _getContextWidth() {
    var contextWidth =
        _key.currentContext?.findRenderObject()?.paintBounds.size.width ?? 0;
    if (contextWidth == 0) {
      contextWidth = _getFontSize().width;
    }
    return contextWidth;
  }

  /// 获取滚动区域宽度
  double _getEmptyWidth() {
    return _contentKey.currentContext
            ?.findRenderObject()
            ?.paintBounds
            .size
            .width ??
        (_size!.width - _style!.getPadding.horizontal);
  }

  /// 获取文字高度
  double _getTextHeight() {
    return _getFontSize().height;
  }

  /// 内容区域
  Widget _contentWidget() {
    Widget? textWidget;

    String? displayText;
    if (_content is String) {
      displayText = _content as String;
    } else if (_content is List<String> && _content.isNotEmpty) {
      displayText = _content[0];
    }

    if (displayText != null) {
      textWidget = SizedBox(
        height: _getTextHeight(),
        child: Align(
          alignment: Alignment.centerLeft,
          child: TDText(
            displayText,
            style: _style?.textStyle,
            maxLines: widget.marquee == true ? 1 : widget.maxLines,
            forceVerticalCenter: true,
          ),
        ),
      );
    } else {
      // 如果 content 类型不支持或为空，返回空容器
      textWidget = const SizedBox.shrink();
    }

    if (widget.marquee == false) {
      return textWidget;
    }

    Widget? child;
    switch (widget.direction) {
      case Axis.horizontal:
        final emptyWidth = _getEmptyWidth();
        final textHeight = _getTextHeight();
        final contextWidth = _getContextWidth();

        child = SingleChildScrollView(
          controller: _scrollController,
          scrollDirection: Axis.horizontal,
          physics: const NeverScrollableScrollPhysics(),
          child: Row(
            children: [
              SizedBox(
                key: _key,
                height: textHeight,
                child: textWidget,
              ),
              SizedBox(width: emptyWidth),
              SizedBox(
                width: math.max(emptyWidth, contextWidth),
                height: textHeight,
                child: textWidget,
              )
            ],
          ),
        );
        break;
      case Axis.vertical:
        var content = _content as List<String>;
        child = SizedBox(
          height: widget.height,
          child: SingleChildScrollView(
            controller: _scrollController,
            scrollDirection: Axis.vertical,
            // physics: const NeverScrollableScrollPhysics(),
            child: Column(
                mainAxisAlignment: MainAxisAlignment.center,
                crossAxisAlignment: CrossAxisAlignment.start,
                children: [
                  for (int i = 0; i < content.length; i++)
                    SizedBox(
                      height: widget.height,
                      child: Align(
                        alignment: Alignment.centerLeft,
                        child: TDText(
                          content[i],
                          style: _style!.textStyle,
                          maxLines: 1,
                          overflow: TextOverflow.ellipsis,
                        ),
                      ),
                    ),
                  SizedBox(
                    key: _key,
                    height: widget.height,
                    child: Align(
                      alignment: Alignment.centerLeft,
                      child: TDText(
                        content[0],
                        style: _style?.textStyle,
                        maxLines: 1,
                        overflow: TextOverflow.ellipsis,
                      ),
                    ),
                  ),
                ]),
          ),
        );
        break;
      default:
        child = textWidget;
        break;
    }
    return child;
  }

  void _onTap(trigger) {
    if (widget.onTap != null) {
      widget.onTap!(trigger);
    }
  }

  @override
  Widget build(BuildContext context) {
    _init();
    _size = MediaQuery.of(context).size;
    return Container(
      padding: _style!.getPadding,
      color: _backgroundColor,
      child: Row(
        crossAxisAlignment: CrossAxisAlignment.center,
        mainAxisAlignment: MainAxisAlignment.start,
        // spacing: 8,
        children: [
          /// 左侧widget
          if (widget.left != null)
            widget.left!
          else if (widget.prefixIcon != null)
            GestureDetector(
              onTap: () => _onTap('prefix-icon'),
              child: Container(
                margin: const EdgeInsets.only(right: 8),
                child: Icon(
                  widget.prefixIcon,
                  color: _style?.leftIconColor,
                  size: widget.height,
                ),
              ),
            ),

          /// 中间内容
          Expanded(
            key: _contentKey,
            child: GestureDetector(
              onTap: () => _onTap('context'),
              child: _contentWidget(),
            ),
          ),

          /// 左侧widget
          if (widget.right != null)
            widget.right!
          else if (widget.suffixIcon != null)
            GestureDetector(
                onTap: () => _onTap('suffix-icon'),
                child: Container(
                  margin: const EdgeInsets.only(left: 8),
                  child: Icon(
                    widget.suffixIcon,
                    color: _style?.rightIconColor,
                    size: widget.height,
                  ),
                )),
        ],
      ),
    );
  }
}

NoticeBar.notice_bar_style:
import 'package:flutter/material.dart';

import '../../theme/td_colors.dart';
import '../../theme/td_theme.dart';
import 'td_notice_bar.dart';

/// 公告栏类型
enum TDNoticeBarType {
  /// 静止（默认）
  none,

  /// 滚动
  scroll,

  /// 步进
  step
}

/// 公告栏主题
enum TDNoticeBarTheme {
  /// 信息（默认）
  info,

  /// 成功
  success,

  /// 警告
  warning,

  /// 错误
  error
}

/// 公告栏样式
class TDNoticeBarStyle {
  TDNoticeBarStyle({
    this.context,
    this.backgroundColor,
    this.textStyle,
    this.leftIconColor,
    this.rightIconColor,
    this.padding,
  });

  /// 上下文
  BuildContext? context;

  /// 公告栏背景色
  Color? backgroundColor;

  /// 公告栏左侧图标颜色
  Color? leftIconColor;

  /// 公告栏右侧图标颜色
  Color? rightIconColor;

  /// 公告栏内边距
  EdgeInsetsGeometry? padding;

  /// 公告栏内容样式
  TextStyle? textStyle;

  /// 公告栏内边距，用于获取默认值
  EdgeInsetsGeometry get getPadding =>
      padding ??
      const EdgeInsets.only(top: 13, bottom: 13, left: 16, right: 12);

  /// 根据主题生成样式
  TDNoticeBarStyle.generateTheme(
    BuildContext context, {
    TDNoticeBarTheme? theme = TDNoticeBarTheme.info,
  }) {
    rightIconColor = TDTheme.of(context).textColorPlaceholder;
    textStyle = textStyle ??
        TextStyle(
          color: TDTheme.of(context).textColorPrimary,
          fontSize: 14,
          height: 1,
          fontWeight: FontWeight.normal,
          fontStyle: FontStyle.normal,
        );
    switch (theme) {
      case TDNoticeBarTheme.warning:
        leftIconColor = TDTheme.of(context).warningNormalColor;
        backgroundColor = TDTheme.of(context).warningLightColor;
        break;
      case TDNoticeBarTheme.error:
        leftIconColor = TDTheme.of(context).errorNormalColor;
        backgroundColor = TDTheme.of(context).errorLightColor;
        break;
      case TDNoticeBarTheme.success:
        leftIconColor = TDTheme.of(context).successNormalColor;
        backgroundColor = TDTheme.of(context).successLightColor;
        break;
      default:
        leftIconColor = TDTheme.of(context).brandNormalColor;
        backgroundColor = TDTheme.of(context).brandLightColor;
        break;
    }
  }
}

Picker.no_wave_behavior:
import 'dart:io';
import 'dart:ui';

import 'package:flutter/material.dart';
import '../../util/platform_util.dart';

/// 去掉ListView上下滑动的波纹
class NoWaveBehavior extends ScrollBehavior {
  @override
  Widget buildOverscrollIndicator(
      BuildContext context, Widget child, ScrollableDetails details) {
    if (PlatformUtil.isAndroid || PlatformUtil.isFuchsia) {
      return child;
    } else {
      return super.buildOverscrollIndicator(context, child, details);
    }
  }

  // 增加mouse拖拽
  @override
  Set<PointerDeviceKind> get dragDevices => {
    PointerDeviceKind.touch,
    PointerDeviceKind.stylus,
    PointerDeviceKind.invertedStylus,
    PointerDeviceKind.trackpad,
    // The VoiceAccess sends pointer events with unknown type when scrolling
    // scrollables.
    PointerDeviceKind.unknown,
    PointerDeviceKind.mouse,
  };

}

Picker.date_picker:
import 'dart:math';

import 'package:flutter/material.dart';

import '../../../tdesign_flutter.dart';
import '../../util/context_extension.dart';
import 'no_wave_behavior.dart';

typedef DatePickerCallback = void Function(Map<String, int> selected);

enum DateTypeKey { year, month, weekDay, day, hour, minute, second }

/// 时间选择器
class TDDatePicker extends StatefulWidget {
  const TDDatePicker({
    this.title,
    this.titleHeight,
    this.titleDividerColor,
    this.onConfirm,
    this.onCancel,
    this.onChange,
    this.onSelectedItemChanged,
    this.leftText,
    this.rightText,
    this.leftTextStyle,
    this.centerTextStyle,
    this.rightTextStyle,
    this.padding,
    this.leftPadding,
    this.topPadding,
    this.rightPadding,
    this.topRadius,
    this.backgroundColor,
    this.customSelectWidget,
    this.header = true,
    this.itemDistanceCalculator,
    required this.model,
    this.pickerHeight = 200,
    this.pickerItemCount = 5,
    this.isTimeUnit,
    this.itemBuilder,
    Key? key,
  }) : super(key: key);

  /// 选择器标题
  final String? title;

  /// 标题高度
  final double? titleHeight;

  /// 标题分割线颜色
  final Color? titleDividerColor;

  /// 选择器确认按钮回调
  final DatePickerCallback? onConfirm;

  /// 选择器取消按钮回调
  final DatePickerCallback? onCancel;

  /// 选择器值改变回调
  final DatePickerCallback? onChange;

  /// 选择器选中项改变回调
  final void Function(int wheelIndex, int index)? onSelectedItemChanged;

  /// 左侧按钮文案
  final String? leftText;

  /// 右侧按钮文案
  final String? rightText;

  /// 自定义左侧文案样式
  final TextStyle? leftTextStyle;

  /// 自定义中间文案样式
  final TextStyle? centerTextStyle;

  /// 自定义右侧文案样式
  final TextStyle? rightTextStyle;

  /// 适配padding
  final EdgeInsets? padding;

  /// 顶部填充
  final double? topPadding;

  /// 左边填充
  final double? leftPadding;

  /// 右边填充
  final double? rightPadding;

  /// 顶部圆角
  final double? topRadius;

  /// 背景颜色
  final Color? backgroundColor;

  /// 根据距离计算字体颜色、透明度、粗细
  final ItemDistanceCalculator? itemDistanceCalculator;

  /// 是否显示头部内容
  final bool header;

  /// 选择器List的视窗高度，默认200
  final double pickerHeight;

  /// 选择器List视窗中item个数，pickerHeight / pickerItemCount，即item高度
  final int pickerItemCount;

  /// 自定义选择框样式
  final Widget? customSelectWidget;

  /// 数据模型
  final DatePickerModel model;

  /// 是否时间显示
  final bool? isTimeUnit;

  /// 自定义item构建
  final ItemBuilderType? itemBuilder;

  @override
  State<StatefulWidget> createState() => _TDDatePickerState();
}

class _TDDatePickerState extends State<TDDatePicker> {
  double pickerHeight = 0;
  static const _pickerTitleHeight = 56.0;

  @override
  void initState() {
    super.initState();
    pickerHeight = widget.pickerHeight;
  }

  @override
  void dispose() {
    widget.model.removeListener();
    super.dispose();
  }

  bool useAll() {
    return widget.model.useYear &&
        widget.model.useMonth &&
        widget.model.useDay &&
        widget.model.useHour &&
        widget.model.useMinute &&
        widget.model.useSecond;
  }

  selectListItem(String ev) {
    var selected = <String, int>{
      'year': widget.model.useYear
          ? widget.model.yearFixedExtentScrollController.selectedItem +
              widget.model.data[0][0]
          : -1,
      'month': widget.model.useMonth
          ? widget.model.monthFixedExtentScrollController.selectedItem +
              widget.model.data[1][0]
          : -1,
      'day': widget.model.useDay
          ? widget.model.dayFixedExtentScrollController.selectedItem +
              widget.model.data[2][0]
          : -1,
      'weekDay': widget.model.useWeekDay
          ? widget.model.weekDayFixedExtentScrollController.selectedItem +
              widget.model.data[3][0]
          : -1,
      'hour': widget.model.useHour
          ? selectItemValue(widget.model.data[4],
              widget.model.hourFixedExtentScrollController.selectedItem)
          : -1,
      'minute': widget.model.useMinute
          ? selectItemValue(widget.model.data[5],
              widget.model.minuteFixedExtentScrollController.selectedItem)
          : -1,
      'second': widget.model.useSecond
          ? selectItemValue(widget.model.data[6],
              widget.model.secondFixedExtentScrollController.selectedItem)
          : -1,
    };
    if (ev == 'onCancel' && widget.onCancel != null) {
      widget.onCancel!(selected);
    } else if (ev == 'onConfirm' && widget.onConfirm != null) {
      widget.onConfirm!(selected);
    } else if (ev == 'onChange' && widget.onChange != null) {
      widget.onChange!(selected);
    } else {
      Navigator.of(context).pop();
    }
  }

  int selectItemValue(List items, int itemIndex) {
    /// 选择列表索引对应的项的值
    return items[itemIndex];
  }

  @override
  Widget build(BuildContext context) {
    var maxWidth = MediaQuery.of(context).size.width;
    return Container(
      width: maxWidth,
      padding: widget.padding ??
          EdgeInsets.only(bottom: MediaQuery.of(context).padding.bottom),
      decoration: BoxDecoration(
        color: widget.backgroundColor ?? TDTheme.of(context).bgColorContainer,
        borderRadius: BorderRadius.vertical(
          top: Radius.circular(
              widget.topRadius ?? TDTheme.of(context).radiusExtraLarge),
        ),
      ),
      child: Column(
        mainAxisSize: MainAxisSize.min,
        children: [
          if (widget.header) _buildHeader(context),
          SizedBox(
            height: pickerHeight,
            child: Stack(
              alignment: Alignment.center,
              children: [
                Padding(
                  padding: const EdgeInsets.only(left: 16, right: 16),
                  child: widget.customSelectWidget ??
                      Container(
                        height: 40,
                        decoration: BoxDecoration(
                            color:
                                TDTheme.of(context).bgColorSecondaryContainer,
                            borderRadius: BorderRadius.all(Radius.circular(
                                TDTheme.of(context).radiusDefault))),
                      ),
                ),
                Container(
                    height: pickerHeight,
                    width: maxWidth,
                    padding: const EdgeInsets.only(left: 32, right: 32),
                    child: Row(
                      children: [
                        widget.model.useYear
                            ? useAll()
                                ? SizedBox(
                                    child: buildList(context, 0),
                                    width: 64,
                                  )
                                : Expanded(child: buildList(context, 0))
                            : Container(),
                        widget.model.useMonth
                            ? Expanded(child: buildList(context, 1))
                            : Container(),
                        widget.model.useDay
                            ? Expanded(child: buildList(context, 2))
                            : Container(),
                        widget.model.useWeekDay
                            ? Expanded(child: buildList(context, 3))
                            : Container(),
                        widget.model.useHour
                            ? Expanded(child: buildList(context, 4))
                            : Container(),
                        widget.model.useMinute
                            ? Expanded(child: buildList(context, 5))
                            : Container(),
                        widget.model.useSecond
                            ? Expanded(child: buildList(context, 6))
                            : Container(),
                      ],
                    )),
                // 蒙层
                Positioned(
                  top: 0,
                  child: IgnorePointer(
                    ignoring: true,
                    child: Container(
                      height: _pickerTitleHeight,
                      width: MediaQuery.of(context).size.width,
                      decoration: BoxDecoration(
                          gradient: LinearGradient(
                              begin: Alignment.topCenter,
                              end: Alignment.bottomCenter,
                              colors: [
                            TDTheme.of(context).bgColorContainer,
                            TDTheme.of(context).bgColorContainer.withOpacity(0)
                          ])),
                    ),
                  ),
                ),
                Positioned(
                  bottom: 0,
                  child: IgnorePointer(
                    ignoring: true,
                    child: Container(
                      height: _pickerTitleHeight,
                      width: MediaQuery.of(context).size.width,
                      decoration: BoxDecoration(
                          gradient: LinearGradient(
                              begin: Alignment.bottomCenter,
                              end: Alignment.topCenter,
                              colors: [
                            TDTheme.of(context).bgColorContainer,
                            TDTheme.of(context).bgColorContainer.withOpacity(0)
                          ])),
                    ),
                  ),
                )
              ],
            ),
          )
        ],
      ),
    );
  }

  Widget buildList(context, int whichLine) {
    /// whichLine参数表示这个列表表示的是年，还是月还是日......
    var maxWidth = MediaQuery.of(context).size.width;
    return MediaQuery.removePadding(
        context: context,
        removeTop: true,
        child: ScrollConfiguration(
          behavior: NoWaveBehavior(),
          child: ListWheelScrollView.useDelegate(
              itemExtent: pickerHeight / widget.pickerItemCount,
              diameterRatio: 100,
              controller: widget.model.controllers[whichLine],
              physics: whichLine == 3
                  ? const NeverScrollableScrollPhysics()
                  : const FixedExtentScrollPhysics(),
              onSelectedItemChanged: (index) {
                if (whichLine == 0 ||
                    whichLine == 1 ||
                    whichLine == 2 ||
                    whichLine == 4 ||
                    whichLine == 5 ||
                    whichLine == 6) {
                  // 年月的改变会引起日的改变, 年的改变会引起月的改变
                  setState(() {
                    switch (whichLine) {
                      case 0:
                        widget.model.refreshMonthDataAndController();
                        widget.model.refreshDayDataAndController();
                        if (widget.model.useWeekDay) {
                          widget.model.refreshWeekDayDataAndController();
                        }
                        break;
                      case 1:
                        widget.model.refreshDayDataAndController();
                        if (widget.model.useWeekDay) {
                          widget.model.refreshWeekDayDataAndController();
                        }
                        break;
                      case 2:
                        if (widget.model.useWeekDay) {
                          widget.model.refreshWeekDayDataAndController();
                        }
                        break;
                    }
                    if (useAll()) {
                      widget.model
                          .refreshTimeDataInitialAndController(whichLine);
                    }

                    /// 使用动态高度，强制列表组件的state刷新，以展现更新的数据，详见下方链接
                    /// FIX:https://github.com/flutter/flutter/issues/22999
                    pickerHeight =
                        pickerHeight - Random().nextDouble() / 100000000;
                  });
                }
                if (widget.onChange != null) {
                  selectListItem('onChange');
                }
                widget.onSelectedItemChanged?.call(whichLine, index);
              },
              childDelegate: ListWheelChildBuilderDelegate(
                  childCount: widget.model.data[whichLine].length,
                  builder: (context, index) {
                    return Container(
                        alignment: Alignment.center,
                        height: pickerHeight / widget.pickerItemCount,
                        width: maxWidth,
                        child: TDItemWidget(
                          colIndex: whichLine,
                          index: index,
                          itemHeight: pickerHeight / widget.pickerItemCount,
                          content: whichLine == 3
                              ? weekUnitMap(
                                  widget.model.data[whichLine][index] - 1)
                              : widget.model.data[whichLine][index].toString() +
                                  timeUnitMap(widget.model.mapping[whichLine]),
                          fixedExtentScrollController:
                              widget.model.controllers[whichLine],
                          itemDistanceCalculator: widget.itemDistanceCalculator,
                          itemBuilder: widget.itemBuilder,
                        ));
                  })),
        ));
  }

  Widget _buildHeader(BuildContext context) {
    final padding = TDTheme.of(context).spacer16;

    return Container(
      padding: EdgeInsets.only(
        left: widget.leftPadding ?? padding,
        right: widget.rightPadding ?? padding,
        top: widget.topPadding ?? padding,
      ),
      decoration: BoxDecoration(
        border: Border(
          bottom: BorderSide(
            width: 0.5,
            color: widget.titleDividerColor ?? Colors.transparent,
          ),
        ),
      ),

      /// 减去分割线的空间
      height: getTitleHeight() - 0.5,
      child: Row(
        crossAxisAlignment: CrossAxisAlignment.center,
        children: [
          /// 左边按钮
          GestureDetector(
            onTap: () {
              selectListItem('onCancel');
            },
            behavior: HitTestBehavior.opaque,
            child: TDText(
              widget.leftText ?? context.resource.cancel,
              style: widget.leftTextStyle ??
                  TextStyle(
                      fontSize: TDTheme.of(context).fontBodyLarge!.size,
                      color: TDTheme.of(context).textColorSecondary),
            ),
          ),

          /// 中间title
          Expanded(
            child: Center(
              child: TDText(
                widget.title ?? '',
                style: widget.centerTextStyle ??
                    TextStyle(
                      fontSize: TDTheme.of(context).fontBodyLarge!.size,
                      fontWeight: FontWeight.w600,
                      color: TDTheme.of(context).textColorPrimary,
                    ),
              ),
            ),
          ),

          /// 右边按钮
          GestureDetector(
            onTap: () {
              selectListItem('onConfirm');
            },
            behavior: HitTestBehavior.opaque,
            child: TDText(
              widget.rightText ?? context.resource.confirm,
              style: widget.rightTextStyle ??
                  TextStyle(
                      fontSize: TDTheme.of(context).fontBodyLarge!.size,
                      color: TDTheme.of(context).brandNormalColor),
            ),
          ),
        ],
      ),
    );
  }

  timeUnitMap(String name) {
    if (widget.isTimeUnit != null && widget.isTimeUnit == true) {
      // 使用 widget.model.mapping 做为键
      var times = {
        widget.model.mapping[0]: context.resource.yearLabel,
        widget.model.mapping[1]: context.resource.monthLabel,
        widget.model.mapping[2]: context.resource.dateLabel,
        widget.model.mapping[3]: context.resource.weeksLabel,
        widget.model.mapping[4]: context.resource.hours,
        widget.model.mapping[5]: context.resource.minutes,
        widget.model.mapping[6]: context.resource.seconds
      };
      return times[name] ?? '';
    } else {
      return '';
    }
  }

  weekUnitMap(int index) {
    if (index < 0 || index > 6) {
      return '';
    }
    return [
      context.resource.monday,
      context.resource.tuesday,
      context.resource.wednesday,
      context.resource.thursday,
      context.resource.friday,
      context.resource.saturday,
      context.resource.sunday,
    ][index];
  }

  double getTitleHeight() => widget.titleHeight ?? _pickerTitleHeight;
}

// 时间选择器的数据类
class DatePickerModel {
  bool useYear;
  bool useMonth;
  bool useDay;
  bool useWeekDay;
  bool useHour;
  bool useMinute;
  bool useSecond;
  List<int> dateStart;
  List<int> dateEnd;
  List<int>? dateInitial;
  List<int> Function(DateTypeKey key, List<int> nums)? filterItems;
  final mapping = [
    'year',
    'month',
    'date',
    'weeks',
    'hours',
    'minutes',
    'seconds'
  ];

  late DateTime initialTime;

  /// 这四项随滑动而更新，注意初始化
  late int yearIndex;
  late int monthIndex;
  late int dayIndex;
  late int weekDayIndex;
  late int hourIndex;
  late int minuteIndex;
  late int secondIndex;
  late List<List<int>> data = [
    [],
    [],
    [],
    [],
    [],
    [],
    [],
  ];
  late var controllers;
  late FixedExtentScrollController yearFixedExtentScrollController;
  late FixedExtentScrollController monthFixedExtentScrollController;
  late FixedExtentScrollController dayFixedExtentScrollController;
  late FixedExtentScrollController weekDayFixedExtentScrollController;
  late FixedExtentScrollController hourFixedExtentScrollController;
  late FixedExtentScrollController minuteFixedExtentScrollController;
  late FixedExtentScrollController secondFixedExtentScrollController;

  DatePickerModel({
    required this.useYear,
    required this.useMonth,
    required this.useDay,
    required this.useHour,
    required this.useMinute,
    required this.useSecond,
    required this.useWeekDay,
    required this.dateStart,
    required this.dateEnd,
    this.dateInitial,
    this.filterItems,
  }) {
    assert(!useWeekDay || (!useSecond && !useMinute && !useHour),
        'WeekDay can only used with Year, Month and Day!');
    setInitialTime();
    setInitialYearData();
    setInitialMonthData();
    setInitialDayData();
    setInitialTimeData();
    setInitialWeekDayData();
    setControllers();
    addListener();
  }

  void setInitialTime() {
    dateStart = List.generate(
        6, (index) => index < dateStart.length ? dateStart[index] : 0);
    var startTime = DateTime(dateStart[0], dateStart[1], dateStart[2],
        dateStart[3], dateStart[4], dateStart[5]);
    dateEnd = List.generate(
        6, (index) => index < dateEnd.length ? dateEnd[index] : 0);
    var endTime = DateTime(
      dateEnd[0],
      dateEnd[1],
      dateEnd[2],
      dateEnd[3],
      dateEnd[4],
      dateEnd[5],
    );
    if (dateInitial != null) {
      var initList = List.generate(
          6, (index) => index < dateInitial!.length ? dateInitial![index] : 0);
      initialTime = DateTime(initList[0], initList[1], initList[2], initList[3],
          initList[4], initList[5]);
      if (initialTime.isBefore(startTime)) {
        initialTime = startTime;
      } else if (initialTime.isAfter(endTime)) {
        initialTime = endTime;
      }
      return;
    }

    var now = DateTime.now();
    if (now.isBefore(startTime)) {
      initialTime = startTime.copyWith();
    } else if (now.isAfter(endTime)) {
      initialTime = startTime.copyWith();
    } else {
      initialTime = now;
    }
  }

  void setInitialYearData() {
    var years = List.generate(
        dateEnd[0] - dateStart[0] + 1, (index) => index + dateStart[0]);
    data[0] = useYear && filterItems != null
        ? filterItems!(DateTypeKey.year, years)
        : years;
  }

  void setInitialMonthData() {
    late List<int> month;
    if (dateEnd[0] == dateStart[0]) {
      month = List.generate(
          dateEnd[1] - dateStart[1] + 1, (index) => index + dateStart[1]);
    } else if (initialTime.year == dateStart[0]) {
      month =
          List.generate(12 - dateStart[1] + 1, (index) => index + dateStart[1]);
    } else if (initialTime.year == dateEnd[0]) {
      month = List.generate(dateEnd[1], (index) => index + 1);
    } else {
      month = List.generate(12, (index) => index + 1);
    }
    data[1] = useMonth && filterItems != null
        ? filterItems!(DateTypeKey.month, month)
        : month;
  }

  void setInitialDayData() {
    late List<int> day;
    if (dateEnd[0] == dateStart[0] && dateEnd[1] == dateStart[1]) {
      day = List.generate(
          dateEnd[2] - dateStart[2] + 1, (index) => index + dateStart[2]);
    } else if (initialTime.year == dateStart[0] &&
        initialTime.month == dateStart[1]) {
      day = List.generate(
          DateTime(initialTime.year, initialTime.month + 1, 0).day -
              dateStart[2] +
              1,
          (index) => index + dateStart[2]);
    } else if (initialTime.year == dateEnd[0] &&
        initialTime.month == dateEnd[1]) {
      day = List.generate(dateEnd[2], (index) => index + 1);
    } else {
      day = List.generate(
          DateTime(initialTime.year, initialTime.month + 1, 0).day,
          (index) => index + 1);
    }
    data[2] = useDay && filterItems != null
        ? filterItems!(DateTypeKey.day, day)
        : day;
  }

  void setInitialWeekDayData() {
    var weekDay = [1, 2, 3, 4, 5, 6, 7];
    data[3] = useWeekDay && filterItems != null
        ? filterItems!(DateTypeKey.weekDay, weekDay)
        : weekDay;
  }

  void setInitialTimeData() {
    var hour = List.generate(24, (index) => index);
    var minute = List.generate(60, (index) => index);
    var second = List.generate(60, (index) => index);
    if (dateStart.length > 3) {
      if (!useYear &&
          !useMonth &&
          !useDay &&
          dateEnd[0] == dateStart[0] &&
          dateEnd[1] == dateStart[1] &&
          dateEnd[2] == dateStart[2]) {
        hour = List.generate(
            max(0, dateEnd[3] - dateStart[3] + 1), (i) => i + dateStart[3]);
        minute = List.generate(
            max(0, dateEnd[4] - dateStart[4] + 1), (i) => i + dateStart[4]);
        second = List.generate(
            max(0, dateEnd[5] - dateStart[5] + 1), (i) => i + dateStart[5]);

        data[4] = useHour && filterItems != null
            ? filterItems!(DateTypeKey.hour, hour)
            : hour;
        data[5] = useMinute && filterItems != null
            ? filterItems!(DateTypeKey.minute, minute)
            : minute;
        data[6] = useSecond && filterItems != null
            ? filterItems!(DateTypeKey.second, second)
            : second;
        return;
      }
      if (initialTime.hour >= dateStart[3]) {
        hour =
            List.generate(24 - dateStart[3], (index) => index + dateStart[3]);
      }
      if (initialTime.minute >= dateStart[4]) {
        minute =
            List.generate(60 - dateStart[4], (index) => index + dateStart[4]);
      }
      if (initialTime.second >= dateStart[5]) {
        second =
            List.generate(60 - dateStart[5], (index) => index + dateStart[5]);
      }
    }
    data[4] = useHour && filterItems != null
        ? filterItems!(DateTypeKey.hour, hour)
        : hour;
    data[5] = useMinute && filterItems != null
        ? filterItems!(DateTypeKey.minute, minute)
        : minute;
    data[6] = useSecond && filterItems != null
        ? filterItems!(DateTypeKey.second, second)
        : second;
  }

  void setControllers() {
    /// 初始化Index
    yearIndex = initialTime.year - data[0][0];
    monthIndex = initialTime.month - data[1][0];
    dayIndex = initialTime.day - data[2][0];
    weekDayIndex = initialTime.weekday - 1;
    hourIndex = initialTime.hour - data[4][0];
    minuteIndex = initialTime.minute - data[5][0];
    secondIndex = initialTime.second - data[6][0];
    controllers = [
      FixedExtentScrollController(initialItem: yearIndex),
      FixedExtentScrollController(initialItem: monthIndex),
      FixedExtentScrollController(initialItem: dayIndex),
      FixedExtentScrollController(initialItem: weekDayIndex),
      FixedExtentScrollController(initialItem: hourIndex),
      FixedExtentScrollController(initialItem: minuteIndex),
      FixedExtentScrollController(initialItem: secondIndex)
    ];
    yearFixedExtentScrollController = controllers[0];
    monthFixedExtentScrollController = controllers[1];
    dayFixedExtentScrollController = controllers[2];
    weekDayFixedExtentScrollController = controllers[3];
    hourFixedExtentScrollController = controllers[4];
    minuteFixedExtentScrollController = controllers[5];
    secondFixedExtentScrollController = controllers[6];
  }

  void _yearListener() {
    yearIndex = yearFixedExtentScrollController.selectedItem;
  }

  void _monthListener() {
    monthIndex = monthFixedExtentScrollController.selectedItem;
  }

  void _dayListener() {
    dayIndex = dayFixedExtentScrollController.selectedItem;
  }

  void _weekDayListener() {
    weekDayIndex = weekDayFixedExtentScrollController.selectedItem;
  }

  void _hourDayListener() {
    hourIndex = hourFixedExtentScrollController.selectedItem;
  }

  void _minuteDayListener() {
    minuteIndex = minuteFixedExtentScrollController.selectedItem;
  }

  void _secondDayListener() {
    secondIndex = secondFixedExtentScrollController.selectedItem;
  }

  void addListener() {
    /// 给年月日加上监控
    yearFixedExtentScrollController.addListener(_yearListener);
    monthFixedExtentScrollController.addListener(_monthListener);
    dayFixedExtentScrollController.addListener(_dayListener);
    weekDayFixedExtentScrollController.addListener(_weekDayListener);
    hourFixedExtentScrollController.addListener(_hourDayListener);
    minuteFixedExtentScrollController.addListener(_minuteDayListener);
    secondFixedExtentScrollController.addListener(_secondDayListener);
  }

  void removeListener() {
    /// 移除年月日的监控
    yearFixedExtentScrollController.removeListener(_yearListener);
    monthFixedExtentScrollController.removeListener(_monthListener);
    dayFixedExtentScrollController.removeListener(_dayListener);
    weekDayFixedExtentScrollController.removeListener(_weekDayListener);
    hourFixedExtentScrollController.removeListener(_hourDayListener);
    minuteFixedExtentScrollController.removeListener(_minuteDayListener);
    secondFixedExtentScrollController.removeListener(_secondDayListener);
  }

  void refreshMonthDataAndController() {
    var selectedYear = yearIndex + data[0][0];
    late List<int> month;
    if (dateEnd[0] == dateStart[0]) {
      month = List.generate(
          dateEnd[1] - dateStart[1] + 1, (index) => index + dateStart[1]);
    } else if (selectedYear == dateStart[0]) {
      month =
          List.generate(12 - dateStart[1] + 1, (index) => index + dateStart[1]);
    } else if (selectedYear == dateEnd[0]) {
      month = List.generate(dateEnd[1], (index) => index + 1);
    } else {
      month = List.generate(12, (index) => index + 1);
    }
    data[1] = useMonth && filterItems != null
        ? filterItems!(DateTypeKey.month, month)
        : month;
    monthFixedExtentScrollController.jumpToItem(
        monthIndex > data[1].length - 1 ? data[1].length - 1 : monthIndex);
  }

  void refreshDayDataAndController() {
    /// 在刷新日数据时，年月数据已经是最新的
    var selectedYear = yearIndex + data[0][0];
    var selectedMonth = monthIndex + data[1][0];
    late List<int> day;
    if (dateEnd[0] == dateStart[0] && dateEnd[1] == dateStart[1]) {
      day = List.generate(
          dateEnd[2] - dateStart[2] + 1, (index) => index + dateStart[2]);
    } else if (selectedYear == dateStart[0] && selectedMonth == dateStart[1]) {
      day = List.generate(
          DateTime(selectedYear, selectedMonth + 1, 0).day - dateStart[2] + 1,
          (index) => index + dateStart[2]);
    } else if (selectedYear == dateEnd[0] && selectedMonth == dateEnd[1]) {
      day = List.generate(dateEnd[2], (index) => index + 1);
    } else {
      day = List.generate(DateTime(selectedYear, selectedMonth + 1, 0).day,
          (index) => index + 1);
    }
    data[2] = useDay && filterItems != null
        ? filterItems!(DateTypeKey.day, day)
        : day;
    dayFixedExtentScrollController.jumpToItem(
        dayIndex > data[2].length - 1 ? data[2].length - 1 : dayIndex);
  }

  void refreshWeekDayDataAndController() {
    var date = DateTime(
        data[0][yearFixedExtentScrollController.selectedItem],
        data[1][monthFixedExtentScrollController.selectedItem],
        data[2][dayFixedExtentScrollController.selectedItem]);
    weekDayFixedExtentScrollController.jumpToItem(date.weekday - 1);
  }

  void refreshTimeDataInitialAndController(int wheel) {
    var selectedYear = yearIndex + data[0][0];
    var selectedMonth = monthIndex + data[1][0];
    var selectDay = dayIndex + data[2][0];
    if (wheel <= 2) {
      refreshHourData(
          selectedYear: selectedYear,
          selectedMonth: selectedMonth,
          selectDay: selectDay);
      refreshMinuteData(
          selectedYear: selectedYear,
          selectedMonth: selectedMonth,
          selectDay: selectDay);
    } else {
      refreshMinuteData(
          selectedYear: selectedYear,
          selectedMonth: selectedMonth,
          selectDay: selectDay);
    }
    refreshSecondData(
        selectedYear: selectedYear,
        selectedMonth: selectedMonth,
        selectDay: selectDay);
  }

  void refreshHourData(
      {required int selectedYear,
      required int selectedMonth,
      required int selectDay}) {
    var selectHour = selectDay == data[2][0] ? 0 : hourIndex;
    late List<int> hour;
    if (selectedYear == dateStart[0] &&
        selectedMonth == dateStart[1] &&
        selectDay == dateStart[2]) {
      hour =
          List.generate(24 - (dateStart[3]), (index) => index + dateStart[3]);
    } else if (selectedYear == dateEnd[0] &&
        selectedMonth == dateEnd[1] &&
        selectDay == dateEnd[2]) {
      hour = dateEnd[3] >= dateStart[3]
          ? List.generate(dateEnd[3] + 1, (index) => index)
          : List.generate(24 - dateStart[3], (index) => index);
    } else {
      hour = List.generate(24, (index) => index);
    }
    data[4] = useHour && filterItems != null
        ? filterItems!(DateTypeKey.hour, hour)
        : hour;
    hourFixedExtentScrollController.jumpToItem(selectHour > 0 ? hourIndex : 0);
  }

  void refreshMinuteData(
      {required int selectedYear,
      required int selectedMonth,
      required int selectDay}) {
    var selectHour = hourIndex + data[4][0];
    late List<int> minute;
    if (selectedYear == dateStart[0] &&
        selectedMonth == dateStart[1] &&
        selectDay == dateStart[2] &&
        selectHour == dateStart[3]) {
      minute =
          List.generate(60 - (dateStart[4]), (index) => index + dateStart[4]);
    } else if (selectedYear == dateEnd[0] &&
        selectedMonth == dateEnd[1] &&
        selectDay == dateEnd[2] &&
        selectHour == dateEnd[3]) {
      minute = dateEnd[4] >= dateStart[4]
          ? List.generate(dateEnd[4] + 1, (index) => index)
          : List.generate(60 - (dateStart[4]), (index) => index);
    } else {
      minute = List.generate(60, (index) => index);
    }
    data[5] = useMinute && filterItems != null
        ? filterItems!(DateTypeKey.minute, minute)
        : minute;
  }

  void refreshSecondData(
      {required int selectedYear,
      required int selectedMonth,
      required int selectDay}) {
    var selectHour = hourIndex + data[4][0];
    var selectMinute = minuteIndex + data[5][0];
    late List<int> second;
    if (selectedYear == dateStart[0] &&
        selectedMonth == dateStart[1] &&
        selectDay == dateStart[2] &&
        selectHour == dateStart[3] &&
        selectMinute == dateStart[4]) {
      second =
          List.generate(60 - (dateStart[5]), (index) => index + dateStart[5]);
    } else if (selectedYear == dateEnd[0] &&
        selectedMonth == dateEnd[1] &&
        selectDay == dateEnd[2] &&
        selectHour == dateEnd[3] &&
        selectMinute == dateEnd[4]) {
      second = dateEnd[5] >= dateStart[5]
          ? List.generate(dateEnd[5] + 1, (index) => index)
          : List.generate(60 - (dateStart[5]), (index) => index);
    } else {
      second = List.generate(60, (index) => index);
    }
    data[6] = useSecond && filterItems != null
        ? filterItems!(DateTypeKey.second, second)
        : second;
  }

  Map<String, int> getSelectedMap() {
    var map = <String, int>{
      'year': yearIndex + data[0][0],
      'month': monthIndex + data[1][0],
      'day': dayIndex + data[2][0],
    };
    return map;
  }
}

Picker.item_widget:
import 'package:flutter/material.dart';
import '../../../tdesign_flutter.dart';

typedef ItemBuilderType = Widget? Function(
  /// 上下文
  BuildContext context,

  /// 文字内容
  String content,

  /// 列号
  int colIndex,

  /// 行号
  int index,

  /// 根据距离计算字体颜色、透明度、粗细
  ItemDistanceCalculator itemDistanceCalculator,

  /// 子项此时离中心的距离
  double distance,
);

/// 所有选择器的子项组件
class TDItemWidget extends StatefulWidget {
  final String content;
  final FixedExtentScrollController fixedExtentScrollController;
  final int colIndex;
  final int index;
  final double itemHeight;
  final ItemDistanceCalculator? itemDistanceCalculator;
  final ItemBuilderType? itemBuilder;

  const TDItemWidget({
    required this.fixedExtentScrollController,
    required this.colIndex,
    required this.index,
    required this.content,
    required this.itemHeight,
    this.itemDistanceCalculator,
    this.itemBuilder,
    Key? key,
  }) : super(key: key);

  @override
  _TDItemWidgetState createState() => _TDItemWidgetState();
}

class _TDItemWidgetState extends State<TDItemWidget> {
  /// 子项监听滚动，从而刷新自身的颜色
  VoidCallback? listener;
  ItemDistanceCalculator? _itemDistanceCalculator;

  @override
  void initState() {
    super.initState();
    listener = () => setState(() {});
    _itemDistanceCalculator = widget.itemDistanceCalculator;

    /// 子项注册滚动监听
    widget.fixedExtentScrollController.addListener(listener!);
  }

  @override
  Widget build(BuildContext context) {
    /// 子项此时离中心的距离
    /// 不要使用widget.fixedExtentScrollController.selectedItem
    /// 其中selectedItem会报错，原因是一开始minScrollExtent为空
    var distance =
        (widget.fixedExtentScrollController.offset / widget.itemHeight -
                widget.index)
            .abs()
            .toDouble();
    _itemDistanceCalculator ??= ItemDistanceCalculator();
    return widget.itemBuilder?.call(
          context,
          widget.content,
          widget.colIndex,
          widget.index,
          _itemDistanceCalculator!,
          distance,
        ) ??
        TDText(
          widget.content,
          maxLines: 1,
          overflow: TextOverflow.ellipsis,
          style: TextStyle(
            fontWeight:
                _itemDistanceCalculator!.calculateFontWeight(context, distance),
            fontSize: _itemDistanceCalculator!.calculateFont(context, distance),
            color: _itemDistanceCalculator!.calculateColor(context, distance),
          ),
        );
  }

  @override
  void dispose() {
    /// 在销毁前完成监听注销
    widget.fixedExtentScrollController.removeListener(listener!);
    super.dispose();
  }
}

class ItemDistanceCalculator {
  ItemDistanceCalculator();

  Color calculateColor(BuildContext context, double distance) {
    /// 线性插值
    if (distance < 0.5) {
      return TDTheme.of(context).textColorPrimary;
    } else {
      return TDTheme.of(context).textDisabledColor;
    }
  }

  FontWeight calculateFontWeight(BuildContext context, double distance) {
    if (distance < 0.5) {
      return FontWeight.w600;
    } else {
      return FontWeight.w400;
    }
  }

  double calculateFont(BuildContext context, double distance) {
    return TDTheme.of(context).fontBodyLarge!.size;
  }
}

Picker.multi_picker:
import 'dart:math';

import 'package:flutter/material.dart';
import '../../../tdesign_flutter.dart';

import '../../util/context_extension.dart';
import 'no_wave_behavior.dart';

typedef MultiPickerCallback = void Function(List selected);

/// 项之间无联动的多项选择器
class TDMultiPicker extends StatelessWidget {
  /// 选择器标题
  final String? title;

  /// 选择器确认按钮回调
  final MultiPickerCallback? onConfirm;

  /// 选择器取消按钮回调
  final MultiPickerCallback? onCancel;

  /// todo 选择器数据改变时回调
  final MultiPickerCallback? onChange;

  /// 选择器的数据源
  final List<List<String>> data;

  /// 选择器List的视窗高度，默认200
  final double pickerHeight;

  /// 选择器List视窗中item个数，pickerHeight / pickerItemCount，即item高度
  final int pickerItemCount;

  /// 自定义选择框样式
  final Widget? customSelectWidget;

  /// 右侧按钮文案
  final String? rightText;

  /// 左侧按钮文案
  final String? leftText;

  /// 自定义左侧文案样式
  final TextStyle? leftTextStyle;

  /// 自定义右侧文案样式
  final TextStyle? rightTextStyle;

  /// 自定义中间文案样式
  final TextStyle? centerTextStyle;

  /// 标题高度
  final double? titleHeight;

  /// 顶部填充
  final double? topPadding;

  /// 左边填充
  final double? leftPadding;

  /// 右边填充
  final double? rightPadding;

  /// 标题分割线颜色
  final Color? titleDividerColor;

  /// 背景颜色
  final Color? backgroundColor;

  /// 顶部圆角
  final double? topRadius;

  /// 不同距离自选项计算策略
  final ItemDistanceCalculator? itemDistanceCalculator;

  /// 适配padding
  final EdgeInsets? padding;

  /// 若为null表示全部从零开始
  final List<int>? initialIndexes;

  /// 自定义item构建
  final ItemBuilderType? itemBuilder;

  /// 是否显示头部内容
  final bool header;

  static const _pickerTitleHeight = 56.0;

  const TDMultiPicker({
    this.title,
    required this.onConfirm,
    this.onCancel,
    this.onChange,
    required this.data,
    this.pickerHeight = 200,
    this.pickerItemCount = 5,
    this.initialIndexes,
    this.rightText,
    this.leftText,
    this.leftTextStyle,
    this.rightTextStyle,
    this.centerTextStyle,
    this.titleHeight,
    this.topPadding,
    this.leftPadding,
    this.rightPadding,
    this.titleDividerColor,
    this.backgroundColor,
    this.topRadius,
    this.padding,
    this.itemDistanceCalculator,
    this.customSelectWidget,
    this.itemBuilder,
    this.header = true,
    Key? key,
  }) : super(key: key);

  @override
  Widget build(BuildContext context) {
    final dataLength = data.length;

    var indexes = initialIndexes ?? List.generate(dataLength, (i) => 0);

    var controllers = List.generate(
      dataLength,
      (i) => FixedExtentScrollController(initialItem: indexes[i]),
    );

    final maxWidth = MediaQuery.of(context).size.width;

    return Container(
      width: maxWidth,
      padding: padding ??
          EdgeInsets.only(bottom: MediaQuery.of(context).padding.bottom),
      decoration: BoxDecoration(
        color: backgroundColor ?? TDTheme.of(context).bgColorContainer,
        borderRadius: BorderRadius.vertical(
          top: Radius.circular(
              topRadius ?? TDTheme.of(context).radiusExtraLarge),
        ),
      ),
      child: Column(
        mainAxisSize: MainAxisSize.min,
        children: [
          if (header) _buildHeader(context, controllers),
          Stack(
            alignment: Alignment.center,
            children: [
              Padding(
                padding: const EdgeInsets.symmetric(horizontal: 16),
                child: customSelectWidget ??
                    Container(
                      height: 40,
                      decoration: BoxDecoration(
                        color: TDTheme.of(context).bgColorSecondaryContainer,
                        borderRadius: BorderRadius.all(
                            Radius.circular(TDTheme.of(context).radiusDefault)),
                      ),
                    ),
              ),
              // 列表
              Container(
                padding: const EdgeInsets.symmetric(horizontal: 32),
                height: pickerHeight,
                width: maxWidth,
                child: Row(
                  children: List.generate(
                    dataLength,
                    (i) => Expanded(child: _buildList(context, i, controllers)),
                  ),
                ),
              ),
              // 蒙层
              Positioned(
                top: 0,
                child: IgnorePointer(
                  ignoring: true,
                  child: Container(
                    height: _pickerTitleHeight,
                    width: MediaQuery.of(context).size.width,
                    decoration: BoxDecoration(
                      gradient: LinearGradient(
                        begin: Alignment.topCenter,
                        end: Alignment.bottomCenter,
                        colors: [
                          TDTheme.of(context).bgColorContainer,
                          TDTheme.of(context).bgColorContainer.withOpacity(0)
                        ],
                      ),
                    ),
                  ),
                ),
              ),
              Positioned(
                bottom: 0,
                child: IgnorePointer(
                  ignoring: true,
                  child: Container(
                    height: _pickerTitleHeight,
                    width: MediaQuery.of(context).size.width,
                    decoration: BoxDecoration(
                      gradient: LinearGradient(
                        begin: Alignment.bottomCenter,
                        end: Alignment.topCenter,
                        colors: [
                          TDTheme.of(context).bgColorContainer,
                          TDTheme.of(context).bgColorContainer.withOpacity(0)
                        ],
                      ),
                    ),
                  ),
                ),
              )
            ],
          ),
        ],
      ),
    );
  }

  Widget _buildHeader(
    BuildContext context,
    List<FixedExtentScrollController> controllers,
  ) {
    final padding = TDTheme.of(context).spacer16;

    return Container(
      padding: EdgeInsets.only(
        left: leftPadding ?? padding,
        right: rightPadding ?? padding,
        top: topPadding ?? padding,
      ),
      decoration: BoxDecoration(
        border: Border(
          bottom: BorderSide(
            width: 0.5,
            color: titleDividerColor ?? Colors.transparent,
          ),
        ),
      ),
      height: getTitleHeight(),
      child: Row(
        crossAxisAlignment: CrossAxisAlignment.center,
        children: [
          /// 左边按钮
          GestureDetector(
              onTap: () {
                if (onCancel != null) {
                  onCancel!(controllers
                      .map((controller) => controller.selectedItem)
                      .toList());
                } else {
                  Navigator.of(context).pop();
                }
              },
              behavior: HitTestBehavior.opaque,
              child: TDText(
                leftText ?? context.resource.cancel,
                style: leftTextStyle ??
                    TextStyle(
                        fontSize: TDTheme.of(context).fontBodyLarge!.size,
                        color: TDTheme.of(context).textColorSecondary),
              )),

          /// 中间title
          Expanded(
            child: Center(
              child: TDText(
                title ?? '',
                style: centerTextStyle ??
                    TextStyle(
                        fontSize: TDTheme.of(context).fontTitleLarge!.size,
                        fontWeight: FontWeight.w600,
                        color: TDTheme.of(context).textColorPrimary),
              ),
            ),
          ),

          // 右边按钮
          GestureDetector(
            onTap: () {
              if (onConfirm != null) {
                onConfirm!(controllers
                    .map((controller) => controller.selectedItem)
                    .toList());
              }
            },
            behavior: HitTestBehavior.opaque,
            child: TDText(
              rightText ?? context.resource.confirm,
              style: rightTextStyle ??
                  TextStyle(
                      fontSize: TDTheme.of(context).fontBodyLarge!.size,
                      color: TDTheme.of(context).brandNormalColor),
            ),
          ),
        ],
      ),
    );
  }

  double getTitleHeight() => titleHeight ?? _pickerTitleHeight;

  Widget _buildList(
    context,
    int position,
    List<FixedExtentScrollController> controllers,
  ) {
    var maxWidth = MediaQuery.of(context).size.width;
    return MediaQuery.removePadding(
        context: context,
        removeTop: true,
        child: ScrollConfiguration(
          behavior: NoWaveBehavior(),
          child: ListWheelScrollView.useDelegate(
              itemExtent: pickerHeight / pickerItemCount,
              diameterRatio: 100,
              controller: controllers[position],
              physics: const FixedExtentScrollPhysics(),
              childDelegate: ListWheelChildBuilderDelegate(
                  childCount: data[position].length,
                  builder: (context, index) {
                    return Container(
                        key: UniqueKey(),
                        alignment: Alignment.center,
                        height: pickerHeight / pickerItemCount,
                        width: maxWidth,
                        child: TDItemWidget(
                          colIndex: position,
                          index: index,
                          key: UniqueKey(),
                          itemHeight: pickerHeight / pickerItemCount,
                          content: data[position][index],
                          itemDistanceCalculator: itemDistanceCalculator,
                          fixedExtentScrollController: controllers[position],
                          itemBuilder: itemBuilder,
                        ));
                  })),
        ));
  }
}

