class _TDMultiLinkedPickerState extends State<TDMultiLinkedPicker> {
  late MultiLinkedPickerModel model;

  double pickerHeight = 0;

  static const _pickerTitleHeight = 56.0;

  @override
  void initState() {
    super.initState();
    pickerHeight = widget.pickerHeight;
    model = MultiLinkedPickerModel(
      data: widget.data,
      columnNum: widget.columnNum,
      initialData: widget.selectedData,
      keepSameSelection: widget.keepSameSelection,
    );
  }

  @override
  Widget build(BuildContext context) {
    final maxWidth = MediaQuery.of(context).size.width;
    return Container(
      width: maxWidth,
      padding: widget.padding ??
          EdgeInsets.only(bottom: MediaQuery.of(context).padding.bottom),
      decoration: BoxDecoration(
        color: widget.backgroundColor ?? TDTheme.of(context).bgColorContainer,
        borderRadius: BorderRadius.vertical(
          top: Radius.circular(
              widget.topRadius ?? TDTheme.of(context).radiusExtraLarge),
        ),
      ),
      child: Column(
        mainAxisSize: MainAxisSize.min,
        children: [
          if (widget.header) _buildHeader(context),
          Stack(
            alignment: Alignment.center,
            children: [
              Padding(
                padding: const EdgeInsets.symmetric(horizontal: 16),
                child: widget.customSelectWidget ??
                    Container(
                      height: 40,
                      decoration: BoxDecoration(
                          color: TDTheme.of(context).bgColorSecondaryContainer,
                          borderRadius: BorderRadius.all(Radius.circular(
                              TDTheme.of(context).radiusDefault))),
                    ),
              ),

              // 列表
              Container(
                  padding: const EdgeInsets.symmetric(horizontal: 32),
                  height: pickerHeight,
                  width: maxWidth,
                  child: Row(
                    children: List.generate(
                      widget.columnNum,
                      (i) => Expanded(child: buildList(context, i)),
                    ),
                  )),
              // 蒙层
              Positioned(
                top: 0,
                child: IgnorePointer(
                  ignoring: true,
                  child: Container(
                    height: _pickerTitleHeight,
                    width: MediaQuery.of(context).size.width,
                    decoration: BoxDecoration(
                      gradient: LinearGradient(
                        begin: Alignment.topCenter,
                        end: Alignment.bottomCenter,
                        colors: [
                          TDTheme.of(context).bgColorContainer,
                          TDTheme.of(context).bgColorContainer.withOpacity(0)
                        ],
                      ),
                    ),
                  ),
                ),
              ),
              Positioned(
                bottom: 0,
                child: IgnorePointer(
                  ignoring: true,
                  child: Container(
                    height: _pickerTitleHeight,
                    width: MediaQuery.of(context).size.width,
                    decoration: BoxDecoration(
                      gradient: LinearGradient(
                        begin: Alignment.bottomCenter,
                        end: Alignment.topCenter,
                        colors: [
                          TDTheme.of(context).bgColorContainer,
                          TDTheme.of(context).bgColorContainer.withOpacity(0)
                        ],
                      ),
                    ),
                  ),
                ),
              )
            ],
          ),
        ],
      ),
    );
  }

  Widget buildList(context, int position) {
    // position参数表示这个第几列
    var maxWidth = MediaQuery.of(context).size.width;
    return MediaQuery.removePadding(
      context: context,
      removeTop: true,
      child: ScrollConfiguration(
        behavior: NoWaveBehavior(),
        child: NotificationListener<ScrollNotification>(
          onNotification: (ScrollNotification notification) {
            // 滚动到底部加载更多
            if (notification is ScrollEndNotification) {
              final metrics = notification.metrics;
              if (metrics.pixels >= metrics.maxScrollExtent - 10) {
                if (model.loadMoreData(position)) {
                  setState(() {});
                }
              }
            }
            return false;
          },
          child: ListWheelScrollView.useDelegate(
              itemExtent: pickerHeight / widget.pickerItemCount,
              diameterRatio: 100,
              controller: model.controllers[position],
              physics: const FixedExtentScrollPhysics(),
              onSelectedItemChanged: (index) {
                if (index >= 0 && index < model.presentData[position].length) {
                  setState(() {
                    model.refreshPresentDataAndController(
                      position,
                      index,
                      false,
                    );
                    if (index >= model.presentData[position].length - 5 &&
                        model.hasMoreData[position]) {
                      if (model.loadMoreData(position)) {
                        // 延迟一下再刷新，避免连续setState
                        Future.delayed(const Duration(milliseconds: 50), () {
                          if (mounted) {
                            setState(() {});
                          }
                        });
                      }
                    }

                    /// todo 通过随机数改变高度来触发UI刷新，这是hack式的解决方案！有待优化！
                    /// fix https://github.com/flutter/flutter/issues/22999
                    pickerHeight =
                        pickerHeight - Random().nextDouble() / 100000000;
                  });
                }
              },
              childDelegate: ListWheelChildBuilderDelegate(
                  childCount: model.presentData[position].length +
                      (model.hasMoreData[position] ? 1 : 0),
                  builder: (context, index) {
                    if (index >= model.presentData[position].length) {
                      // 加载更多指示器
                      return Container(
                        alignment: Alignment.center,
                        height: pickerHeight / widget.pickerItemCount,
                        child: Text(
                          context.resource.loadingWithPoint,
                          style: TextStyle(
                            color: TDTheme.of(context).textColorPlaceholder,
                          ),
                        ),
                      );
                    }
                    if (index < 0 ||
                        index >= model.presentData[position].length) {
                      return Container();
                    }
                    return Container(
                        alignment: Alignment.center,
                        height: pickerHeight / widget.pickerItemCount,
                        width: maxWidth,
                        child: TDItemWidget(
                          colIndex: position,
                          index: index,
                          itemHeight: pickerHeight / widget.pickerItemCount,
                          content:
                              model.presentData[position][index].toString(),
                          fixedExtentScrollController:
                              model.controllers[position],
                          itemDistanceCalculator: widget.itemDistanceCalculator,
                          itemBuilder: widget.itemBuilder,
                        ));
                  })),
        ),
      ),
    );
  }

  Widget _buildHeader(BuildContext context) {
    final padding = TDTheme.of(context).spacer16;

    return Container(
      padding: EdgeInsets.only(
        left: widget.leftPadding ?? padding,
        right: widget.rightPadding ?? padding,
        top: widget.topPadding ?? padding,
      ),
      decoration: BoxDecoration(
        border: Border(
          bottom: BorderSide(
            width: 0.5,
            color: widget.titleDividerColor ?? Colors.transparent,
          ),
        ),
      ),
      height: getTitleHeight() - 0.5,
      child: Row(
        crossAxisAlignment: CrossAxisAlignment.center,
        children: [
          /// 左边按钮
          GestureDetector(
              onTap: () {
                if (widget.onCancel != null) {
                  widget.onCancel!(model.selectedData);
                } else {
                  Navigator.of(context).pop();
                }
              },
              behavior: HitTestBehavior.opaque,
              child: TDText(
                widget.leftText ?? context.resource.cancel,
                style: widget.leftTextStyle ??
                    TextStyle(
                      fontSize: TDTheme.of(context).fontBodyLarge!.size,
                      color: TDTheme.of(context).textColorSecondary,
                    ),
              )),

          /// 中间title
          Expanded(
            child: Center(
              child: TDText(
                widget.title ?? '',
                style: widget.centerTextStyle ??
                    TextStyle(
                      fontSize: TDTheme.of(context).fontTitleLarge!.size,
                      fontWeight: FontWeight.w700,
                      color: TDTheme.of(context).textColorPrimary,
                    ),
              ),
            ),
          ),

          /// 右边按钮
          GestureDetector(
            onTap: () {
              if (widget.onConfirm != null) {
                widget.onConfirm!(model.selectedData);
              }
            },
            behavior: HitTestBehavior.opaque,
            child: TDText(
              widget.rightText ?? context.resource.confirm,
              style: widget.rightTextStyle ??
                  TextStyle(
                    fontSize: TDTheme.of(context).fontBodyLarge!.size,
                    color: TDTheme.of(context).brandNormalColor,
                  ),
            ),
          ),
        ],
      ),
    );
  }

  double getTitleHeight() => widget.titleHeight ?? _pickerTitleHeight;
}

class MultiLinkedPickerModel {
  /// 占位字符
  static const placeData = '';

  /// 总的数据
  late Map data;

  /// 选中数据下标
  late List<int> selectedIndexes;

  /// 总列数
  late int columnNum;

  /// 选中数据
  late List selectedData;

  late List<FixedExtentScrollController> controllers = [];

  /// 每一列展示的数据
  late List<List> presentData = [];

  /// 是否保留相同选项
  bool keepSameSelection = false;

  /// 添加一个常量定义每页加载数量
  static const int pageSize = 10;

  /// 每列的当前页码
  late List<int> currentPages;

  /// 每列是否还有更多数据
  late List<bool> hasMoreData;

  /// 每列的总数据量
  late List<int> totalCounts;

  MultiLinkedPickerModel({
    required this.data,
    required this.columnNum,
    required List initialData,
    this.keepSameSelection = false,
  }) {
    selectedData = [];
    selectedIndexes = [];
    currentPages = List.generate(columnNum, (_) => 0);
    hasMoreData = List.generate(columnNum, (_) => true);
    totalCounts = List.generate(columnNum, (_) => 0);
    for (var i = 0; i < columnNum; ++i) {
      if (i >= initialData.length) {
        selectedData.add('');
      } else {
        selectedData.add(initialData[i]?.toString() ?? '');
      }
      selectedIndexes.add(0);
    }
    _init(initialData);
  }

  void _init(List initialData) {
    controllers.clear();
    presentData.clear();
    for (var i = 0; i < columnNum; ++i) {
      if (i >= presentData.length) {
        presentData.add([placeData]);
      }
      List currentLevelData;
      if (i == 0) {
        currentLevelData = _getNextLevelDataPaginated(0, 0);
        if (currentLevelData.isEmpty) {
          currentLevelData = [placeData];
        }
      } else {
        currentLevelData = _getNextLevelDataPaginated(i, 0);
      }
      // 处理选中项
      var selectedIndex = currentLevelData.indexOf(selectedData[i]);
      if (selectedIndex < 0) {
        selectedData[i] =
            currentLevelData.isNotEmpty ? currentLevelData.first : placeData;
        selectedIndex = 0;
      }
      selectedIndexes[i] = selectedIndex;
      presentData[i] = currentLevelData;
      // 创建控制器
      controllers.add(FixedExtentScrollController(
          initialItem: selectedIndex.clamp(0, currentLevelData.length - 1)));
    }
  }

  List _getNextLevelDataPaginated(int level, int page) {
    try {
      dynamic currentData = data;
      for (var i = 0; i < level; i++) {
        if (currentData is Map && currentData.containsKey(selectedData[i])) {
          currentData = currentData[selectedData[i]];
        } else {
          return [placeData];
        }
      }
      List allData;
      if (currentData is Map) {
        allData = currentData.keys.toList();
      } else if (currentData is List) {
        allData = currentData;
      } else {
        allData = [currentData?.toString() ?? placeData];
      }
      totalCounts[level] = allData.length;
      int start = page * pageSize;
      int end = start + pageSize;
      if (start >= allData.length) {
        return [];
      }
      if (end > allData.length) {
        end = allData.length;
      }
      hasMoreData[level] = end < allData.length;
      return allData.sublist(start, end);
    } catch (e) {
      return [placeData];
    }
  }

  bool loadMoreData(int columnIndex) {
    if (columnIndex >= columnNum || !hasMoreData[columnIndex]) {
      return false;
    }
    List newData;
    int nextPage = currentPages[columnIndex] + 1;
    if (columnIndex == 0) {
      newData = _getNextLevelDataPaginated(0, nextPage);
    } else {
      newData = _getNextLevelDataPaginated(columnIndex, nextPage);
    }
    if (newData.isNotEmpty) {
      presentData[columnIndex].addAll(newData);
      currentPages[columnIndex] = nextPage;
      return true;
    } else {
      hasMoreData[columnIndex] = false;
    }
    return false;
  }

  /// [position] 变动的列
  /// [selectedIndex] 对应选中的index
  /// [jump] 是否需要jumpToItem
  void refreshPresentDataAndController(
    int position,
    int selectedIndex,
    bool jump,
  ) {
    // 严格的边界检查
    if (position >= presentData.length ||
        selectedIndex >= presentData[position].length ||
        position >= controllers.length) {
      return;
    }
    selectedIndex = selectedIndex.clamp(0, presentData[position].length - 1);
    var selectValue = presentData[position][selectedIndex];
    // 更新选中的数据
    selectedData[position] = selectValue;
    selectedIndexes[position] = selectedIndex;
    if (jump) {
      controllers[position].jumpToItem(selectedIndex);
    }
    // 检查是否需要预加载更多数据
    if (selectedIndex >= presentData[position].length - 5 &&
        hasMoreData[position]) {
      loadMoreData(position);
    }
    if (position < columnNum - 1) {
      List nextColumnData;
      if (presentData[position].length == 1 &&
          presentData[position].first == placeData) {
        nextColumnData = [placeData];
      } else {
        nextColumnData = _getNextLevelDataPaginated(position + 1, 0);
        currentPages[position + 1] = 0;
        hasMoreData[position + 1] = true;
      }
      if (nextColumnData.isEmpty) {
        nextColumnData = [placeData];
      }
      while (presentData.length <= position + 1) {
        presentData.add([placeData]);
      }
      presentData[position + 1] = nextColumnData;
      while (controllers.length <= position + 1) {
        controllers.add(FixedExtentScrollController(initialItem: 0));
      }
      refreshPresentDataAndController(position + 1, 0, true);
    }
  }
}

Popover.popover_widget:
import 'package:flutter/material.dart';
import '../../../tdesign_flutter.dart';

enum TDPopoverTheme {
  /// 暗色
  dark,

  /// 亮色
  light,

  /// 品牌色
  info,

  /// 成功
  success,

  /// 警告
  warning,

  /// 错误
  error
}

enum TDPopoverPlacement {
  /// 上左
  topLeft,

  /// 上
  top,

  /// 上右
  topRight,

  /// 右上
  rightTop,

  /// 右
  right,

  /// 右下
  rightBottom,

  /// 下右
  bottomRight,

  /// 下
  bottom,

  /// 下左
  bottomLeft,

  /// 左下
  leftBottom,

  /// 左
  left,

  /// 左上
  leftTop
}

typedef OnTap = Function(String? content);
typedef OnLongTap = Function(String? content);

class TDPopoverWidget extends StatefulWidget {
  const TDPopoverWidget({
    super.key,
    required this.context,
    this.content,
    this.contentWidget,
    this.offset = 4,
    this.theme,
    this.placement,
    this.showArrow = true,
    this.arrowSize = 8,
    this.padding,
    this.width,
    this.height,
    this.onTap,
    this.onLongTap,
    this.radius,
  });

  /// 上下文
  final BuildContext context;

  /// 显示内容
  final String? content;

  /// 自定义内容
  final Widget? contentWidget;

  /// 偏移
  final double offset;

  /// 弹出气泡主题
  final TDPopoverTheme? theme;

  /// 浮层出现位置
  final TDPopoverPlacement? placement;

  /// 是否显示浮层箭头
  final bool? showArrow;

  /// 箭头大小
  final double arrowSize;

  /// 内容内边距
  final EdgeInsetsGeometry? padding;

  /// 内容宽度（包含padding，实际高度：height - paddingLeft - paddingRight）
  final double? width;

  /// 内容高度（包含padding，实际高度：height - paddingTop - paddingBottom）
  final double? height;

  /// 点击事件
  final OnTap? onTap;

  /// 长按事件
  final OnLongTap? onLongTap;

  /// 圆角
  final BorderRadius? radius;

  @override
  State<TDPopoverWidget> createState() => _TDPopoverWidgetState();
}

class _TDPopoverWidgetState extends State<TDPopoverWidget> {
  late Color _color;

  late Color _backgroundColor;

  @override
  void initState() {
    super.initState();
    _initTheme();
    if (widget.contentWidget != null) {
      if (widget.width == null) {
        throw FlutterError(
            'width must not be null when contentWidget is not null');
      }
      if (widget.height == null) {
        throw FlutterError(
            'height must not be null when contentWidget is not null');
      }
    }
  }

  /// 绘制箭头
  Widget _drawArrow() {
    var border = Border(
        right: BorderSide(
          width: widget.arrowSize,
          color: Colors.transparent,
          style: BorderStyle.solid,
        ),
        bottom: BorderSide(
          width: widget.arrowSize,
          color: _backgroundColor,
          style: BorderStyle.solid,
        ),
        left: BorderSide(
          width: widget.arrowSize,
          color: Colors.transparent,
          style: BorderStyle.solid,
        ));
    if (widget.placement == TDPopoverPlacement.bottom ||
        widget.placement == TDPopoverPlacement.bottomLeft ||
        widget.placement == TDPopoverPlacement.bottomRight) {
      border = Border(
          top: BorderSide(
            width: widget.arrowSize,
            color: _backgroundColor,
            style: BorderStyle.solid,
          ),
          right: BorderSide(
            width: widget.arrowSize,
            color: Colors.transparent,
            style: BorderStyle.solid,
          ),
          left: BorderSide(
            width: widget.arrowSize,
            color: Colors.transparent,
            style: BorderStyle.solid,
          ));
    } else if (widget.placement == TDPopoverPlacement.left ||
        widget.placement == TDPopoverPlacement.leftTop ||
        widget.placement == TDPopoverPlacement.leftBottom) {
      border = Border(
          top: BorderSide(
            width: widget.arrowSize,
            color: Colors.transparent,
            style: BorderStyle.solid,
          ),
          bottom: BorderSide(
            width: widget.arrowSize,
            color: Colors.transparent,
            style: BorderStyle.solid,
          ),
          right: BorderSide(
            width: widget.arrowSize,
            color: _backgroundColor,
            style: BorderStyle.solid,
          ));
    } else if (widget.placement == TDPopoverPlacement.right ||
        widget.placement == TDPopoverPlacement.rightTop ||
        widget.placement == TDPopoverPlacement.rightBottom) {
      border = Border(
          top: BorderSide(
            width: widget.arrowSize,
            color: Colors.transparent,
            style: BorderStyle.solid,
          ),
          bottom: BorderSide(
            width: widget.arrowSize,
            color: Colors.transparent,
            style: BorderStyle.solid,
          ),
          left: BorderSide(
            width: widget.arrowSize,
            color: _backgroundColor,
            style: BorderStyle.solid,
          ));
    }
    return Container(
      width: 0,
      height: 0,
      decoration: BoxDecoration(border: border),
    );
  }

  /// 初始化主题
  void _initTheme() {
    switch (widget.theme) {
      case TDPopoverTheme.info:
        _color = TDTheme.of(widget.context).brandNormalColor;
        _backgroundColor = TDTheme.of(widget.context).brandLightColor;
        break;
      case TDPopoverTheme.success:
        _color = TDTheme.of(widget.context).successNormalColor;
        _backgroundColor = TDTheme.of(widget.context).successLightColor;
        break;
      case TDPopoverTheme.warning:
        _color = TDTheme.of(widget.context).warningNormalColor;
        _backgroundColor = TDTheme.of(widget.context).warningLightColor;
        break;
      case TDPopoverTheme.error:
        _color = TDTheme.of(widget.context).errorNormalColor;
        _backgroundColor = TDTheme.of(widget.context).errorLightColor;
        break;
      case TDPopoverTheme.light:
        _color = TDTheme.of(widget.context).grayColor14;
        _backgroundColor = TDTheme.of(widget.context).whiteColor1;
        break;
      default:
        _color = TDTheme.of(widget.context).whiteColor1;
        _backgroundColor = TDTheme.of(widget.context).grayColor14;
        break;
    }
  }

  /// 获取触发元素大小
  Rect? _getWidgetBounds(BuildContext context) {
    final box = context.findRenderObject() as RenderBox?;
    return box?.semanticBounds;
  }

  /// 获取触发元素坐标
  Offset? _getWidgetLocalToGlobal(BuildContext context) {
    final box = context.findRenderObject() as RenderBox?;
    return box?.localToGlobal(Offset.zero);
  }

  /// 获取Y坐标
  double _getOffsetTop(Offset? widgetLocalToGlobal) {
    var widgetBounds = _getWidgetBounds(widget.context);
    var dy = widgetLocalToGlobal?.dy ?? 0;
    var arrowSize = widget.showArrow ?? false ? widget.arrowSize : 0;
    var contentSize = _getContentSize();
    var popoverHeight = widget.height ??
        (widget.padding != null ? widget.padding!.vertical : 24) +
            (widget.height ?? contentSize.height);
    switch (widget.placement) {
      case TDPopoverPlacement.bottomLeft:
      case TDPopoverPlacement.bottom:
      case TDPopoverPlacement.bottomRight:
        return dy + (widgetBounds?.height ?? 0) + widget.offset;
      case TDPopoverPlacement.rightTop:
      case TDPopoverPlacement.leftTop:
        return dy;
      case TDPopoverPlacement.rightBottom:
      case TDPopoverPlacement.leftBottom:
        return dy - (popoverHeight - (widgetBounds?.height ?? 0));
      case TDPopoverPlacement.right:
      case TDPopoverPlacement.left:
        return dy - (popoverHeight - (widgetBounds?.height ?? 0)) / 2;
      default:
        return dy - popoverHeight - widget.offset - arrowSize;
    }
  }

  /// 获取X坐标
  double _getOffsetLeft(Offset? widgetLocalToGlobal) {
    var widgetBounds = _getWidgetBounds(widget.context);
    var widgetWidth = widgetBounds?.width ?? 0;
    var contentSize = _getContentSize();
    var popoverWidth = widget.width ??
        (widget.padding != null ? widget.padding!.horizontal : 24) +
            contentSize.width;
    var dx = widgetLocalToGlobal?.dx ?? 0;
    switch (widget.placement) {
      case TDPopoverPlacement.topLeft:
      case TDPopoverPlacement.bottomLeft:
        return dx;
      case TDPopoverPlacement.topRight:
      case TDPopoverPlacement.bottomRight:
        return dx + widgetWidth - popoverWidth;
      case TDPopoverPlacement.rightTop:
      case TDPopoverPlacement.right:
      case TDPopoverPlacement.rightBottom:
        return dx + widgetWidth + widget.offset;
      case TDPopoverPlacement.leftTop:
      case TDPopoverPlacement.left:
      case TDPopoverPlacement.leftBottom:
        return dx - popoverWidth - widget.arrowSize - widget.offset;
      default:
        return dx - (popoverWidth - widgetWidth) / 2;
    }
  }

  /// 获取箭头Widget
  /// todo 通过 CustomPainter 绘制箭头进行优化
  Widget _getArrowWidget() {
    var margin = EdgeInsets.only(top: widget.arrowSize);
    switch (widget.placement) {
      case TDPopoverPlacement.topLeft:
        margin =
            EdgeInsets.only(top: widget.arrowSize, left: widget.arrowSize + 12);
        break;
      case TDPopoverPlacement.topRight:
        margin = EdgeInsets.only(
            top: widget.arrowSize, right: widget.arrowSize + 12);
        break;
      case TDPopoverPlacement.bottomLeft:
        margin = EdgeInsets.only(
            bottom: widget.arrowSize, left: widget.arrowSize + 12);
        break;
      case TDPopoverPlacement.bottom:
        margin = EdgeInsets.only(bottom: widget.arrowSize);
        break;
      case TDPopoverPlacement.bottomRight:
        margin = EdgeInsets.only(
            bottom: widget.arrowSize, right: widget.arrowSize + 12);
        break;
      case TDPopoverPlacement.rightTop:
        margin =
            EdgeInsets.only(top: widget.arrowSize + 6, right: widget.arrowSize);
        break;
      case TDPopoverPlacement.right:
        margin = EdgeInsets.only(right: widget.arrowSize);
        break;
      case TDPopoverPlacement.rightBottom:
        margin = EdgeInsets.only(
            bottom: widget.arrowSize + 6, right: widget.arrowSize);
        break;
      case TDPopoverPlacement.leftTop:
        margin =
            EdgeInsets.only(top: widget.arrowSize + 6, left: widget.arrowSize);
        break;
      case TDPopoverPlacement.left:
        margin = EdgeInsets.only(left: widget.arrowSize);
        break;
      case TDPopoverPlacement.leftBottom:
        margin = EdgeInsets.only(
            bottom: widget.arrowSize + 6, left: widget.arrowSize);
        break;
      default:
        margin = EdgeInsets.only(top: widget.arrowSize);
    }
    return Container(
      margin: margin,
      child: _drawArrow(),
    );
  }

  /// 获取弹出内容大小
  Size _getContentSize() {
    if (widget.contentWidget != null) {
      return Size(widget.width!, widget.height!);
    }
    return _getTextSize();
  }

  /// 获取文本内容大小
  Size _getTextSize() {
    var textPainter = TextPainter(
      text: TextSpan(
        text: widget.content,
        style: TextStyle(
          color: _color,
          letterSpacing: 0,
          fontSize: 16,
          height: 1.5,
        ),
      ),
      locale: Localizations.localeOf(context),
      textDirection: TextDirection.ltr,
    )..layout(
        maxWidth: (widget.width ?? 300) -
            (widget.padding != null ? widget.padding!.horizontal : 24));
    return textPainter.size;
  }

  Widget _getContainerWidget() {
    return Container(
      width: widget.width,
      height: widget.height,
      padding: widget.padding ?? const EdgeInsets.all(12),
      decoration: BoxDecoration(
          borderRadius: widget.radius ??
              BorderRadius.circular(TDTheme.of(context).radiusDefault),
          color: _backgroundColor,
          boxShadow: const [
            BoxShadow(
                color: Color(0x0d000000),
                offset: Offset(0, 6),
                blurRadius: 30,
                spreadRadius: 5),
            BoxShadow(
                color: Color(0x0a000000),
                offset: Offset(0, 16),
                blurRadius: 24,
                spreadRadius: 2),
            BoxShadow(
                color: Color(0x14000000),
                offset: Offset(0, 8),
                blurRadius: 10,
                spreadRadius: -5),
          ]),
      child: widget.contentWidget != null
          ? widget.contentWidget!
          : TDText(widget.content,
              style: TextStyle(
                color: _color,
                letterSpacing: 0,
                fontSize: 16,
                height: 1.5,
              )),
    );
  }

  /// 获取子Widget
  Widget _getChild() {
    var children = [
      _getContainerWidget(),
      Visibility(
        visible: widget.showArrow ?? false,
        child: _getArrowWidget(),
      )
    ];
    var axis = CrossAxisAlignment.center;
    var direction = VerticalDirection.down;

    /// 设置子Widget垂直排列顺序
    switch (widget.placement) {
      case TDPopoverPlacement.bottom:
      case TDPopoverPlacement.bottomLeft:
      case TDPopoverPlacement.bottomRight:
        direction = VerticalDirection.up;
        break;
      case TDPopoverPlacement.right:
      case TDPopoverPlacement.rightTop:
      case TDPopoverPlacement.rightBottom:

        /// 反转内容和箭头
        children = [
          Visibility(
            visible: widget.showArrow ?? false,
            child: _getArrowWidget(),
          ),
          _getContainerWidget(),
        ];
        break;
      default:
        direction = VerticalDirection.down;
        break;
    }

    /// 改变Row和Column交叉轴对齐位置，从而实现箭头位置
    switch (widget.placement) {
      case TDPopoverPlacement.topLeft:
      case TDPopoverPlacement.bottomLeft:
        axis = CrossAxisAlignment.start;
        break;
      case TDPopoverPlacement.topRight:
      case TDPopoverPlacement.bottomRight:
        axis = CrossAxisAlignment.end;
        break;
      case TDPopoverPlacement.rightTop:
      case TDPopoverPlacement.leftTop:
        axis = CrossAxisAlignment.start;
        break;
      case TDPopoverPlacement.rightBottom:
      case TDPopoverPlacement.leftBottom:
        axis = CrossAxisAlignment.end;
        break;
      default:
        axis = CrossAxisAlignment.center;
    }

    /// 横向布局
    if (widget.placement == TDPopoverPlacement.right ||
        widget.placement == TDPopoverPlacement.rightTop ||
        widget.placement == TDPopoverPlacement.rightBottom ||
        widget.placement == TDPopoverPlacement.left ||
        widget.placement == TDPopoverPlacement.leftBottom ||
        widget.placement == TDPopoverPlacement.leftTop) {
      return Row(
        crossAxisAlignment: axis,
        children: children,
      );
    }

    /// 纵向布局
    return Column(
      crossAxisAlignment: axis,
      verticalDirection: direction,
      children: children,
    );
  }

  @override
  Widget build(BuildContext context) {
    _initTheme();
    var widgetLocalToGlobal = _getWidgetLocalToGlobal(widget.context);
    var top = _getOffsetTop(widgetLocalToGlobal);
    var left = _getOffsetLeft(widgetLocalToGlobal);
    return Stack(
      children: [
        Positioned(
          top: top,
          left: left,
          child: _getChild(),
        ),
      ],
    );
  }
}

Popup.popup_panel:
import 'dart:math';

import 'package:flutter/material.dart';
import '../../../tdesign_flutter.dart';
import '../../util/context_extension.dart';

typedef PopupClick = Function();

/// 弹窗基类
abstract class TDPopupBasePanel extends StatefulWidget {
  const TDPopupBasePanel({
    Key? key,
    required this.child,
    this.title,
    this.titleColor,
    this.backgroundColor,
    this.radius,
    this.draggable = false,
    this.maxHeightRatio = 0.9,
    this.minHeightRatio = 0.3,
  }) : super(key: key);

  /// 子控件
  final Widget child;

  /// 标题
  final String? title;

  /// 标题颜色
  final Color? titleColor;

  /// 背景颜色
  final Color? backgroundColor;

  /// 圆角
  final double? radius;

  /// 边缘是否可拖动
  final bool draggable;

  /// 最大高度比例
  final double maxHeightRatio;

  /// 最小高度比例
  final double minHeightRatio;

  @override
  State<TDPopupBasePanel> createState();
}

abstract class _TDPopupBaseState<T extends TDPopupBasePanel> extends State<T>
    with SingleTickerProviderStateMixin {
  final GlobalKey _childKey = GlobalKey();
  static const _dragHandleHeight = 24.0;
  static const _headerHeight = 58.0;

  late AnimationController _controller;
  double _maxHeight = 0;
  double _minHeight = 0;
  double _currentHeight = 0;
  bool _isFullscreen = false;
  bool _isAnimating = false;
  bool _isDragging = false;

  @override
  void initState() {
    super.initState();
    _controller = AnimationController(
      duration: const Duration(milliseconds: 400),
      vsync: this,
    );
    if (widget.draggable) {
      _controller.addListener(_updateHeight);
    }
    WidgetsBinding.instance.addPostFrameCallback((_) => _measureChildHeight());
  }

  /// 测量子组件高度并更新弹窗布局参数
  /// 1.获取子组件渲染尺寸
  /// 2.计算实际需要的基础高度
  /// 3.动态计算计算最大最小高度比例
  /// 4.更新动画控制器状态
  void _measureChildHeight() {
    // 获取子组件渲染对象
    final context = _childKey.currentContext;
    if (context == null) {
      return;
    }
    final renderBox = context.findRenderObject() as RenderBox?;
    if (renderBox == null || !renderBox.hasSize) {
      return;
    }

    final screenHeight = MediaQuery.of(context).size.height;
    final childHeight = renderBox.size.height;
    final headerHeight = widget.draggable ? _headerHeight : _headerHeight;
    final baseHeight = _dragHandleHeight + headerHeight + childHeight;

    // 动态计算最大最小高度
    final maxHeightByRatio = screenHeight * widget.maxHeightRatio;
    final minHeightByRatio = screenHeight * widget.minHeightRatio;

    // 内容高度和比例约束
    _maxHeight = min(baseHeight, maxHeightByRatio);
    _minHeight = max(baseHeight * 0.5, minHeightByRatio);
    if (_minHeight > _maxHeight) {
      _minHeight = _maxHeight;
    }

    // 初始化当前高度
    _currentHeight = baseHeight.clamp(_minHeight, _maxHeight);
    // 同步动画控制器
    _controller.value = (_currentHeight - _minHeight) /
        (_maxHeight - _minHeight).clamp(0.1, 1.0);
  }

  void _updateHeight() => setState(() {
        _currentHeight =
            _minHeight + (_maxHeight - _minHeight) * _controller.value;
      });

  void _toggleFullscreen(bool fullscreen) {
    if (_isAnimating || _isFullscreen == fullscreen) {
      return;
    }

    setState(() {
      _isFullscreen = fullscreen;
      _maxHeight = fullscreen
          ? MediaQuery.of(context).size.height
          : MediaQuery.of(context).size.height * widget.maxHeightRatio;
    });

    _controller.animateTo(
      fullscreen ? 1.0 : 0.0,
      duration: const Duration(milliseconds: 350),
      curve: Curves.fastOutSlowIn,
    );
  }

  void _animateTo(double height) {
    if (_isAnimating) {
      return;
    }
    _isAnimating = true;

    final value = (height - _minHeight) / (_maxHeight - _minHeight);
    _controller
        .animateTo(
          value.clamp(0.0, 1.0),
          duration: const Duration(milliseconds: 300),
          curve: Curves.easeOutBack,
        )
        .whenComplete(() => _isAnimating = false);
  }

  Widget _buildDragHandle() {
    if (!widget.draggable) {
      return const SizedBox.shrink();
    }

    return GestureDetector(
      behavior: HitTestBehavior.opaque,
      onVerticalDragUpdate: _handleDragUpdate,
      onVerticalDragEnd: _handleDragEnd,
      onDoubleTap: () => _toggleFullscreen(!_isFullscreen),
      child: Container(
        height: _dragHandleHeight,
        alignment: Alignment.center,
        child: Container(
          width: 48,
          height: 4,
          decoration: BoxDecoration(
            color: TDTheme.of(context).componentStrokeColor,
            borderRadius: BorderRadius.circular(2),
          ),
        ),
      ),
    );
  }

  @override
  Widget build(BuildContext context) {
    WidgetsBinding.instance.addPostFrameCallback((_) {
      // 每次 build 都测量子内容高度，确保内容变化时高度自适应 （拖动时不测量）
      if (!_isDragging) {
        _measureChildHeight();
      }
    });

    return AnimatedBuilder(
      animation: _controller,
      builder: (context, _) => RepaintBoundary(
        child: Container(
          height: _currentHeight,
          decoration: BoxDecoration(
            color:
                widget.backgroundColor ?? TDTheme.of(context).bgColorContainer,
            borderRadius: _isFullscreen
                ? null
                : BorderRadius.vertical(
                    top: Radius.circular(
                        widget.radius ?? TDTheme.of(context).radiusExtraLarge)),
          ),
          child: Column(children: [
            _buildDragHandle(),
            buildHeader(context),
            SizedBox(
              child: _buildContent(),
            ),
          ]),
        ),
      ),
    );
  }

  Widget _buildContent() => NotificationListener<ScrollNotification>(
        onNotification: (notification) {
          if (notification is ScrollUpdateNotification) {
            final metrics = notification.metrics;
            if ((metrics.pixels <= 0 ||
                    metrics.pixels >= metrics.maxScrollExtent) &&
                notification.dragDetails != null) {
              _handleDragUpdate(notification.dragDetails!);
            }
          }
          return false;
        },
        child: Container(
          key: _childKey,
          child: widget.child,
        ),
      );

  @protected
  void _handleDragUpdate(DragUpdateDetails details);

  @protected
  void _handleDragEnd(DragEndDetails details);

  @protected
  Widget buildHeader(BuildContext context);

  void _baseHandleDragUpdate(DragUpdateDetails details) {
    _isDragging = true;
    if (_isAnimating || !widget.draggable) {
      return;
    }

    final newHeight = _currentHeight - details.primaryDelta! * 1.2;
    _currentHeight = newHeight.clamp(_minHeight, _maxHeight);
    _controller.value =
        (_currentHeight - _minHeight) / (_maxHeight - _minHeight);
  }

  void _baseHandleDragEnd(DragEndDetails details) {
    final velocity = details.velocity.pixelsPerSecond.dy;
    final predictedHeight = _currentHeight + velocity * 0.15;

    if (predictedHeight > _maxHeight * 0.7 || velocity < -800) {
      _animateTo(_maxHeight);
    } else if (predictedHeight < _minHeight * 1.3 || velocity > 800) {
      _animateTo(_minHeight);
    }
    _isDragging = false;
  }
}

