Popup.popup_route:
import 'dart:math';

import 'package:flutter/material.dart';
import 'package:flutter/rendering.dart';

const Duration _bottomSheetEnterDuration = Duration(milliseconds: 250);
const Duration _bottomSheetExitDuration = Duration(milliseconds: 200);

/// 从屏幕弹出的方向
enum SlideTransitionFrom { top, right, left, bottom, center }

/// 从屏幕的某个方向滑动弹出的Dialog框的路由，比如从顶部、底部、左、右滑出页面
class TDSlidePopupRoute<T> extends PopupRoute<T> {
  TDSlidePopupRoute({
    required this.builder,
    this.barrierLabel,
    this.modalBarrierColor = Colors.black54,
    this.isDismissible = true,
    this.modalBarrierFull = false,
    this.slideTransitionFrom = SlideTransitionFrom.bottom,
    this.modalWidth,
    this.modalHeight,
    this.modalTop = 0,
    this.modalLeft = 0,
    this.open,
    this.opened,
    this.close,
    this.barrierClick,
    this.focusMove = false,
  });

  /// 控件构建器
  final WidgetBuilder builder;

  /// 蒙层颜色
  final Color? modalBarrierColor;

  /// 点击蒙层能否关闭
  final bool isDismissible;

  /// 是否全屏显示蒙层
  final bool modalBarrierFull;

  /// 设置从屏幕的哪个方向滑出
  final SlideTransitionFrom slideTransitionFrom;

  /// 弹出框宽度
  final double? modalWidth;

  /// 弹出框高度
  final double? modalHeight;

  /// 弹出框顶部距离
  final double? modalTop;

  /// 弹出框左侧距离
  final double? modalLeft;

  /// 打开前事件
  final VoidCallback? open;

  /// 打开后事件
  final VoidCallback? opened;

  /// 关闭前事件
  final VoidCallback? close;

  /// 蒙层点击事件，仅在[modalBarrierFull]为false时触发
  final VoidCallback? barrierClick;

  /// 是否有输入框获取焦点时整体平移避免输入框被遮挡
  final bool focusMove;

  Color get _barrierColor => modalBarrierColor ?? Colors.black54;

  @override
  Duration get transitionDuration => _bottomSheetEnterDuration;

  @override
  Duration get reverseTransitionDuration => _bottomSheetExitDuration;

  @override
  bool get barrierDismissible => isDismissible;

  @override
  final String? barrierLabel;

  @override
  Color get barrierColor => modalBarrierFull ? _barrierColor : Colors.transparent;

  /// 键盘焦点对象的Y坐标
  var _focusY = 0.0;

  /// 键盘焦点对象的高度
  var _focusHeight = 0.0;

  /// 键盘出现后bottom的偏移量
  var _lastBottom = 0.0;

  // 实现转场动画
  @override
  Widget buildTransitions(
    BuildContext context,
    Animation<double> animation,
    Animation<double> secondaryAnimation,
    Widget child,
  ) {
    var animValue = decelerateEasing.transform(animation.value);
    return Stack(
      children: [
        if (!modalBarrierFull)
          _getPositionWidget(
            context,
            IgnorePointer(
              ignoring: true,
              child: Container(
                color: _barrierColor.withAlpha((animValue * _barrierColor.alpha).toInt()),
                child: GestureDetector(
                  onTap: () {
                    barrierClick?.call();
                    if (isDismissible) {
                      Navigator.pop(context);
                    }
                  },
                  onDoubleTap: () {
                    barrierClick?.call();
                    if (isDismissible) {
                      Navigator.pop(context);
                    }
                  },
                ),
              ),
            ),
          ),
        _getPositionWidget(
          context,
          Align(
            alignment: slideTransitionFromToAlignment(slideTransitionFrom),
            child: slideTransitionFrom != SlideTransitionFrom.center
                ? FractionalTranslation(
                    translation: _getOffset(animValue, slideTransitionFrom),
                    child: ClipRect(
                      clipper: RectClipper(animValue, slideTransitionFrom),
                      child: child,
                    ),
                  )
                : Transform(
                    transform: Matrix4.diagonal3Values(animValue, animValue, 1),
                    alignment: Alignment.center,
                    child: child,
                  ),
          ),
        ),
      ],
    );
  }

  @override
  Widget buildPage(BuildContext context, Animation<double> animation, Animation<double> secondaryAnimation) {
    return Material(
      child: builder.call(context),
      color: Colors.transparent,
    );
  }

  @override
  TickerFuture didPush() {
    startFocusListener(navigator!.context);
    open?.call();
    animation?.addStatusListener((status) {
      if (status == AnimationStatus.completed) {
        opened?.call();
      }
    });
    return super.didPush();
  }

  @override
  void dispose() {
    stopFocusListener(navigator!.context);
    super.dispose();
  }

  /// 监听焦点变化
  void startFocusListener(BuildContext context) {
    FocusManager.instance.addListener(_handleFocusChange);
  }

  /// 停止监听焦点变化
  void stopFocusListener(BuildContext context) {
    FocusManager.instance.removeListener(_handleFocusChange);
  }

  void _handleFocusChange() {
    // 获取当前的焦点节点
    var focusNode = FocusManager.instance.primaryFocus;
    if (focusNode != null && focusNode.context != null) {
      var renderObject = focusNode.context!.findRenderObject();
      if (renderObject is RenderPointerListener) {
        _focusY = renderObject.localToGlobal(Offset.zero).dy;
        _focusHeight = renderObject.size.height;
      }
    }
    (focusNode?.context as Element?)?.markNeedsBuild();
  }

  @override
  bool didPop(T? result) {
    close?.call();
    return super.didPop(result);
  }

  Widget _getPositionWidget(BuildContext context, Widget child) {
    var bottom = 0.0;
    var mediaQuery = MediaQuery.of(context);
    if (slideTransitionFrom == SlideTransitionFrom.bottom) {
      bottom = mediaQuery.viewInsets.bottom;
    } else {
      if ((_focusY + mediaQuery.viewInsets.bottom + _focusHeight) > mediaQuery.size.height) {
        bottom = -(mediaQuery.size.height - (_focusY + mediaQuery.viewInsets.bottom + _focusHeight + 10));
        _lastBottom = bottom;
      } else {
        if (_lastBottom > 0.0) {
          bottom = max((_lastBottom -= 5), 0).toDouble();
        }
      }
    }

    var screenSize = mediaQuery.size;
    var _modalTop = (modalTop ?? 0).clamp(0, screenSize.height).toDouble() - (focusMove ? bottom : 0);
    var _modalLeft = (modalLeft ?? 0).clamp(0, screenSize.width).toDouble();
    var _modalHeight = (modalHeight ?? screenSize.height).clamp(0, screenSize.height - _modalTop).toDouble();
    var _modalWidth = (modalWidth ?? screenSize.width).clamp(0, screenSize.width - _modalLeft).toDouble();
    return Positioned(
      top: _modalTop,
      bottom: screenSize.height - _modalTop - _modalHeight,
      left: _modalLeft,
      right: screenSize.width - _modalLeft - _modalWidth,
      child: child,
    );
  }

  Offset _getOffset(double animValue, SlideTransitionFrom slideTransitionFrom) {
    switch (slideTransitionFrom) {
      case SlideTransitionFrom.top:
        return Offset(0, animValue - 1);
      case SlideTransitionFrom.right:
        return Offset(1 - animValue, 0);
      case SlideTransitionFrom.left:
        return Offset(animValue - 1, 0);
      case SlideTransitionFrom.bottom:
        return Offset(0, 1 - animValue);
      default:
        return const Offset(0, 0);
    }
  }
}

Alignment slideTransitionFromToAlignment(SlideTransitionFrom from) {
  switch (from) {
    case SlideTransitionFrom.top:
      return Alignment.topCenter;
    case SlideTransitionFrom.right:
      return Alignment.centerRight;
    case SlideTransitionFrom.left:
      return Alignment.centerLeft;
    case SlideTransitionFrom.bottom:
      return Alignment.bottomCenter;
    case SlideTransitionFrom.center:
      return Alignment.center;
  }
}

class RectClipper extends CustomClipper<Rect> {
  final double animValue;
  final SlideTransitionFrom slideTransitionFrom;

  RectClipper(this.animValue, this.slideTransitionFrom);

  @override
  Rect getClip(Size size) {
    switch (slideTransitionFrom) {
      case SlideTransitionFrom.top:
        return Rect.fromLTWH(0, size.height * (1 - animValue), size.width, size.height);
      case SlideTransitionFrom.right:
        return Rect.fromLTWH(0, 0, size.width * animValue, size.height);
      case SlideTransitionFrom.left:
        return Rect.fromLTWH(size.width * (1 - animValue), 0, size.width, size.height);
      case SlideTransitionFrom.bottom:
        return Rect.fromLTWH(0, 0, size.width, size.height * animValue);
      default:
        return Rect.fromLTWH(0, 0, size.width, size.height);
    }
  }

  @override
  bool shouldReclip(covariant CustomClipper<Rect> oldClipper) {
    return oldClipper != this;
  }
}

Progress.progress:
import 'package:flutter/material.dart';

import '../../../tdesign_flutter.dart';
import './td_progress_circular.dart';

enum TDProgressType { linear, circular, micro, button }

enum TDProgressLabelPosition { inside, left, right }

enum TDProgressStatus { primary, warning, danger, success }

abstract class TDLabelWidget extends Widget {
  const TDLabelWidget({super.key});
}

class TDTextLabel extends Text implements TDLabelWidget {
  const TDTextLabel(
    String data, {
    Key? key,
    TextStyle? style,
  }) : super(data, key: key, style: style);
}

class TDIconLabel extends Icon implements TDLabelWidget {
  const TDIconLabel(
    IconData icon, {
    Key? key,
    double? size,
    Color? color,
  }) : super(icon, key: key, size: size, color: color);
}

class TDProgress extends StatelessWidget {
  TDProgress({
    Key? key,
    required this.type,
    double? value,
    this.label,
    this.progressStatus = TDProgressStatus.primary,
    this.progressLabelPosition = TDProgressLabelPosition.inside,
    double? strokeWidth,
    this.color,
    this.backgroundColor,
    this.linearBorderRadius,
    double? circleRadius,
    this.showLabel = true,
    this.customProgressLabel,
    this.labelWidgetWidth,
    this.labelWidgetAlignment,
    this.onTap,
    this.onLongPress,
    int animationDuration = 300,
  })  : value = _validateProgress(value),
        strokeWidth = _validatePositiveDouble(strokeWidth),
        circleRadius = _validatePositiveDouble(circleRadius),
        animationDuration = _validatePositiveInt(animationDuration),
        super(key: key);

  /// 进度条类型
  final TDProgressType type;

  /// 进度值（0.0 到 1.0 之间的正数）
  final double? value;

  /// 进度条标签
  final TDLabelWidget? label;

  /// 进度条状态
  final TDProgressStatus progressStatus;

  /// 标签显示位置
  final TDProgressLabelPosition progressLabelPosition;

  /// 进度条粗细（正数）
  final double? strokeWidth;

  /// 进度条颜色
  final Color? color;

  /// 进度条背景颜色
  final Color? backgroundColor;

  /// 条形进度条末端形状
  final BorderRadiusGeometry? linearBorderRadius;

  /// 环形进度条半径（正数）
  final double? circleRadius;

  /// 是否显示标签
  final bool showLabel;

  /// 自定义标签
  final Widget? customProgressLabel;

  /// 自定义标签宽度
  final double? labelWidgetWidth;

  /// 自定义标签对齐方式
  final Alignment? labelWidgetAlignment;

  /// 点击事件
  final VoidCallback? onTap;

  /// 长按事件
  final VoidCallback? onLongPress;

  /// 动画持续时间（正整数，单位为毫秒）
  final int? animationDuration;

  static double? _validateProgress(double? value) =>
      value == null ? 0 : value.clamp(0.0, 1.0);

  static double? _validatePositiveDouble(double? value) => value == null
      ? null
      : value <= 0
          ? 0
          : value;

  static int _validatePositiveInt(int? value) =>
      value == null || value <= 0 ? 0 : value;

  @override
  Widget build(BuildContext context) {
    final defaultValues = _getDefaultValues(context, type);
    return ProgressIndicator(
      value: value,
      label: label,
      progressStatus: progressStatus,
      progressLabelPosition: progressLabelPosition,
      strokeWidth: strokeWidth ?? defaultValues.strokeWidth,
      color: color,
      backgroundColor: backgroundColor ?? defaultValues.backgroundColor,
      linearBorderRadius:
          linearBorderRadius ?? defaultValues.linearBorderRadius,
      circleRadius: circleRadius ?? defaultValues.circleRadius,
      showLabel: showLabel,
      customProgressLabel: customProgressLabel,
      labelWidgetWidth: labelWidgetWidth,
      labelWidgetAlignment: labelWidgetAlignment,
      onTap: onTap,
      onLongPress: onLongPress,
      type: type,
      context: context,
    );
  }

  _DefaultValues _getDefaultValues(BuildContext context, TDProgressType type) {
    switch (type) {
      case TDProgressType.linear:
        return _DefaultValues(
          strokeWidth: 20.0,
          backgroundColor: TDTheme.of(context).bgColorComponent,
          linearBorderRadius: BorderRadius.circular(20),
          circleRadius: 0,
        );
      case TDProgressType.circular:
        return _DefaultValues(
          strokeWidth: 5.0,
          backgroundColor: TDTheme.of(context).bgColorComponent,
          linearBorderRadius: BorderRadius.circular(20),
          circleRadius: 100.0,
        );
      case TDProgressType.micro:
        return _DefaultValues(
          strokeWidth: 2.0,
          backgroundColor: TDTheme.of(context).bgColorComponent,
          linearBorderRadius: BorderRadius.circular(20),
          circleRadius: 25.0,
        );
      case TDProgressType.button:
        return _DefaultValues(
          strokeWidth: 50.0,
          backgroundColor: TDTheme.of(context).brandNormalColor,
          linearBorderRadius: BorderRadius.circular(8),
          circleRadius: 0,
        );
    }
  }
}

class _DefaultValues {
  final double strokeWidth;
  final Color backgroundColor;
  final BorderRadiusGeometry linearBorderRadius;
  final double circleRadius;

  _DefaultValues({
    required this.strokeWidth,
    required this.backgroundColor,
    required this.linearBorderRadius,
    required this.circleRadius,
  });
}

class ProgressIndicator extends StatefulWidget {
  final double? value;
  final TDLabelWidget? label;
  final TDProgressLabelPosition progressLabelPosition;
  final double strokeWidth;
  final double circleRadius;
  final BorderRadiusGeometry linearBorderRadius;
  final Color? color;
  final Color backgroundColor;
  final TDProgressType type;
  final TDProgressStatus progressStatus;
  final bool showLabel;
  final Widget? customProgressLabel;
  final double? labelWidgetWidth;
  final Alignment? labelWidgetAlignment;
  final VoidCallback? onTap;
  final VoidCallback? onLongPress;
  final int animationDuration;
  final BuildContext? context;

  const ProgressIndicator({
    Key? key,
    this.value,
    this.label,
    this.progressLabelPosition = TDProgressLabelPosition.inside,
    required this.strokeWidth,
    required this.linearBorderRadius,
    required this.circleRadius,
    this.color,
    required this.backgroundColor,
    required this.type,
    this.progressStatus = TDProgressStatus.primary,
    this.showLabel = true,
    this.customProgressLabel,
    this.labelWidgetWidth,
    this.labelWidgetAlignment,
    this.onTap,
    this.onLongPress,
    this.animationDuration = 300,
    this.context,
  }) : super(key: key);

  @override
  _ProgressIndicatorState createState() => _ProgressIndicatorState();
}

class _ProgressIndicatorState extends State<ProgressIndicator>
    with SingleTickerProviderStateMixin {
  late AnimationController _animationController;
  late Animation<double> _animation;
  late Color _effectiveColor;
  late Widget _effectiveLabel;

  @override
  void initState() {
    super.initState();
    _animationController = AnimationController(
        vsync: this,
        duration: Duration(milliseconds: widget.animationDuration));
    _updateAnimation();
    _updateEffectiveColor();
    _updateEffectiveLabel();
  }

  @override
  void didUpdateWidget(ProgressIndicator oldWidget) {
    super.didUpdateWidget(oldWidget);
    if (oldWidget.value != widget.value) {
      _updateAnimation(oldWidgetValue: oldWidget.value);
      _updateEffectiveLabel();
    }
    if (oldWidget.color != widget.color ||
        oldWidget.progressStatus != widget.progressStatus) {
      _updateEffectiveColor();
    }
    if (oldWidget.label != widget.label ||
        oldWidget.progressStatus != widget.progressStatus) {
      _updateEffectiveLabel();
    }
  }

  void _updateEffectiveColor() {
    _effectiveColor =
        widget.color ?? _getColorFromStatus(widget.progressStatus);
  }

  void _updateEffectiveLabel() {
    _effectiveLabel =
        widget.label ?? _getDefaultLabelFromStatus(widget.progressStatus);
  }

  void _updateAnimation({double? oldWidgetValue}) {
    _animation = Tween<double>(
            begin: oldWidgetValue ?? _animationController.value,
            end: widget.value)
        .animate(_animationController);
    _animationController.forward(from: 0);
  }

  Widget _getDefaultLabelFromStatus(TDProgressStatus status) {
    final showAutoText = widget.value != null;
    final showInsideLabel =
        widget.progressLabelPosition == TDProgressLabelPosition.inside &&
            widget.type != TDProgressType.circular;
    final showIconBorder = widget.type == TDProgressType.linear;

    Widget getAutoText() => showAutoText && widget.type != TDProgressType.micro
        ? Text('${(widget.value! * 100).round()}%')
        : const Text('');

    final statusWidgets = {
      TDProgressStatus.primary: getAutoText(),
      TDProgressStatus.warning: showInsideLabel
          ? getAutoText()
          : showIconBorder
              ? const Icon(TDIcons.error_circle_filled)
              : const Icon(TDIcons.error),
      TDProgressStatus.danger: showInsideLabel
          ? getAutoText()
          : showIconBorder
              ? const Icon(TDIcons.close_circle_filled)
              : const Icon(TDIcons.close),
      TDProgressStatus.success: showInsideLabel
          ? getAutoText()
          : showIconBorder
              ? const Icon(TDIcons.check_circle_filled)
              : const Icon(TDIcons.check),
    };

    return statusWidgets[status] ?? getAutoText();
  }

  Color _getColorFromStatus(TDProgressStatus status) {
    switch (status) {
      case TDProgressStatus.primary:
        return TDTheme.of(widget.context).brandNormalColor;
      case TDProgressStatus.warning:
        return TDTheme.of(widget.context).warningNormalColor;
      case TDProgressStatus.danger:
        return TDTheme.of(widget.context).errorNormalColor;
      case TDProgressStatus.success:
        return TDTheme.of(widget.context).successNormalColor;
    }
  }

  @override
  void dispose() {
    _animationController.dispose();
    super.dispose();
  }

  @override
  Widget build(BuildContext context) {
    return Column(
      mainAxisSize: MainAxisSize.min,
      children: [
        if (widget.type == TDProgressType.linear)
          _buildLinearProgress()
        else if (widget.type == TDProgressType.circular)
          _buildCircularProgress()
        else if (widget.type == TDProgressType.micro)
          _buildMicroProgress()
        else if (widget.type == TDProgressType.button)
          _buildButtonProgress()
      ],
    );
  }

  Widget _buildLinearProgress() {
    return LayoutBuilder(
      builder: (context, constraints) {
        final maxWidth = constraints.maxWidth;

        if (widget.value != null &&
            widget.progressLabelPosition == TDProgressLabelPosition.inside) {
          return _buildInsideLabel(maxWidth);
        }
        return _buildOutsideLabel(maxWidth);
      },
    );
  }

  Widget _buildInsideLabel(double maxWidth) {
    return AnimatedBuilder(
      animation: _animation,
      builder: (context, child) {
        final progressWidth = _animation.value * maxWidth;
        return ClipRRect(
            borderRadius:
                BorderRadius.circular(TDTheme.of(context).radiusRound),
            child: Stack(
              children: [
                _buildBackgroundContainer(),
                if (widget.value! > 0.1)
                  _buildProgressContainerWithLabel(progressWidth)
                else
                  _buildProgressContainerWithLabelOutside(progressWidth),
              ],
            ));
      },
    );
  }

  Widget _buildOutsideLabel(double maxWidth) {
    return AnimatedBuilder(
      animation: _animation,
      builder: (context, child) {
        return Row(
          crossAxisAlignment: CrossAxisAlignment.center,
          mainAxisAlignment: MainAxisAlignment.spaceBetween,
          // spacing: TDTheme.of(context).spacer8,
          textDirection:
              widget.progressLabelPosition == TDProgressLabelPosition.right
                  ? TextDirection.rtl
                  : TextDirection.ltr,
          children: [
            Container(
              alignment: widget.labelWidgetAlignment ??
                  (widget.progressLabelPosition == TDProgressLabelPosition.left
                      ? Alignment.centerRight
                      : Alignment.centerLeft),
              constraints:
                  BoxConstraints(minWidth: widget.labelWidgetWidth ?? 0),
              child: widget.customProgressLabel ??
                  _buildLabelWidget(TDTheme.of(context).textColorPrimary),
            ),
            SizedBox(width: TDTheme.of(context).spacer8),
            Expanded(
              child: ClipRRect(
                  borderRadius:
                      BorderRadius.circular(TDTheme.of(context).radiusRound),
                  child: LayoutBuilder(
                    builder: (context, constraints) {
                      return Stack(
                        children: [
                          _buildBackgroundContainer(),
                          Container(
                            height: widget.strokeWidth,
                            width: constraints.maxWidth * _animation.value,
                            decoration: BoxDecoration(
                              color: _effectiveColor,
                              borderRadius: widget.linearBorderRadius,
                            ),
                          ),
                        ],
                      );
                    },
                  )),
            ),
          ],
        );
      },
    );
  }

  Widget _buildBackgroundContainer() {
    return Container(
      height: widget.strokeWidth,
      decoration: BoxDecoration(
        borderRadius: widget.linearBorderRadius,
        color: widget.backgroundColor,
      ),
    );
  }

  Widget _buildProgressContainerWithLabel(double progressWidth) {
    return Container(
      height: widget.strokeWidth,
      width: progressWidth,
      decoration: BoxDecoration(
        color: _effectiveColor,
        borderRadius: widget.linearBorderRadius,
      ),
      child: widget.showLabel
          ? Align(
              alignment: Alignment.centerRight,
              child: Padding(
                padding: const EdgeInsets.symmetric(horizontal: 6.0),
                child: _buildLabelWidget(TDTheme.of(context).textColorAnti),
              ),
            )
          : null,
    );
  }

  Widget _buildProgressContainerWithLabelOutside(double progressWidth) {
    return Row(
      mainAxisAlignment: MainAxisAlignment.start,
      children: [
        Container(
          height: widget.strokeWidth,
          width: progressWidth,
          decoration: BoxDecoration(
            color: _effectiveColor,
            borderRadius: BorderRadius.only(
              topLeft:
                  widget.linearBorderRadius.resolve(TextDirection.ltr).topLeft,
              bottomLeft: widget.linearBorderRadius
                  .resolve(TextDirection.ltr)
                  .bottomLeft,
              topRight: Radius.circular(widget.linearBorderRadius
                  .resolve(TextDirection.ltr)
                  .topRight
                  .x),
              bottomRight: Radius.circular(widget.linearBorderRadius
                  .resolve(TextDirection.ltr)
                  .bottomRight
                  .x),
            ),
          ),
        ),
        if (widget.showLabel)
          Padding(
            padding: const EdgeInsets.symmetric(horizontal: 8.0),
            child: _buildLabelWidget(TDTheme.of(context).textColorPrimary),
          ),
      ],
    );
  }

  Widget _buildLabelWidget(Color labelColor) {
    late double iconSize;
    late double fontSize;
    late FontWeight fontWeight;

    switch (widget.type) {
      case TDProgressType.linear:
        if (widget.progressLabelPosition != TDProgressLabelPosition.inside) {
          fontSize = widget.strokeWidth > 14 ? widget.strokeWidth : 14;
          iconSize = widget.strokeWidth > 20 ? widget.strokeWidth : 20;
        } else {
          fontSize = widget.strokeWidth * 0.6;
          iconSize = widget.strokeWidth;
        }
        fontWeight = FontWeight.normal;
        break;
      case TDProgressType.circular:
        iconSize = widget.circleRadius * 0.4;
        fontSize = widget.circleRadius * 0.15;
        fontWeight = FontWeight.bold;
        break;
      case TDProgressType.micro:
        iconSize = widget.circleRadius * 0.5;
        fontSize = widget.circleRadius * 0.2;
        fontWeight = FontWeight.normal;
        break;
      case TDProgressType.button:
        iconSize = widget.strokeWidth * 0.3;
        fontSize = widget.strokeWidth * 0.3;
        fontWeight = FontWeight.normal;
        break;
    }

    return IconTheme(
      data: IconThemeData(color: _effectiveColor, size: iconSize),
      child: DefaultTextStyle(
        style: TextStyle(
          color: labelColor,
          fontSize: fontSize,
          fontWeight: fontWeight,
        ),
        child: _effectiveLabel,
      ),
    );
  }

  Widget _buildCircularProgress() {
    return AnimatedBuilder(
      animation: _animation,
      builder: (context, child) {
        return Stack(
          alignment: Alignment.center,
          children: [
            SizedBox(
              height: widget.circleRadius,
              width: widget.circleRadius,
              child: Padding(
                padding: EdgeInsets.all(widget.strokeWidth / 2),
                child: TDProgressCircular(
                  strokeWidth: widget.strokeWidth,
                  circleRadius: widget.circleRadius,
                  value: _animation.value,
                  backgroundColor: widget.backgroundColor,
                  valueColor: AlwaysStoppedAnimation<Color>(_effectiveColor),
                ),
              ),
            ),
            if (widget.showLabel)
              _buildLabelWidget(TDTheme.of(widget.context).textColorPrimary),
          ],
        );
      },
    );
  }

  Widget _buildMicroProgress() {
    return AnimatedBuilder(
        animation: _animation,
        builder: (context, child) {
          return GestureDetector(
              onTap: widget.onTap,
              onLongPress: widget.onLongPress,
              child: Stack(
                alignment: Alignment.center,
                children: [
                  _buildMicroOutline(),
                  if (widget.showLabel)
                    _buildLabelWidget(
                        TDTheme.of(widget.context).textColorPrimary),
                ],
              ));
        });
  }

  Widget _buildMicroOutline() {
    return SizedBox(
      height: widget.circleRadius,
      width: widget.circleRadius,
      child: Padding(
        padding: EdgeInsets.all(widget.strokeWidth / 2),
        child: TDProgressCircular(
          strokeWidth: widget.strokeWidth,
          circleRadius: widget.circleRadius,
          value: _animation.value,
          backgroundColor: widget.backgroundColor,
          valueColor: AlwaysStoppedAnimation<Color>(_effectiveColor),
        ),
      ),
    );
  }

  Widget _buildButtonProgress() {
    return LayoutBuilder(
      builder: (context, constraints) {
        final maxWidth = constraints.maxWidth;
        return AnimatedBuilder(
            animation: _animation,
            builder: (context, child) {
              final progressWidth = maxWidth * _animation.value;
              return ClipRRect(
                borderRadius: widget.linearBorderRadius,
                child: GestureDetector(
                    onTap: widget.onTap,
                    onLongPress: widget.onLongPress,
                    child: Stack(
                      children: [
                        _buildBackgroundContainer(),
                        _buildButtonActiveContainer(progressWidth),
                        if (widget.showLabel) _buildButtonLabel(maxWidth),
                      ],
                    )),
              );
            });
      },
    );
  }

  Widget _buildButtonActiveContainer(double progressWidth) {
    return Container(
      height: widget.strokeWidth,
      width: progressWidth,
      decoration: BoxDecoration(
        gradient: LinearGradient(
          colors: [
            _effectiveColor,
            TDTheme.of(widget.context).brandDisabledColor.withOpacity(.5)
          ],
        ),
      ),
    );
  }

  Widget _buildButtonLabel(double maxWidth) {
    return Container(
      height: widget.strokeWidth,
      alignment: Alignment.center,
      child: _buildLabelWidget(TDTheme.of(widget.context).fontWhColor1),
    );
  }
}

Progress.progress_circular:
import 'dart:math' as math;

import 'package:flutter/material.dart';

/// 私有组件类，用于展示环形进度条
class TDProgressCircular extends StatelessWidget {
  final double _value;
  final double strokeWidth;
  final Color backgroundColor;
  final Animation<Color> valueColor;
  final double circleRadius;

  const TDProgressCircular({
    Key? key,
    required double value,
    required this.strokeWidth,
    required this.backgroundColor,
    required this.valueColor,
    required this.circleRadius,
  })  : _value = value,
        super(key: key);

  double get value => _value.clamp(0.0, 1.0);

  @override
  Widget build(BuildContext context) {
    return SizedBox(
      width: circleRadius * 2,
      height: circleRadius * 2,
      child: CustomPaint(
        painter: _TDProgressCircularPainter(
          value: value,
          backgroundColor: backgroundColor,
          valueColor: valueColor,
          circleRadius: circleRadius,
          strokeWidth: strokeWidth,
        ),
      ),
    );
  }
}

class _TDProgressCircularPainter extends CustomPainter {
  final double value;
  final Color backgroundColor;
  final Animation<Color> valueColor;
  final double circleRadius;
  final double strokeWidth;

  _TDProgressCircularPainter({
    required this.value,
    required this.backgroundColor,
    required this.valueColor,
    required this.circleRadius,
    required this.strokeWidth,
  });

  @override
  void paint(Canvas canvas, Size size) {
    final center = size.width / 2;
    final radius = center - strokeWidth / 2;

    final backgroundPaint = Paint()
      ..color = backgroundColor
      ..style = PaintingStyle.stroke
      ..strokeWidth = strokeWidth;

    final valuePaint = Paint()
      ..color = valueColor.value
      ..style = PaintingStyle.stroke
      ..strokeWidth = strokeWidth
      ..strokeCap = StrokeCap.round;

    canvas.drawCircle(Offset(center, center), radius, backgroundPaint);

    final sweepAngle = 2 * math.pi * value;
    canvas.drawArc(
      Rect.fromCircle(center: Offset(center, center), radius: radius),
      -math.pi / 2,
      sweepAngle,
      false,
      valuePaint,
    );
  }

  @override
  bool shouldRepaint(covariant CustomPainter oldDelegate) {
    return true;
  }
}

