import 'package:flutter/material.dart';

import '../../../tdesign_flutter.dart';
import '../../util/context_extension.dart';

///
/// 搜索框的样式
///
enum TDSearchStyle {
  /// 方形
  square,

  /// 圆形
  round,
}

///
/// 搜索框对齐方式
///
enum TDSearchAlignment {
  /// 默认头部对齐
  left,

  /// 居中
  center,
}

typedef TDSearchBarEvent = void Function(String value);
typedef TDSearchBarClearEvent = bool? Function(String value);
typedef TDSearchBarCallBack = void Function();

class TDSearchBar extends StatefulWidget {
  const TDSearchBar({
    Key? key,
    this.placeHolder,
    this.style = TDSearchStyle.square,
    this.alignment = TDSearchAlignment.left,
    this.onTextChanged,
    this.onSubmitted,
    this.onEditComplete,
    this.onTapOutside,
    this.onInputClick,
    this.autoHeight = false,
    this.padding = const EdgeInsets.symmetric(horizontal: 16, vertical: 8),
    this.autoFocus = false,
    this.mediumStyle = false,
    this.cursorHeight,
    this.needCancel = false,
    this.controller,
    this.backgroundColor,
    this.action = '',
    this.onActionClick,
    this.onClearClick,
    this.focusNode,
    this.inputAction,
    this.enabled,
    this.readOnly,
  }) : super(key: key);

  /// 预设文案
  final String? placeHolder;

  /// 样式
  final TDSearchStyle? style;

  /// 对齐方式，居中或这头部对齐
  final TDSearchAlignment? alignment;

  /// 背景颜色
  final Color? backgroundColor;

  /// 是否自动计算高度
  final bool autoHeight;

  /// 内部填充
  final EdgeInsets padding;

  /// 是否自动获取焦点
  final bool autoFocus;

  /// 是否在导航栏中的样式
  final bool mediumStyle;

  /// 光标的高
  final double? cursorHeight;

  /// 是否需要取消按钮
  final bool needCancel;

  /// 控制器
  final TextEditingController? controller;

  /// 文字改变回调
  final TDSearchBarEvent? onTextChanged;

  /// 提交回调
  final TDSearchBarEvent? onSubmitted;

  /// 编辑完成回调
  final TDSearchBarCallBack? onEditComplete;

  /// 点击输入框外部回调
  final TapRegionCallback? onTapOutside;

  /// 自定义操作文字
  final String action;

  /// 输入框点击事件
  final GestureTapCallback? onInputClick;

  /// 自定义操作回调
  final TDSearchBarEvent? onActionClick;

  /// 自定义操作回调
  final TDSearchBarClearEvent? onClearClick;

  /// 自定义焦点
  final FocusNode? focusNode;

  /// 键盘动作类型
  final TextInputAction? inputAction;

  /// 是否禁用
  final bool? enabled;

  /// 是否只读
  final bool? readOnly;

  @override
  State<StatefulWidget> createState() => _TDSearchBarState();
}

class _TDSearchBarState extends State<TDSearchBar>
    with TickerProviderStateMixin {
  late FocusNode focusNode = FocusNode();
  final TextEditingController controller = TextEditingController();
  final GlobalKey _textFieldKey = GlobalKey();

  bool clearBtnHide = true;
  bool cancelBtnHide = true;

  @override
  void initState() {
    super.initState();
    if (widget.controller == null) {
      controller.addListener(() {
        var clearVisible = controller.text.isNotEmpty;
        _updateClearBtnVisible(clearVisible);
      });
    } else {
      widget.controller?.addListener(() {
        var clearVisible = widget.controller?.text.isNotEmpty;
        _updateClearBtnVisible(clearVisible!);
      });
    }
    _updateFocusNode();
  }

  void _updateFocusNode() {
    focusNode = widget.focusNode ?? focusNode;
    focusNode.addListener(() {
      setState(() {
        cancelBtnHide = !focusNode.hasFocus;
      });
    });
  }

  @override
  void didUpdateWidget(covariant TDSearchBar oldWidget) {
    super.didUpdateWidget(oldWidget);
    _updateFocusNode();
  }

  void _updateClearBtnVisible(bool visible) {
    setState(() {
      clearBtnHide = !visible;
    });
  }

  void _cleanInputText() {
    if (!(widget.onClearClick?.call(controller.text) ?? false)) {
      // 如果外部没处理,则走默认清除逻辑
      if (widget.controller == null) {
        controller.clear();
      } else {
        widget.controller?.clear();
      }
    }
  }

  Font? getSize(BuildContext context) {
    return widget.mediumStyle
        ? TDTheme.of(context).fontBodyMedium
        : TDTheme.of(context).fontBodyLarge;
  }

  Widget actionBtn(BuildContext context, String? text,
      {String? action, TDSearchBarEvent? onActionClick}) {
    return GestureDetector(
      onTap: () {
        onActionClick!(text ?? '');
      },
      child: Container(
        padding: const EdgeInsets.only(left: 16),
        child: Text(action!,
            style: TextStyle(
                fontSize: getSize(context)?.size,
                color: TDTheme.of(context).brandNormalColor)),
      ),
    );
  }

  @override
  Widget build(BuildContext context) {
    return Container(
      padding: widget.padding,
      height: widget.autoHeight ? double.infinity : 56,
      color: widget.backgroundColor ?? TDTheme.of(context).bgColorContainer,
      child: Stack(alignment: AlignmentDirectional.center, children: [
        Row(
          children: [
            Expanded(
              flex: 1,
              child: Container(
                height: double.infinity,
                decoration: BoxDecoration(
                    color: TDTheme.of(context).bgColorSecondaryContainer,
                    borderRadius: BorderRadius.circular(
                        widget.style == TDSearchStyle.square ? 4 : 28)),
                child: Row(
                  crossAxisAlignment: CrossAxisAlignment.center,
                  children: [
                    const SizedBox(
                      width: 12,
                    ),
                    Icon(
                      TDIcons.search,
                      size: widget.mediumStyle ? 20 : 24,
                      color: TDTheme.of(context).textColorPlaceholder,
                    ),
                    const Padding(padding: EdgeInsets.only(left: 3)),
                    Expanded(
                      flex: 1,
                      child: Container(
                        margin: const EdgeInsets.only(bottom: 1),
                        // 为了适配TextField与Text的差异，后续需要做通用适配
                        child: TextField(
                          key: _textFieldKey,
                          controller: widget.controller ?? controller,
                          autofocus: widget.autoFocus,
                          cursorColor: TDTheme.of(context).brandNormalColor,
                          cursorHeight: widget.cursorHeight,
                          textAlign:
                              widget.alignment == TDSearchAlignment.center
                                  ? TextAlign.center
                                  : TextAlign.left,
                          focusNode: focusNode,
                          onTap: widget.onInputClick,
                          onChanged: widget.onTextChanged,
                          onSubmitted: widget.onSubmitted,
                          onEditingComplete: widget.onEditComplete,
                          onTapOutside: widget.onTapOutside,
                          style: TextStyle(
                              textBaseline: TextBaseline.ideographic,
                              fontSize: getSize(context)?.size,
                              color: TDTheme.of(context).textColorPrimary),
                          decoration: InputDecoration(
                            hintText: widget.placeHolder,
                            hintStyle: TextStyle(
                              fontSize: getSize(context)?.size,
                              color: TDTheme.of(context).textColorPlaceholder,
                              textBaseline: TextBaseline.ideographic,
                              overflow: TextOverflow.ellipsis,
                            ),
                            hintMaxLines: 1,
                            border: InputBorder.none,
                            isCollapsed: true,
                            // filled: true,
                            // fillColor: TDTheme.of(context).bgColorSecondaryContainer,
                          ),
                          maxLines: 1,
                          textInputAction: widget.inputAction,
                          readOnly: widget.readOnly ?? false,
                          enabled: widget.enabled,
                          cursorOpacityAnimates: false,
                        ),
                      ),
                    ),
                    const Padding(padding: EdgeInsets.only(right: 9)),
                    Offstage(
                      offstage: clearBtnHide,
                      child: GestureDetector(
                          onTap: () {
                            _cleanInputText();
                            if (widget.onTextChanged != null) {
                              widget.onTextChanged!('');
                            }
                          },
                          child: Icon(
                            TDIcons.close_circle_filled,
                            size: widget.mediumStyle ? 17 : 21,
                            color: TDTheme.of(context).textColorPlaceholder,
                          )),
                    ),
                    const Padding(padding: EdgeInsets.only(right: 9)),
                  ],
                ),
              ),
            ),
            widget.action.isNotEmpty
                ? actionBtn(
                    context,
                    controller.text,
                    action: widget.action,
                    onActionClick: widget.onActionClick ?? (String text) {},
                  )
                : Offstage(
                    offstage: cancelBtnHide || !widget.needCancel,
                    child: GestureDetector(
                      onTap: () {
                        _cleanInputText();
                        if (widget.onTextChanged != null) {
                          widget.onTextChanged!('');
                        }
                        focusNode.unfocus();
                      },
                      child: Container(
                        padding: const EdgeInsets.only(left: 16),
                        child: Text(context.resource.cancel,
                            style: TextStyle(
                                fontSize: getSize(context)?.size,
                                color: TDTheme.of(context).brandNormalColor)),
                      ),
                    ),
                  ),
          ],
        ),
      ]),
    );
  }
}

Sidebar.sidebar:
import 'package:flutter/material.dart';

import '../../../tdesign_flutter.dart';
import 'td_wrap_sidebar_item.dart';

enum TDSideBarStyle {
  normal,
  outline,
}

class SideItemProps {
  int index;
  int value;
  bool? disabled;
  IconData? icon;
  String? label;
  TDBadge? badge;
  TextStyle? textStyle;

  SideItemProps({
    required this.value,
    required this.index,
    this.disabled,
    this.icon,
    this.label,
    this.badge,
    this.textStyle,
  });
}

class TDSideBar extends StatefulWidget {
  const TDSideBar({
    Key? key,
    this.value,
    this.defaultValue,
    this.selectedColor,
    this.children = const [],
    this.onChanged,
    this.onSelected,
    this.height,
    this.controller,
    this.contentPadding,
    this.selectedTextStyle,
    this.style = TDSideBarStyle.normal,
    this.loading,
    this.loadingWidget,
    this.selectedBgColor,
    this.unSelectedBgColor,
    this.unSelectedColor,
  }) : super(key: key);

  /// 选项值
  final int? value;

  /// 默认值
  final int? defaultValue;

  /// 单项
  final List<TDSideBarItem> children;

  /// 选中值发生变化（Controller控制）
  final ValueChanged<int>? onChanged;

  /// 选中值发生变化（点击事件）
  final ValueChanged<int>? onSelected;

  /// 选中值后颜色
  final Color? selectedColor;

  /// 未选中颜色
  final Color? unSelectedColor;

  /// 选中样式
  final TextStyle? selectedTextStyle;

  /// 样式
  final TDSideBarStyle style;

  /// 高度
  final double? height;

  /// 自定义文本框内边距
  final EdgeInsetsGeometry? contentPadding;

  /// 控制器
  final TDSideBarController? controller;

  /// 加载效果
  final bool? loading;

  /// 自定义加载动画
  final Widget? loadingWidget;

  /// 选择的背景颜色
  final Color? selectedBgColor;

  /// 未选择的背景颜色
  final Color? unSelectedBgColor;

  @override
  State<TDSideBar> createState() => _TDSideBarState();
}

class _TDSideBarState extends State<TDSideBar> {
  late List<SideItemProps> displayChildren;
  late int? currentValue;
  late int? currentIndex;
  final _scrollerController = ScrollController();
  final GlobalKey globalKey = GlobalKey();
  final double itemHeight = 56.0;
  bool _loading = false;

  // 查找某值对应项
  SideItemProps findSideItem(int value) {
    return displayChildren.where((element) => element.value == value).first;
  }

  // 选中某值
  void selectValue(int value, {bool needScroll = false}) {
    SideItemProps? item;
    for (var element in displayChildren) {
      if (element.value == value) {
        item = element;
      }
    }

    if (needScroll && item != null) {
      try {
        var height = globalKey.currentContext!.size!.height;
        var offset = _scrollerController.offset;
        var distance = item.index * itemHeight - offset;
        if (distance + itemHeight > height) {
          _scrollerController.animateTo(offset + itemHeight,
              duration: const Duration(milliseconds: 100),
              curve: Curves.easeIn);
        } else if (distance < 0) {
          _scrollerController.animateTo(offset - itemHeight,
              duration: const Duration(milliseconds: 100),
              curve: Curves.easeIn);
        }
      } catch (e) {
        print(e);
      }
    }

    if (item != null) {
      onSelect(item, isController: true);
    }
  }

  @override
  void initState() {
    super.initState();

    _loading = widget.loading ?? widget.controller?.loading ?? false;
    // controller注册事件
    if (widget.controller != null) {
      widget.controller!.addListener(() {
        selectValue(widget.controller!.currentValue, needScroll: true);
        _loading = widget.controller!.loading;
        getDisplayChildren();
        setState(() {});
      });
    }

    displayChildren = widget.children
        .asMap()
        .entries
        .map((entry) => SideItemProps(
            index: entry.key,
            disabled: entry.value.disabled,
            value: entry.value.value,
            icon: entry.value.icon,
            label: entry.value.label,
            textStyle: entry.value.textStyle,
            badge: entry.value.badge))
        .toList();

    currentValue = widget.value ??
        widget.defaultValue ??
        (displayChildren.isNotEmpty ? displayChildren[0].value : null);
    if (currentValue != null) {
      try {
        final item = findSideItem(currentValue!);
        currentIndex = item.index;
      } catch (e) {
        currentIndex = null;
        currentValue = null;
      }
    } else {
      currentIndex = null;
    }
  }

  void getDisplayChildren() {
    if (widget.controller != null && widget.controller!.children.isNotEmpty) {
      displayChildren = widget.controller!.children
          .asMap()
          .entries
          .map((entry) => SideItemProps(
              index: entry.key,
              disabled: entry.value.disabled,
              value: entry.value.value,
              icon: entry.value.icon,
              label: entry.value.label,
              textStyle: entry.value.textStyle,
              badge: entry.value.badge))
          .toList();
    } else if (widget.children.isNotEmpty) {
      displayChildren = widget.children
          .asMap()
          .entries
          .map((entry) => SideItemProps(
              index: entry.key,
              disabled: entry.value.disabled,
              value: entry.value.value,
              icon: entry.value.icon,
              label: entry.value.label,
              textStyle: entry.value.textStyle,
              badge: entry.value.badge))
          .toList();
    } else {
      displayChildren = [];
    }
  }

  // 选中某项
  void onSelect(SideItemProps item, {isController = false}) {
    if (currentIndex != item.index) {
      if (isController) {
        if (widget.onChanged != null) {
          widget.onChanged!(item.value);
        }
      } else {
        if (widget.onSelected != null) {
          widget.onSelected!(item.value);
        }
      }

      setState(() {
        currentIndex = item.index;
      });
    }
  }

  @override
  Widget build(BuildContext context) {
    if (_loading) {
      widget.controller?.loading = true;
      if (widget.loadingWidget != null) {
        return widget.loadingWidget!;
      }
      return SizedBox(
        width: MediaQuery.of(context).size.width,
        child: const Align(
          child:
              TDLoading(icon: TDLoadingIcon.circle, size: TDLoadingSize.large),
        ),
      );
    }
    return ConstrainedBox(
        key: globalKey,
        constraints: BoxConstraints(
            minWidth: 106,
            maxHeight: MediaQuery.of(context).size.height -
                MediaQuery.of(context).padding.top),
        child: SizedBox(
            height: widget.height ?? MediaQuery.of(context).size.height,
            child: MediaQuery.removePadding(
                context: context,
                removeTop: true,
                removeBottom: true,
                child: ListView.builder(
                    physics: const ClampingScrollPhysics(),
                    itemCount: displayChildren.length,
                    controller: _scrollerController,
                    itemBuilder: (BuildContext context, int index) {
                      var ele = displayChildren[index];

                      return TDWrapSideBarItem(
                        style: widget.style,
                        value: ele.value,
                        icon: ele.icon,
                        disabled: ele.disabled ?? false,
                        label: ele.label ?? '',
                        badge: ele.badge,
                        textStyle: ele.textStyle,
                        selected: currentIndex == ele.index,
                        selectedColor: widget.selectedColor,
                        unSelectedColor: widget.unSelectedColor,
                        selectedTextStyle: widget.selectedTextStyle,
                        contentPadding: widget.contentPadding,
                        topAdjacent: currentIndex != null &&
                            currentIndex! + 1 == ele.index,
                        bottomAdjacent: currentIndex != null &&
                            currentIndex! - 1 == ele.index,
                        selectedBgColor: widget.selectedBgColor ??
                            TDTheme.of(context).bgColorContainer,
                        unSelectedBgColor: widget.unSelectedBgColor ??
                            TDTheme.of(context).bgColorSecondaryContainer,
                        onTap: () {
                          if (!(ele.disabled ?? false)) {
                            onSelect(ele, isController: false);
                          }
                        },
                      );
                    }))));
  }

  @override
  void didUpdateWidget(covariant TDSideBar oldWidget) {
    getDisplayChildren();
    super.didUpdateWidget(oldWidget);
  }
}

Sidebar.sidebar_controller:
import 'package:flutter/material.dart';

import '../../../tdesign_flutter.dart';

class TDSideBarController extends ChangeNotifier {
  int currentValue = 0;
  List<SideItemProps> children = [];
  bool _loading = false;

  void selectTo(int value) {
    currentValue = value;
    notifyListeners();
  }

  void init(List<SideItemProps> data) {
    closeLoading(false, needNotify: false);
    children = data;
    notifyListeners();
  }

  void closeLoading(bool load, { bool needNotify = true }) {
    _loading = load;
    if(needNotify) {
      notifyListeners();
    }
  }

  set loading (bool load) {
    _loading = load;
  }

  bool get loading => _loading;

  @override
  void dispose() {
    super.dispose();
    currentValue = 0;
  }
}

