import 'package:flutter/material.dart';

import '../../../tdesign_flutter.dart';

class TDWrapSideBarItem extends StatelessWidget {
  const TDWrapSideBarItem({
    Key? key,
    this.badge,
    required this.disabled,
    this.icon,
    this.label = '',
    this.contentPadding,
    this.textStyle = const TextStyle(
      fontSize: 16,
      height: 1.5,
    ),
    this.selectedTextStyle,
    this.value = -1,
    this.selected = false,
    this.selectedColor,
    this.topAdjacent = false,
    this.bottomAdjacent = false,
    this.onTap,
    this.selectedBgColor,
    this.unSelectedBgColor,
    this.unSelectedColor,
    required this.style,
  }) : super(key: key);

  final TDBadge? badge;
  final bool disabled;
  final IconData? icon;
  final String label;
  final EdgeInsetsGeometry? contentPadding;
  final TextStyle? textStyle;
  final TextStyle? selectedTextStyle;
  final int value;
  final bool selected;
  final Color? selectedColor;
  final Color? selectedBgColor;
  final Color? unSelectedColor;
  final Color? unSelectedBgColor;
  final bool topAdjacent;
  final bool bottomAdjacent;
  final VoidCallback? onTap;
  final TDSideBarStyle style;

  static const preLineWidth = 3.0;

  @override
  Widget build(BuildContext context) {
    return GestureDetector(
      onTap: onTap,
      child: style == TDSideBarStyle.normal
          ? renderNormalItem(context)
          : renderOutlineItem(context),
    );
  }

  Widget renderNormalItem(BuildContext context) {
    return Container(
      decoration: BoxDecoration(
        color: selectedBgColor ?? TDTheme.of(context).bgColorContainer,
      ),
      child: Container(
        decoration: BoxDecoration(
          color: selected
              ? selectedBgColor ?? TDTheme.of(context).bgColorContainer
              : unSelectedBgColor ??
                  TDTheme.of(context).bgColorSecondaryContainer,
          borderRadius: BorderRadius.only(
            topRight: Radius.circular(
                topAdjacent ? TDTheme.of(context).radiusLarge : 0),
            bottomRight: Radius.circular(
                bottomAdjacent ? TDTheme.of(context).radiusLarge : 0),
          ),
        ),
        child: Row(
          children: [
            renderPreLine(context),
            Expanded(
                child: Padding(
                    padding: contentPadding ?? const EdgeInsets.all(16),
                    child: renderMainContent(context)))
          ],
        ),
      ),
    );
  }

  Widget renderOutlineItem(BuildContext context) {
    return ConstrainedBox(
        constraints: const BoxConstraints(minHeight: 56),
        child: Container(
          // height: 86,
          decoration: BoxDecoration(
              color: TDTheme.of(context).bgColorSecondaryContainer),
          padding: const EdgeInsets.all(8),
          child: Container(
            decoration: BoxDecoration(
                color: selected && !disabled
                    ? TDTheme.of(context).bgColorContainer
                    : null,
                borderRadius:
                    BorderRadius.circular(TDTheme.of(context).radiusDefault)),
            padding: const EdgeInsets.all(8),
            child: renderMainContent(context),
          ),
        ));
  }

  Widget renderMainContent(BuildContext context) {
    return Row(
      crossAxisAlignment: CrossAxisAlignment.center,
      children: [
        renderIcon(context),
        Expanded(child: renderLabel(context)),
        if (label.length > 4) renderBadge(context),
        // SizedBox(
        //   width: !disabled && selected ? 0 : preLineWidth,
        // )
      ],
    );
  }

  Widget renderPreLine(BuildContext context) {
    return Visibility(
      visible: !disabled && selected,
      replacement: const SizedBox(
        width: preLineWidth,
      ),
      child: Column(
        mainAxisAlignment: MainAxisAlignment.center,
        children: [
          Container(
            width: preLineWidth,
            height: 14,
            decoration: BoxDecoration(
                color: selectedTextStyle != null
                    ? selectedTextStyle?.color
                    : (selectedColor ?? TDTheme.of(context).brandNormalColor),
                borderRadius: BorderRadius.circular(4)),
          )
        ],
      ),
    );
  }

  Widget renderIcon(BuildContext context) {
    final iconColor = () {
      if (disabled) {
        return TDTheme.of(context).textDisabledColor;
      }
      if (!selected) {
        return unSelectedColor ?? TDTheme.of(context).textColorPrimary;
      }
      if (selectedTextStyle?.color != null) {
        return selectedTextStyle!.color!;
      }
      return selectedColor ?? TDTheme.of(context).brandNormalColor;
    }();

    return Visibility(
      visible: icon != null,
      child: Padding(
        padding: const EdgeInsets.only(right: 2),
        child: Icon(icon, size: 20, color: iconColor),
      ),
    );
  }

  Widget renderLabel(BuildContext context) {
    return TDText.rich(
      TextSpan(
        children: [
          WidgetSpan(
              child: TDText(
            label,
            style: selected
                ? (selectedTextStyle ?? TextStyle(color: selectedColor))
                : textStyle,
            fontWeight:
                selected && !disabled ? FontWeight.w600 : FontWeight.w400,
            textColor: disabled
                ? TDTheme.of(context).textDisabledColor
                : selected
                    ? selectedColor ?? TDTheme.of(context).brandNormalColor
                    : unSelectedColor ?? TDTheme.of(context).textColorPrimary,
            // forceVerticalCenter: true,
          )),

          /// todo label.length 长度小于则不展示，为什么？？？
          /// 应再判断有无icon，无icon时位置够，可以展示 badge
          if (label.length <= 4 || icon == null)
            WidgetSpan(
                child: SizedBox(
              width: 1,
              height: 16,
              child: Stack(
                clipBehavior: Clip.none,
                children: [
                  badge != null
                      ? Positioned(top: -6, child: badge!)
                      : Container()
                ],
              ),
            ))
        ],
      ),
      softWrap: true,
      style: selectedTextStyle,
    );
  }

  Widget renderBadge(BuildContext context) {
    return SizedBox(
      width: 1,
      height: 40,
      child: Stack(
        clipBehavior: Clip.none,
        children: [
          badge != null ? Positioned(top: -6, child: badge!) : Container()
        ],
      ),
    );
  }
}

Skeleton.skeleton:
import 'package:flutter/material.dart';

import '../../../tdesign_flutter.dart';

/// 骨架图动画
enum TDSkeletonAnimation {
  /// 渐变
  gradient,

  /// 闪烁
  flashed,
}

/// 骨架图风格
enum TDSkeletonTheme {
  /// 头像
  avatar,

  /// 图片
  image,

  /// 文本
  text,

  /// 段落
  paragraph,
}

class TDSkeleton extends StatefulWidget {
  factory TDSkeleton({
    Key? key,
    TDSkeletonAnimation? animation,
    int delay = 0,
    TDSkeletonTheme theme = TDSkeletonTheme.text,
  }) {
    assert(delay >= 0);

    var objects = <List<TDSkeletonRowColObj>>[];

    // 根据风格创建骨架图
    switch (theme) {
      case TDSkeletonTheme.avatar:
        objects = const [
          [TDSkeletonRowColObj.circle()]
        ];
        break;
      case TDSkeletonTheme.image:
        objects = const [
          [
            TDSkeletonRowColObj.rect(
              width: 72,
              height: 72,
              flex: null,
            )
          ]
        ];
        break;
      case TDSkeletonTheme.text:
        objects = const [
          [
            TDSkeletonRowColObj.text(flex: 24),
            TDSkeletonRowColObj.spacer(width: 16),
            TDSkeletonRowColObj.text(flex: 76),
          ],
          [TDSkeletonRowColObj.text()],
        ];
        break;
      case TDSkeletonTheme.paragraph:
        objects = [
          for (int i = 0; i < 3; i++) [const TDSkeletonRowColObj.text()],
          const [
            TDSkeletonRowColObj.text(flex: 55),
            TDSkeletonRowColObj.spacer(flex: 45),
          ],
        ];
        break;
    }

    return TDSkeleton.fromRowCol(
      key: key,
      animation: animation,
      delay: delay,
      rowCol: TDSkeletonRowCol(objects: objects),
    );
  }

  /// 从行列框架创建骨架屏
  const TDSkeleton.fromRowCol({
    super.key,
    this.animation,
    this.delay = 0,
    required this.rowCol,
  }) : assert(delay >= 0);

  /// 动画效果
  final TDSkeletonAnimation? animation;

  /// 延迟显示加载时间
  final int delay;

  /// 自定义行列数量、宽度高度、间距等
  final TDSkeletonRowCol rowCol;

  @override
  _TDSkeletonState createState() => _TDSkeletonState();
}

class _TDSkeletonState extends State<TDSkeleton>
    with SingleTickerProviderStateMixin {
  /// 动画控制器
  late final AnimationController? _controller;

  /// 动画效果
  late final Animation<double>? _animation;

  /// 加载状态
  bool _isLoading = true;

  /// 加载控件
  static final _loadingWidget = Container();

  /// 闪烁透明度
  static const _animationFlashed = .3;

  /// 静态渐变
  static LinearGradient _animationGradient(BuildContext context) =>
      LinearGradient(
        colors: [
          Colors.transparent,
          TDTheme.of(context).bgColorSecondaryContainerActive,
          Colors.transparent,
        ],
        // 15 deg
        begin: const Alignment(-1, -0.268),
        end: const Alignment(1, 0.268),
        tileMode: TileMode.clamp,
      );

  @override
  void initState() {
    super.initState();

    // 根据动画效果创建动画控制器
    switch (widget.animation) {
      case TDSkeletonAnimation.gradient:
        _controller = AnimationController(
          duration: const Duration(milliseconds: 1500),
          vsync: this,
        )..repeat();
        _animation = Tween<double>(begin: -1, end: 1).animate(_controller!)
          ..addListener(() => setState(() {}));
        break;
      case TDSkeletonAnimation.flashed:
        _controller = AnimationController(
          duration: const Duration(seconds: 1),
          vsync: this,
        )..repeat(reverse: true);
        _animation = Tween<double>(begin: 1, end: _animationFlashed)
            .animate(_controller!)
          ..addListener(() => setState(() {}));
        break;
      default:
        _controller = null;
        _animation = null;
    }

    // 延迟显示加载效果
    Future.delayed(Duration(milliseconds: widget.delay),
        () => setState(() => _isLoading = false));
  }

  Widget Function(TDSkeletonRowColObj) _buildObj(BuildContext context) =>
      (TDSkeletonRowColObj obj) {
        // 骨架图对象
        Widget skeletonObj = Container(
          width: obj.width,
          height: obj.height,
          margin: obj.margin,
          decoration: BoxDecoration(
            color: obj.style.background(context),
            borderRadius:
                BorderRadius.circular(obj.style.borderRadius(context)),
          ),
        );

        // 动画效果
        switch (widget.animation) {
          case TDSkeletonAnimation.gradient:
            skeletonObj = ShaderMask(
              blendMode: BlendMode.srcATop,
              shaderCallback: (bounds) =>
                  _animationGradient(context).createShader(
                Rect.fromLTWH(
                  bounds.width * _animation!.value,
                  0,
                  bounds.width,
                  bounds.height,
                ),
              ),
              child: skeletonObj,
            );
            break;
          case TDSkeletonAnimation.flashed:
            skeletonObj = Opacity(
              opacity: _animation!.value,
              child: skeletonObj,
            );
            break;
          default:
        }

        // 根据弹性因子创建弹性布局
        return obj.flex == null
            ? skeletonObj
            : Flexible(flex: obj.flex!, child: skeletonObj);
      };

  @override
  Widget build(BuildContext context) {
    // 加载状态返回空容器
    if (_isLoading) {
      return _loadingWidget;
    }

    if (widget.rowCol.objects.length == 1) {
      return widget.rowCol.objects.first.length == 1
          // 单个对象
          ? _buildObj(context)(widget.rowCol.objects.first.first)
          // 单行多个对象
          : Flexible(
              child: Row(
              children:
                  widget.rowCol.objects.first.map(_buildObj(context)).toList(),
            ));
    }

    // 多行多个对象
    List<Widget> skeletonRows = widget.rowCol.objects
        .map((row) => Row(children: row.map(_buildObj(context)).toList()))
        .toList();
    final rowSpacing = widget.rowCol.style.rowSpacing(context);
    if (rowSpacing > 0) {
      skeletonRows = skeletonRows
          .expand((row) => [row, SizedBox(height: rowSpacing)])
          .toList();
      if (skeletonRows.isNotEmpty) {
        skeletonRows.removeLast();
      }
    } // 添加行间距
    var skeletonRowCol = Column(children: skeletonRows); // 行列布局

    return widget.rowCol.objects
            .any((row) => row.any((obj) => obj.flex != null))
        // 添加弹性布局
        ? Flexible(
            child: Container(
              constraints: BoxConstraints(
                maxHeight: widget.rowCol.visualHeight(context),
              ), // 限制最大高度
              child: skeletonRowCol,
            ),
          )
        : skeletonRowCol;
  }

  @override
  void dispose() {
    _controller?.dispose();
    super.dispose();
  }
}

Skeleton.skeleton_rowcol:
import 'package:flutter/material.dart';

import '../../../tdesign_flutter.dart';

/// 骨架屏样式
class TDSkeletonRowColStyle {
  const TDSkeletonRowColStyle({
    this.rowSpacing = _defaultRowSpacing,
  });

  /// 行间距
  final double Function(BuildContext) rowSpacing;

  /// 默认行间距
  static double _defaultRowSpacing(BuildContext context) =>
      TDTheme.of(context).spacer16;
}

/// 骨架屏行列框架
class TDSkeletonRowCol {
  TDSkeletonRowCol({
    required this.objects,
    this.style = const TDSkeletonRowColStyle(),
  }) : assert(objects.isNotEmpty && objects.every((row) => row.isNotEmpty));

  /// 行列对象
  final List<List<TDSkeletonRowColObj>> objects;

  /// 样式
  final TDSkeletonRowColStyle style;

  /// 视觉高度
  double visualHeight(BuildContext context) {
    var rowSpacing = style.rowSpacing(context);
    assert(rowSpacing >= 0);
    if (rowSpacing < 0) {
      rowSpacing = 0;
    }

    return objects
            .map((row) =>
                row.fold(.0, (a, b) => a > b.visualHeight ? a : b.visualHeight))
            .fold(.0, (a, b) => a + b) +
        rowSpacing * (objects.length - 1);
  }
}

/// 骨架屏元素样式
class TDSkeletonRowColObjStyle {
  const TDSkeletonRowColObjStyle({
    this.background = _defaultBackground,
    this.borderRadius = _textBorderRadius,
  });

  /// 圆形
  const TDSkeletonRowColObjStyle.circle({this.background = _defaultBackground})
      : borderRadius = _circleBorderRadius;

  /// 矩形
  const TDSkeletonRowColObjStyle.rect({this.background = _defaultBackground})
      : borderRadius = _rectBorderRadius;

  /// 文本
  const TDSkeletonRowColObjStyle.text({this.background = _defaultBackground})
      : borderRadius = _textBorderRadius;

  /// 空白占位符
  const TDSkeletonRowColObjStyle.spacer()
      : background = _transparentBackground,
        borderRadius = _textBorderRadius;

  /// 背景颜色
  final Color Function(BuildContext) background;

  /// 圆角
  final double Function(BuildContext) borderRadius;

  /// 默认背景颜色
  static Color _defaultBackground(BuildContext context) =>
      TDTheme.of(context).bgColorComponent;

  /// 透明背景颜色
  static Color _transparentBackground(BuildContext context) =>
      Colors.transparent;

  /// 圆形圆角
  static double _circleBorderRadius(BuildContext context) =>
      TDTheme.of(context).radiusCircle;

  /// 矩形圆角
  static double _rectBorderRadius(BuildContext context) =>
      TDTheme.of(context).radiusDefault;

  /// 文本圆角
  static double _textBorderRadius(BuildContext context) =>
      TDTheme.of(context).radiusSmall;
}

/// 骨架屏元素
class TDSkeletonRowColObj {
  const TDSkeletonRowColObj({
    this.width,
    this.height = 16,
    this.flex = 1,
    this.margin = EdgeInsets.zero,
    this.style = const TDSkeletonRowColObjStyle(),
  });

  /// 圆形
  const TDSkeletonRowColObj.circle({
    this.width = 48,
    this.height = 48,
    this.flex,
    this.margin = EdgeInsets.zero,
    this.style = const TDSkeletonRowColObjStyle.circle(),
  });

  /// 矩形
  const TDSkeletonRowColObj.rect({
    this.width,
    this.height = 16,
    this.flex = 1,
    this.margin = EdgeInsets.zero,
    this.style = const TDSkeletonRowColObjStyle.rect(),
  });

  /// 文本
  const TDSkeletonRowColObj.text({
    this.width,
    this.height = 16,
    this.flex = 1,
    this.margin = EdgeInsets.zero,
    this.style = const TDSkeletonRowColObjStyle.text(),
  });

  /// 空白占位符
  const TDSkeletonRowColObj.spacer({
    this.width,
    this.height,
    this.flex,
    this.margin = EdgeInsets.zero,
  }) : style = const TDSkeletonRowColObjStyle.spacer();

  /// 宽度
  final double? width;

  /// 高度
  final double? height;

  /// 弹性因子
  final int? flex;

  /// 间距
  final EdgeInsets margin;

  /// 样式
  final TDSkeletonRowColObjStyle style;

  /// 视觉高度
  double get visualHeight => (height ?? 0) + margin.top + margin.bottom;
}

Slider.slider:
///
///  Created by arvinwli@tencent.com on 4/24/23.
///
import 'package:flutter/material.dart';
import '../../theme/td_colors.dart';
import '../../theme/td_spacers.dart';
import '../../theme/td_theme.dart';
import 'td_slider_theme.dart';

enum Position {
  start,
  end,
}

/// 单滑动选择器
class TDSlider extends StatefulWidget {
  /// 默认值
  final double value;

  /// 自定义盒子样式
  final Decoration? boxDecoration;

  /// 左侧标签
  final String? leftLabel;

  /// 右侧标签
  final String? rightLabel;

  /// 滑动变化监听
  final ValueChanged<double>? onChanged;

  /// 滑动开始监听
  final ValueChanged<double>? onChangeStart;

  /// 滑动结束监听
  final ValueChanged<double>? onChangeEnd;

  /// 样式
  final TDSliderThemeData? sliderThemeData;

  ///  Thumb 点击事件 坐标、当前值
  final Function(Offset offset, double value)? onTap;

  ///  Thumb 点击浮标文字 坐标、当前值
  final Function(Offset offset, double value)? onThumbTextTap;

  const TDSlider({
    Key? key,
    required this.value,
    this.boxDecoration,
    this.onChanged,
    this.sliderThemeData,
    this.leftLabel,
    this.rightLabel,
    this.onChangeStart,
    this.onChangeEnd,
    this.onTap,
    this.onThumbTextTap,
  }) : super(key: key);

  @override
  State<StatefulWidget> createState() {
    return TDSliderState();
  }
}

class TDSliderState extends State<TDSlider> {
  final GlobalKey _sliderKey = GlobalKey();
  double value = 0;

  @override
  void initState() {
    super.initState();
    value = widget.value;
  }

  @override
  void didUpdateWidget(covariant TDSlider oldWidget) {
    super.didUpdateWidget(oldWidget);
    value = widget.value;
  }

  bool get _enabled => widget.onChanged != null;

  TextStyle get labelTextStyle => TextStyle(
      fontSize: 16,
      color: _enabled
          ? TDTheme.of(context).textColorPrimary
          : TDTheme.of(context).textDisabledColor);

  Widget get leftLabel => widget.leftLabel?.isNotEmpty == true
      ? Padding(
          padding: const EdgeInsets.only(left: 16),
          child: Text(widget.leftLabel!, style: labelTextStyle),
        )
      : Container();

  Widget get rightLabel => widget.rightLabel?.isNotEmpty == true
      ? Padding(
          padding: const EdgeInsets.only(right: 16),
          child: Text(widget.rightLabel!, style: labelTextStyle),
        )
      : Container();

  @override
  Widget build(BuildContext context) {
    var tdSliderThemeData = widget.sliderThemeData ?? TDSliderThemeData();

    final showValue =
        tdSliderThemeData.showScaleValue || tdSliderThemeData.showThumbValue;

    return Listener(
        onPointerDown: (event) {
          final sliderBox =
              _sliderKey.currentContext?.findRenderObject() as RenderBox?;
          if (sliderBox == null ||
              widget.onThumbTextTap == null ||
              !tdSliderThemeData.showThumbValue) {
            return;
          }

          final localOffset = sliderBox.globalToLocal(event.position);
          final themeData = widget.sliderThemeData ?? TDSliderThemeData();
          final textRect = themeData.sliderMeasureData.thumbTextRect;

          if (textRect != null && textRect.contains(localOffset)) {
            widget.onThumbTextTap?.call(localOffset, value);
          }
        },
        child: Container(
          padding: EdgeInsets.only(
            top: (showValue ? 16 : 0) + 8,
            bottom: 8,
          ),
          decoration: widget.boxDecoration ??
              BoxDecoration(color: TDTheme.of(context).bgColorContainer),
          child: Row(
            // spacing: TDTheme.of(context).spacer8,
            children: [
              leftLabel,
              const SizedBox(width: 8),
              Expanded(
                child: Listener(
                  onPointerDown: (event) {
                    if (!_enabled || widget.onTap == null) {
                      return;
                    }

                    final sliderBox = _sliderKey.currentContext
                        ?.findRenderObject() as RenderBox?;
                    if (sliderBox == null) {
                      return;
                    }

                    final tapOffset = sliderBox.globalToLocal(event.position);
                    widget.onTap?.call(tapOffset, value);
                  },
                  child: SliderTheme(
                    data: tdSliderThemeData.sliderThemeData,
                    child: Slider(
                      key: _sliderKey,
                      value: value,
                      min: tdSliderThemeData.min,
                      max: tdSliderThemeData.max,
                      divisions: tdSliderThemeData.divisions,
                      onChangeStart: widget.onChangeStart,
                      onChangeEnd: widget.onChangeEnd,
                      onChanged: _enabled
                          ? (slideValue) {
                              setState(() {
                                value = slideValue;
                                widget.onChanged?.call(slideValue);
                              });
                            }
                          : null,
                    ),
                  ),
                ),
              ),
              const SizedBox(width: 8),
              rightLabel
            ],
          ),
        ));
  }
}

/// 范围滑动选择器
class TDRangeSlider extends StatefulWidget {
  /// 默认值
  final RangeValues value;

  /// 自定义盒子样式
  final Decoration? boxDecoration;

  /// 左侧标签
  final String? leftLabel;

  /// 右侧标签

  final String? rightLabel;

  /// 滑动变化监听
  final ValueChanged<RangeValues>? onChanged;

  /// 滑动开始监听

  final ValueChanged<RangeValues>? onChangeStart;

  /// 滑动结束监听
  final ValueChanged<RangeValues>? onChangeEnd;

  /// 样式
  final TDSliderThemeData? sliderThemeData;

  /// Thumb 点击事件 位置、坐标、当前值
  final Function(
    Position position,
    Offset offset,
    double value,
  )? onTap;

  /// Thumb 点击浮标文字 位置、坐标、当前值
  final Function(
    Position position,
    Offset offset,
    double value,
  )? onThumbTextTap;

  const TDRangeSlider({
    Key? key,
    required this.value,
    this.boxDecoration,
    this.onChanged,
    this.sliderThemeData,
    this.leftLabel,
    this.rightLabel,
    this.onChangeStart,
    this.onChangeEnd,
    this.onTap,
    this.onThumbTextTap,
  }) : super(key: key);

  @override
  State<StatefulWidget> createState() {
    return _TDRangeSliderState();
  }
}

class _TDRangeSliderState extends State<TDRangeSlider> {
  RangeValues rangeValues = const RangeValues(0, 100);
  final GlobalKey _sliderRangeKey = GlobalKey();

  @override
  void initState() {
    super.initState();
    rangeValues = widget.value;
  }

  @override
  void didUpdateWidget(covariant TDRangeSlider oldWidget) {
    super.didUpdateWidget(oldWidget);
    rangeValues = widget.value;
  }

  bool get _enabled => widget.onChanged != null;

  TextStyle get labelTextStyle => TextStyle(
      fontSize: 16,
      color: _enabled
          ? TDTheme.of(context).textColorPrimary
          : TDTheme.of(context).textDisabledColor);

  Widget get leftLabel => widget.leftLabel?.isNotEmpty == true
      ? Padding(
          padding: const EdgeInsets.only(left: 16),
          child: Text(widget.leftLabel!, style: labelTextStyle),
        )
      : Container();

  Widget get rightLabel => widget.rightLabel?.isNotEmpty == true
      ? Padding(
          padding: const EdgeInsets.only(right: 16),
          child: Text(widget.rightLabel!, style: labelTextStyle),
        )
      : Container();

  @override
  Widget build(BuildContext context) {
    var tdSliderThemeData = widget.sliderThemeData ?? TDSliderThemeData();
    final showValue =
        tdSliderThemeData.showScaleValue || tdSliderThemeData.showThumbValue;

    return Listener(
      onPointerDown: (event) {
        final sliderBox =
            _sliderRangeKey.currentContext?.findRenderObject() as RenderBox?;
        final localOffset =
            sliderBox?.globalToLocal(event.position) ?? Offset.zero;

        if (sliderBox == null ||
            widget.onThumbTextTap == null ||
            !tdSliderThemeData.showThumbValue) {
          return;
        }

        final themeData = widget.sliderThemeData ?? TDSliderThemeData();
        final startTextRect =
            themeData.sliderMeasureData.startRangeThumbTextRect;
        final endTextRect = themeData.sliderMeasureData.endRangeThumbTextRect;

        if (startTextRect?.contains(localOffset) ?? false) {
          widget.onThumbTextTap
              ?.call(Position.start, localOffset, rangeValues.start);
        }
        if (endTextRect?.contains(localOffset) ?? false) {
          widget.onThumbTextTap
              ?.call(Position.end, localOffset, rangeValues.end);
        }
      },
      child: Container(
        padding: EdgeInsets.only(
          top: (showValue ? 16 : 0) + 8,
          bottom: 8,
        ),
        decoration: widget.boxDecoration ??
            BoxDecoration(
              color: TDTheme.of(context).bgColorContainer,
            ),
        child: Row(
          // spacing: 8,
          children: [
            leftLabel,
            const SizedBox(width: 8),
            Expanded(
              child: Listener(
                onPointerDown: (PointerDownEvent event) {
                  if (!_enabled || widget.onTap == null) {
                    return;
                  }

                  final sliderBox = _sliderRangeKey.currentContext
                      ?.findRenderObject() as RenderBox?;
                  if (sliderBox == null) {
                    return;
                  }

                  final tapOffset = sliderBox.globalToLocal(event.position);
                  final sliderWidth = sliderBox.size.width;

                  final sliderTheme = SliderTheme.of(context);
                  final thumbShape = sliderTheme.rangeThumbShape;
                  final thumbSize = thumbShape?.getPreferredSize(
                        _enabled,
                        widget.sliderThemeData?.divisions != null,
                      ) ??
                      const Size(20, 20);

                  final thumbRadius = thumbSize.width / 2;

                  // 计算当前值对应的坐标比例
                  final min = widget.sliderThemeData?.min ?? 0;
                  final max = widget.sliderThemeData?.max ?? 100;
                  final startRatio = (rangeValues.start - min) / (max - min);
                  final endRatio = (rangeValues.end - min) / (max - min);

                  // 计算Thumb中心坐标
                  final startCenterX = startRatio * sliderWidth;
                  final endCenterX = endRatio * sliderWidth;
                  final verticalCenter = sliderBox.size.height / 2;

                  // 检测点击区域
                  final isStartTap =
                      (tapOffset.dx - startCenterX).abs() <= thumbRadius &&
                          (tapOffset.dy - verticalCenter).abs() <= thumbRadius;
                  final isEndTap =
                      (tapOffset.dx - endCenterX).abs() <= thumbRadius &&
                          (tapOffset.dy - verticalCenter).abs() <= thumbRadius;

                  Position position;
                  double tappedValue;

                  if (isStartTap) {
                    position = Position.start;
                    tappedValue = rangeValues.start;
                  } else if (isEndTap) {
                    position = Position.end;
                    tappedValue = rangeValues.end;
                  } else {
                    tappedValue =
                        (tapOffset.dx / sliderWidth) * (max - min) + min;
                    final startDistance =
                        (tappedValue - rangeValues.start).abs();
                    final endDistance = (tappedValue - rangeValues.end).abs();
                    position = startDistance < endDistance
                        ? Position.start
                        : Position.end;
                  }
                  widget.onTap?.call(position, tapOffset, tappedValue);
                },
                child: SliderTheme(
                  data: tdSliderThemeData.sliderThemeData,
                  child: RangeSlider(
                    key: _sliderRangeKey,
                    values: rangeValues,
                    min: tdSliderThemeData.min,
                    max: tdSliderThemeData.max,
                    divisions: tdSliderThemeData.divisions,
                    onChanged: widget.onChanged == null
                        ? null
                        : (slideValue) {
                            setState(() {
                              rangeValues = slideValue;
                              widget.onChanged?.call(slideValue);
                            });
                          },
                    onChangeStart: widget.onChangeStart,
                    onChangeEnd: widget.onChangeEnd,
                  ),
                ),
              ),
            ),
            const SizedBox(width: 8),
            rightLabel,
          ],
        ),
      ),
    );
  }
}

