import 'package:flutter/material.dart';
import 'package:flutter_swiper_null_safety/flutter_swiper_null_safety.dart';

import '../../../tdesign_flutter.dart';

const _kAminatedDuration = 100;

/// TDesign风格的Swiper指示器样式，与flutter_swiper的Swiper结合使用
class TDSwiperPagination extends SwiperPlugin {
  const TDSwiperPagination({
    this.alignment,
    this.key,
    this.margin = const EdgeInsets.all(10.0),
    this.builder = TDSwiperPagination.dots,
  });

  /// 圆点样式
  static const SwiperPlugin dots = TDSwiperDotsPagination();

  /// 圆角矩形 + 圆点样式 默认宽度20，高度6
  static const SwiperPlugin dotsBar =
      TDSwiperDotsPagination(roundedRectangleWidth: 20);

  /// 数字样式
  static const SwiperPlugin fraction = TDFractionPagination();

  /// 箭头样式
  static const SwiperPlugin controls = TDSwiperArrowPagination();

  /// 当 scrollDirection== Axis.horizontal 时，默认Alignment.bottomCenter
  /// 当 scrollDirection== Axis.vertical 时，默认Alignment.centerRight
  final Alignment? alignment;

  /// 指示器和container之间的距离
  final EdgeInsetsGeometry margin;

  /// 具体样式
  final SwiperPlugin builder;

  final Key? key;

  @override
  Widget build(BuildContext context, SwiperPluginConfig config) {
    var alignment = this.alignment ??
        (config.scrollDirection == Axis.horizontal
            ? Alignment.bottomCenter
            : Alignment.centerRight);
    Widget child = Container(
      margin: margin,
      child: builder.build(context, config),
    );
    if (!config.outer) {
      child = Align(
        key: key,
        alignment: alignment,
        child: child,
      );
    }
    return child;
  }
}

/// 圆点指示器
class TDSwiperDotsPagination extends SwiperPlugin {
  /// 当前展示的索引，如果未设置，则为Theme.of(context).primaryColor
  final Color? activeColor;

  /// 如果未设置，则为 Theme.of(context).scaffoldBackgroundColor
  final Color? color;

  /// 选中状态圆点尺寸
  final double activeSize;

  /// 圆点尺寸
  final double size;

  /// 圆点间距
  final double space;

  /// 圆角矩形宽度（高度仍为activeSize）
  final double? roundedRectangleWidth;

  /// 动画效果 默认100ms，设置为 0 则无动画
  final int? animationDuration;

  final Key? key;

  const TDSwiperDotsPagination({
    this.activeColor,
    this.color,
    this.key,
    this.size = 6.0,
    this.activeSize = 6.0,
    this.space = 8.0,
    this.roundedRectangleWidth,
    this.animationDuration,
  });

  @override
  Widget build(BuildContext context, SwiperPluginConfig config) {
    if (config.itemCount > 20) {
      print('warning: The itemCount is too big, '
          'we suggest use TDFractionPaginationBuilder');
    }
    var activeColor = this.activeColor ??
        (config.outer
            ? TDTheme.of(context).brandNormalColor
            : TDTheme.of(context).whiteColor1);
    var color = this.color ??
        (config.outer
            ? TDTheme.of(context).bgColorComponentHover
            : TDTheme.of(context).fontWhColor2);

    if (config.indicatorLayout != PageIndicatorLayout.NONE &&
        config.layout == SwiperLayout.DEFAULT) {
      return PageIndicator(
        count: config.itemCount,
        controller: config.pageController,
        layout: config.indicatorLayout,
        size: size,
        activeColor: activeColor,
        color: color,
        space: space,
      );
    }

    var list = <Widget>[];

    var itemCount = config.itemCount;
    var activeIndex = config.activeIndex;

    for (var i = 0; i < itemCount; ++i) {
      var active = i == activeIndex;
      var isActivityRectangle =
          roundedRectangleWidth != null && roundedRectangleWidth! > 0 && active;
      double? scalableLen;
      double? fixedLen;

      scalableLen = isActivityRectangle
          ? roundedRectangleWidth
          : (active ? activeSize : size);
      fixedLen = active ? activeSize : size;

      list.add(Container(
        key: Key('pagination_$i'),
        margin: EdgeInsets.all(space),
        child: AnimatedContainer(
          duration:
              Duration(milliseconds: animationDuration ?? _kAminatedDuration),
          width: config.scrollDirection == Axis.horizontal
              ? scalableLen
              : fixedLen,
          height: config.scrollDirection == Axis.horizontal
              ? fixedLen
              : scalableLen,
          decoration: BoxDecoration(
              color: active ? activeColor : color,
              borderRadius: BorderRadius.circular(activeSize / 2)),
        ),
      ));
    }

    return Flex(
      direction: config.scrollDirection,
      // spacing: space,
      key: key,
      mainAxisSize: MainAxisSize.min,
      children: list,
    );
  }
}

/// 数字指示器
class TDFractionPagination extends SwiperPlugin {
  /// container宽度
  final double? width;

  /// container高度
  final double? height;

  /// 圆角角度
  final double? borderRadius;

  /// 背景色
  final Color? backgroundColor;

  /// 如果未设置，则为 Theme.of(context).scaffoldBackgroundColor
  final Color? color;

  /// 当前展示的索引，如果未设置，则为Theme.of(context).primaryColor
  final Color? activeColor;

  final TextStyle? textStyle;

  final TextStyle? activeTextStyle;

  final Key? key;

  const TDFractionPagination({
    this.width,
    this.height,
    this.borderRadius,
    this.backgroundColor,
    this.color = Colors.white,
    this.textStyle = const TextStyle(fontSize: 12.0, color: Colors.white),
    this.activeTextStyle = const TextStyle(fontSize: 12.0, color: Colors.white),
    this.key,
    this.activeColor = Colors.white,
  });

  @override
  Widget build(BuildContext context, SwiperPluginConfig config) {
    return Container(
      width: width ?? 37,
      height: height ?? 24,
      alignment: Alignment.center,
      decoration: BoxDecoration(
        color: backgroundColor ?? TDTheme.of(context).textColorPlaceholder,
        borderRadius: BorderRadius.circular(
            borderRadius ?? TDTheme.of(context).radiusRound),
      ),
      child: Row(
        key: key,
        mainAxisSize: MainAxisSize.min,
        children: <Widget>[
          Text('${config.activeIndex + 1}', style: activeTextStyle),
          Text('/${config.itemCount}', style: textStyle)
        ],
      ),
    );
  }
}

/// 箭头指示器
class TDSwiperArrowPagination extends SwiperPlugin {
  /// 当设置 loop = false 时，滑动到边界是否自动隐藏边界箭头
  final bool? autoHideWhenAtBoundary;

  /// 左箭头widget
  final Widget? backArrow;

  /// 右箭头widget
  final Widget? forwardArrow;

  /// 背景圆形半径
  final double? radius;

  /// 背景圆形颜色
  final Color? backgroundColor;

  const TDSwiperArrowPagination({
    this.radius,
    this.backgroundColor,
    this.backArrow,
    this.forwardArrow,
    this.autoHideWhenAtBoundary = true,
  });

  @override
  Widget build(BuildContext context, SwiperPluginConfig config) {
    var itemCount = config.itemCount;
    var activeIndex = config.activeIndex;

    return Row(children: [
      _buildIcon(
        context,
        visible: config.loop ||
            ((autoHideWhenAtBoundary ?? false) && activeIndex != 0),
        arrowWidget: backArrow,
        icon: Icons.arrow_back_ios_outlined,
        onTap: config.controller.previous,
      ),
      const Spacer(),
      _buildIcon(
        context,
        visible: config.loop ||
            ((autoHideWhenAtBoundary ?? false) && activeIndex != itemCount - 1),
        arrowWidget: forwardArrow,
        icon: Icons.arrow_forward_ios_outlined,
        onTap: config.controller.next,
      ),
    ]);
  }

  Widget _buildIcon(
    BuildContext context, {
    Widget? arrowWidget,
    required IconData icon,
    required bool visible,
    required Function() onTap,
  }) {
    return Visibility(
      visible: visible,
      child: GestureDetector(
        child: CircleAvatar(
          radius: radius ?? 10.0,
          backgroundColor:
              backgroundColor ?? TDTheme.of(context).textColorPlaceholder,
          child: arrowWidget ?? Icon(icon, color: Colors.white, size: 10.0),
        ),
        onTap: onTap,
      ),
    );
  }
}

SwipeCell.swipe_cell:
import 'package:flutter/gestures.dart';
import 'package:flutter/material.dart';
import 'package:flutter_slidable/flutter_slidable.dart';

import '../../util/list_ext.dart';
import '../cell/td_cell.dart';
import 'td_swipe_cell_action.dart';
import 'td_swipe_cell_inherited.dart';
import 'td_swipe_cell_panel.dart';

export 'package:flutter_slidable/flutter_slidable.dart';

enum TDSwipeDirection { right, left }

/// 滑动单元格组件
class TDSwipeCell extends StatefulWidget {
  const TDSwipeCell({
    Key? key,
    this.slidableKey,
    required this.cell,
    this.disabled = false,
    this.opened = const [false, false],
    this.right,
    this.left,
    this.onChange,
    this.controller,
    this.groupTag,
    this.closeWhenOpened = true,
    this.closeWhenTapped = true,
    this.dragStartBehavior = DragStartBehavior.start,
    this.direction = Axis.horizontal,
    this.duration = const Duration(milliseconds: 200),
  }) : super(key: key);

  /// 滑动组件的 Key
  final Key? slidableKey;

  /// 单元格 [TDCell]
  final Widget cell;

  /// 是否禁用滑动
  final bool? disabled;

  /// 默认打开，[left, right]
  final List<bool>? opened;

  /// 右侧滑动操作项面板
  final TDSwipeCellPanel? right;

  /// 左侧滑动操作项面板
  final TDSwipeCellPanel? left;

  /// 滑动展开事件
  final Function(TDSwipeDirection direction, bool open)? onChange;

  /// 自定义控制滑动窗口
  final SlidableController? controller;

  /// 组，配置后，[closeWhenOpened]、[closeWhenTapped]才起作用
  final Object? groupTag;

  /// 当同一组（[groupTag]）中的一个[TDSwipeCell]打开时，是否关闭组中的所有其他[TDSwipeCell]
  final bool? closeWhenOpened;

  /// 当同一组（[groupTag]）中的一个[TDSwipeCell]被点击时，是否应该关闭组中的所有[TDSwipeCell]
  ///
  /// [cell]组件被点击时必须传递点击事件，执行`TDSwipeCellInherited.of(context)?.cellClick()`
  final bool? closeWhenTapped;

  /// 处理拖动开始行为的方式[GestureDetector.dragStartBehavior]
  final DragStartBehavior? dragStartBehavior;

  /// 可拖动的方向
  final Axis? direction;

  /// 打开关闭动画时长
  final Duration? duration;

  Duration get getDuration => duration ?? const Duration(milliseconds: 200);

  static final Map<Object, List<SlidableController>> _controllers = {};

  static void _pushController(SlidableController controller, Object? tag,
      {bool del = false}) {
    if (tag == null) {
      return;
    }
    if (del) {
      if (_controllers.keys.contains(tag)) {
        _controllers[tag]!.remove(controller);
      }
    } else {
      if (_controllers.keys.contains(tag)) {
        if (!_controllers[tag]!.contains(controller)) {
          _controllers[tag]!.add(controller);
        }
      } else {
        _controllers[tag] = [controller];
      }
    }
  }

  /// 根据[groupTag]关闭[TDSwipeCell]
  ///
  /// current：保留当前不关闭
  static void close(Object? tag, {SlidableController? current}) {
    if (tag == null || !_controllers.keys.contains(tag)) {
      return;
    }
    _controllers[tag]!.forEach((element) {
      if (element != current) {
        element.close();
      }
    });
  }

  /// 获取上下文最近的[controller]
  static SlidableController? of(BuildContext context) {
    return Slidable.of(context);
  }

  @override
  _TDSwipeCellState createState() => _TDSwipeCellState();
}

class _TDSwipeCellState extends State<TDSwipeCell>
    with TickerProviderStateMixin {
  late final SlidableController controller;
  final confirmListenable = ValueNotifier<TDSwipeCellAction?>(null);
  TDSwipeDirection? openDirection;

  @override
  void initState() {
    super.initState();
    controller = (widget.controller ?? SlidableController(this))
      ..actionPaneType.addListener(_handleActionPanelTypeChanged)
      ..animation.addStatusListener((status) {
        confirmListenable.value = null;
      });
    TDSwipeCell._pushController(controller, widget.groupTag);
    WidgetsBinding.instance.addPostFrameCallback((timeStamp) {
      if ((widget.opened?.length ?? 0) > 0 && widget.opened![0] == true) {
        controller.openStartActionPane(duration: widget.getDuration);
      }
      if ((widget.opened?.length ?? 0) > 1 && widget.opened![1] == true) {
        controller.openEndActionPane(duration: widget.getDuration);
      }
    });
  }

  @override
  void didUpdateWidget(covariant TDSwipeCell oldWidget) {
    super.didUpdateWidget(oldWidget);
    if (oldWidget.controller != widget.controller) {
      controller.actionPaneType.removeListener(_handleActionPanelTypeChanged);
      TDSwipeCell._pushController(controller, widget.groupTag, del: true);
      controller = (widget.controller ?? SlidableController(this))
        ..actionPaneType.addListener(_handleActionPanelTypeChanged);
      TDSwipeCell._pushController(controller, widget.groupTag);
    }
  }

  @override
  void dispose() {
    controller.actionPaneType.removeListener(_handleActionPanelTypeChanged);
    controller.dispose();
    TDSwipeCell._pushController(controller, widget.groupTag, del: true);
    super.dispose();
  }

  @override
  Widget build(BuildContext context) {
    final rightConfirmLength = widget.right?.confirms?.length ?? 0;
    final leftConfirmLength = widget.left?.confirms?.length ?? 0;

    final slidable = Slidable(
      key: widget.slidableKey ?? UniqueKey(),
      closeOnScroll: false,
      child: widget.cell,
      controller: controller,
      enabled: !(widget.disabled ?? false),
      groupTag: widget.groupTag,
      startActionPane: widget.left?.build(context),
      endActionPane: widget.right?.build(context),
      dragStartBehavior: widget.dragStartBehavior ?? DragStartBehavior.start,
      direction: widget.direction ?? Axis.horizontal,
    );
    return TDSwipeCellInherited(
      duration: widget.getDuration,
      controller: controller,
      cellClick: () {
        if (widget.closeWhenTapped == true) {
          TDSwipeCell.close(widget.groupTag);
        }
      },
      actionClick: (action) {
        final isLeft = openDirection == TDSwipeDirection.left;
        final panel = isLeft ? widget.left! : widget.right!;
        final index = panel.children.indexOf(action);
        final confirm = panel.confirms
            ?.find((element) => element.confirmIndex?.contains(index) == true);
        confirmListenable.value = confirm;
        return confirm != null;
      },
      child: rightConfirmLength > 0 || leftConfirmLength > 0
          ? ValueListenableBuilder(
              valueListenable: confirmListenable,
              builder: (BuildContext context, value, Widget? child) {
                return Stack(
                  children: [
                    slidable,
                    _confirmWidget(),
                  ],
                );
              },
            )
          : slidable,
    );
  }

  Widget _confirmWidget() {
    final isHorizontal = widget.direction == Axis.horizontal;
    final isLeft = openDirection == TDSwipeDirection.left;
    final pane = isLeft ? widget.left : widget.right;
    final extentRatio = pane?.extentRatio ?? 0.3;
    return Positioned.fill(
      child: FractionallySizedBox(
        alignment: isHorizontal
            ? (isLeft ? Alignment.centerLeft : Alignment.centerRight)
            : (isLeft ? Alignment.topCenter : Alignment.bottomCenter),
        widthFactor: isHorizontal ? extentRatio : null,
        heightFactor: isHorizontal ? null : extentRatio,
        child: AnimatedSwitcher(
          duration: widget.getDuration,
          transitionBuilder: (child, animation) {
            return SlideTransition(
              child: child,
              position: Tween<Offset>(
                begin: isLeft ? const Offset(-1, 0) : const Offset(1, 0),
                end: isLeft ? const Offset(0, 0) : const Offset(0, 0),
              ).animate(animation),
            );
          },
          child: confirmListenable.value ?? const SizedBox.shrink(),
        ),
      ),
    );
  }

  void _handleActionPanelTypeChanged() {
    switch (controller.actionPaneType.value) {
      case ActionPaneType.none:
        widget.onChange?.call(openDirection!, false);
        openDirection = null;
        break;
      case ActionPaneType.start:
        if (widget.closeWhenOpened == true) {
          TDSwipeCell.close(widget.groupTag, current: controller);
        }
        openDirection = TDSwipeDirection.left;
        widget.onChange?.call(openDirection!, true);
        break;
      case ActionPaneType.end:
        if (widget.closeWhenOpened == true) {
          TDSwipeCell.close(widget.groupTag, current: controller);
        }
        openDirection = TDSwipeDirection.right;
        widget.onChange?.call(openDirection!, true);
        break;
    }
  }
}

SwipeCell.swipe_cell_action:
import 'package:flutter/material.dart';

import '../../theme/basic.dart';
import '../../theme/td_colors.dart';
import '../../theme/td_fonts.dart';
import '../../theme/td_theme.dart';
import '../text/td_text.dart';
import 'td_swipe_cell_inherited.dart';
import 'td_swipe_cell_panel.dart';

/// 滑动单元格操作按钮
class TDSwipeCellAction extends StatelessWidget {
  const TDSwipeCellAction({
    Key? key,
    this.flex = 1,
    this.backgroundColor,
    this.autoClose = true,
    this.onPressed,
    this.icon,
    this.iconColor,
    this.iconSize = 18,
    this.spacing = 2,
    this.label,
    this.labelStyle,
    this.direction = Axis.horizontal,
    this.confirmIndex,
    this.builder,
  })  : assert((flex ?? 1) > 0, 'flex must be greater than 0'),
        assert(icon != null || label != null, 'icon or label must not be null'),
        super(key: key);

  /// 宽度占比，默认为 1，[TDSwipeCellPanel.confirms]下无效（失踪占满整个[TDSwipeCellPanel]宽度）
  final int? flex;

  /// 背景颜色
  final Color? backgroundColor;

  /// 点击后自动关闭
  final bool? autoClose;

  /// 点击回调
  final void Function(BuildContext context)? onPressed;

  /// 图标
  final IconData? icon;

  /// 图标颜色，默认label字体颜色
  final Color? iconColor;

  /// 图标大小
  final double? iconSize;

  /// 图标和标题的间距
  final double? spacing;

  /// 标题
  final String? label;

  /// 标题样式
  final TextStyle? labelStyle;

  /// 图标和标题的排列方向
  final Axis? direction;

  /// 指定[TDSwipeCellPanel.children]的索引，来打开该[TDSwipeCellAction]
  /// [TDSwipeCellPanel.confirms]参数下才配置该参数
  final List<int>? confirmIndex;

  /// 自定义构建
  final WidgetBuilder? builder;

  @override
  Widget build(BuildContext context) {
    final fontSize = TDTheme.of(context).fontMarkMedium ??
        Font(size: 14, lineHeight: 22, fontWeight: FontWeight.w600);
    final children = <Widget>[
      if (icon != null)
        Icon(
          icon,
          size: iconSize ?? 18,
          color: labelStyle?.color ?? TDTheme.of(context).textColorAnti,
        ),
      if (icon != null && label != null) SizedBox(width: spacing ?? 2),
      if (label != null)
        Flexible(
          child: TDText(
            label,
            forceVerticalCenter: true,
            font: fontSize,
            textColor: TDTheme.of(context).textColorAnti,
            style: labelStyle,
            overflow: TextOverflow.ellipsis,
            maxLines: 1,
          ),
        ),
    ];
    final child = GestureDetector(
      onTap: () {
        _handleTap(context);
      },
      child: builder?.call(context) ??
          Container(
            width: double.infinity,
            height: double.infinity,
            color: backgroundColor,
            child: Flex(
              mainAxisAlignment: MainAxisAlignment.center,
              direction: direction ?? Axis.horizontal,
              children: children,
            ),
          ),
    );
    return confirmIndex?.isNotEmpty == true
        ? child
        : Expanded(
            flex: flex ?? 1,
            child: child,
          );
  }

  void _handleTap(BuildContext context) {
    final swipeInherited = TDSwipeCellInherited.of(context)!;
    var openConfirm = swipeInherited.actionClick(this);
    if (openConfirm == true) {
      return;
    }
    onPressed?.call(context);
    if (autoClose ?? true) {
      swipeInherited.controller.close(duration: swipeInherited.duration);
    }
  }
}

