import 'dart:ui' show lerpDouble;

import 'package:flutter/cupertino.dart';
import 'package:flutter/foundation.dart';
import 'package:flutter/gestures.dart';
import 'package:flutter/material.dart';
import 'package:flutter/rendering.dart';
import 'package:flutter/services.dart';

// Examples can assume:
// bool _lights = false;
// void setState(VoidCallback fn) { }

/// An iOS-style switch.
///
/// Used to toggle the on/off state of a single setting.
///
/// The switch itself does not maintain any state. Instead, when the state of
/// the switch changes, the widget calls the [onChanged] callback. Most widgets
/// that use a switch will listen for the [onChanged] callback and rebuild the
/// switch with a new [value] to update the visual appearance of the switch.
///
/// {@tool dartpad}
/// This example shows a toggleable [TDCupertinoSwitch]. When the thumb slides to
/// the other side of the track, the switch is toggled between on/off.
///
/// ** See code in examples/api/lib/cupertino/switch/cupertino_switch.0.dart **
/// {@end-tool}
///
/// {@tool snippet}
///
/// This sample shows how to use a [TDCupertinoSwitch] in a [ListTile]. The
/// [MergeSemantics] is used to turn the entire [ListTile] into a single item
/// for accessibility tools.
///
/// ```dart
/// MergeSemantics(
///   child: ListTile(
///     title: const Text('Lights'),
///     trailing: TDCupertinoSwitch(
///       value: _lights,
///       onChanged: (bool value) { setState(() { _lights = value; }); },
///     ),
///     onTap: () { setState(() { _lights = !_lights; }); },
///   ),
/// )
/// ```
/// {@end-tool}
///
/// See also:
///
///  * [Switch], the Material Design equivalent.
///  * <https://developer.apple.com/ios/human-interface-guidelines/controls/switches/>
class TDCupertinoSwitch extends StatefulWidget {
  /// Creates an iOS-style switch.
  ///
  /// The [value] parameter must not be null.
  /// The [dragStartBehavior] parameter defaults to [DragStartBehavior.start] and must not be null.
  const TDCupertinoSwitch({
    Key? key,
    required this.value,
    required this.onChanged,
    this.activeColor,
    this.trackColor,
    this.thumbColor,
    this.thumbView,
    this.dragStartBehavior = DragStartBehavior.start,
  }) : super(key: key);

  /// Whether this switch is on or off.
  ///
  /// Must not be null.
  final bool value;

  /// Called when the user toggles with switch on or off.
  ///
  /// The switch passes the new value to the callback but does not actually
  /// change state until the parent widget rebuilds the switch with the new
  /// value.
  ///
  /// If null, the switch will be displayed as disabled, which has a reduced opacity.
  ///
  /// The callback provided to onChanged should update the state of the parent
  /// [StatefulWidget] using the [State.setState] method, so that the parent
  /// gets rebuilt; for example:
  ///
  /// ```dart
  /// TDCupertinoSwitch(
  ///   value: _giveVerse,
  ///   onChanged: (bool newValue) {
  ///     setState(() {
  ///       _giveVerse = newValue;
  ///     });
  ///   },
  /// )
  /// ```
  final ValueChanged<bool>? onChanged;

  /// The color to use when this switch is on.
  ///
  /// Defaults to [CupertinoColors.systemGreen] when null and ignores
  /// the [CupertinoTheme] in accordance to native iOS behavior.
  final Color? activeColor;

  /// The color to use for the background when the switch is off.
  ///
  /// Defaults to [CupertinoColors.secondarySystemFill] when null.
  final Color? trackColor;

  /// The color to use for the thumb of the switch.
  ///
  /// Defaults to [CupertinoColors.white] when null.
  final Color? thumbColor;

  /// The custom widget over the thumb.
  final Widget? thumbView;

  /// {@template flutter.cupertino.TDCupertinoSwitch.dragStartBehavior}
  /// Determines the way that drag start behavior is handled.
  ///
  /// If set to [DragStartBehavior.start], the drag behavior used to move the
  /// switch from on to off will begin at the position where the drag gesture won
  /// the arena. If set to [DragStartBehavior.down] it will begin at the position
  /// where a down event was first detected.
  ///
  /// In general, setting this to [DragStartBehavior.start] will make drag
  /// animation smoother and setting it to [DragStartBehavior.down] will make
  /// drag behavior feel slightly more reactive.
  ///
  /// By default, the drag start behavior is [DragStartBehavior.start].
  ///
  /// See also:
  ///
  ///  * [DragGestureRecognizer.dragStartBehavior], which gives an example for
  ///    the different behaviors.
  ///
  /// {@endtemplate}
  final DragStartBehavior dragStartBehavior;

  @override
  State<TDCupertinoSwitch> createState() => _TDCupertinoSwitchState();

  @override
  void debugFillProperties(DiagnosticPropertiesBuilder properties) {
    super.debugFillProperties(properties);
    properties.add(FlagProperty('value',
        value: value, ifTrue: 'on', ifFalse: 'off', showName: true));
    properties.add(ObjectFlagProperty<ValueChanged<bool>>(
        'onChanged', onChanged,
        ifNull: 'disabled'));
  }
}

class _TDCupertinoSwitchState extends State<TDCupertinoSwitch>
    with TickerProviderStateMixin {
  late TapGestureRecognizer _tap;
  late HorizontalDragGestureRecognizer _drag;

  late AnimationController _positionController;
  late CurvedAnimation position;

  late AnimationController _reactionController;
  late Animation<double> _reaction;

  bool get isInteractive => widget.onChanged != null;

  // A non-null boolean value that changes to true at the end of a drag if the
  // switch must be animated to the position indicated by the widget's value.
  bool needsPositionAnimation = false;

  @override
  void initState() {
    super.initState();

    _tap = TapGestureRecognizer()
      ..onTapDown = _handleTapDown
      ..onTapUp = _handleTapUp
      ..onTap = _handleTap
      ..onTapCancel = _handleTapCancel;
    _drag = HorizontalDragGestureRecognizer()
      ..onStart = _handleDragStart
      ..onUpdate = _handleDragUpdate
      ..onEnd = _handleDragEnd
      ..dragStartBehavior = widget.dragStartBehavior;

    _positionController = AnimationController(
      duration: _kToggleDuration,
      value: widget.value ? 1.0 : 0.0,
      vsync: this,
    );
    position = CurvedAnimation(
      parent: _positionController,
      curve: Curves.linear,
    );
    _reactionController = AnimationController(
      duration: _kReactionDuration,
      vsync: this,
    );
    _reaction = CurvedAnimation(
      parent: _reactionController,
      curve: Curves.ease,
    );
  }

  @override
  void didUpdateWidget(TDCupertinoSwitch oldWidget) {
    super.didUpdateWidget(oldWidget);
    _drag.dragStartBehavior = widget.dragStartBehavior;

    if (needsPositionAnimation || oldWidget.value != widget.value) {
      _resumePositionAnimation(isLinear: needsPositionAnimation);
    }
  }

  // `isLinear` must be true if the position animation is trying to move the
  // thumb to the closest end after the most recent drag animation, so the curve
  // does not change when the controller's value is not 0 or 1.
  //
  // It can be set to false when it's an implicit animation triggered by
  // widget.value changes.
  void _resumePositionAnimation({bool isLinear = true}) {
    needsPositionAnimation = false;
    position
      ..curve = isLinear ? Curves.linear : Curves.ease
      ..reverseCurve = isLinear ? Curves.linear : Curves.ease.flipped;
    if (widget.value) {
      _positionController.forward();
    } else {
      _positionController.reverse();
    }
  }

  void _handleTapDown(TapDownDetails details) {
    if (isInteractive) {
      needsPositionAnimation = false;
    }
    _reactionController.forward();
  }

  void _handleTap() {
    if (isInteractive) {
      widget.onChanged!(!widget.value);
      _emitVibration();
    }
  }

  void _handleTapUp(TapUpDetails details) {
    if (isInteractive) {
      needsPositionAnimation = false;
      _reactionController.reverse();
    }
  }

  void _handleTapCancel() {
    if (isInteractive) {
      _reactionController.reverse();
    }
  }

  void _handleDragStart(DragStartDetails details) {
    if (isInteractive) {
      needsPositionAnimation = false;
      _reactionController.forward();
      _emitVibration();
    }
  }

  void _handleDragUpdate(DragUpdateDetails details) {
    if (isInteractive) {
      position
        ..curve = Curves.linear
        ..reverseCurve = Curves.linear;
      final delta = details.primaryDelta! / _kTrackInnerLength;
      switch (Directionality.of(context)) {
        case TextDirection.rtl:
          _positionController.value -= delta;
          break;
        case TextDirection.ltr:
          _positionController.value += delta;
          break;
      }
    }
  }

  void _handleDragEnd(DragEndDetails details) {
    // Deferring the animation to the next build phase.
    setState(() {
      needsPositionAnimation = true;
    });
    // Call onChanged when the user's intent to change value is clear.
    if (position.value >= 0.5 != widget.value) {
      widget.onChanged!(!widget.value);
    }
    _reactionController.reverse();
  }

  void _emitVibration() {
    switch (defaultTargetPlatform) {
      case TargetPlatform.iOS:
        HapticFeedback.lightImpact();
        break;
      default:
        break;
    }
  }

  @override
  Widget build(BuildContext context) {
    if (needsPositionAnimation) {
      _resumePositionAnimation();
    }
    return MouseRegion(
      cursor: isInteractive && kIsWeb
          ? SystemMouseCursors.click
          : MouseCursor.defer,
      child: Opacity(
        opacity:
            widget.onChanged == null ? _kTDCupertinoSwitchDisabledOpacity : 1.0,
        child: _TDCupertinoSwitchRenderObjectWidget(
          value: widget.value,
          activeColor: CupertinoDynamicColor.resolve(
            widget.activeColor ?? CupertinoColors.systemGreen,
            context,
          ),
          trackColor: CupertinoDynamicColor.resolve(
              widget.trackColor ?? CupertinoColors.secondarySystemFill,
              context),
          thumbColor: CupertinoDynamicColor.resolve(
              widget.thumbColor ?? CupertinoColors.white, context),
          onChanged: widget.onChanged,
          textDirection: Directionality.of(context),
          state: this,
          child: widget.thumbView,
        ),
      ),
    );
  }

  @override
  void dispose() {
    _tap.dispose();
    _drag.dispose();

    _positionController.dispose();
    _reactionController.dispose();
    super.dispose();
  }
}

class _TDCupertinoSwitchRenderObjectWidget
    extends SingleChildRenderObjectWidget {
  const _TDCupertinoSwitchRenderObjectWidget({
    required this.child,
    required this.value,
    required this.activeColor,
    required this.trackColor,
    required this.thumbColor,
    required this.onChanged,
    required this.textDirection,
    required this.state,
  });

  final bool value;
  final Color activeColor;
  final Color trackColor;
  final Color thumbColor;
  final ValueChanged<bool>? onChanged;
  final _TDCupertinoSwitchState state;
  final TextDirection textDirection;

  /// The widget below this widget in the tree.
  ///
  /// {@macro flutter.widgets.ProxyWidget.child}
  @override
  final Widget? child;

  @override
  _RenderTDCupertinoSwitch createRenderObject(BuildContext context) {
    return _RenderTDCupertinoSwitch(
      value: value,
      activeColor: activeColor,
      trackColor: trackColor,
      thumbColor: thumbColor,
      onChanged: onChanged,
      textDirection: textDirection,
      state: state,
    );
  }

  @override
  void updateRenderObject(
      BuildContext context, _RenderTDCupertinoSwitch renderObject) {
    renderObject
      ..value = value
      ..activeColor = activeColor
      ..trackColor = trackColor
      ..thumbColor = thumbColor
      ..onChanged = onChanged
      ..textDirection = textDirection;
  }
}

const double _kTrackWidth = 45.0;
const double _kTrackHeight = 28.0;
const double _kTrackRadius = _kTrackHeight / 2.0;
const double _kTrackInnerStart = _kTrackHeight / 2.0;
const double _kTrackInnerEnd = _kTrackWidth - _kTrackInnerStart;
const double _kTrackInnerLength = _kTrackInnerEnd - _kTrackInnerStart;
const double _kSwitchWidth = 45.0;
const double _kSwitchHeight = 28.0;
// Opacity of a disabled switch, as eye-balled from iOS Simulator on Mac.
const double _kTDCupertinoSwitchDisabledOpacity = 0.5;
// In the normal switch case, the closed thumb is smaller than the open thumb.
// Same size as if there is a child widget on thumb.
const double _kSwitchOnThumbRadius = 11;
const double _kSwitchOffThumbRadius = 8;
const double _kSwitchOnThumbMargin = (_kTrackHeight - _kSwitchOnThumbRadius * 2) / 2;
const double _kSwitchOffThumbMargin = (_kTrackHeight - _kSwitchOffThumbRadius * 2) / 2;

const Duration _kReactionDuration = Duration(milliseconds: 300);
const Duration _kToggleDuration = Duration(milliseconds: 200);

class _RenderTDCupertinoSwitch extends RenderConstrainedBox {
  _RenderTDCupertinoSwitch({
    required bool value,
    required Color activeColor,
    required Color trackColor,
    required Color thumbColor,
    ValueChanged<bool>? onChanged,
    required TextDirection textDirection,
    required _TDCupertinoSwitchState state,
  })  : _value = value,
        _activeColor = activeColor,
        _trackColor = trackColor,
        _thumbPainter = CupertinoThumbPainter.switchThumb(color: thumbColor),
        _onChanged = onChanged,
        _textDirection = textDirection,
        _state = state,
        super(
            additionalConstraints: const BoxConstraints.tightFor(
                width: _kSwitchWidth, height: _kSwitchHeight)) {
    state.position.addListener(markNeedsPaint);
    state._reaction.addListener(markNeedsPaint);
  }

  final _TDCupertinoSwitchState _state;

  bool get value => _value;
  bool _value;

  set value(bool value) {
    if (value == _value) {
      return;
    }
    _value = value;
    markNeedsSemanticsUpdate();
  }

  Color get activeColor => _activeColor;
  Color _activeColor;

  set activeColor(Color value) {
    if (value == _activeColor) {
      return;
    }
    _activeColor = value;
    markNeedsPaint();
  }

  Color get trackColor => _trackColor;
  Color _trackColor;

  set trackColor(Color value) {
    if (value == _trackColor) {
      return;
    }
    _trackColor = value;
    markNeedsPaint();
  }

  Color get thumbColor => _thumbPainter.color;
  CupertinoThumbPainter _thumbPainter;

  set thumbColor(Color value) {
    if (value == thumbColor) {
      return;
    }
    _thumbPainter = CupertinoThumbPainter.switchThumb(color: value);
    markNeedsPaint();
  }

  ValueChanged<bool>? get onChanged => _onChanged;
  ValueChanged<bool>? _onChanged;

  set onChanged(ValueChanged<bool>? value) {
    if (value == _onChanged) {
      return;
    }
    final wasInteractive = isInteractive;
    _onChanged = value;
    if (wasInteractive != isInteractive) {
      markNeedsPaint();
      markNeedsSemanticsUpdate();
    }
  }

  TextDirection get textDirection => _textDirection;
  TextDirection _textDirection;

  set textDirection(TextDirection value) {
    if (_textDirection == value) {
      return;
    }
    _textDirection = value;
    markNeedsPaint();
  }

  bool get isInteractive => onChanged != null;

  double get thumbRadius {
    if (child == null) {
      final value = _state.position.value;
      return (_kSwitchOnThumbRadius - _kSwitchOffThumbRadius) * value + _kSwitchOffThumbRadius;
    }
    return _kSwitchOnThumbRadius;
  }

  double get thumbMargin {
    if (child == null) {
      return value ? _kSwitchOffThumbMargin : _kSwitchOffThumbMargin;
    }
    return _kSwitchOnThumbMargin;
  }

  @override
  bool hitTestSelf(Offset position) => true;

  @override
  void handleEvent(PointerEvent event, BoxHitTestEntry entry) {
    assert(debugHandleEvent(event, entry));
    if (event is PointerDownEvent && isInteractive) {
      _state._drag.addPointer(event);
      _state._tap.addPointer(event);
    }
  }

  @override
  void describeSemanticsConfiguration(SemanticsConfiguration config) {
    super.describeSemanticsConfiguration(config);

    if (isInteractive) {
      config.onTap = _state._handleTap;
    }

    config.isEnabled = isInteractive;
    config.isToggled = _value;
  }

  @override
  void paint(PaintingContext context, Offset offset) {
    final canvas = context.canvas;

    final currentValue = _state.position.value;
    final currentReactionValue = _state._reaction.value;

    final double visualPosition;
    switch (textDirection) {
      case TextDirection.rtl:
        visualPosition = 1.0 - currentValue;
        break;
      case TextDirection.ltr:
        visualPosition = currentValue;
        break;
    }

    final paint = Paint()
      ..color = Color.lerp(trackColor, activeColor, currentValue)!;

    final trackRect = Rect.fromLTWH(
      offset.dx + (size.width - _kTrackWidth) / 2.0,
      offset.dy + (size.height - _kTrackHeight) / 2.0,
      _kTrackWidth,
      _kTrackHeight,
    );
    final trackRRect = RRect.fromRectAndRadius(
        trackRect, const Radius.circular(_kTrackRadius));
    canvas.drawRRect(trackRRect, paint);

    final currentThumbExtension =
        CupertinoThumbPainter.extension * currentReactionValue;
    final thumbLeft = lerpDouble(
      trackRect.left + _kTrackInnerStart - thumbRadius,
      trackRect.left + _kTrackInnerEnd - thumbRadius - currentThumbExtension,
      visualPosition,
    )!;
    final thumbRight = lerpDouble(
      trackRect.left + _kTrackInnerStart + thumbRadius + currentThumbExtension,
      trackRect.left + _kTrackInnerEnd + thumbRadius,
      visualPosition,
    )!;
    final thumbCenterY = offset.dy + size.height / 2.0;
    final thumbBounds = Rect.fromLTRB(
      thumbLeft,
      thumbCenterY - thumbRadius,
      thumbRight,
      thumbCenterY + thumbRadius,
    );

    _clipRRectLayer.layer = context
        .pushClipRRect(needsCompositing, Offset.zero, thumbBounds, trackRRect,
            (PaintingContext innerContext, Offset offset) {
      _thumbPainter.paint(innerContext.canvas, thumbBounds);
    }, oldLayer: _clipRRectLayer.layer);
    if (child != null) {
      context.paintChild(child!, Offset(thumbBounds.left + thumbMargin, 0));
    }
  }

  final LayerHandle<ClipRRectLayer> _clipRRectLayer =
      LayerHandle<ClipRRectLayer>();

  @override
  void dispose() {
    _clipRRectLayer.layer = null;
    super.dispose();
  }

  @override
  void debugFillProperties(DiagnosticPropertiesBuilder description) {
    super.debugFillProperties(description);
    description.add(FlagProperty('value',
        value: value, ifTrue: 'checked', ifFalse: 'unchecked', showName: true));
    description.add(FlagProperty('isInteractive',
        value: isInteractive,
        ifTrue: 'enabled',
        ifFalse: 'disabled',
        showName: true,
        defaultValue: true));
  }
}

Switch.switch:
import 'package:flutter/cupertino.dart';
import 'package:flutter/material.dart';
import 'package:flutter/widgets.dart';

import '../../../tdesign_flutter.dart';
import '../../util/context_extension.dart';
import 'td_cupertino_switch.dart';

/// 开关改变事件处理
typedef OnSwitchChanged = bool Function(bool value);

enum TDSwitchSize { large, medium, small }

enum TDSwitchType { fill, text, loading, icon }

class TDSwitch extends StatefulWidget {
  const TDSwitch({
    Key? key,
    this.enable = true,
    this.isOn = false,
    this.size = TDSwitchSize.medium,
    this.type = TDSwitchType.fill,
    this.trackOnColor,
    this.trackOffColor,
    this.thumbContentOnColor,
    this.thumbContentOffColor,
    this.thumbContentOnFont,
    this.thumbContentOffFont,
    this.onChanged,
    this.openText,
    this.closeText,
  }) : super(key: key);

  /// 是否可点击
  final bool enable;

  /// 是否打开
  final bool isOn;

  /// 开启时轨道颜色
  final Color? trackOnColor;

  /// 关闭时轨道颜色
  final Color? trackOffColor;

  /// 开启时ThumbView的颜色
  final Color? thumbContentOnColor;

  /// 关闭时ThumbView的颜色
  final Color? thumbContentOffColor;

  /// 开启时ThumbView的字体样式
  final TextStyle? thumbContentOnFont;

  /// 关闭时ThumbView的字体样式
  final TextStyle? thumbContentOffFont;

  /// 尺寸：大、中、小
  final TDSwitchSize? size;

  /// 类型：填充、文本、加载
  final TDSwitchType? type;

  /// 改变事件
  final OnSwitchChanged? onChanged;

  /// 打开文案
  final String? openText;

  /// 关闭文案
  final String? closeText;

  @override
  State<StatefulWidget> createState() {
    return TDSwitchState();
  }
}

class TDSwitchState extends State<TDSwitch> {
  bool isOn = false;

  @override
  void initState() {
    super.initState();
    isOn = widget.isOn;
  }

  @override
  void didUpdateWidget(covariant TDSwitch oldWidget) {
    super.didUpdateWidget(oldWidget);
    isOn = widget.isOn;
  }

  @override
  Widget build(BuildContext context) {
    final theme = TDTheme.of(context);
    final switchEnable = widget.enable && widget.type != TDSwitchType.loading;
    final trackOnColor = widget.trackOnColor ?? theme.brandNormalColor;
    final trackOffColor = widget.trackOffColor ?? theme.textDisabledColor;
    final thumbContentOnColor =
        widget.thumbContentOnColor ?? theme.brandNormalColor;
    final thumbContentOffColor =
        widget.thumbContentOffColor ?? theme.textDisabledColor;
    final thumbContentOnFont =
        widget.thumbContentOnFont ?? const TextStyle(fontSize: 14);
    final thumbContentOffFont =
        widget.thumbContentOffFont ?? const TextStyle(fontSize: 14);
    Widget current = TDCupertinoSwitch(
      value: isOn,
      activeColor: trackOnColor,
      trackColor: trackOffColor,
      onChanged: (value) {
        var process = widget.onChanged?.call(value) ?? false;
        // 如果外部未处理，才需要自定刷新开关，如果已处理则不需要刷新
        if (!process) {
          isOn = value;
          setState(() {});
        }
      },
      thumbView: _getThumbView(
        thumbContentOnColor,
        thumbContentOffColor,
        thumbContentOnFont,
        thumbContentOffFont,
      ),
    );
    if (!switchEnable) {
      current = Opacity(
        opacity: 0.4,
        child: IgnorePointer(
          ignoring: !switchEnable,
          child: current,
        ),
      );
    }
    return SizedBox(
      width: _getWidth(),
      height: _getHeight(),
      child: FittedBox(
        child: current,
      ),
    );
    // return ConstrainedBox( _getWidth(), height: _getHeight(), child: current);
  }

  double _getWidth() {
    switch (widget.size) {
      case TDSwitchSize.large:
        return 52;
      case TDSwitchSize.medium:
        return 45;
      case TDSwitchSize.small:
        return 39;
      default:
        return 45;
    }
  }

  double _getHeight() {
    switch (widget.size) {
      case TDSwitchSize.large:
        return 32;
      case TDSwitchSize.medium:
        return 28;
      case TDSwitchSize.small:
        return 24;
      default:
        return 28;
    }
  }

  Widget? _getThumbView(
    Color thumbContentOnColor,
    Color thumbContentOffColor,
    TextStyle thumbContentOnFont,
    TextStyle thumbContentOffFont,
  ) {
    switch (widget.type) {
      case TDSwitchType.text:
        return Stack(
          children: [
            Container(
              alignment: Alignment.center,
              width: 16,
              child: TDText(
                isOn
                    ? (widget.openText ?? context.resource.open)
                    : (widget.closeText ?? context.resource.close),
                textColor: isOn ? thumbContentOnColor : thumbContentOffColor,
                forceVerticalCenter: true,
                maxLines: 1,
                style: isOn ? thumbContentOnFont : thumbContentOffFont,
              ),
            )
          ],
        );
      case TDSwitchType.loading:
        return Container(
          alignment: Alignment.centerLeft,
          child: TDCircleIndicator(
            color: thumbContentOnColor,
            size: 16,
            lineWidth: 3,
          ),
        );
      case TDSwitchType.icon:
        return Container(
          alignment: Alignment.centerLeft,
          child: Icon(
            isOn ? TDIcons.check : TDIcons.close,
            size: 16,
            color: isOn ? thumbContentOnColor : thumbContentOffColor,
          ),
        );
      case TDSwitchType.fill:
      default:
        return null;
    }
  }
}

Tabbar.bottom_tab_bar:
import 'package:flutter/material.dart';

import '../../../tdesign_flutter.dart';

/// 展开项 向下箭头宽
const double _kArrowWidth = 13.5;

/// 展开项  向下箭头高
const double _kArrowHeight = 8;

/// 展开项选项弹窗 单个item最低高度
const double _kMenuItemMinHeight = 23;

/// 展开项弹窗 单个item默认高度
const double _kDefaultMenuItemHeight = 48;

/// 展开项弹窗 单个item默认宽度为按钮宽度-20
const double _kDefaultMenuItemWidthShrink = 20;

/// 导航栏默认高度
const double _kDefaultTabBarHeight = 56;

/// 展开项弹窗弹出动画时间
const Duration _kPopupMenuDuration = Duration(milliseconds: 10);

enum TDBottomTabBarBasicType {
  /// 单层级纯文本标签栏
  text,

  /// 文本加图标标签栏
  iconText,

  /// 纯图标标签栏
  icon,

  /// 双层级纯文本标签栏
  expansionPanel,
}

enum TDBottomTabBarComponentType {
  /// 普通样式
  normal,

  /// 带胶囊背景的item选中样式
  label
}

enum TDBottomTabBarOutlineType {
  /// 填充样式
  filled,

  /// 胶囊样式
  capsule
}

/// 飘新配置
class BadgeConfig {
  BadgeConfig({
    required this.showBadge,
    TDBadge? tdBadge,
    this.badgeTopOffset,
    this.badgeRightOffset,
  }) : tdBadge = tdBadge ?? const TDBadge(TDBadgeType.redPoint);

  /// 是否展示消息
  final bool showBadge;

  /// 消息样式（未设置但 showBadge 为 true，则默认使用红点）
  final TDBadge? tdBadge;

  /// 消息顶部偏移量
  final double? badgeTopOffset;

  /// 消息右侧偏移量
  final double? badgeRightOffset;
}

/// 单个 tab 配置
class TDBottomTabBarTabConfig {
  TDBottomTabBarTabConfig(
      {required this.onTap,
      this.selectedIcon,
      this.unselectedIcon,
      this.tabText,
      this.selectTabTextStyle,
      this.unselectTabTextStyle,
      this.badgeConfig,
      this.popUpButtonConfig,
      this.onLongPress,
      this.allowMultipleTaps = false})
      : assert(() {
          if (badgeConfig?.showBadge ?? false) {
            if (badgeConfig?.tdBadge == null) {
              throw FlutterError('[NavigationTab] if set showBadge = true, '
                  'you must set a tdBadge instance');
            }
          }
          return true;
        }());

  /// 选中时图标
  final Widget? selectedIcon;

  /// 未选中时图标
  final Widget? unselectedIcon;

  /// tab 文本
  final String? tabText;

  /// 文本已选择样式 basicType为text时必填
  final TextStyle? selectTabTextStyle;

  /// 文本未选择样式 basicType为text时必填
  final TextStyle? unselectTabTextStyle;

  /// tab点击事件
  final GestureTapCallback? onTap;

  /// 消息配置
  final BadgeConfig? badgeConfig;

  /// 弹窗配置
  final TDBottomTabBarPopUpBtnConfig? popUpButtonConfig;

  /// onTap 方法允许点击多次
  final bool allowMultipleTaps;

  /// 长按事件
  final GestureLongPressCallback? onLongPress;
}

class TDBottomTabBar extends StatefulWidget {
  TDBottomTabBar(
    this.basicType, {
    Key? key,
    this.componentType = TDBottomTabBarComponentType.label,
    this.outlineType = TDBottomTabBarOutlineType.filled,
    required this.navigationTabs,
    this.barHeight = _kDefaultTabBarHeight,
    this.useVerticalDivider,
    this.dividerHeight,
    this.dividerThickness,
    this.dividerColor,
    this.showTopBorder = true,
    this.topBorder,
    this.useSafeArea = true,
    this.selectedBgColor,
    this.unselectedBgColor,
    this.backgroundColor,
    this.centerDistance,
    this.currentIndex,
    this.needInkWell = false,
  })  : assert(() {
          if (navigationTabs.isEmpty) {
            throw FlutterError('[TDBottomTabBar] please set at least one tab!');
          }
          if (basicType == TDBottomTabBarBasicType.text) {
            for (final item in navigationTabs) {
              if (item.tabText == null) {
                throw FlutterError(
                    '[TDBottomTabBar] type is TDBottomBarType.text, but not set tabText.');
              }
            }
          }
          if (basicType == TDBottomTabBarBasicType.icon) {
            for (final item in navigationTabs) {
              if (item.selectedIcon == null || item.unselectedIcon == null) {
                throw FlutterError(
                    '[TDBottomTabBar] type is TDBottomBarType.icon,'
                    'but has no set icon.');
              }
            }
          }
          if (basicType == TDBottomTabBarBasicType.iconText) {
            for (final item in navigationTabs) {
              if (item.tabText == null ||
                  item.selectedIcon == null ||
                  item.unselectedIcon == null) {
                throw FlutterError(
                    '[TDBottomTabBar] type is TDBottomBarType.iconText,'
                    'but not set tabText or icon.');
              }
            }
          }
          if (currentIndex != null &&
              (currentIndex < 0 || currentIndex >= navigationTabs.length)) {
            throw FlutterError(
                '[TDBottomTabBar] currentIndex must in [0,navigationTabs.length)');
          }
          return true;
        }()),
        super(key: key);

  /// 基本样式（纯文本、纯图标、图标+文本）
  final TDBottomTabBarBasicType basicType;

  /// 选项样式 默认label
  final TDBottomTabBarComponentType? componentType;

  /// 标签栏样式 默认filled
  final TDBottomTabBarOutlineType? outlineType;

  /// tabs配置
  final List<TDBottomTabBarTabConfig> navigationTabs;

  /// tab高度
  final double? barHeight;

  /// 是否使用竖线分隔（如果选项样式为 label，则强制为 false）
  final bool? useVerticalDivider;

  /// 分割线高度（可选）
  final double? dividerHeight;

  /// 分割线厚度（可选）
  final double? dividerThickness;

  /// 分割线颜色（可选）
  final Color? dividerColor;

  /// 是否展示bar上边线（设置为true 但是topBorder样式未设置，则使用默认值，非胶囊型才生效）
  final bool? showTopBorder;

  /// 上边线样式
  final BorderSide? topBorder;

  /// 使用安全区域
  final bool useSafeArea;

  /// 选中时背景颜色
  final Color? selectedBgColor;

  /// 未选中时背景颜色
  final Color? unselectedBgColor;

  /// 背景颜色 （可选）
  final Color? backgroundColor;

  /// icon与文本中间距离（可选）
  final double? centerDistance;

  /// 选中的index（可选）
  final int? currentIndex;

  /// 是否需要水波纹效果
  final bool needInkWell;

  @override
  State<TDBottomTabBar> createState() => _TDBottomTabBarState();
}

class _TDBottomTabBarState extends State<TDBottomTabBar> {
  int _selectedIndex = 0;

  @override
  void initState() {
    super.initState();
    _selectedIndex = widget.currentIndex ?? 0;
  }

  @override
  void didUpdateWidget(covariant TDBottomTabBar oldWidget) {
    super.didUpdateWidget(oldWidget);
    _selectedIndex = widget.currentIndex ?? _selectedIndex;
  }

  @override
  Widget build(BuildContext context) {
    var isCapsuleOutlineType =
        widget.outlineType == TDBottomTabBarOutlineType.capsule;
    return LayoutBuilder(
      builder: (BuildContext context, BoxConstraints constraints) {
        /// -2 是为了增加边框
        var maxWidth =
            double.parse(constraints.biggest.width.toStringAsFixed(1)) - 2;

        /// 胶囊样式 比正常样式宽度要小32
        if (isCapsuleOutlineType) {
          maxWidth -= 32;
        }
        var itemWidth = maxWidth / widget.navigationTabs.length;

        Widget result = Container(
            height: widget.barHeight ?? _kDefaultTabBarHeight,
            alignment: Alignment.center,
            margin: isCapsuleOutlineType
                ? const EdgeInsets.symmetric(horizontal: 16)
                : null,
            decoration: BoxDecoration(
                color: widget.backgroundColor ??
                    TDTheme.of(context).bgColorContainer,
                borderRadius: isCapsuleOutlineType
                    ? BorderRadius.circular(TDTheme.of(context).radiusCircle)
                    : null,
                border: widget.showTopBorder! && !isCapsuleOutlineType
                    ? Border(
                        top: widget.topBorder ??
                            BorderSide(
                                color: TDTheme.of(context).componentStrokeColor,
                                width: 0.5))
                    : null,
                boxShadow: isCapsuleOutlineType
                    ? TDTheme.of(context).shadowsTop
                    : null),
            child: Stack(alignment: Alignment.center, children: [
              Row(
                  mainAxisAlignment: MainAxisAlignment.spaceEvenly,
                  children:
                      List.generate(widget.navigationTabs.length, (index) {
                    return _item(index, itemWidth);
                  })),
              _verticalDivider(),
            ]));
        if (widget.useSafeArea) {
          result = SafeArea(child: result);
        }
        return result;
      },
    );
  }

  void _onTap(int index) {
    setState(() {
      if (_selectedIndex != index ||
          widget.navigationTabs[index].allowMultipleTaps) {
        widget.navigationTabs[index].onTap?.call();
      }
      if (_selectedIndex != index) {
        _selectedIndex = index;
      }
    });
  }

  Widget _item(int index, double itemWidth) {
    var tabItemConfig = widget.navigationTabs[index];
    return Container(
        height: widget.barHeight ?? _kDefaultTabBarHeight,
        width: itemWidth,
        alignment: Alignment.center,
        padding: EdgeInsets.only(
            top: 7,
            bottom:
                widget.basicType == TDBottomTabBarBasicType.iconText ? 5 : 7),
        child: TDBottomTabBarItemWithBadge(
          basicType: widget.basicType,
          componentType:
              widget.componentType ?? TDBottomTabBarComponentType.label,
          outlineType: widget.outlineType ?? TDBottomTabBarOutlineType.filled,
          itemConfig: tabItemConfig,
          isSelected: index == _selectedIndex,
          itemHeight: widget.barHeight ?? _kDefaultTabBarHeight,
          itemWidth: itemWidth,
          tabsLength: widget.navigationTabs.length,
          selectedBgColor: widget.selectedBgColor,
          unselectedBgColor: widget.unselectedBgColor,
          centerDistance: widget.centerDistance ?? 0,
          needInkWell: widget.needInkWell,
          onTap: () {
            _onTap(index);
          },
          onLongPress: () {
            tabItemConfig.onLongPress?.call();
          },
        ));
  }

  Widget _verticalDivider() {
    return Visibility(
      visible: widget.componentType != TDBottomTabBarComponentType.label &&
          (widget.useVerticalDivider ?? false),
      child: Row(
        mainAxisAlignment: MainAxisAlignment.spaceEvenly,
        children: List.generate(widget.navigationTabs.length - 1, (index) {
          return SizedBox(
            width: widget.dividerThickness ?? 0.5,
            height: widget.dividerHeight ?? 32,
            child: VerticalDivider(
              color: widget.dividerColor ??
                  TDTheme.of(context).componentStrokeColor,
              thickness: widget.dividerThickness ?? 0.5,
            ),
          );
        }),
      ),
    );
  }
}

class TDBottomTabBarItemWithBadge extends StatelessWidget {
  const TDBottomTabBarItemWithBadge({
    Key? key,
    required this.basicType,
    required this.componentType,
    required this.outlineType,
    required this.itemConfig,
    required this.isSelected,
    required this.itemHeight,
    required this.itemWidth,
    required this.onTap,
    required this.tabsLength,
    required this.selectedBgColor,
    required this.unselectedBgColor,
    required this.centerDistance,
    this.onLongPress,
    this.needInkWell = false,
  }) : super(key: key);

  /// tab基本类型
  final TDBottomTabBarBasicType basicType;

  /// tab选中背景类型
  final TDBottomTabBarComponentType componentType;

  //
  final TDBottomTabBarOutlineType outlineType;

  /// 单个tab的属性配置
  final TDBottomTabBarTabConfig itemConfig;

  /// 选中状态
  final bool isSelected;

  /// tab高度
  final double itemHeight;

  /// tab宽度
  final double itemWidth;

  /// 点击事件
  final GestureTapCallback onTap;

  /// tab总个数
  final int tabsLength;

  /// 选中时背景颜色
  final Color? selectedBgColor;

  /// 未选中时背景颜色
  final Color? unselectedBgColor;

  /// icon与文本中间距离
  final double centerDistance;

  /// 长按事件
  final GestureLongPressCallback? onLongPress;

  /// 是否需要水波纹效果
  final bool needInkWell;

  @override
  Widget build(BuildContext context) {
    return GestureDetector(
      behavior: HitTestBehavior.opaque,
      onTap: () => handleTap(context),
      onLongPress: () {
        onLongPress?.call();
      },
      child: Container(
        height: itemHeight,
        alignment: Alignment.center,
        child: Stack(
          alignment: Alignment.center,
          clipBehavior: Clip.none,
          children: [
            if (isSelected || unselectedBgColor != null)
              Visibility(
                visible: componentType == TDBottomTabBarComponentType.label,
                child: Container(
                  /// 设计稿上 tab个数大于3时，左右边距为8，小于等于3时，左右边距为12
                  width: itemWidth - (tabsLength > 3 ? 16 : 24),
                  height: basicType == TDBottomTabBarBasicType.text ||
                          basicType == TDBottomTabBarBasicType.expansionPanel
                      ? 32
                      : null,
                  decoration: BoxDecoration(
                    color: isSelected
                        ? selectedBgColor ?? TDTheme.of(context).brandLightColor
                        : unselectedBgColor,
                    borderRadius: const BorderRadius.all(Radius.circular(24)),
                  ),
                ),
              ),
            _buildItem(context),
          ],
        ),
      ),
    );
  }

  Widget _badge(BadgeConfig? badgeConfig) {
    if (badgeConfig?.showBadge ?? false) {
      if (badgeConfig?.tdBadge != null) {
        return badgeConfig!.tdBadge!;
      }
    }
    return Container();
  }

  Widget _constructItem(
      BuildContext context, BadgeConfig? badgeConfig, bool isInOrOutCapsule) {
    Widget child = Container();
    if (basicType == TDBottomTabBarBasicType.text) {
      child = _textItem(context, itemConfig, isSelected,
          TDTheme.of(context).fontTitleMedium!);
    }
    if (basicType == TDBottomTabBarBasicType.expansionPanel) {
      if (itemConfig.popUpButtonConfig != null) {
        child = Row(
          mainAxisSize: MainAxisSize.min,
          children: [
            Icon(
              TDIcons.view_list,
              size: 16.0,
              color: isSelected
                  ? TDTheme.of(context).brandNormalColor
                  : TDTheme.of(context).textColorPrimary,
            ),
            const SizedBox(width: 5),
            _textItem(context, itemConfig, isSelected,
                TDTheme.of(context).fontTitleMedium!)
          ],
        );
      } else {
        child = _textItem(context, itemConfig, isSelected,
            TDTheme.of(context).fontTitleMedium!);
      }
    }
    if (basicType == TDBottomTabBarBasicType.icon) {
      var selectedIcon = itemConfig.selectedIcon;
      var unSelectedIcon = itemConfig.unselectedIcon;
      child = isSelected ? selectedIcon! : unSelectedIcon!;
    }

    if (basicType == TDBottomTabBarBasicType.iconText) {
      var selectedIcon = itemConfig.selectedIcon;
      var unSelectedIcon = itemConfig.unselectedIcon;
      child = Column(
        mainAxisAlignment: MainAxisAlignment.center,
        crossAxisAlignment: CrossAxisAlignment.center,
        children: [
          isSelected ? selectedIcon! : unSelectedIcon!,
          if (centerDistance > 0)
            SizedBox(
              height: centerDistance,
            ),
          itemConfig.tabText?.isNotEmpty ?? false
              ? _textItem(
                  context,
                  itemConfig,
                  isSelected,
                  TDTheme.of(context).fontBodyExtraSmall!,
                )
              : Container()
        ],
      );
    }

    var top = badgeConfig?.badgeTopOffset ?? -2;
    var right = badgeConfig?.badgeRightOffset ?? -10;
    return Stack(
      clipBehavior: Clip.none,
      children: [
        child,
        Visibility(
            visible: badgeConfig?.showBadge ?? false,
            child:
                Positioned(top: top, right: right, child: _badge(badgeConfig))),
      ],
    );
  }

  Widget _textItem(BuildContext context, TDBottomTabBarTabConfig config,
      bool isSelected, Font font) {
    return TDText(
      config.tabText,
      font: font,
      fontWeight: isSelected ? FontWeight.bold : FontWeight.normal,
      style:
          isSelected ? config.selectTabTextStyle : config.unselectTabTextStyle,
      textColor: isSelected
          ? TDTheme.of(context).brandNormalColor
          : TDTheme.of(context).textColorPrimary,
      forceVerticalCenter: true,
    );
  }

  _buildItem(BuildContext context) {
    var badgeConfig = itemConfig.badgeConfig;
    var isInOrOutCapsule = componentType == TDBottomTabBarComponentType.label ||
        outlineType == TDBottomTabBarOutlineType.capsule;

    var child = Container(
      alignment: Alignment.center,
      padding: EdgeInsets.only(
        top: isInOrOutCapsule ? 3.0 : 2.0,
        bottom: isInOrOutCapsule
            ? (basicType == TDBottomTabBarBasicType.iconText ? 0.0 : 1.0)
            : 0.0,
      ),
      color: Colors.transparent,
      child: _constructItem(context, badgeConfig, isInOrOutCapsule),
    );

    if (!needInkWell) {
      return child;
    }
    return Material(
      color: Colors.transparent,
      borderRadius: isInOrOutCapsule ? BorderRadius.circular(24) : null,
      child: InkWell(
        borderRadius: isInOrOutCapsule ? BorderRadius.circular(24) : null,
        splashFactory: InkRipple.splashFactory,
        splashColor: selectedBgColor ?? TDTheme.of(context).brandLightColor,
        highlightColor: selectedBgColor ?? TDTheme.of(context).brandLightColor,
        onTap: () => handleTap(context),
        child: child,
      ),
    );
  }

  void handleTap(BuildContext context) {
    onTap.call();

    var popUpButtonConfig = itemConfig.popUpButtonConfig;
    if (popUpButtonConfig != null) {
      Navigator.push(
          context,
          PopRoute(
            child: PopupDialog(
              itemWidth - _kDefaultMenuItemWidthShrink,
              btnContext: context,
              config: popUpButtonConfig.popUpDialogConfig,
              items: popUpButtonConfig.items,
              onClickMenu: (value) {
                popUpButtonConfig.onChanged(value);
              },
            ),
          ));
    }
  }
}

/// 展开项配置
class TDBottomTabBarPopUpBtnConfig {
  TDBottomTabBarPopUpBtnConfig(
      {required this.items, required this.onChanged, this.popUpDialogConfig})
      : assert(() {
          if (popUpDialogConfig != null) {
            if ((popUpDialogConfig.arrowHeight != null &&
                    popUpDialogConfig.arrowHeight! <= 0.0) ||
                (popUpDialogConfig.arrowWidth != null &&
                    popUpDialogConfig.arrowWidth! <= 0.0)) {
              throw FlutterError(
                  '[TDBottomTabBarPopUpBtnConfig] arrowHeight or arrowHeight can '
                  'not set less than or equal to zero');
            }
          }
          return true;
        }());

  /// 选项list
  final List<PopUpMenuItem> items;

  /// 统一在 onChanged 中处理各item点击事件
  final ValueChanged<String> onChanged;

  /// 弹窗UI配置
  final TDBottomTabBarPopUpShapeConfig? popUpDialogConfig;
}

/// 弹窗UI配置
class TDBottomTabBarPopUpShapeConfig {
  TDBottomTabBarPopUpShapeConfig(
      {this.popUpWidth,
      this.popUpItemHeight = _kDefaultMenuItemHeight,
      this.backgroundColor,
      this.radius,
      this.arrowWidth,
      this.arrowHeight});

  /// 弹窗宽度（不设置，默认为按钮宽度 - 20）
  final double? popUpWidth;

  /// 单个选项高度 所有选项等高 不设置则使用默认值 48
  final double? popUpItemHeight;

  /// 弹窗背景颜色
  final Color? backgroundColor;

  /// panel圆角 默认0
  final double? radius;

  /// 箭头宽度 默认13.5
  final double? arrowWidth;

  /// 箭头高度 默认8
  final double? arrowHeight;
}

/// 弹窗菜单item
class PopUpMenuItem extends StatelessWidget {
  const PopUpMenuItem({
    Key? key,
    this.itemWidget,
    required this.value,
    this.alignment = AlignmentDirectional.center,
  }) : super(key: key);

  /// 选项widget
  final Widget? itemWidget;

  /// 选项值
  final String value;

  /// 对齐方式
  final AlignmentGeometry alignment;

  @override
  Widget build(BuildContext context) {
    return Container(
      constraints: const BoxConstraints(minHeight: _kMenuItemMinHeight),
      decoration: BoxDecoration(
        color: TDTheme.of(context).bgColorContainer,
        borderRadius: BorderRadius.circular(TDTheme.of(context).radiusDefault),
      ),
      alignment: alignment,
      child: itemWidget ??
          TDText(
            value,
            style: const TextStyle(fontSize: 16, fontWeight: FontWeight.w400),
          ),
    );
  }
}

class PopRoute extends PopupRoute {
  Widget child;

  PopRoute({required this.child});

  @override
  Color? get barrierColor => Colors.transparent;

  @override
  bool get barrierDismissible => true;

  @override
  String? get barrierLabel => 'popUpMenuBarrierLabel';

  @override
  Widget buildPage(BuildContext context, Animation<double> animation,
      Animation<double> secondaryAnimation) {
    return child;
  }

  @override
  Duration get transitionDuration => _kPopupMenuDuration;
}

class PopupDialog extends StatefulWidget {
  /// 按钮context
  final BuildContext btnContext;

  /// 点击事件
  final ValueChanged<String> onClickMenu;

  /// 弹窗选项列表
  final List<PopUpMenuItem> items;

  /// 弹窗配置
  final TDBottomTabBarPopUpShapeConfig? config;

  /// 默认弹窗宽度
  final double defaultPopUpWidth;

  const PopupDialog(this.defaultPopUpWidth,
      {Key? key,
      required this.btnContext,
      required this.onClickMenu,
      required this.items,
      required this.config})
      : super(key: key);

  @override
  PopupDialogState createState() => PopupDialogState();
}

class PopupDialogState extends State<PopupDialog> {
  RenderBox? button;
  RenderBox? overlay;
  RelativeRect? position;
  Size? size;

  @override
  void initState() {
    super.initState();
    button = widget.btnContext.findRenderObject() as RenderBox;
    size = button!.size;
    overlay =
        Overlay.of(widget.btnContext)?.context.findRenderObject() as RenderBox;
    position = RelativeRect.fromRect(
      Rect.fromPoints(
        button!.localToGlobal(Offset.zero, ancestor: overlay),
        button!.localToGlobal(Offset.zero, ancestor: overlay),
      ),
      Offset.zero & overlay!.size,
    );
  }

  @override
  Widget build(BuildContext context) {
    var popUpItemHeight =
        widget.config?.popUpItemHeight ?? _kDefaultMenuItemHeight;
    var popUpItemWidth = widget.config?.popUpWidth ?? widget.defaultPopUpWidth;
    var menuItems = widget.items
        .map((e) => GestureDetector(
            behavior: HitTestBehavior.opaque,
            onTap: () {
              widget.onClickMenu(e.value);
              Navigator.of(context).pop();
            },
            child: SizedBox(
              height: popUpItemHeight,
              child: e,
            )))
        .toList();

    return Material(
      type: MaterialType.transparency,
      child: GestureDetector(
        onTap: () => Navigator.of(context).pop(),
        child: Stack(
          children: <Widget>[
            Container(
              width: MediaQuery.of(context).size.width,
              height: MediaQuery.of(context).size.height,
              color: Colors.transparent,
            ),
            Positioned(

                /// 这里 -8 是因为widget.btnContext是TDBottomTabBarItemWithBadge的，它在父widget内有8dp的padding
                /// -4 是设计稿上箭头和tab有4dp的距离
                top: position!.top -
                    (popUpItemHeight * widget.items.length +
                        (widget.config?.arrowHeight ?? _kArrowHeight)) -
                    8 -
                    4,
                right: position!.right - (popUpItemWidth + size!.width) / 2,
                child: Container(
                  width: popUpItemWidth,
                  height: popUpItemHeight * widget.items.length +
                      (widget.config?.arrowHeight ?? _kArrowHeight),
                  decoration:
                      BoxDecoration(boxShadow: TDTheme.of(context).shadowsTop),
                  child: CustomPaint(
                    painter: PanelWithDownArrow(
                        config: widget.config,
                        backgroundColor: widget.config?.backgroundColor ??
                            TDTheme.of(context).bgColorContainer),
                    child: Container(
                      alignment: Alignment.topCenter,
                      height: popUpItemHeight * widget.items.length,
                      child: Container(
                        constraints: BoxConstraints(
                            maxHeight: popUpItemHeight * widget.items.length),
                        child: Stack(
                          children: [
                            Column(children: menuItems),
                            Column(
                              mainAxisAlignment: MainAxisAlignment.spaceEvenly,
                              children: List.generate(
                                  widget.items.length - 1,
                                  (index) => Padding(
                                        padding: const EdgeInsets.symmetric(
                                            horizontal: 8.0),
                                        child: Divider(
                                          thickness: 0.5,
                                          height: 0.5,
                                          color: TDTheme.of(context)
                                              .componentStrokeColor,
                                        ),
                                      )),
                            )
                          ],
                        ),
                      ),
                    ),
                  ),
                ))
          ],
        ),
      ),
    );
  }
}

/// 带下箭头的展开panel
class PanelWithDownArrow extends CustomPainter {
  TDBottomTabBarPopUpShapeConfig? config;
  Color backgroundColor;

  PanelWithDownArrow({this.config, required this.backgroundColor});

  @override
  void paint(Canvas canvas, Size size) {
    var paint = Paint()
      ..isAntiAlias = true
      ..color = backgroundColor
      ..style = PaintingStyle.fill;
    var path = Path();
    var panelWidth = size.width;
    var panelHeight = size.height - (config?.arrowHeight ?? _kArrowHeight);

    canvas.drawRRect(
        RRect.fromRectAndRadius(Rect.fromLTWH(0, 0, panelWidth, panelHeight),
            Radius.circular(config?.radius ?? 0.0)),
        paint);

    /// 下方箭头
    if (config?.arrowWidth != 0.0 && config?.arrowHeight != 0.0) {
      var left = (panelWidth - _kArrowWidth) / 2;
      var right = (panelWidth + _kArrowWidth) / 2;
      var bottom = panelHeight + _kArrowHeight;
      if (config?.arrowWidth != null) {
        left = (panelWidth - config!.arrowWidth!) / 2;
        right = (panelWidth + config!.arrowWidth!) / 2;
      }
      if (config?.arrowHeight != null) {
        bottom = panelHeight + config!.arrowHeight!;
      }

      path.moveTo(left, panelHeight);
      path.lineTo(panelWidth / 2, bottom);
      path.lineTo(right, panelHeight);
      canvas.drawPath(path, paint);
    }
  }

  @override
  bool shouldRepaint(covariant CustomPainter oldDelegate) {
    return false;
  }
}

Table.table:
import 'dart:math' as math;
import 'package:flutter/material.dart';

import '../../../tdesign_flutter.dart';
import '../../util/context_extension.dart';

typedef OnCellTap = void Function(int rowIndex, dynamic row, TDTableCol col);
typedef OnScroll = void Function(ScrollController controller);
typedef OnSelect = void Function(List<dynamic>? data);
typedef OnRowSelect = void Function(int index, bool checked);

class TDTable extends StatefulWidget {
  const TDTable({
    super.key,
    this.bordered,
    required this.columns,
    this.data,
    this.empty,
    this.height,
    this.rowHeight,
    this.loading = false,
    this.loadingWidget,
    this.showHeader = true,
    this.footerWidget,
    this.stripe = false,
    this.backgroundColor,
    this.width,
    this.defaultSort,
    this.onCellTap,
    this.onScroll,
    this.onSelect,
    this.onRowSelect,
  });

  /// 是否显示表格边框
  final bool? bordered;

  /// 列配置
  final List<TDTableCol> columns;

  /// 数据源
  final List<dynamic>? data;

  /// 空表格呈现样式
  final TDTableEmpty? empty;

  /// 表格高度，超出后会出现滚动条
  final double? height;

  /// 行高
  final double? rowHeight;

  /// 加载中状态
  final bool? loading;

  /// 自定义加载中状态
  final Widget? loadingWidget;

  /// 是否显示表头
  final bool? showHeader;

  /// 自定义表尾
  final Widget? footerWidget;

  /// 斑马纹
  final bool? stripe;

  /// 表格背景色
  final Color? backgroundColor;

  /// 表格宽度
  final double? width;

  /// 默认排序
  final String? defaultSort;

  /// 单元格点击事件
  final OnCellTap? onCellTap;

  /// 表格滚动事件
  final OnScroll? onScroll;

  /// 选中行事件
  final OnSelect? onSelect;

  /// 行选择事件
  final OnRowSelect? onRowSelect;

  @override
  State<TDTable> createState() => TDTableState();
}

class TDTableState extends State<TDTable> {
  bool? _sortable;
  String? _sortKey;
  int _hasChecked = 0;
  int _totalSelectable = 0;
  bool _checkAll = false;
  late TDTableCol _selectableCol;
  late List<bool> _checkedList;
  final _scrollController = ScrollController();

  /// 获取单元格对齐方式
  Alignment _getVerticalAlign(TDTableColAlign x) {
    var xPos = 0.0;
    switch (x) {
      case TDTableColAlign.left:
        xPos = -1;
        break;
      case TDTableColAlign.center:
        xPos = 0;
        break;
      case TDTableColAlign.right:
        xPos = 1;
        break;
    }
    return Alignment(xPos, 0);
  }

  /// 过滤列配置
  List<TDTableCol> _getCol(TDTableColFixed fixed) {
    return widget.columns.where((col) => col.fixed == fixed).toList();
  }

  /// 生成表头
  Widget _getTableHeader(BuildContext context) {
    var fixedLeftCol = _getCol(TDTableColFixed.left);
    var fixedNonCol = _getCol(TDTableColFixed.none);
    var fixedRightCol = _getCol(TDTableColFixed.right);
    var start = 0;
    var fixedLeftCells = <Widget>[],
        cells = <Widget>[],
        fixedRightCells = <Widget>[];
    for (var i = 0; i < fixedLeftCol.length; i++) {
      var cell = _getCell(fixedLeftCol[i], true, null, start, i == 0);
      if (fixedLeftCol[i].width != null) {
        fixedLeftCells.add(SizedBox(width: fixedLeftCol[i].width, child: cell));
      } else {
        fixedLeftCells.add(Expanded(flex: 1, child: cell));
      }
      start++;
    }
    start = fixedLeftCol.length;
    for (var i = 0; i < fixedNonCol.length; i++) {
      var cell = _getCell(fixedNonCol[i], true, null, start, i == 0);
      if (fixedNonCol[i].width != null) {
        cells.add(SizedBox(width: fixedNonCol[i].width, child: cell));
      } else {
        cells.add(Expanded(flex: 1, child: cell));
      }
      start++;
    }
    for (var i = 0; i < fixedRightCol.length; i++) {
      var cell = _getCell(fixedRightCol[i], true, null, start, i == 0);
      if (fixedRightCol[i].width != null) {
        fixedRightCells
            .add(SizedBox(width: fixedRightCol[i].width, child: cell));
      } else {
        fixedRightCells.add(Expanded(flex: 1, child: cell));
      }
      start++;
    }
    return Row(children: [...fixedLeftCells, ...cells, ...fixedRightCells]);
  }

  /// 生成表格内容
  Widget _getTableContent(BuildContext context) {
    if (widget.loading ?? false) {
      return Align(
        alignment: Alignment.center,
        child: Padding(
          padding: const EdgeInsets.symmetric(vertical: 32),
          child: widget.loadingWidget ??
              const TDLoading(size: TDLoadingSize.large),
        ),
      );
    }
    if (widget.data == null || widget.data!.isEmpty) {
      return _buildEmpty();
    }
    var cells = <Widget>[];
    var fixedLeftCol = _getCol(TDTableColFixed.left);
    var fixedNonCol = _getCol(TDTableColFixed.none);
    var fixedRightCol = _getCol(TDTableColFixed.right);
    var headerCol = [...fixedLeftCol, ...fixedNonCol, ...fixedRightCol];
    for (var i = 0; i < widget.data!.length; i++) {
      var data = widget.data![i];
      var row = <Widget>[];
      for (var j = 0; j < headerCol.length; j++) {
        var cell = _getCell(
          headerCol[j],
          false,
          data,
          i,
          j == (fixedLeftCol.length - 1) ||
              j == (fixedLeftCol.length + fixedNonCol.length),
        );
        if (headerCol[j].width != null) {
          row.add(SizedBox(width: headerCol[j].width, child: cell));
        } else {
          row.add(Expanded(flex: 1, child: cell));
        }
      }
      cells.add(Container(
        color: (widget.stripe ?? false) && i % 2 == 0
            ? TDTheme.of(context).bgColorSecondaryContainer
            : TDTheme.of(context).bgColorContainer,
        child: Row(children: row),
      ));
    }
    if (widget.footerWidget != null){
      cells.add(widget.footerWidget!);
    }
    return Column(
      children: cells,
    );
  }

  /// 获取单元格
  Widget _getCell(TDTableCol col, bool isHeader, dynamic data, int index,
      bool fixedBorder) {
    var title = isHeader ? (col.title ?? '') : (data[col.colKey] ?? '');
    var ellipsis = (isHeader ? col.ellipsisTitle : col.ellipsis) ?? false;
    var sortable = col.sortable ?? false;

    // 单元格边框
    var halfBorder =
        BorderSide(width: 0.5, color: TDTheme.of(context).componentStrokeColor);
    var doubleBorder =
        BorderSide(width: 1, color: TDTheme.of(context).componentStrokeColor);
    var topBorder = BorderSide.none,
        rightBorder = BorderSide.none,
        leftBorder = BorderSide.none;
    var bottomBorder = halfBorder;
    if (widget.bordered ?? false) {
      rightBorder = halfBorder;
    }
    if (fixedBorder && col.fixed == TDTableColFixed.left) {
      rightBorder = doubleBorder;
    }
    if (fixedBorder && col.fixed == TDTableColFixed.right) {
      leftBorder = doubleBorder;
    }

    // 单元格内容
    var text = _getCellText(col, title, ellipsis, isHeader, sortable, index);
    var content = text;
    if ((col.selection ?? false) && col.cellBuilder == null) {
      var checkBox;
      // 行选择框
      if (_notEmptyData()) {
        var enable = col.selectable?.call(index, widget.data?[index]) ?? true;
        checkBox = TDCheckbox(
          id: 'index:$index',
          checked: _checkedList[index],
          enable: enable,
          customIconBuilder: (context, checked) {
            if (checked) {
              return Icon(TDIcons.check_rectangle_filled,
                  size: 16, color: TDTheme.of(context).brandNormalColor);
            }
            return Icon(TDIcons.rectangle,
                size: 16,
                color: enable
                    ? TDTheme.of(context).textColorPrimary
                    : TDTheme.of(context).textColorPlaceholder);
          },
          onCheckBoxChanged: (checked) {
            setState(() {
              _checkedList[index] = checked;
              if (checked) {
                _hasChecked += 1;
              } else {
                _hasChecked -= 1;
              }
              _checkAll = _hasChecked == _totalSelectable;
              var selectList = [];
              for (var i = 0; i < _checkedList.length; i++) {
                if (_checkedList[i]) {
                  selectList.add(widget.data![i]);
                }
              }
              widget.onSelect?.call(selectList);
              widget.onRowSelect?.call(index, checked);
            });
          },
        );
      }

      // 表头选择框
      if (isHeader) {
        checkBox = TDCheckbox(
          id: 'header',
          checked: _checkAll,
          customIconBuilder: (context, checked) {
            if (_hasChecked == 0) {
              return Icon(
                TDIcons.rectangle,
                size: 16,
                color: TDTheme.of(context).textColorPlaceholder,
              );
            }
            var allCheck = _hasChecked >= _totalSelectable;
            var halfSelected =
                _hasChecked > 0 && _hasChecked < _totalSelectable;
            return getAllIcon(allCheck, halfSelected);
          },
          onCheckBoxChanged: (checked) {
            setState(() {
              if (!_notEmptyData() && checked) {
                _hasChecked = _totalSelectable = 1;
              }
              _checkAll = checked;
              _hasChecked = checked ? _totalSelectable : 0;
              for (var i = 0; i < widget.data!.length; i++) {
                // 不选中selectable == false的行
                if (_selectableCol.selectable!(i, widget.data![i])) {
                  _checkedList[i] = checked;
                }
              }
              widget.onSelect?.call(checked ? widget.data : []);
            });
          },
        );
      }

      content = Row(
        children: [
          checkBox,
          text,
        ],
      );
    }

    // 单元格构建
    var cell = GestureDetector(
      onTap: () {
        if (isHeader == false) {
          widget.onCellTap?.call(index, data, col);
        }
      },
      child: Container(
          decoration: BoxDecoration(
            border: Border(
              top: topBorder,
              right: rightBorder,
              bottom: bottomBorder,
              left: leftBorder,
            ),
          ),
          child: Padding(
            padding: const EdgeInsets.symmetric(vertical: 8, horizontal: 16),
            child: SizedBox(
              height: widget.rowHeight ?? 22,
              child: Align(
                alignment: _getVerticalAlign(col.align!),
                child: content,
              ),
            ),
          )),
    );
    return cell;
  }

  /// 获取单元格内容
  Widget _getCellText(TDTableCol col, String title, bool ellipsis,
      bool isHeader, bool sortable, int index) {
    var overflow = ellipsis ? TextOverflow.ellipsis : TextOverflow.visible;
    var titleWidget = TDText(title,
        maxLines: 1,
        overflow: overflow,
        style: TextStyle(
          color: isHeader
              ? TDTheme.of(context).textColorPlaceholder
              : TDTheme.of(context).textColorPrimary,
          fontSize: 14,
          height: 1,
          letterSpacing: 0,
        ));

    // 表头（需考虑排序模式）
    if (isHeader) {
      var selectColor = TDTheme.of(context).brandNormalColor;
      var unSelectColor = TDTheme.of(context).textColorPlaceholder;
      return Row(
        crossAxisAlignment: CrossAxisAlignment.center,
        mainAxisSize: MainAxisSize.min,
        children: [
          titleWidget,
          Visibility(
            visible: isHeader && sortable,
            child: Padding(
              padding: const EdgeInsets.only(left: 4),
              child: GestureDetector(
                onTap: () {
                  setState(() {
                    if (_sortKey != col.colKey) {
                      _sortable = true;
                    } else {
                      if (_sortable == false) {
                        _sortable = null;
                      } else {
                        _sortable = !(_sortable ?? false);
                      }
                    }
                    _sortKey = col.colKey;
                    widget.data?.sort((a, b) {
                      if (_sortable == false) {
                        return b[col.colKey].compareTo(a[col.colKey]);
                      }
                      return a[col.colKey].compareTo(b[col.colKey]);
                    });
                  });
                },
                // 绘制双向箭头
                child: CustomPaint(
                  size: const Size(16, 16),
                  painter: ChevronPainter(
                    upColor: (_sortable == true) && (_sortKey == col.colKey)
                        ? selectColor
                        : unSelectColor,
                    downColor: (_sortable == false) && (_sortKey == col.colKey)
                        ? selectColor
                        : unSelectColor,
                  ),
                ),
              ),
            ),
          )
        ],
      );
    }
    // 自定义单元格内容
    if (col.cellBuilder != null) {
      return Builder(builder: (_) => col.cellBuilder!(_, index));
    }
    return titleWidget;
  }

  /// 获取表格宽度
  double _getColsWidth() {
    var width = 0.0;
    widget.columns.forEach((col) {
      width += (col.width ?? 0);
    });
    return width;
  }

  bool _notEmptyData() {
    return widget.data != null && widget.data!.isNotEmpty;
  }

  @override
  void initState() {
    super.initState();
    _sortKey = widget.defaultSort;
    _sortable = widget.defaultSort != null;
    _scrollController.addListener(() {
      widget.onScroll?.call(_scrollController);
    });
    _initCols();
  }

  @override
  void didUpdateWidget(covariant TDTable oldWidget) {
    super.didUpdateWidget(oldWidget);
    _initCols();
  }

  void _initCols() {
    _totalSelectable = 0;
    _hasChecked = 0;
    _checkedList = List.generate((widget.data?.length ?? 0), (index) => false);
    var cols = widget.columns.where((col) => col.selection ?? false);
    if (cols.length > 1) {
      throw FlutterError('selectable column must be only one');
    }
    if (widget.data != null && cols.isNotEmpty) {
      _selectableCol = cols.first;
      var data = widget.data!;
      for (var i = 0; i < data.length; i++) {
        var check = _selectableCol.checked?.call(i, data[i]) ?? false;
        _checkedList[i] = check;
        if (check) {
          _hasChecked++;
        }
        if (_selectableCol.selectable?.call(i, data[i]) ?? false) {
          _totalSelectable++;
        }
      }
    }
  }

  /// 生成固定列表格
  Widget _getFixedTable(BuildContext context) {
    // 对列进行分类
    var fixedLeftCol = _getCol(TDTableColFixed.left);
    var fixedNonCol = _getCol(TDTableColFixed.none);
    var fixedRightCol = _getCol(TDTableColFixed.right);

    // 获取竖向单元格内容
    var fixedLeftTitle = _getCellsText(fixedLeftCol);
    var fixedNonTitle = _getCellsText(fixedNonCol);
    var fixedRightTitle = _getCellsText(fixedRightCol);

    // 计算单元格宽度（单元格默认平分）
    var width = widget.width ?? MediaQuery.of(context).size.width;
    var cellWidth = width / widget.columns.length;

    // 生成左侧固定列
    var fixedLeftCols =
        _getVerticalCell(fixedLeftCol, fixedLeftTitle, cellWidth);
    // 生成非固定列
    var fixedNonCols = _getVerticalCell(fixedNonCol, fixedNonTitle, cellWidth);
    // 生成右侧固定列
    var fixedRightCols =
        _getVerticalCell(fixedRightCol, fixedRightTitle, cellWidth);

    // 固定列宽度
    var fixedCellsWidth = 0.0;
    for (var tableCol in widget.columns) {
      if (tableCol.fixed == TDTableColFixed.left ||
          tableCol.fixed == TDTableColFixed.right) {
        fixedCellsWidth += (tableCol.width ?? cellWidth);
      }
    }

    // 计算非固定列宽度
    var fixedNonCellsWidth = 0.0;
    for (var col in fixedNonCol) {
      // 存在用户自定义宽度  否则使用默认宽度
      fixedNonCellsWidth += col.width ?? cellWidth;
    }

    // 非固定列宽度超过剩余宽度 需要开启滚动
    if ((width - fixedCellsWidth) < fixedNonCellsWidth) {
      var content = [Row(children: fixedNonCols), _buildEmpty()];
      if (widget.loading ?? false) {
        content = [
          Row(children: fixedNonCols),
          Align(
            alignment: Alignment.center,
            child: Padding(
              padding: const EdgeInsets.symmetric(vertical: 32),
              child: widget.loadingWidget ??
                  const TDLoading(size: TDLoadingSize.large),
            ),
          ),
        ];
      }
      return Container(
        width: width,
        color: widget.backgroundColor ?? TDTheme.of(context).bgColorContainer,
        child: Row(
          mainAxisAlignment: MainAxisAlignment.start,
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            Column(children: [...fixedLeftCols]),
            SizedBox(
              width: width - fixedCellsWidth,
              child: SingleChildScrollView(
                scrollDirection: Axis.horizontal,
                child: Column(children: content),
              ),
            ),
            Column(
              mainAxisSize: MainAxisSize.min,
              children: [...fixedRightCols],
            )
          ],
        ),
      );
    }
    var child = Container(
      width: width,
      color: widget.backgroundColor ?? TDTheme.of(context).bgColorContainer,
      child: Row(
        children: [
          ...fixedLeftCols,
          ...fixedNonCols,
          ...fixedRightCols,
        ],
      ),
    );
    var placeholder = _buildEmpty();
    if (widget.loading ?? false) {
      placeholder = Align(
        alignment: Alignment.center,
        child: Padding(
          padding: const EdgeInsets.symmetric(vertical: 32),
          child: widget.loadingWidget ??
              const TDLoading(size: TDLoadingSize.large),
        ),
      );
    }
    return Container(
      color: widget.backgroundColor ?? TDTheme.of(context).bgColorContainer,
      child: Column(children: [child, placeholder]),
    );
  }

  /// 空数据内容
  Widget _buildEmpty() {
    return Visibility(
      visible: widget.data == null || widget.data!.isEmpty,
      child: Align(
        alignment: Alignment.center,
        child: Padding(
          padding: const EdgeInsets.only(top: 16, bottom: 38),
          child: TDEmpty(
            image: Visibility(
              visible: widget.empty?.assetUrl != null,
              child: _buildEmptyImage(),
            ),
            emptyText: widget.empty?.text ?? context.resource.emptyData,
          ),
        ),
      ),
    );
  }

  TDImage _buildEmptyImage() {
    var url = widget.empty?.assetUrl ?? '';
    if (url.startsWith('http')) {
      return TDImage(imgUrl: url);
    }
    return TDImage(assetUrl: url);
  }

  /// 竖向生成单元格
  List<Widget> _getVerticalCell(
      List<TDTableCol> cols, List<List<String>> titles, double cellWidth) {
    var rows = <Widget>[];
    for (var i = 0; i < titles.length; i++) {
      var cells = <Widget>[];
      for (var j = 0; j < titles[i].length; j++) {
        var col = cols[i];
        var cell = _getCell(col, j == 0, j == 0 ? '' : widget.data?[j - 1], i,
            i == titles.length - 1);
        cells.add(SizedBox(width: col.width ?? cellWidth, child: cell));
      }
      rows.add(Column(children: cells));
    }
    return rows;
  }

  /// 获取每列单元格内容
  List<List<String>> _getCellsText(List<TDTableCol> cols) {
    var list = <List<String>>[];
    for (var col in cols) {
      var titles = <String>[];
      titles.add(col.title ?? '');
      if (widget.loading == false) {
        var dataList = <String>[];
        for (var i = 0; i < (widget.data?.length ?? 0); i++) {
          var data = widget.data![i];
          dataList.add(data[col.colKey] ?? '');
        }
        titles..addAll(dataList);
      }
      list.add(titles);
    }
    return list;
  }

  /// 半选图标
  Widget getAllIcon(bool checked, bool halfSelected) {
    return Icon(
        checked
            ? TDIcons.check_rectangle_filled
            : halfSelected
                ? TDIcons.minus_rectangle_filled
                : TDIcons.check_rectangle,
        size: 16,
        color: (checked || halfSelected)
            ? TDTheme.of(context).brandNormalColor
            : TDTheme.of(context).textDisabledColor);
  }

  @override
  Widget build(BuildContext context) {
    // 固定列  按列生成
    // 非固定列  按行生成

    // 自定义表格宽度 默认屏幕宽度
    var width = widget.width ?? MediaQuery.of(context).size.width;
    var fixedCols = [
      ..._getCol(TDTableColFixed.left),
      ..._getCol(TDTableColFixed.right)
    ];

    // 存在固定列
    if (fixedCols.isNotEmpty) {
      return _getFixedTable(context);
    }

    // 表格超宽
    if (width < _getColsWidth()) {
      return Container(
        width: width,
        color: widget.backgroundColor ?? TDTheme.of(context).bgColorContainer,
        child: SingleChildScrollView(
          scrollDirection: Axis.horizontal,
          physics: const ClampingScrollPhysics(),
          child: Column(
            children: [
              Visibility(
                visible: widget.showHeader == true,
                child: _getTableHeader(context),
              ),
              SizedBox(
                height: widget.height,
                child: SingleChildScrollView(
                  controller: _scrollController,
                  physics: const BouncingScrollPhysics(),
                  child: _getTableContent(context),
                ),
              )
            ],
          ),
        ),
      );
    }
    return Container(
      width: width,
      color: widget.backgroundColor ?? TDTheme.of(context).bgColorContainer,
      child: Column(
        children: [
          Visibility(
            visible: widget.showHeader == true,
            child: _getTableHeader(context),
          ),
          SizedBox(
            height: widget.height,
            child: SingleChildScrollView(
              controller: _scrollController,
              physics: const BouncingScrollPhysics(),
              child: _getTableContent(context),
            ),
          )
        ],
      ),
    );
  }
}

class ChevronPainter extends CustomPainter {
  ChevronPainter({
    required this.upColor,
    required this.downColor,
  });

  /// 线条颜色（向上）
  final Color upColor;

  /// 线条颜色（向下）
  final Color downColor;

  @override
  void paint(Canvas canvas, Size size) {
    final upPaint = Paint()
      ..color = upColor
      ..strokeWidth = 1.4
      ..style = PaintingStyle.stroke
      ..strokeCap = StrokeCap.round;

    final clientX = size.width;
    final clientY = size.height;
    final centerX = clientX / 2;
    final centerY = clientY / 2;

    // 向上箭头
    final upPath = Path();
    upPath.moveTo(3.6, centerY - 1.8);
    upPath.lineTo(centerX, 2);
    upPath.lineTo(clientX - 3.6, centerY - 1.8);
    upPath.close();

    // 向下箭头
    final downPaint = Paint()
      ..color = downColor
      ..strokeWidth = 1.5
      ..style = PaintingStyle.stroke
      ..strokeCap = StrokeCap.round;
    final downPath = Path();
    downPath.moveTo(3.6, centerY + 1.8);
    downPath.lineTo(centerX, clientY - 2);
    downPath.lineTo(clientX - 3.6, centerY + 1.8);
    downPath.close();

    canvas.drawPath(upPath, upPaint);
    canvas.drawPath(downPath, downPaint);
  }

  @override
  bool shouldRepaint(covariant CustomPainter oldDelegate) {
    return true;
  }
}

