import 'package:flutter/material.dart';

import '../../../tdesign_flutter.dart';

enum TDTabSize { large, small }

enum TDTabOutlineType {
  /// 填充样式
  filled,

  /// 胶囊样式
  capsule,

  /// 卡片
  card
}

class TDTab extends Tab {
  /// 文字内容
  @override
  final String? text;

  /// 子widget
  @override
  final Widget? child;

  /// 图标
  @override
  final Widget? icon;

  /// 图标
  final TDBadge? badge;

  /// 图标间距
  @override
  final EdgeInsetsGeometry iconMargin;

  /// tab高度
  @override
  final double? height;

  /// 中间内容高度
  final double? contentHeight;

  /// 中间内容宽度
  final EdgeInsetsGeometry? textMargin;

  /// 是否可用，默认true
  final bool enable;

  /// 选项卡尺寸
  final TDTabSize size;

  /// 选项卡样式
  final TDTabOutlineType outlineType;

  @override
  const TDTab({
    Key? key,
    this.text,
    this.child,
    this.icon,
    this.badge,
    this.height,
    this.contentHeight,
    this.textMargin,
    this.size = TDTabSize.small,
    this.outlineType = TDTabOutlineType.filled,
    this.enable = true,
    this.iconMargin = const EdgeInsets.only(bottom: 4.0, right: 4.0),
  }) : super(
          key: key,
          text: text,
          child: child,
          icon: icon,
          height: height,
          iconMargin: iconMargin,
        );

  final double _kTabHeight = 48.0;
  final double _kTextAndIconTabHeight = 72.0;

  @override
  Widget build(BuildContext context) {
    final double calculatedHeight;
    Widget label;
    if (icon == null) {
      calculatedHeight = _kTabHeight;
      label = _buildLabelText(context);
    } else if (text == null && child == null) {
      calculatedHeight = _kTabHeight;
      label = icon!;
    } else {
      calculatedHeight = _kTextAndIconTabHeight;
      label = Row(
        mainAxisAlignment: MainAxisAlignment.center,
        crossAxisAlignment: CrossAxisAlignment.center,
        children: <Widget>[
          icon ?? Container(),
          SizedBox(
            width: iconMargin.horizontal,
            height: iconMargin.vertical,
          ),
          _buildLabelText(context),
        ],
      );
    }
    if (badge != null) {
      label = Stack(
        alignment: Alignment.bottomLeft,
        children: [
          Container(margin: textMargin, child: label),
          Positioned(
            child: badge!,
            right: 0,
            top: 0,
          ),
        ],
      );
    }

    return IgnorePointer(
      ignoring: !enable,
      child: Container(
        alignment: Alignment.center,
        margin: outlineType == TDTabOutlineType.capsule
            ? const EdgeInsets.symmetric(horizontal: 16)
            : null,
        height: height ?? calculatedHeight,
        child: Center(
          widthFactor: 1.0,
          child: label,
        ),
      ),
    );
  }

  Widget _buildLabelText(BuildContext context) {
    if (child != null) {
      return DefaultTextStyle(
        child: child!,
        style: DefaultTextStyle.of(context)
            .style
            .copyWith(fontSize: TDTheme.of(context).fontBodySmall?.size ?? 14),
      );
    }
    return Text(
      text!,
      softWrap: false,
      overflow: TextOverflow.fade,
      style: TextStyle(fontSize: _getFontSize(context)),
    );
  }

  double _getFontSize(BuildContext context) {
    final defaultTextStyle = DefaultTextStyle.of(context);
    if (defaultTextStyle.style.fontSize != null) {
      return defaultTextStyle.style.fontSize!;
    }
    if (size == TDTabSize.large) {
      return 16.0;
    } else {
      return 14.0;
    }
  }
}

Tabs.tab_bar:
import 'dart:ui' as ui;

import 'package:flutter/material.dart';

import '../../../tdesign_flutter.dart';
import 'td_horizontal_tab_bar.dart';

enum TDTabBarOutlineType {
  /// 填充样式
  filled,

  /// 胶囊样式
  capsule,

  /// 卡片
  card
}

class TDTabBar extends StatefulWidget {
  const TDTabBar({
    Key? key,
    required this.tabs,
    this.controller,
    this.decoration,
    this.backgroundColor,
    this.indicatorColor,
    this.indicatorWidth,
    this.indicatorHeight,
    this.labelColor,
    this.unselectedLabelColor,
    this.isScrollable = false,
    this.unselectedLabelStyle,
    this.labelStyle,
    this.width,
    this.height,
    this.indicatorPadding,
    this.labelPadding,
    this.indicator,
    this.physics,
    this.onTap,
    this.outlineType = TDTabBarOutlineType.filled,
    this.showIndicator = false,
    this.dividerColor,
    this.dividerHeight = 0.5,
    this.selectedBgColor,
    this.unSelectedBgColor,
    this.tabAlignment,
  })  : assert(
          backgroundColor == null || decoration == null,
          'Cannot provide both a backgroundColor and a decoration\n'
          'To provide both, use "decoration: BoxDecoration(color: color)".',
        ),
        super(key: key);

  /// tab数组
  final List<TDTab> tabs;

  /// tab控制器
  final TabController? controller;

  /// tabBar修饰
  final Decoration? decoration;

  /// tabBar背景色，当outlineType为card时控制选中tab颜色
  final Color? backgroundColor;

  /// tabBar下标颜色
  final Color? indicatorColor;

  /// tabBar下标高度
  final double? indicatorHeight;

  /// tabBar下标宽度
  final double? indicatorWidth;

  /// tabBar 已选标签颜色
  final Color? labelColor;

  /// tabBar未选标签颜色
  final Color? unselectedLabelColor;

  /// 是否滚动
  final bool isScrollable;

  /// 已选label字体
  final TextStyle? labelStyle;

  /// unselectedLabel字体
  final TextStyle? unselectedLabelStyle;

  /// tabBar宽度
  final double? width;

  /// tabBar高度
  final double? height;

  /// 引导padding
  final EdgeInsets? indicatorPadding;

  /// 自定义引导控件
  final Decoration? indicator;

  /// 是否展示引导控件
  final bool showIndicator;

  /// 自定义滑动
  final ScrollPhysics? physics;

  /// 点击事件
  final Function(int)? onTap;

  /// tab间距
  final EdgeInsetsGeometry? labelPadding;

  /// 选项卡样式
  final TDTabBarOutlineType outlineType;

  /// 分割线颜色
  final Color? dividerColor;

  /// 分割线高度,小于等于0则不展示分割线
  final double dividerHeight;

  /// 被选中背景色，只有outlineType为capsule时有效
  final Color? selectedBgColor;

  /// 未选中背景色，只有outlineType为capsule时有效
  final Color? unSelectedBgColor;

  final TabAlignment? tabAlignment;

  @override
  State<StatefulWidget> createState() => _TDTabBarState();
}

class _TDTabBarState extends State<TDTabBar> {
  /// 默认高度
  static const double _defaultHeight = 48;

  @override
  Widget build(BuildContext context) {
    return Container(
      width: widget.width ?? MediaQuery.of(context).size.width,
      height: widget.height ?? _defaultHeight,
      decoration: widget.decoration ??
          (widget.outlineType == TDTabBarOutlineType.card
              ? BoxDecoration(
                  color: widget.backgroundColor ??
                      TDTheme.of(context).bgColorContainer)
              : BoxDecoration(
                  color: widget.backgroundColor ??
                      TDTheme.of(context).bgColorContainer,
                  border: widget.dividerHeight <= 0
                      ? null
                      : Border(
                          bottom: BorderSide(
                              color: widget.dividerColor ??
                                  TDTheme.of(context).componentStrokeColor,
                              width: widget.dividerHeight)))),
      child: TDHorizontalTabBar(
        physics: widget.physics,
        isScrollable: widget.isScrollable,
        indicator: widget.indicator ?? _getIndicator(),
        indicatorColor: widget.indicatorColor,
        unselectedLabelColor: widget.unselectedLabelColor,
        labelColor: widget.labelColor ?? TDTheme.of(context).brandNormalColor,
        labelStyle: widget.labelStyle ?? _getLabelStyle(),
        labelPadding: widget.labelPadding ?? const EdgeInsets.all(8),
        unselectedLabelStyle:
            widget.unselectedLabelStyle ?? _getUnSelectLabelStyle(),
        tabs: widget.tabs,
        indicatorPadding: widget.indicatorPadding ?? EdgeInsets.zero,
        outlineType: widget.outlineType,
        controller: widget.controller,
        backgroundColor: widget.backgroundColor,
        selectedBgColor: widget.selectedBgColor,
        unSelectedBgColor: widget.unSelectedBgColor ??
            TDTheme.of(context).bgColorSecondaryContainer,
        tabAlignment: widget.tabAlignment,
        onTap: (index) {
          widget.onTap?.call(index);
        },
      ),
    );
  }

  TextStyle _getUnSelectLabelStyle() {
    return TextStyle(
        fontWeight: FontWeight.w400,
        color: TDTheme.of(context).textColorPrimary);
  }

  TextStyle _getLabelStyle() {
    return TextStyle(
        fontWeight: FontWeight.w600,
        color: TDTheme.of(context).textColorPrimary);
  }

  Decoration _getIndicator() {
    return widget.showIndicator
        ? TDTabBarIndicator(
            context: context,
            indicatorHeight: widget.indicatorHeight,
            indicatorWidth: widget.indicatorWidth,
            indicatorColor: widget.indicatorColor,
          )
        : TDNoneIndicator();
  }
}

/// TDesign自定义下标
class TDTabBarIndicator extends Decoration {
  final BuildContext? context;
  final double? indicatorWidth;
  final double? indicatorHeight;
  final Color? indicatorColor;

  const TDTabBarIndicator({
    this.context,
    this.indicatorWidth,
    this.indicatorHeight,
    this.indicatorColor,
  });

  @override
  BoxPainter createBoxPainter([VoidCallback? onChanged]) =>
      _TDTabBarIndicatorPainter(this, onChanged!);
}

class _TDTabBarIndicatorPainter extends BoxPainter {
  /// 下标宽度
  static const double _defaultIndicatorWidth = 16;

  /// 下标高度
  static const double _defaultIndicatorHeight = 3;

  final TDTabBarIndicator decoration;

  final _paint = Paint();

  _TDTabBarIndicatorPainter(this.decoration, VoidCallback onChanged) {
    /// 下标颜色
    _paint.color = decoration.indicatorColor ??
        TDTheme.of(decoration.context).brandNormalColor;
    _paint.strokeCap = StrokeCap.round;
  }

  @override
  void paint(Canvas canvas, Offset offset, ImageConfiguration configuration) {
    canvas.drawLine(
        Offset(offset.dx + (configuration.size!.width - _indicatorWidth()) / 2,
            configuration.size!.height - _indicatorHeight() / 2),
        Offset(offset.dx + (configuration.size!.width + _indicatorWidth()) / 2,
            configuration.size!.height - _indicatorHeight() / 2),
        _paint..strokeWidth = _indicatorHeight());
  }

  double _indicatorHeight() =>
      decoration.indicatorHeight ?? _defaultIndicatorHeight;

  double _indicatorWidth() =>
      decoration.indicatorWidth ?? _defaultIndicatorWidth;
}

/// TDesign自定义下标 竖向
class TDTabBarVerticalIndicator extends Decoration {
  final BuildContext? context;
  final double? indicatorWidth;
  final double? indicatorHeight;

  const TDTabBarVerticalIndicator({
    this.context,
    this.indicatorWidth,
    this.indicatorHeight,
  });

  @override
  BoxPainter createBoxPainter([VoidCallback? onChanged]) =>
      _TDTabBarVerticalIndicatorPainter(this, onChanged!);
}

class _TDTabBarVerticalIndicatorPainter extends BoxPainter {
  /// 下标宽度
  static const double _defaultIndicatorWidth = 1.5;

  /// 下标高度
  static const double _defaultIndicatorHeight = 54;

  final TDTabBarVerticalIndicator decoration;

  final _paint = Paint();

  _TDTabBarVerticalIndicatorPainter(this.decoration, VoidCallback onChanged) {
    /// 下标颜色
    _paint.color = TDTheme.of(decoration.context).brandNormalColor;
    _paint.strokeCap = StrokeCap.round;
  }

  @override
  void paint(Canvas canvas, Offset offset, ImageConfiguration configuration) {
    canvas.drawLine(
        Offset(
          0 + _indicatorWidth() / 2,
          offset.dx + (configuration.size!.width - _indicatorHeight()) / 2,
        ),
        Offset(
          0 + _indicatorWidth() / 2,
          offset.dx + (configuration.size!.width + _indicatorHeight()) / 2,
        ),
        _paint..strokeWidth = _indicatorWidth());
  }

  double _indicatorHeight() =>
      decoration.indicatorHeight ?? _defaultIndicatorHeight;

  double _indicatorWidth() =>
      decoration.indicatorWidth ?? _defaultIndicatorWidth;
}

/// TDesign不展示下标
class TDNoneIndicator extends Decoration {
  @override
  BoxPainter createBoxPainter([VoidCallback? onChanged]) =>
      _TDNoneIndicatorPainter();
}

class _TDNoneIndicatorPainter extends BoxPainter {
  @override
  void paint(Canvas canvas, Offset offset, ImageConfiguration configuration) {}
}

