import 'package:flutter/material.dart';

import '../../../tdesign_flutter.dart';

/// 点击型标签组件，点击时内部更改自身状态
/// 支持样式：方形/圆角/半圆/带关闭图标
class TDSelectTag extends StatefulWidget {
  const TDSelectTag(
    this.text, {
    this.theme,
    this.icon,
    this.iconWidget,
    this.selectStyle,
    this.unSelectStyle,
    this.disableSelectStyle,
    this.onSelectChanged,
    this.isSelected = false,
    this.disableSelect = false,
    this.size = TDTagSize.medium,
    this.padding,
    this.forceVerticalCenter = true,
    this.isOutline = false,
    this.shape = TDTagShape.square,
    this.isLight = false,
    this.needCloseIcon = false,
    this.onCloseTap,
    this.fixedWidth,
    Key? key,
  }) : super(key: key);

  /// 标签内容
  final String text;

  /// 主题
  final TDTagTheme? theme;

  /// 图标内容，可随状态改变颜色
  final IconData? icon;

  /// 自定义图标内容，需自处理颜色
  final Widget? iconWidget;

  /// 选中的标签样式
  final TDTagStyle? selectStyle;

  /// 未选中标签样式
  final TDTagStyle? unSelectStyle;

  /// 不可选标签样式
  final TDTagStyle? disableSelectStyle;

  /// 标签点击，选中状态改变时的回调
  final ValueChanged<bool>? onSelectChanged;

  /// 是否选中
  final bool isSelected;

  /// 是否禁用选择
  final bool disableSelect;

  /// 标签大小
  final TDTagSize size;

  /// 自定义模式下的间距
  final EdgeInsets? padding;

  /// 是否强制中文文字居中
  final bool forceVerticalCenter;

  /// 是否为描边类型，默认不是
  final bool isOutline;

  /// 标签形状
  final TDTagShape shape;

  /// 是否为浅色
  final bool isLight;

  /// 关闭图标
  final bool needCloseIcon;

  /// 关闭图标点击事件
  final GestureTapCallback? onCloseTap;

  /// 标签的固定宽度
  final double? fixedWidth;

  @override
  _TDClickTagState createState() => _TDClickTagState();
}

class _TDClickTagState extends State<TDSelectTag> {
  bool _isSelected = false;

  @override
  void initState() {
    super.initState();
    _isSelected = widget.isSelected;
  }

  @override
  Widget build(BuildContext context) {
    Widget result = TDTag(
      widget.text,
      icon: widget.icon,
      iconWidget: widget.iconWidget,
      style: _getStyle(),
      size: widget.size,
      padding: widget.padding,
      forceVerticalCenter: widget.forceVerticalCenter,
      needCloseIcon: widget.needCloseIcon,
      onCloseTap: widget.onCloseTap,
      fixedWidth: widget.fixedWidth,
    );
    if (!widget.disableSelect) {
      result = GestureDetector(
        onTap: () {
          setState(() {
            _isSelected = !_isSelected;
            widget.onSelectChanged?.call(_isSelected);
          });
        },
        child: result,
      );
    }
    return result;
  }

  TDTagStyle? _getStyle() {
    if (widget.disableSelect) {
      return _getDisableSelectStyle();
    }
    return _isSelected ? _getSelectStyle() : _getUnSelectStyle();
  }

  TDTagStyle _getDisableSelectStyle() {
    if (widget.disableSelectStyle != null) {
      return widget.disableSelectStyle!;
    }
    return TDTagStyle.generateDisableSelectStyle(
        context, widget.isLight, widget.isOutline, widget.shape);
  }

  TDTagStyle _getSelectStyle() {
    if (widget.selectStyle != null) {
      return widget.selectStyle!;
    }
    return widget.isOutline
        ? TDTagStyle.generateOutlineStyleByTheme(
            context, widget.theme, widget.isLight, widget.shape)
        : TDTagStyle.generateFillStyleByTheme(
            context, widget.theme, widget.isLight, widget.shape);
  }

  TDTagStyle _getUnSelectStyle() {
    if (widget.unSelectStyle != null) {
      return widget.unSelectStyle!;
    }
    return widget.isOutline
        ? TDTagStyle.generateOutlineStyleByTheme(
            context, TDTagTheme.defaultTheme, widget.isLight, widget.shape)
        : TDTagStyle.generateFillStyleByTheme(
            context, TDTagTheme.defaultTheme, widget.isLight, widget.shape);
  }

  @override
  void didUpdateWidget(covariant TDSelectTag oldWidget) {
    super.didUpdateWidget(oldWidget);
    _isSelected = widget.isSelected;
  }
}

Tag.tag:
import 'package:flutter/material.dart';
import '../../../tdesign_flutter.dart';

/// 展示型标签组件，仅展示，内部不可更改自身状态
/// 支持样式：方形/圆角/半圆/带关闭图标
class TDTag extends StatelessWidget {
  const TDTag(
    this.text, {
    this.theme,
    this.icon,
    this.iconWidget,
    this.textColor,
    this.backgroundColor,
    this.font,
    this.fontWeight,
    this.style,
    this.size = TDTagSize.medium,
    this.padding,
    this.forceVerticalCenter = true,
    this.isOutline = false,
    this.shape = TDTagShape.square,
    this.isLight = false,
    this.disable = false,
    this.needCloseIcon = false,
    this.onCloseTap,
    this.overflow,
    this.fixedWidth,
    Key? key,
  }) : super(key: key);

  /// 标签内容
  final String text;

  /// 主题
  final TDTagTheme? theme;

  /// 图标内容，可随状态改变颜色
  final IconData? icon;

  /// 自定义图标内容，需自处理颜色
  final Widget? iconWidget;

  /// 文字颜色，优先级高于style的textColor
  final Color? textColor;

  /// 背景颜色，优先级高于style的backgroundColor
  final Color? backgroundColor;

  /// 字体尺寸，优先级高于style的font
  final Font? font;

  /// 字体粗细，优先级高于style的fontWeight
  final FontWeight? fontWeight;

  /// 标签样式
  final TDTagStyle? style;

  /// 标签大小
  final TDTagSize size;

  /// 自定义模式下的间距
  final EdgeInsets? padding;

  /// 是否强制中文文字居中
  final bool forceVerticalCenter;

  /// 是否为描边类型，默认不是
  final bool isOutline;

  /// 标签形状
  final TDTagShape shape;

  /// 是否为浅色
  final bool isLight;

  /// 是否为禁用状态
  final bool disable;

  /// 关闭图标
  final bool needCloseIcon;

  /// 文字溢出处理
  final TextOverflow? overflow;

  /// 关闭图标点击事件
  final GestureTapCallback? onCloseTap;

  /// 标签的固定宽度
  final double? fixedWidth;

  @override
  Widget build(BuildContext context) {
    var innerStyle = _getInnerStyle(context);

    Widget child = TDText(
      text,
      overflow: overflow ?? TextOverflow.ellipsis,
      forceVerticalCenter: forceVerticalCenter,
      textColor: textColor ?? innerStyle.getTextColor,
      font: font ?? innerStyle.font ?? _getFont(context),
      fontWeight: fontWeight ?? innerStyle.fontWeight,
    );

    var innerIcon = getIcon(innerStyle);
    if (innerIcon != null || needCloseIcon) {
      var children = <Widget>[];
      if (innerIcon != null) {
        children.add(Container(
          margin: const EdgeInsets.only(right: 4),
          width: 14,
          height: 14,
          child: innerIcon,
        ));
      }
      children.add(child);
      if (needCloseIcon) {
        children.add(
          GestureDetector(
            onTap: onCloseTap,
            child: Container(
              margin: const EdgeInsets.only(left: 4),
              child: Icon(
                TDIcons.close,
                color: innerStyle.closeIconColor ??
                    TDTheme.of(context).textColorAnti,
                size: 14,
              ),
            ),
          ),
        );
      }
      child = Row(
        // spacing: TDTheme.of(context).spacer4,
        mainAxisSize: MainAxisSize.min,
        children: children,
      );
    }

    return Container(
      width: fixedWidth,
      padding: padding ?? _getPadding(innerStyle.border),
      decoration: BoxDecoration(
          color: backgroundColor ?? innerStyle.getBackgroundColor,
          border: Border.all(
              width: innerStyle.border, color: innerStyle.getBorderColor),
          borderRadius: innerStyle.getBorderRadius),
      child: Align(
        widthFactor: 1,
        child: child,
      ),
    );
  }

  Widget? getIcon(TDTagStyle innerStyle) {
    if (iconWidget != null) {
      return iconWidget;
    }
    if (icon != null) {
      return RichText(
        overflow: TextOverflow.visible,
        text: TextSpan(
          text: String.fromCharCode(icon!.codePoint),
          style: TextStyle(
            inherit: false,
            color: innerStyle.textColor,
            height: 1,
            fontSize: _getIconSize(),
            fontFamily: icon!.fontFamily,
            package: icon!.fontPackage,
          ),
        ),
      );
    }
    return null;
  }

  TDTagStyle _getInnerStyle(BuildContext context) {
    if (style != null) {
      return style!;
    }
    if (disable) {
      return TDTagStyle.generateDisableSelectStyle(
          context, isLight, isOutline, shape);
    }
    return isOutline
        ? TDTagStyle.generateOutlineStyleByTheme(context, theme, isLight, shape)
        : TDTagStyle.generateFillStyleByTheme(context, theme, isLight, shape);
  }

  Font? _getFont(BuildContext context) {
    switch (size) {
      case TDTagSize.extraLarge:
        return TDTheme.of(context).fontBodyMedium;
      case TDTagSize.large:
        return TDTheme.of(context).fontBodyMedium;
      case TDTagSize.small:
        return TDTheme.of(context).fontBodyExtraSmall;
      default:
        return TDTheme.of(context).fontBodySmall;
    }
  }

  /// 计算padding，需去除描边的宽对，对内描边
  EdgeInsets _getPadding(double border) {
    var hPadding = 0.0;
    var vPadding = 0.0;
    switch (size) {
      case TDTagSize.extraLarge:
        hPadding = 16;
        vPadding = 9;
        break;
      case TDTagSize.large:
        hPadding = 8;
        vPadding = 3;
        break;
      case TDTagSize.medium:
        hPadding = 8;
        vPadding = 2;
        break;
      case TDTagSize.small:
        hPadding = 6;
        vPadding = 2;
        break;
      default:
        return EdgeInsets.zero;
    }
    if (hPadding >= border) {
      hPadding = hPadding - border;
    } else {
      hPadding = 0;
    }
    if (vPadding >= border) {
      vPadding = vPadding - border;
    } else {
      vPadding = 0;
    }
    return EdgeInsets.only(
      left: hPadding,
      right: hPadding,
      top: vPadding,
      bottom: vPadding,
    );
  }

  double _getIconSize() {
    switch (size) {
      case TDTagSize.extraLarge:
        return 16;
      case TDTagSize.large:
        return 16;
      case TDTagSize.medium:
        return 14;
      case TDTagSize.small:
        return 12;
      default:
        return 14;
    }
  }
}

Tag.tag_styles:
import 'package:flutter/material.dart';
import '../../../tdesign_flutter.dart';

/// Tag展示类型
enum TDTagTheme {
  /// 默认
  defaultTheme,

  /// 常规
  primary,

  /// 警告
  warning,

  /// 危险
  danger,

  /// 成功
  success,
}

/// 标签尺寸
enum TDTagSize { extraLarge, large, medium, small, custom }

/// 标签形状
enum TDTagShape { square, round, mark }

/// 标签样式
class TDTagStyle {
  TDTagStyle({
    this.context,
    this.textColor,
    this.backgroundColor,
    this.font,
    this.fontWeight,
    this.border = 0,
    this.borderColor,
    this.borderRadius,
  });

  /// 上下文，方便获取主题内容
  BuildContext? context;

  /// 文字颜色
  Color? textColor;

  /// 背景颜色
  Color? backgroundColor;

  /// 边框颜色
  Color? borderColor;

  /// 关闭图标颜色
  Color? closeIconColor;

  /// 圆角
  BorderRadiusGeometry? borderRadius;

  /// 字体尺寸
  Font? font;

  /// 字体粗细
  FontWeight? fontWeight;

  /// 线框粗细
  double border = 0;

  /// 字体颜色，与属性不同名，方便子类自定义处理
  Color get getTextColor => textColor ?? TDTheme.of(context).fontWhColor1;

  /// 背景颜色，与属性不同名，方便子类自定义处理
  Color get getBackgroundColor =>
      backgroundColor ?? TDTheme.of(context).brandNormalColor;

  /// 线框颜色，与属性不同名，方便子类自定义处理
  Color get getBorderColor => borderColor ?? Colors.transparent;

  /// 圆角，，与属性不同名，方便子类自定义处理
  BorderRadiusGeometry get getBorderRadius =>
      borderRadius ?? BorderRadius.circular(0);

  /// 根据主题生成填充Tag样式
  TDTagStyle.generateFillStyleByTheme(
    BuildContext context,
    TDTagTheme? theme,
    bool light,
    TDTagShape shape,
  ) {
    this.context = context;
    switch (theme) {
      case TDTagTheme.primary:
        textColor = light
            ? TDTheme.of(context).brandNormalColor
            : TDTheme.of(context).textColorAnti;
        backgroundColor = light
            ? TDTheme.of(context).brandLightColor
            : TDTheme.of(context).brandNormalColor;
        break;
      case TDTagTheme.warning:
        textColor = light
            ? TDTheme.of(context).warningNormalColor
            : TDTheme.of(context).textColorAnti;
        backgroundColor = light
            ? TDTheme.of(context).warningLightColor
            : TDTheme.of(context).warningNormalColor;
        break;
      case TDTagTheme.danger:
        textColor = light
            ? TDTheme.of(context).errorNormalColor
            : TDTheme.of(context).textColorAnti;
        backgroundColor = light
            ? TDTheme.of(context).errorLightColor
            : TDTheme.of(context).errorNormalColor;
        break;
      case TDTagTheme.success:
        textColor = light
            ? TDTheme.of(context).successNormalColor
            : TDTheme.of(context).textColorAnti;
        backgroundColor = light
            ? TDTheme.of(context).successLightColor
            : TDTheme.of(context).successNormalColor;
        break;
      case TDTagTheme.defaultTheme:
      default:
        textColor = TDTheme.of(context).textColorPrimary;
        backgroundColor = light
            ? TDTheme.of(context).bgColorSecondaryContainer
            : TDTheme.of(context).bgColorComponent;
    }
    switch (shape) {
      case TDTagShape.square:
        borderRadius = BorderRadius.circular(TDTheme.of(context).radiusSmall);
        break;
      case TDTagShape.round:
        borderRadius = BorderRadius.circular(TDTheme.of(context).radiusRound);
        break;
      case TDTagShape.mark:
        borderRadius = BorderRadius.only(
          topRight: Radius.circular(TDTheme.of(context).radiusRound),
          bottomRight: Radius.circular(TDTheme.of(context).radiusRound),
        );
        break;
    }
    closeIconColor = textColor;
    borderColor = backgroundColor;
  }

  /// 根据主题生成描边Tag样式
  TDTagStyle.generateOutlineStyleByTheme(
    BuildContext context,
    TDTagTheme? theme,
    bool light,
    TDTagShape shape,
  ) {
    this.context = context;
    switch (theme) {
      case TDTagTheme.primary:
        borderColor = TDTheme.of(context).brandNormalColor;
        textColor = TDTheme.of(context).brandNormalColor;
        backgroundColor =
            light ? TDTheme.of(context).brandLightColor : Colors.transparent;
        break;
      case TDTagTheme.warning:
        borderColor = TDTheme.of(context).warningNormalColor;
        textColor = TDTheme.of(context).warningNormalColor;
        backgroundColor =
            light ? TDTheme.of(context).warningLightColor : Colors.transparent;
        break;
      case TDTagTheme.danger:
        borderColor = TDTheme.of(context).errorNormalColor;
        textColor = TDTheme.of(context).errorNormalColor;
        backgroundColor =
            light ? TDTheme.of(context).errorLightColor : Colors.transparent;
        break;
      case TDTagTheme.success:
        borderColor = TDTheme.of(context).successNormalColor;
        textColor = TDTheme.of(context).successNormalColor;
        backgroundColor =
            light ? TDTheme.of(context).successLightColor : Colors.transparent;
        break;
      case TDTagTheme.defaultTheme:
      default:
        borderColor = TDTheme.of(context).componentBorderColor;
        textColor = TDTheme.of(context).textColorPrimary;
        backgroundColor = light
            ? TDTheme.of(context).bgColorSecondaryContainer
            : Colors.transparent;
    }
    switch (shape) {
      case TDTagShape.square:
        borderRadius = BorderRadius.circular(TDTheme.of(context).radiusSmall);
        break;
      case TDTagShape.round:
        borderRadius = BorderRadius.circular(TDTheme.of(context).radiusRound);
        break;
      case TDTagShape.mark:
        borderRadius = BorderRadius.only(
            topRight: Radius.circular(TDTheme.of(context).radiusRound),
            bottomRight: Radius.circular(TDTheme.of(context).radiusRound));
        break;
    }
    border = 1;
    closeIconColor = textColor;
  }

  /// 根据主题生成禁用Tag样式
  TDTagStyle.generateDisableSelectStyle(
    BuildContext context,
    bool isLight,
    bool isOutline,
    TDTagShape shape,
  ) {
    borderColor = TDTheme.of(context).componentBorderColor;
    textColor = TDTheme.of(context).textDisabledColor;
    backgroundColor = isOutline && !isLight
        ? Colors.transparent
        : TDTheme.of(context).bgColorComponentDisabled;
    switch (shape) {
      case TDTagShape.square:
        borderRadius = BorderRadius.circular(TDTheme.of(context).radiusSmall);
        break;
      case TDTagShape.round:
        borderRadius = BorderRadius.circular(TDTheme.of(context).radiusRound);
        break;
      case TDTagShape.mark:
        borderRadius = BorderRadius.only(
            topRight: Radius.circular(TDTheme.of(context).radiusRound),
            bottomRight: Radius.circular(TDTheme.of(context).radiusRound));
        break;
    }
    border = isOutline ? 1 : 0;
  }
}

Text.font_loader:
import 'package:flutter/material.dart';
import 'package:flutter/services.dart';

import '../../../tdesign_flutter.dart';

/// 线上字体加载工具
class TDFontLoader {
  /// 缓存字体 FontLoader，防止重复加载
  static final _record = <String, bool>{};

  /// 加载字体资源
  static Future<bool> load({
    required String name,
    required String fontFamilyUrl,
  }) async {
    try {
      if (!(_record[name] ?? false)) {
        var fontLoader = FontLoader(name);

        fontLoader.addFont(Future(() async {
          var uri = Uri.parse(fontFamilyUrl);
          var bundle = NetworkAssetBundle(uri);
          return await bundle.load('');
        }));

        await fontLoader.load();
        _record[name] = true;
      }
      return true;
    } catch (e) {
      print(
          'TDFontLoader load error, name: ${name}, fontFamilyUrl: $fontFamilyUrl}, e: $e');
    }
    return false;
  }
}

/// 懒加载 FontWidget
class TDFontLoaderWidget extends StatefulWidget {
  const TDFontLoaderWidget({
    Key? key,
    required this.textWidget,
    required this.fontFamilyUrl,
  }) : super(key: key);

  final TDText textWidget;

  /// FontFamily 的下载地址
  final String fontFamilyUrl;

  @override
  State<TDFontLoaderWidget> createState() => _TDFontLoaderWidgetState();
}

class _TDFontLoaderWidgetState extends State<TDFontLoaderWidget> {
  bool fontFamilyLoaded = false;

  @override
  void initState() {
    super.initState();
    loadFont();
  }

  void loadFont() async {
    if ((widget.textWidget.fontFamily?.fontFamily.isNotEmpty ?? false) &&
        widget.fontFamilyUrl.isNotEmpty) {
      try {
        if (await TDFontLoader.load(
            name: widget.textWidget.fontFamily!.fontFamily,
            fontFamilyUrl: widget.fontFamilyUrl)) {
          setState(() {});
        }
      } catch (e) {
        print(
            'TDFontLoader loadFont error, data: ${widget.textWidget.data}, fontFamily: ${widget.textWidget.fontFamilyUrl}, e: $e');
      }
    }

    fontFamilyLoaded = true;
  }

  @override
  Widget build(BuildContext context) {
    final textWidget = widget.textWidget;

    return TDText(
      textWidget.data,
      font: textWidget.font,
      fontWeight: textWidget.fontWeight ?? FontWeight.w400,
      fontFamily: textWidget.fontFamily,
      textColor: textWidget.textColor,
      backgroundColor: textWidget.backgroundColor,
      isTextThrough: textWidget.isTextThrough,
      lineThroughColor: textWidget.lineThroughColor,
      package: textWidget.package,
      forceVerticalCenter: textWidget.forceVerticalCenter,
      style: textWidget.style,
      strutStyle: textWidget.strutStyle,
      textAlign: textWidget.textAlign,
      textDirection: textWidget.textDirection,
      locale: textWidget.locale,
      softWrap: textWidget.softWrap,
      overflow: textWidget.overflow,
      textScaleFactor: textWidget.textScaleFactor,
      maxLines: textWidget.maxLines,
      semanticsLabel: textWidget.semanticsLabel,
      textWidthBasis: textWidget.textWidthBasis,
      textHeightBehavior: textWidget.textHeightBehavior,
      isInFontLoader: true,
    );
  }
}

