import 'package:flutter/material.dart';
import 'package:flutter/services.dart';

import '../../theme/basic.dart';
import '../../theme/td_colors.dart';
import '../../theme/td_font_family.dart';
import '../../theme/td_fonts.dart';
import '../../theme/td_radius.dart';
import '../../theme/td_spacers.dart';
import '../../theme/td_theme.dart';
import '../input/input_view.dart';
import '../input/td_input.dart';
import '../text/td_text.dart';

enum TDTextareaLayout { vertical, horizontal }

/// 用于多行文本信息输入
class TDTextarea extends StatefulWidget {
  const TDTextarea(
      {Key? key,
      this.width,
      this.textStyle,
      this.backgroundColor,
      this.decoration,
      this.labelStyle,
      this.required,
      this.readOnly = false,
      this.autofocus = false,
      this.onEditingComplete,
      this.onSubmitted,
      this.hintText,
      this.inputType,
      this.onChanged,
      this.inputFormatters,
      this.inputDecoration,
      this.maxLines,
      this.minLines = 4,
      this.focusNode,
      this.controller,
      this.cursorColor,
      this.hintTextStyle,
      this.labelWidget,
      this.textInputBackgroundColor,
      this.size = TDInputSize.large,
      this.maxLength,
      this.maxLengthEnforcement,
      this.allowInputOverMax = false,
      this.additionInfo = '',
      this.additionInfoColor,
      this.textAlign,
      this.label,
      this.indicator = false,
      this.layout = TDTextareaLayout.horizontal,
      this.autosize,
      this.labelIcon,
      this.labelWidth,
      this.margin,
      this.padding,
      this.textareaDecoration,
      this.bordered,
      this.showBottomDivider = true})
      : super(key: key);

  /// 输入框宽度
  final double? width;

  /// 输入框背景色
  final Color? backgroundColor;

  /// 输入框样式(包括标签)
  final Decoration? decoration;

  /// 输入框样式(不包括标签)
  final Decoration? textareaDecoration;

  /// 输入框标题
  final String? label;

  /// 输入框标题图标
  final Widget? labelIcon;

  /// 输入框标题宽度
  final double? labelWidth;

  /// label组件，支持自定义
  final Widget? labelWidget;

  /// 是否必填标志（红色*）
  final bool? required;

  /// 是否只读
  final bool? readOnly;

  /// 提示文案
  final String? hintText;

  /// 键盘类型，数字、字母
  final TextInputType? inputType;

  /// 输入文本变化时回调
  final ValueChanged<String>? onChanged;

  /// 显示输入内容，如限制长度(LengthLimitingTextInputFormatter(6))
  final List<TextInputFormatter>? inputFormatters;

  /// controller 用户获取或者赋值输入内容
  final TextEditingController? controller;

  /// 最大输入行数
  final int? maxLines;

  /// 最小输入行数
  final int? minLines;

  /// 获取或者取消焦点使用
  final FocusNode? focusNode;

  /// 是否自动获取焦点
  final bool? autofocus;

  /// 点击键盘完成按钮时触发的回调
  final VoidCallback? onEditingComplete;

  /// 点击键盘完成按钮时触发的回调, 参数值为输入的内容
  final ValueChanged<String>? onSubmitted;

  /// 自定义输入框TextField组件样式
  final InputDecoration? inputDecoration;

  /// 文本颜色
  final TextStyle? textStyle;

  /// 提示文本颜色，默认为文本颜色
  final TextStyle? hintTextStyle;

  /// 文本框背景色
  final Color? textInputBackgroundColor;

  /// 游标颜色
  final Color? cursorColor;

  /// 输入框规格
  final TDInputSize? size;

  /// 最大字数限制
  final int? maxLength;

  /// 如何执行输入长度限制
  final MaxLengthEnforcement? maxLengthEnforcement;

  /// 超出[maxLength]之后是否还允许输入
  final bool? allowInputOverMax;

  /// 错误提示信息
  final String? additionInfo;

  /// 错误提示颜色
  final Color? additionInfoColor;

  /// 文字对齐方向
  final TextAlign? textAlign;

  /// 左侧标签文本样式
  final TextStyle? labelStyle;

  /// 否显示文本计数器，如 0/140（必须设置maxLength）
  final bool? indicator;

  /// 标题输入框布局方式。可选项：vertical/horizontal
  final TDTextareaLayout? layout;

  /// 是否自动增高，值为 true 时，[maxLines]不生效
  final bool? autosize;

  /// 外边距
  final EdgeInsetsGeometry? margin;

  /// 内边距
  final EdgeInsetsGeometry? padding;

  /// 是否显示外边框
  final bool? bordered;

  /// 边框外部下划线
  final bool? showBottomDivider;

  @override
  _TDTextareaState createState() => _TDTextareaState();
}

class _TDTextareaState extends State<TDTextarea> {
  final _hasFocus = ValueNotifier<bool>(false);
  late FocusNode _focusNode;

  @override
  void initState() {
    super.initState();
    _focusNode = widget.focusNode ?? FocusNode();
    _focusNode.addListener(_focusListener);
  }

  @override
  void dispose() {
    _focusNode.removeListener(_focusListener);
    if (widget.focusNode == null) {
      _focusNode.dispose();
    }
    super.dispose();
  }

  void _focusListener() {
    _hasFocus.value = _focusNode.hasFocus;
  }

  @override
  Widget build(BuildContext context) {
    var padding = _getInputPadding(context);
    var textareaView = _getTextareaView(
        context, _getInputView(context), _getIndicatorView(context));
    var container =
        _getContainer(context, _getLabelView(context), textareaView);
    if (widget.bordered == true || widget.decoration != null) {
      return container;
    }
    return Stack(
      children: [
        container,
        if (widget.showBottomDivider != null &&
            widget!.showBottomDivider == true)
          Positioned(
              bottom: 0,
              left: padding,
              right: 0,
              child: Divider(
                height: 0.5,
                color: TDTheme.of(context).componentStrokeColor,
              )),
      ],
    );
  }

  Widget _getLabelView(BuildContext context) {
    var padding = _getInputPadding(context);
    var isHorizontal = widget.layout == TDTextareaLayout.horizontal;
    var fontSize = isHorizontal
        ? TDTheme.of(context).fontBodyLarge?.size
        : TDTheme.of(context).fontBodyMedium?.size;
    if ((widget.label == null || widget.label == '') &&
        widget.labelIcon == null &&
        widget.labelWidget == null) {
      return const SizedBox.shrink();
    }
    return Container(
      width: widget.labelWidth,
      padding: isHorizontal
          ? EdgeInsets.only(right: padding)
          : EdgeInsets.only(bottom: TDTheme.of(context).spacer8),
      child: Row(
        mainAxisSize: MainAxisSize.min,
        children: [
          widget.labelIcon ?? const SizedBox.shrink(),
          widget.label != null && widget.label != ''
              ? Flexible(
                  child: Padding(
                    padding: EdgeInsets.only(
                        left: widget.labelIcon != null
                            ? TDTheme.of(context).spacer4
                            : 0),
                    child: TDText(
                      widget.label!,
                      maxLines: isHorizontal ? 2 : 1,
                      overflow: TextOverflow.ellipsis,
                      style: widget.labelStyle ?? TextStyle(fontSize: fontSize),
                    ),
                  ),
                )
              : const SizedBox.shrink(),
          widget.labelWidget ?? const SizedBox.shrink(),
          widget.required == true
              ? Padding(
                  padding: EdgeInsets.only(left: TDTheme.of(context).spacer4),
                  child: TDText(
                    '*',
                    style: TextStyle(
                        color: TDTheme.of(context).errorColor6,
                        fontSize: fontSize,
                        height: 1.3),
                  ),
                )
              : const SizedBox.shrink(),
        ],
      ),
    );
  }

  Widget _getInputView(BuildContext context) {
    return SingleChildScrollView(
      child: ConstrainedBox(
        constraints: const BoxConstraints(minHeight: 24), // 设置最小高度为24
        child: TDInputView(
          textStyle: widget.textStyle ??
              TextStyle(color: TDTheme.of(context).textColorPrimary),
          readOnly: widget.readOnly ?? false,
          autofocus: widget.autofocus ?? false,
          onEditingComplete: widget.onEditingComplete,
          onSubmitted: widget.onSubmitted,
          hintText: widget.hintText,
          inputType: widget.inputType,
          textAlign: widget.textAlign,
          onChanged: (val) {
            setState(() {});
            if (widget.onChanged != null) {
              widget.onChanged!(val);
            }
          },
          inputFormatters: [
            ...(widget.inputFormatters ?? []),
            ...(widget.maxLength != null && !(widget.allowInputOverMax ?? false)
                ? [
                    LengthLimitingTextInputFormatter(
                      widget.maxLength,
                      maxLengthEnforcement: widget.maxLengthEnforcement,
                    )
                  ]
                : [])
          ],
          inputDecoration: widget.inputDecoration,
          minLines: widget.minLines,
          maxLines: widget.autosize == true ? null : widget.maxLines,
          focusNode: _focusNode,
          isCollapsed: true,
          hintTextStyle: widget.hintTextStyle ??
              TextStyle(
                  color: widget.readOnly == true
                      ? TDTheme.of(context).textDisabledColor
                      : TDTheme.of(context).textColorPlaceholder),
          cursorColor: widget.cursorColor,
          textInputBackgroundColor: widget.textInputBackgroundColor,
          controller: widget.controller,
          contentPadding: EdgeInsets.zero,
        ),
      ),
    );
  }

  Widget _getIndicatorView(BuildContext context) {
    var padding = _getInputPadding(context);
    var showAdditionInfo =
        widget.additionInfo != '' && widget.additionInfo != null;
    var showIndicator = widget.indicator == true && widget.maxLength != null;
    var widgetList = <Widget>[];
    if (showAdditionInfo) {
      widgetList.add(
        Expanded(
          child: ValueListenableBuilder(
            valueListenable: _hasFocus,
            builder: (context, value, child) {
              return Opacity(
                opacity: value ? 0 : 1,
                child: TDText(
                  widget.additionInfo!,
                  style: TextStyle(
                    fontSize: TDTheme.of(context).fontBodySmall?.size,
                    color: widget.additionInfoColor ??
                        TDTheme.of(context).textColorPlaceholder,
                  ),
                ),
              );
            },
          ),
        ),
      );
    }
    if (showAdditionInfo && showIndicator) {
      widgetList.add(SizedBox(width: padding));
    }
    if (showIndicator) {
      widgetList.add(TDText(
        '${widget.controller?.text.length ?? 0}/${widget.maxLength}',
        style: TextStyle(
            fontSize: TDTheme.of(context).fontBodySmall?.size,
            color: TDTheme.of(context).textColorPlaceholder),
      ));
    }
    return Visibility(
      visible: showIndicator || showAdditionInfo,
      child: Row(
        mainAxisAlignment: MainAxisAlignment.end,
        crossAxisAlignment: CrossAxisAlignment.end,
        children: widgetList,
      ),
    );
  }

  Widget _getTextareaView(
      BuildContext context, Widget inputView, Widget indicatorView) {
    var padding = _getInputPadding(context);
    return Container(
      decoration: widget.textareaDecoration ??
          (widget.bordered == true
              ? BoxDecoration(
                  color: widget.decoration != null
                      ? null
                      : (widget.backgroundColor ??
                          TDTheme.of(context).bgColorContainer),
                  borderRadius:
                      BorderRadius.circular(TDTheme.of(context).radiusDefault),
                  border: Border.all(
                      color: TDTheme.of(context).componentBorderColor),
                )
              : null),
      padding: widget.bordered == true ? EdgeInsets.all(padding) : null,
      child: Column(
        children: [
          inputView,
          indicatorView,
        ],
      ),
    );
  }

  Widget _getContainer(
      BuildContext context, Widget labelView, Widget textareaView) {
    var padding = _getInputPadding(context);
    var isHorizontal = widget.layout == TDTextareaLayout.horizontal;
    return Container(
      width: widget.width,
      decoration: widget.decoration,
      padding: widget.padding ?? EdgeInsets.all(padding),
      margin: widget.margin,
      child: isHorizontal
          ? Row(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                labelView,
                Expanded(
                  child: textareaView,
                ),
              ],
            )
          : Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                labelView,
                textareaView,
              ],
            ),
    );
  }

  /// 获取输入框规格
  double _getInputPadding(BuildContext context) {
    switch (widget.size) {
      case TDInputSize.small:
        return TDTheme.of(context).spacer12;
      case TDInputSize.large:
        return TDTheme.of(context).spacer16;
      default:
        return TDTheme.of(context).spacer16;
    }
  }
}

TimeCounter.time_counter:
import 'dart:math';

import 'package:flutter/material.dart';
import 'package:flutter/scheduler.dart';
import '../../../tdesign_flutter.dart';
import '../../util/context_extension.dart';
import '../../util/list_ext.dart';

RegExp _timeReg = RegExp(r'D+|H+|m+|s+|S+');

String _toDigits(int n, int l) => n.toString().padLeft(l, '0');

String _getMark(String format, String? type) {
  var part = format.split(type ?? '')[1];
  if (part.isEmpty) {
    return '';
  }
  return part.split('')[0];
}

/// 计时组件
class TDTimeCounter extends StatefulWidget {
  const TDTimeCounter({
    Key? key,
    this.autoStart = true,
    this.content = 'default',
    this.format = 'HH:mm:ss',
    this.millisecond = false,
    this.size = TDTimeCounterSize.medium,
    this.splitWithUnit = false,
    this.theme = TDTimeCounterTheme.defaultTheme,
    required this.time,
    this.style,
    this.onChange,
    this.onFinish,
    this.direction = TDTimeCounterDirection.down,
    this.controller,
  }) : super(key: key);

  /// 是否自动开始倒计时
  final bool autoStart;

  /// 'default' / Widget Function(int time) / Widget
  final dynamic content;

  /// 时间格式，DD-日，HH-时，mm-分，ss-秒，SSS-毫秒（分隔符必须为长度为1的非空格的字符）
  final String format;

  /// 是否开启毫秒级渲染
  final bool millisecond;

  /// 尺寸
  final TDTimeCounterSize size;

  /// 使用时间单位分割
  final bool splitWithUnit;

  /// 风格
  final TDTimeCounterTheme theme;

  /// 必需；计时时长，单位毫秒
  final int time;

  /// 自定义样式，有则优先用它，没有则根据size和theme选取
  final TDTimeCounterStyle? style;

  /// 时间变化时触发回调
  final Function(int time)? onChange;

  /// 计时结束时触发回调
  final VoidCallback? onFinish;

  /// 计时方向，默认倒计时
  final TDTimeCounterDirection direction;

  /// 控制器，可控制开始/暂停/继续/重置
  final TDTimeCounterController? controller;

  @override
  _TDTimeCounterState createState() => _TDTimeCounterState();
}

class _TDTimeCounterState extends State<TDTimeCounter>
    with SingleTickerProviderStateMixin {
  late TDTimeCounterStyle _style;
  late Map<String, String> timeUnitMap;
  Ticker? _ticker;
  int _time = 0;
  int _tempMilliseconds = 0;
  int _maxTime = 0;

  @override
  void initState() {
    super.initState();
    resetTimer(widget.time, false);
    widget.controller?.addListener(_onControllerChanged);
  }

  @override
  void didChangeDependencies() {
    super.didChangeDependencies();
    _style = widget.style ??
        TDTimeCounterStyle.generateStyle(
          context,
          size: widget.size,
          theme: widget.theme,
          splitWithUnit: widget.splitWithUnit,
        );
    timeUnitMap = {
      'D': context.resource.days,
      'H': context.resource.hours,
      'm': context.resource.minutes,
      's': context.resource.seconds,
      'S': context.resource.milliseconds,
    };
  }

  @override
  void didUpdateWidget(TDTimeCounter oldWidget) {
    super.didUpdateWidget(oldWidget);
    if (widget.controller != oldWidget.controller) {
      oldWidget.controller?.removeListener(_onControllerChanged);
      widget.controller?.addListener(_onControllerChanged);
    }
    if (widget.time != oldWidget.time) {
      resetTimer(widget.time, false);
    }
  }

  @override
  void dispose() {
    _ticker?.dispose();
    widget.controller?.removeListener(_onControllerChanged);
    super.dispose();
  }

  /// 开始倒计时
  void startTimer() {
    if (_ticker?.isActive == true) {
      return;
    }
    _tempMilliseconds = 0;
    _ticker ??= createTicker((Duration elapsed) {
      if ((widget.direction == TDTimeCounterDirection.down && _time > 0) ||
          widget.direction == TDTimeCounterDirection.up && _time < _maxTime) {
        setState(() {
          if (widget.direction == TDTimeCounterDirection.down) {
            _time =
                max(_time - (elapsed.inMilliseconds - _tempMilliseconds), 0);
          } else {
            _time = min(
                _time + (elapsed.inMilliseconds - _tempMilliseconds), _maxTime);
          }
        });
        _tempMilliseconds = elapsed.inMilliseconds;
        widget.onChange?.call(_time);
      } else {
        pauseTimer();
        widget.onFinish?.call();
      }
      setState(() {});
    });
    _ticker!.start();
  }

  /// 暂停
  void pauseTimer() {
    _ticker?.stop();
  }

  /// 继续
  void resumeTimer() {
    startTimer();
  }

  /// 重置计时
  void resetTimer([int? time, bool update = true]) {
    _ticker?.stop();
    if (widget.direction == TDTimeCounterDirection.down) {
      _time = time ?? widget.time;
    } else {
      _time = 0;
      _maxTime = time ?? widget.time;
    }
    if (update) {
      setState(() {});
    }
    if (widget.autoStart) {
      WidgetsBinding.instance.addPostFrameCallback((timeStamp) {
        startTimer();
      });
    }
  }

  void _onControllerChanged() {
    switch (widget.controller?.value) {
      case TDTimeCounterStatus.start:
        startTimer();
        break;
      case TDTimeCounterStatus.pause:
        pauseTimer();
        break;
      case TDTimeCounterStatus.resume:
        resumeTimer();
        break;
      case TDTimeCounterStatus.reset:
        resetTimer(widget.controller?.time);
        break;
      default:
        break;
    }
  }

  @override
  Widget build(BuildContext context) {
    if (widget.content == 'default') {
      return Row(
          mainAxisSize: MainAxisSize.min, children: _buildTimeWidget(context));
    }
    if (widget.content is Function) {
      return widget.content(_time);
    }
    return widget.content;
  }

  List<Widget> _buildTimeWidget(BuildContext context) {
    final format = widget.millisecond
        ? '${widget.format.replaceAll(RegExp(r':S+$'), '')}:SSS'
        : widget.format;
    final matches = _timeReg.allMatches(format);
    final timeMap = _getTimeMap(matches.map((e) => e.group(0) ?? '').toList());
    return matches
        .map((match) {
          final timeType = match.group(0) ?? '';
          return _buildTextWidget(
            timeMap[timeType] ?? '0',
            widget.splitWithUnit
                ? timeUnitMap[timeType[0]] ?? ''
                : _getMark(format, timeType),
          );
        })
        .expand((element) => element)
        .toList();
  }

  List<Widget> _buildTextWidget(
    String time,
    String split,
  ) {
    final children = <Widget>[
      Container(
        width: _style.timeWidth,
        height: _style.timeHeight,
        padding: _style.timePadding,
        margin: _style.timeMargin,
        decoration: _style.timeBox,
        child: Center(
          child: TDText(
            time,
            style: TextStyle(
              fontFamily: _style.timeFontFamily?.fontFamily,
              package: _style.timeFontFamily?.package,
              fontSize: _style.timeFontSize,
              height: _style.timeFontHeight,
              fontWeight: _style.timeFontWeight,
              color: _style.timeColor,
            ),
          ),
        ),
      ),
    ];
    if (split.isNotEmpty) {
      children.addAll([
        SizedBox(width: _style.space),
        TDText(
          split,
          style: TextStyle(
            fontSize: _style.splitFontSize,
            height: _style.splitFontHeight,
            fontWeight: _style.splitFontWeight,
            color: _style.splitColor,
          ),
        ),
        SizedBox(width: _style.space),
      ]);
    }
    return children;
  }

  Map<String, String> _getTimeMap(List<String> timeType) {
    var duration = Duration(milliseconds: _time);
    final map = <String, String>{};
    final dayKey = timeType.find((item) => item.startsWith('D'));
    final hourKey = timeType.find((item) => item.startsWith('H'));
    final minuteKey = timeType.find((item) => item.startsWith('m'));
    final secondKey = timeType.find((item) => item.startsWith('s'));
    final millisecondKey = timeType.find((item) => item.startsWith('S'));
    if (dayKey != null) {
      final length = dayKey.length;
      map[dayKey] = _toDigits(duration.inDays, length);
      duration = duration - Duration(days: duration.inDays);
    }
    if (hourKey != null) {
      final length = hourKey.length;
      final upNum = length > 2 ? pow(10, length).toInt() : 24;
      final time = duration.inHours.remainder(upNum);
      map[hourKey] = _toDigits(time, length);
      duration = duration - Duration(hours: time);
    }
    if (minuteKey != null) {
      final length = minuteKey.length;
      final upNum = length > 2 ? pow(10, length).toInt() : 60;
      final time = duration.inMinutes.remainder(upNum);
      map[minuteKey] = _toDigits(time, length);
      duration = duration - Duration(minutes: time);
    }
    if (secondKey != null) {
      final length = secondKey.length;
      final upNum = length > 2 ? pow(10, length).toInt() : 60;
      final time = duration.inSeconds.remainder(upNum);
      map[secondKey] = _toDigits(time, length);
      duration = duration - Duration(seconds: time);
    }
    if (millisecondKey != null) {
      final length = millisecondKey.length;
      map[millisecondKey] = _toDigits(duration.inMilliseconds, length);
    }
    return map;
  }
}

TimeCounter.time_counter_controller:
import 'package:flutter/cupertino.dart';

/// 计时组件控制器转态
enum TDTimeCounterStatus {
  /// 开始
  start,

  /// 暂停
  pause,

  /// 继续
  resume,

  /// 重置
  reset,

  /// 空，默认值
  idle,
}

/// 倒计时组件控制器，可控制开始(`start()`)/暂停(`pause()`)/继续(`resume()`)/重置(`reset([int? time])`)
class TDTimeCounterController extends ValueNotifier<TDTimeCounterStatus> {
  TDTimeCounterController() : super(TDTimeCounterStatus.idle);

  int? _time;

  int? get time => _time;

  /// 开始
  void start() {
    value = TDTimeCounterStatus.start;
  }

  /// 暂停
  void pause() {
    value = TDTimeCounterStatus.pause;
  }

  /// 继续
  void resume() {
    value = TDTimeCounterStatus.resume;
  }

  /// 重置
  void reset([int? time]) {
    if (value == TDTimeCounterStatus.reset) {
       _time = time;
      notifyListeners();
    } else {
      _time = time;
      value = TDTimeCounterStatus.reset;
    }
  }
}

TimeCounter.time_counter_style:
import 'package:flutter/material.dart';
import '../../../tdesign_flutter.dart';

/// 计时组件计时方向
enum TDTimeCounterDirection {
  /// 倒计时
  down,

  /// 正向计时
  up
}

/// 计时组件尺寸
enum TDTimeCounterSize {
  /// 小
  small,

  /// 中等
  medium,

  /// 大
  large,
}

/// 计时组件风格
enum TDTimeCounterTheme {
  /// 默认
  defaultTheme,

  /// 圆形
  round,

  /// 方形
  square,
}

/// 计时组件样式
class TDTimeCounterStyle {
  TDTimeCounterStyle({
    this.timeWidth,
    this.timeHeight,
    this.timePadding,
    this.timeMargin,
    this.timeBox,
    this.timeFontFamily,
    this.timeFontSize,
    this.timeFontHeight,
    this.timeFontWeight,
    this.timeColor,
    this.splitFontSize,
    this.splitFontHeight,
    this.splitFontWeight,
    this.splitColor,
    this.space,
  });

  /// 时间容器宽度
  double? timeWidth;

  /// 时间容器高度
  double? timeHeight;

  /// 时间容器内边距
  EdgeInsets? timePadding;

  /// 时间容器外边距
  EdgeInsets? timeMargin;

  /// 时间容器装饰
  BoxDecoration? timeBox;

  /// 时间字体
  FontFamily? timeFontFamily;

  /// 时间字体尺寸
  double? timeFontSize;

  /// 时间字体行高
  double? timeFontHeight;

  /// 时间字体粗细
  FontWeight? timeFontWeight;

  /// 时间字体颜色
  Color? timeColor;

  /// 分隔符字体尺寸
  double? splitFontSize;

  /// 分隔符字体行高
  double? splitFontHeight;

  /// 分隔符字体粗细
  FontWeight? splitFontWeight;

  /// 分隔符字体颜色
  Color? splitColor;

  /// 时间与分隔符的间隔
  double? space;

  /// 生成默认样式
  TDTimeCounterStyle.generateStyle(
    BuildContext context, {
    TDTimeCounterSize? size,
    TDTimeCounterTheme? theme,
    bool? splitWithUnit,
  }) {
    timeFontFamily = TDTheme.defaultData().numberFontFamily;
    late Font? font;
    switch (size ?? TDTimeCounterSize.medium) {
      case TDTimeCounterSize.small:
        if (theme == TDTimeCounterTheme.defaultTheme) {
          timeWidth = timeHeight = null;
          font = TDTheme.of(context).fontBodyMedium;
          timeFontSize = splitFontSize = font?.size ?? 14;
          timeFontHeight =
              splitFontHeight = font?.height ?? (22 / timeFontSize!);
        } else {
          timeWidth = timeHeight = 20;
          font = TDTheme.of(context).fontBodySmall;
          timeFontSize = splitFontSize = font?.size ?? 12;
          timeFontHeight = splitFontHeight = null;
        }
        space = TDTheme.of(context).spacer4 / 2;
        break;
      case TDTimeCounterSize.medium:
        if (theme == TDTimeCounterTheme.defaultTheme) {
          timeWidth = timeHeight = null;
          font = TDTheme.of(context).fontBodyLarge;
          timeFontSize = splitFontSize = font?.size ?? 16;
          timeFontHeight =
              splitFontHeight = font?.height ?? (24 / timeFontSize!);
        } else {
          timeWidth = timeHeight = 24;
          font = TDTheme.of(context).fontBodyMedium;
          timeFontSize = splitFontSize = font?.size ?? 14;
          timeFontHeight = splitFontHeight = null;
        }
        space = TDTheme.of(context).spacer8 / 2;
        break;
      case TDTimeCounterSize.large:
        if (theme == TDTimeCounterTheme.defaultTheme) {
          timeWidth = timeHeight = null;
          font = TDTheme.of(context).fontBodyExtraLarge;
          timeFontSize = splitFontSize = font?.size ?? 18;
          timeFontHeight =
              splitFontHeight = font?.height ?? (26 / timeFontSize!);
        } else {
          timeWidth = timeHeight = 28;
          font = TDTheme.of(context).fontBodyLarge;
          timeFontSize = splitFontSize = font?.size ?? 16;
          timeFontHeight = splitFontHeight = null;
        }
        space = TDTheme.of(context).spacer12 / 2;
    }

    switch (theme ?? TDTimeCounterTheme.defaultTheme) {
      case TDTimeCounterTheme.round:
        timeBox = BoxDecoration(
          shape: BoxShape.circle,
          color: TDTheme.of(context).errorNormalColor,
        );
        timeColor = TDTheme.of(context).textColorAnti;
        splitColor = TDTheme.of(context).errorNormalColor;
        break;
      case TDTimeCounterTheme.square:
        timeBox = BoxDecoration(
          shape: BoxShape.rectangle,
          borderRadius: BorderRadius.circular(TDTheme.of(context).radiusSmall),
          color: TDTheme.of(context).errorNormalColor,
        );
        timeColor = TDTheme.of(context).textColorAnti;
        splitColor = TDTheme.of(context).errorNormalColor;
        break;
      case TDTimeCounterTheme.defaultTheme:
        timeBox = null;
        timeColor = splitColor = TDTheme.of(context).textColorPrimary;
        timeWidth = null;
        timeHeight = null;
    }

    if (splitWithUnit ?? false) {
      splitColor = TDTheme.of(context).textColorPrimary;
    }
  }
}

Toast.toast:
import 'dart:async';
import 'package:flutter/cupertino.dart';
import 'package:flutter/material.dart';
import '../../../tdesign_flutter.dart';
import '../../util/auto_size.dart';
import '../../util/context_extension.dart';

enum IconTextDirection {
  /// 横向
  horizontal,

  /// 竖向
  vertical
}

/// Toast配置类，支持独立样式定制
class TDToastConfig {
  final Color? backgroundColor;
  final TextStyle? textStyle;
  final double? iconSize;
  final Color? iconColor;
  final Duration duration;
  final bool preventTap;

  const TDToastConfig({
    this.backgroundColor,
    this.textStyle,
    this.iconSize,
    this.iconColor,
    this.duration = const Duration(milliseconds: 3000),
    this.preventTap = false,
  });
}

/// Toast实例管理类
class _ToastInstance {
  final OverlayEntry overlayEntry;
  final Timer? timer;
  final Timer? disposeTimer;
  bool showing = true;

  _ToastInstance({
    required this.overlayEntry,
    this.timer,
    this.disposeTimer,
  });

  void cancel() {
    timer?.cancel();
    disposeTimer?.cancel();
    overlayEntry.remove();
    showing = false;
  }
}

/// 改进的Toast组件，支持多个实例和独立样式
class TDToast {
  static final Map<String, _ToastInstance> _toastInstances = {};
  static int _instanceCounter = 0;

  /// 生成唯一的Toast ID
  static String _generateToastId() {
    return 'toast_${_instanceCounter++}';
  }

  /// 普通文本Toast
  static String showText(
    String? text, {
    required BuildContext context,
    Duration duration = const Duration(milliseconds: 3000),
    int? maxLines,
    BoxConstraints? constraints,
    bool? preventTap,
    Widget? customWidget,
    Color? backgroundColor,
    TextStyle? textStyle,
    String? toastId,
  }) {
    final id = toastId ?? _generateToastId();
    _showOverlay(
      _TDTextToast(
        text: text,
        maxLines: maxLines,
        constraints: constraints,
        customWidget: customWidget,
        config: TDToastConfig(
          backgroundColor: backgroundColor,
          textStyle: textStyle,
          duration: duration,
          preventTap: preventTap ?? false,
        ),
      ),
      context: context,
      duration: duration,
      preventTap: preventTap,
      toastId: id,
    );
    return id;
  }

  /// 带图标的Toast
  static String showIconText(
    String? text, {
    IconData? icon,
    IconTextDirection direction = IconTextDirection.horizontal,
    required BuildContext context,
    Duration duration = const Duration(milliseconds: 3000),
    bool? preventTap,
    Color? backgroundColor,
    int? maxLines,
    TextStyle? textStyle,
    double? iconSize,
    Color? iconColor,
    String? toastId,
  }) {
    final id = toastId ?? _generateToastId();
    _showOverlay(
      _TDIconTextToast(
        text: text,
        iconData: icon,
        iconTextDirection: direction,
        maxLines: maxLines,
        config: TDToastConfig(
          backgroundColor: backgroundColor,
          textStyle: textStyle,
          iconSize: iconSize,
          iconColor: iconColor,
          duration: duration,
          preventTap: preventTap ?? false,
        ),
      ),
      context: context,
      duration: duration,
      preventTap: preventTap,
      toastId: id,
    );
    return id;
  }

  /// 成功提示Toast
  static String showSuccess(
    String? text, {
    IconTextDirection direction = IconTextDirection.horizontal,
    required BuildContext context,
    Duration duration = const Duration(milliseconds: 3000),
    bool? preventTap,
    Color? backgroundColor,
    int? maxLines,
    TextStyle? textStyle,
    double? iconSize,
    Color? iconColor,
    String? toastId,
  }) {
    return showIconText(
      text,
      icon: TDIcons.check_circle,
      direction: direction,
      context: context,
      duration: duration,
      preventTap: preventTap,
      backgroundColor: backgroundColor,
      maxLines: maxLines,
      textStyle: textStyle,
      iconSize: iconSize,
      iconColor: iconColor,
      toastId: toastId,
    );
  }

  /// 警告Toast
  static String showWarning(
    String? text, {
    IconTextDirection direction = IconTextDirection.horizontal,
    required BuildContext context,
    Duration duration = const Duration(milliseconds: 3000),
    bool? preventTap,
    Color? backgroundColor,
    int? maxLines,
    TextStyle? textStyle,
    double? iconSize,
    Color? iconColor,
    String? toastId,
  }) {
    return showIconText(
      text,
      icon: TDIcons.error_circle,
      direction: direction,
      context: context,
      duration: duration,
      preventTap: preventTap,
      backgroundColor: backgroundColor,
      maxLines: maxLines,
      textStyle: textStyle,
      iconSize: iconSize,
      iconColor: iconColor,
      toastId: toastId,
    );
  }

  /// 失败提示Toast
  static String showFail(
    String? text, {
    IconTextDirection direction = IconTextDirection.horizontal,
    required BuildContext context,
    Duration duration = const Duration(milliseconds: 3000),
    bool? preventTap,
    Color? backgroundColor,
    int? maxLines,
    TextStyle? textStyle,
    double? iconSize,
    Color? iconColor,
    String? toastId,
  }) {
    return showIconText(
      text,
      icon: TDIcons.close_circle,
      direction: direction,
      context: context,
      duration: duration,
      preventTap: preventTap,
      backgroundColor: backgroundColor,
      maxLines: maxLines,
      textStyle: textStyle,
      iconSize: iconSize,
      iconColor: iconColor,
      toastId: toastId,
    );
  }

  /// 带文案的加载Toast
  static String showLoading({
    required BuildContext context,
    String? text,
    Duration duration = const Duration(seconds: 99999999),
    bool? preventTap,
    Widget? customWidget,
    Color? backgroundColor,
    TextStyle? textStyle,
    double? iconSize,
    Color? iconColor,
    String? toastId,
  }) {
    final id = toastId ?? _generateToastId();
    _showOverlay(
      _TDToastLoading(
        text: text,
        customWidget: customWidget,
        config: TDToastConfig(
          backgroundColor: backgroundColor,
          textStyle: textStyle,
          iconSize: iconSize,
          iconColor: iconColor,
          duration: duration,
          preventTap: preventTap ?? false,
        ),
      ),
      context: context,
      duration: duration,
      preventTap: preventTap,
      toastId: id,
    );
    return id;
  }

  /// 不带文案的加载Toast
  static String showLoadingWithoutText({
    required BuildContext context,
    Duration duration = const Duration(seconds: 99999999),
    bool? preventTap,
    Color? backgroundColor,
    double? iconSize,
    Color? iconColor,
    String? toastId,
  }) {
    final id = toastId ?? _generateToastId();
    _showOverlay(
      _TDToastLoadingWithoutText(
        config: TDToastConfig(
          backgroundColor: backgroundColor,
          iconSize: iconSize,
          iconColor: iconColor,
          duration: duration,
          preventTap: preventTap ?? false,
        ),
      ),
      context: context,
      duration: duration,
      preventTap: preventTap,
      toastId: id,
    );
    return id;
  }

  /// 关闭指定的Toast
  static void dismissToast(String toastId) {
    final instance = _toastInstances[toastId];
    if (instance != null) {
      instance.cancel();
      _toastInstances.remove(toastId);
    }
  }

  /// 关闭所有Toast
  static void dismissAll() {
    for (final instance in _toastInstances.values) {
      instance.cancel();
    }
    _toastInstances.clear();
  }

  /// 关闭加载Toast（向后兼容）
  static void dismissLoading() {
    // 关闭所有类型为loading的Toast
    final loadingIds = _toastInstances.entries
        .where((entry) => entry.key.startsWith('toast_'))
        .map((entry) => entry.key)
        .toList();

    for (final id in loadingIds) {
      dismissToast(id);
    }
  }

  static void _showOverlay(
    Widget? widget, {
    required BuildContext context,
    Duration duration = const Duration(milliseconds: 3000),
    bool? preventTap,
    required String toastId,
  }) {
    // 不自动关闭之前的Toast，支持多个Toast同时显示
    final overlayState = Overlay.of(context);

    OverlayEntry overlayEntry;
    if (preventTap ?? false) {
      overlayEntry = OverlayEntry(
        builder: (BuildContext context) => Positioned(
          top: 0,
          right: 0,
          bottom: 0,
          left: 0,
          child: Container(
            color: Colors.transparent,
            child: Align(
              alignment: Alignment.center,
              child: widget,
            ),
          ),
        ),
      );
    } else {
      overlayEntry = OverlayEntry(
        builder: (BuildContext context) => Center(
          child: widget,
        ),
      );
    }

    overlayState?.insert(overlayEntry);

    Timer? timer;
    Timer? disposeTimer;

    if (duration != const Duration(seconds: 99999999)) {
      timer = Timer(duration, () {
        final instance = _toastInstances[toastId];
        if (instance != null && instance.showing) {
          instance.showing = false;
          overlayEntry.markNeedsBuild();

          disposeTimer = Timer(const Duration(milliseconds: 200), () {
            overlayEntry.remove();
            _toastInstances.remove(toastId);
          });
        }
      });
    }

    _toastInstances[toastId] = _ToastInstance(
      overlayEntry: overlayEntry,
      timer: timer,
      disposeTimer: disposeTimer,
    );
  }
}

class _TDIconTextToast extends StatelessWidget {
